INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56644', '2733967', '가회동성당', 'http://tong.visitkorea.or.kr/cms/resource/09/3303909_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/09/3303909_image3_1.jpg', '6', '37.5820858828', '126.9846616856', '', '서울특별시 종로구 북촌로 57 (가회동)', '', '<a href=''https://gahoe.or.kr'' target=''_blank'' title=''새창 : 가회동성당 사이트로 이동''>https://gahoe.or.kr</a>', '가회동성당이 위치한 북촌 일대는 최초의 선교사 주문모(周文謨, 야고보) 신부가 조선에 밀입국하여 1795년 4월 5일 부활 대축일에 최인길(崔仁吉, 마티아)의 집에서 조선 땅에서의 ‘첫 미사’를 집전한 지역이다. 본당 관할구역은 주문모 신부가 강완숙(姜完淑, 골롬바)의 집에 숨어 지내면서 사목활동을 펼쳤던 지역으로서 한국 교회사에서 매우 중요한 의미가 있다.정식으로 본당이 된 것은 1949년이고, 이후 1954년에 성전이 완공되었다. 하지만 성전이 낡아 2011년부터 옛 성전을 허물고 현재의 새 성전을 짓게 되었다. 2013년 11월 21일 준공되었고, 준공 3일 후인 24일(그리스도 왕 대축일)에 입주하여 입주 미사를 봉헌하였다. 현재의 동서양 건축양식이 어우러진 새 성전은 과거의 역사를 되살리고자 2014년 4월 20일 부활 대축일에 서울교구장 염수정 추기경님에 의하여 축성되었다.(출처 : 가회동성당 홈페이지)', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56645', '2763807', '간데메공원', '', '', '6', '37.5728353382', '127.0490783005', '', '서울특별시 동대문구 서울시립대로2길 59 (답십리동)', '', '', '간데메공원은 서울 동대문구 답십리 천변에 위치한 근린공원이다. 어린이 놀이터와 연못, 팔각정 정자, 장미 넝쿨 터널, 소나무 쉼터, 맨발산책로까지 작지만 아기자기하게 조성된 공원이다. 농구장과 배드민턴장도 있다. 전매청 자리를 공원으로 조성하면서 옛날 답십리 일대 간데메(中山) 자연 부락 마을의 토박이 이름을 따서 간데메공원이라 이름을 정했다고 한다.', '1', '11', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56646', '1116925', '갈산근린공원', 'http://tong.visitkorea.or.kr/cms/resource/90/3467490_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/90/3467490_image3_1.jpg', '6', '37.5061176314', '126.8684105358', '', '서울특별시 양천구 신정동 162-56', '', '<a href=''http://www.yangcheon.go.kr/'' target=''_blank'' title=''새창: 양천구 ''>http://www.yangcheon.go.kr/</a>', '대체로 산세가 둥근 양천구의 다른 산들과는 달리 갈산은 동쪽 부분이 벼랑처럼 깎인 절경을 뽐낸다. 안양천의 오랜 침식작용으로 산 정상이 칼날처럼 변한 것으로 ‘칼산’이라는 이름으로 불리다가 ‘갈산’으로 순화되었다. 갈산 입구에는 어린이 교통공원이 들어서 있고, 중턱에는 중심광장과 산책로 등의 쉼터 그리고 정상에는 전망대가 있어 양천을 한눈에 감상할 수 있다. 갈산 정상에는 ‘갈산정’과 함께 그 옆으로 우리나라 최초의 ‘대삼각본점’이 설치되어 있다. 이 삼각점은 모든 측량의 기준이 되는 중요한 국가시설물로 1910년 6월 우리나라 최초로 토지조사 사업을 위해 설치한 측량기준점으로 서울시 미래유산으로 지정된 양천구의 중요한 문화유산이다.', '1', '19', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56647', '294439', '감로암(서울)', 'http://tong.visitkorea.or.kr/cms/resource/22/3481922_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/22/3481922_image3_1.jpg', '6', '37.5753815016', '127.0066515383', '', '서울특별시 종로구 율곡로23길 16 (충신동)', '', '', '동대문에서 대학로로 들어가는 율곡로 대로변에 자그마한 사찰이 있다. 그 이름도 특이하게 목마른 중생에게 구제의 감로를 준다는 ‘감로암’이다. 도심 속 삭막한 빌딩 숲 사이에 아직 개발되지 않은 주택가에 정말 도심에서 보기 힘든 자그마한 암자가 있다. 감로암의 역사는 벌써 100여 년이 흘렀다. 대로변에서 주택가로 나 있는 골목길을 따라 들어서면 보이는 곳에 여느 주택과 별반 차이 없이 건물을 지어 불상을 봉안하고 예배하고 있다. 하지만 여법하게 산문을 만들고 단청을 하며, 기와를 엮어 고풍스러운 가람의 멋을 느끼게 한다. 예부터 관음영험도량으로 알려진 감로암은 동네 어르신들, 특히 할머니들의 다담방, 기도처로 알려져 있다. 감로암은 사찰의 이름처럼 누군가 구제받을 수 있고, 고통에서 벗어날 수 있는 감로가 있는 곳이다. 지금은 마시지 못하지만 보타전 아래에는 여전히 그 옛날 감로의 맛을 느끼게 했던 감로가 흐르고 있다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56648', '264570', '강남', 'http://tong.visitkorea.or.kr/cms/resource/08/1984608_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/08/1984608_image3_1.jpg', '6', '37.4970465429', '127.0281573537', '', '서울특별시 강남구 역삼동', '(역삼동)', '<a href=''http://www.gangnam.go.kr/main.do'' target=''_blank'' title=''새창:  강남구청 ''>http://www.gangnam.go.kr/main.do</a>', '강남역 주변은 강남 지역에 있어서 교통의 핵심인 곳이다. 강남대로를 중심으로 동쪽으로는 테헤란로가 뻗어 있어 잠실 방향과 이어지며, 서쪽으로는 사당 방면으로 연결된다. 교보타워 사거리에서 좌측으로는 고속터미널과 이어져 노량진 및 서부 서울로 연결되며, 그대로 북쪽으로는 한남대교를 거쳐 강북으로 연결된다. 그리고 남쪽으로는 양재를 거쳐 분당 등의 신도시와 성남시 등 수도권으로 연결된다. 많은 유동인구가 강남역으로 모이는 이유는 강남역 부근이 만남을 위한 약속장소로 적합하기 때문이다. 각종 모임이나 행사 또는 개인적인 만남을 갖기에 좋은 지역적, 상권적 특성을 지니고 있다. 따라서 평일에는 주로 저녁 시간대, 주말의 경우는 낮부터 강남역 10, 11번 출구, 롯데시네마 앞(구, 시티극장), 지오다노 매장 앞 등에서 많은 사람들로 붐비는 것을 흔히 볼 수 있다.편리한 교통과 다수의 패션 관련 매장, 극장, 대형서점 등 소비를 하기 좋은 이러한 환경이 거리가 먼 지역의 소비자들까지 강남역으로 모이게 하고 있다. 강남구에서는 내ㆍ외국인 관광객에게 종합관광서비스는 물론, 강남구의 우수 인프라인 의료와 한류에 대하여 보고, 느끼고, 즐길 수 있는 체험형 관광거점시설인 강남관광정보센터를 건립ㆍ운영하고 있으며, 강남관광정보센터는 서울과 강남의 주요 관광명소를 안내하고 관광객 편의시설을 갖춘 ‘종합관광안내센터’와 강남구 특화 병원과 진료 프로그램을 소개하고 체험할 수 있는 ‘메디컬투어센터’, 한류 스타를 활용하여 다양한 한류체험이 가능한 ‘한류체험관’으로 구성되어 있다.', '1', '1', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56649', '2456536', '강남 마이스 관광특구', 'http://tong.visitkorea.or.kr/cms/resource/13/3464913_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/13/3464913_image3_1.jpg', '6', '37.5118092746', '127.0591318945', '', '서울특별시 강남구 영동대로 513', '(삼성동)', '', '일찍이 무역센터가 자리 잡으며 국제 교류의 메카로 손꼽혀온 강남 삼성동 일대는, 세계에서 모여든 최첨단 비즈니스맨에게 한국을 알리는 창구 역할을 해왔다. 여기에 1990년대부터 강남 특유의 젊은 문화가 유입되면서 무역센터 일대는 비즈니스의 장이자 젊은 문화의 랜드마크라는 복합적인 성격을 띠며 발전했다. 2014년 싸이의 ‘강남스타일’ 열풍과 더불어 국제교류 비즈니스 산업과 한류문화의 공생을 강화하기 위해 이 일대가 강남 마이스 관광특구로 지정되었다. 강남 마이스 관광특구는 전시와 컨벤션, 세미나, 국제 전시회, 콘퍼런스 등이 이어지는 코엑스를 중심으로 자리 잡고 있고, 백화점, 호텔, 면세점, 도심공항터미널이 유기적으로 모여 있어, 국내뿐만이 아니라 외국인 관광객이 끊이지 않는다. 강남 마이스 관광특구 일대는 한국판 타임스퀘어로 조성되어 최첨단 디스플레이를 활용해 홀로그램과 증강현실을 체험하고, 건물 외벽에 설치된 초대형 LED 전광판에서 표출되는 화려한 미디어아트와 광고를 감상할 수 있다.', '1', '1', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56650', '127377', '강동예찬시비', '', '', '6', '37.5426873535', '127.120700414', '', '서울특별시 강동구 천호동', '', '<a href=''https://www.gangdong.go.kr/web/culture/contents/gdc020_050_010'' target=''_blank'' title=''새창 : 강동문화포털 홈페이지로 이동''>https://www.gangdong.go.kr</a>', '강동예찬시비는 서울 정도 600년을 기념하기 위하여, 조선 초기의 서거정이 지은 해 질 무렵의 한강변과 강동지역의 아름다운 전원충경을 노래한 시를 옛 광나루였던 광진교 입구에 한강의 쌍돛배의 모습으로 건립한 것이다. 일찍이 6,000년 전에 선사시대의 조상들이 살았으며 삼국시대에 국가의 요충지로 중요시되었던 광나루는, 조선시대에 한강의 5대 나루터의 하나로 번성하였으며 한양에서 한양 뱃길을 건너 구천면 길을 지나는 왕의 능 참배길로도 이용되었다. 이처럼 광나루는 역사적 의미가 클 뿐만 아니라 주변 경관이 수려하여 많은 시민, 묵객 등이 즐겨 찾던 곳이다. 따라서 강동예찬시비는 한강을 조망하는 훌륭한 쉼터이자 지방자치시대를 맞아 지역주민들이 고장에 대한 자긍심과 애향심을 간직할 수 있는 한강변의 문화명소이다.', '1', '2', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56651', '128961', '강변스파랜드', 'http://tong.visitkorea.or.kr/cms/resource/98/3401698_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/98/3401698_image3_1.JPG', '6', '37.5348266305', '127.0916116181', '', '서울특별시 광진구 구의강변로 45', '(구의동)', '<a href=''http://ispaland.co.kr/''target=''_blank'' title=''새창:강변스파랜드''>http://ispaland.co.kr/</a>', '강변스파랜드는 지하 1·2층, 9917.4m²(3,000평) 규모의 사우나를 즐길 수 있는 대규모 스파 시설이다. 재래식 불한증막과 찜질방, 사우나 등의 시설 이용과 함께 한 차원 높은 문화공간 체험이 가능한 곳이다. 지하 2층은 사우나, 지하 1층은 공용 찜질방이다. 세련된 인테리어와 유황 성분이 함유된 지하수 사우나 물은 매끄럽고 피부에도 좋다고 한다. 재래식 소나무장작불한증막, 넓은 공용 홀을 비롯하여 숯방, 소금방, 은피라미드방, 여성전용황토불가마, 황토방, 이글루체험방 등 다양한 찜질방과 온탕, 냉탕, 아쿠아워킹·마사지탕, 습식사우나, 건식사우나 등 각종 사우나시설을 즐길 수 있다. 부대시설로는 모임방, 어린이놀이방, 당구장, 탁구장, 대형게임장, 충전카페, 피부관리실이 있으며 한식당과 치킨·스낵코너에서 식사와 간단한 요깃거리를 해결할 수 있다.  강변스파랜드는 수도권 지하철 2호선 강변역 4번 출구에서 약 340m 정도 떨어진 위치에 있다.', '1', '6', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56652', '809490', '강서습지생태공원', '', '', '6', '37.5860879769', '126.8171490732', '', '서울특별시 강서구 양천로27길 279-23', '(방화동)', '▪ <a href=''https://hangang.seoul.go.kr/www/contents/675.do?mid=482'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://hangang.seoul.go.kr</a>', '방화대교 남쪽 끝에서 행주대교 남쪽 끝 사이 한강 둔치에 있는 생태공원이다. 서울시 생태관광명소 30개소 중 하나로 선정된 강서습지생태공원은 담수지·저습지 등을 조성하고 습생·수생식물을 심은 습지 공원으로 2002년 7월에 개원되었다. 물억새 등 습지식물을 식재하여 새들이 휴식을 취할 수 있도록 하였으며 하중도, 자연관찰로, 습초지 등 습지 생태계를 복원하여 하천의 자연정화 기능을 극대화하고 있다. 습지를 가로질러 두 곳의 탐방로와 조류 조망대 등이 있는데 조류전망대에서는 행주대교와 방화대교, 한강을 한눈에 볼 수 있다. 또한 방화대교 하부, 가족 피크닉장 앞, 행주대교 하부에는 체력단련기구 등을 갖춰 시민의 여가 활동과 스포츠 공간으로의 기능을 하고 있다. 한강을 따라 자전거길과 산책로가 조성되어 있어 다양한 방법으로 강서습지생태공원을 즐길 수 있다.강서습지생태공원은 생태학습프로그램도 운영하고 있으므로 아이들과 함께 습지생태공원의 프로그램을 참여하여 자연을 공부해 보는 것도 좋다. 생태학습프로그램 참여는 [서울시 공공서비스예약사이트]접속>교육강좌>자연과학>[공원명]으로 검색>프로그램 예약신청 을 하면 된다.', '1', '4', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56653', '3043735', '강서역사문화거리', 'http://tong.visitkorea.or.kr/cms/resource/09/3045109_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/09/3045109_image3_1.jpg', '6', '37.5722343802', '126.8366643489', '', '서울특별시 강서구 양천로 291 (마곡동)', '', '<a href=''https://www.gangseo.seoul.kr/munhwa/mh020301'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://www.gangseo.seoul.kr</a>', '강서역사문화거리는 다양한 역사 문화 유적지가 산재한 가양동 일대에 위치한 곳으로, 가양동 일대의 낙후된 경관을 개선해 역사와 문화의 향취가 가득한 거리로 탈바꿈한 것이다. 가양동 궁산 일대는 조선시대 양천현아가 있었던 행정의 중심지인 까닭에 많은 역사 문화유산이 곳곳에 남아있다. 9호선 양천향교역 1번 출구를 나와 마곡지구에서 이어지는 양천로 47길 초입에 설치된 [강서역사문화거리] 임을 알리는 상징 조형물이 세워져 있다. 기둥 형태의 안내판에는 유적지를 소개하는 글과 위치를 담고 있어 누구든 쉽게 유적지를 찾을 수 있다. 겸재정선미술관, 궁산땅굴, 소악루, 양천고성지, 양천향교로 이어지는 강서역사문화거리는 천천히 걸어 1시간 정도 걸리는 코스이다. 해가 저물면 역사문화 거리는 낮의 모습과는 다르게 아름다운 조명이 켜지며 빛을 받은 조형물이 줄을 잇는 모습을 연출한다.', '1', '4', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56654', '2733968', '강서한강공원', 'http://tong.visitkorea.or.kr/cms/resource/11/2779111_image2_1.png', 'http://tong.visitkorea.or.kr/cms/resource/11/2779111_image2_1.png', '6', '37.5860935311', '126.8171601831', '', '서울특별시 강서구 양천로27길 279-23 (방화동)', '', '<a href=''http://hangang.seoul.go.kr/archives/46788/'' target=''_blank'' title=''새창 : 강서한강공원 사이트로 이동''>http://hangang.seoul.go.kr/</a>', '강서한강공원은 한강 남단 가양대교에서 서울~김포시 경계까지이며, 습지생태공원과 체육공원이 결합된 테마형 공원이다. 서울시 생태관광명소 30개소 중 하나로 선정된 강서습지생태공원은 하중도, 자연관찰로, 습초지 등 습지생태계를 복원하여 하천의 자연정화 기능을 제공하고, 방화대교 하부, 가족피크닉장 앞, 행주대교 하부에는 체력단련기구 등을 갖춰 시민의 여가활동과 스포츠 공간으로써의 기능을 하고 있다.', '1', '4', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56655', '2591792', '개봉유수지 생태공원', '', '', '6', '37.4924524597', '126.8632141714', '', '서울특별시 구로구 개봉동', '195-7', '', '개봉유수지 생태공원은 유수지의 특성을 고려한 안정적인 생태기반환경 복원으로 조성하였다. 주변 주거지역과 연계한 다양한 서식공간 조성으로 종다양성을 증진하고 목감천의 하천생태축과 연계한 도시생태 네트워크를 구축했다. 공원 내에는 생태습지, 생태놀이터, 장미수국원, 맨발산책로, 잔디광장 등이 조성되어 있어 아이들의 자연학습공간으로도 좋다.', '1', '7', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56656', '126501', '개운사(서울)', 'http://tong.visitkorea.or.kr/cms/resource/26/2690626_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/26/2690626_image3_1.jpg', '6', '37.5899857439', '127.0280213987', '', '서울특별시 성북구 개운사길 73', '', '<a href=''http://www.gaeunsa.org/'' target=''_blank'' title=''개운사 사이트로 이동''>http://www.gaeunsa.org</a>', '안암산 자락에 자리하고 있는 개운사는 그리 크지는 않지만 한국 불교 개혁의 근원지이고, 현재는 김포에 위치하고 있지만 불교 교육의 근본도량인 중앙승가대학이 있던 곳이다. 이 절은 조선 초 태조 5년(1396)에 왕사(王師)인 무학대사가 동대문 밖 5리 정도 되는 안암산 기슭, 현재 고려대학교 이공대학 부근에 절을 짓고 영도사(永導寺)라고 하였다는 것이 일반적으로 알려져 있다. 그 후 정조 3년(1779) 5월에 정조의 후 궁 원빈(元嬪) 홍 씨가 세상을 떠나자 영도사 자리에 묘소를 정하고, 명인원(明仁圓)이 세워지자 인파당(人波堂) 축홍(竺洪)스님이 영도사를 동쪽으로 몇 리 떨어진 현재의 자리에 절을 옮겨지었다고 하였으나 \'사기(寺記)\'라는 문 현에는 영조 6년(1730)에 영도사가 이전한 것으로 나타난다. 그러나 영도사가 개운사로 이름을 바꾼 시기는 확실하지 않다. 개운사는 20세기 이후에 한국 교육불사와 불교의 진보적인 운동을 주도해 왔다. 1926년 근대 불교의 대석학이었던 박한영 스님이 머물렀고, 이 절의 암자인 대원암에 탄허(呑虛) 스님이 머물면서 역경(譯經) 사업에 종사하였다. 이 절은 현재 조계종이지만 최근까지 태고종에서 관리하였으므로 1955년에 대처승 주최로 전국포교사대회(全國布敎師大會)를 개최하기도 하였다. 1981년부터 중앙승가대학이 이곳을 교육도량으로 사용하면서 현대사 속에서의 개운사 전통을 꽃피우는 계기가 마련되었다. 조선말 고종 10년(1873)에 명부전을 세우고 나서 7년 후인 1880년에는 이벽송(李碧松)대사가 대웅전을 중건하고, 그 후 여러 차례의 중수를 거쳐 오늘에 이르고 있다.(출처: 개운사)', '1', '17', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56657', '3385911', '개화산 호국공원', 'http://tong.visitkorea.or.kr/cms/resource/69/3383069_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/69/3383069_image3_1.JPG', '6', '37.5812522032', '126.804514251', '', '서울특별시 강서구 개화동', '산 81-13', '', '개화산 호국공원은 6·25 전쟁 초기 후퇴하면서 방어선을 구축하고 이를 사수하다 산화한 영웅들을 추모하는 추모의 공간이다. 아군으로 위장한 북한군이 대병력을 침투했고 김포로 후퇴하던 국군은 개화산을 최후 방어선으로 설정했다. 아군은 탄약 보급이 끊겼고 통신도 원만하지 않았다. 결국 육군 제1사단 11, 12, 13연대 1,100여 명은 개화산에서 산화했다. 김포지구 사수를 위해 목숨을 바친 장병의 충혼을 기리기 위해 1994년 이곳에 호국공원을 만들고 위령탑도 세웠다. 매년 6월이면 호국 위령제를 올린다.', '1', '4', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56658', '3440844', '거꾸로하우스', 'http://tong.visitkorea.or.kr/cms/resource/23/3440823_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/23/3440823_image3_1.jpg', '6', '37.5457465853', '127.0361052512', '0507-1357-8670', '서울특별시 성동구 고산자로 71 (성수동1가)', '', 'https://www.playnine.co.kr/', '거꾸로하우스는 서울 성동구 성수동에 위치한 독특한 체험 공간으로, 실내의 모든 가구와 소품이 거꾸로 설치되어 있어 방문객들이 색다른 경험을 할 수 있는 장소이다. 서울숲 인근에 위치해 있어 주변 관광과 함께 즐기기에 좋으며, 매일 화요일부터 일요일까지 운영된다.', '1', '16', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56659', '2733966', '거리공원', 'http://tong.visitkorea.or.kr/cms/resource/86/2780786_image2_1.png', 'http://tong.visitkorea.or.kr/cms/resource/86/2780786_image2_1.png', '6', '37.4997993352', '126.8916141551', '', '서울특별시 구로구 공원로 28', '(구로동)', '', '구로구(九老區)는 옛날에 노인 9명이 오래도록 장수하였다는 전설로부터 유래된 지역이다. 서울특별시 구로구 구로동 106-5번지 가마 산길 거리공원오거리에서 구로구 구로동 3-55번지 신도림역에 이르는 보조 간 선급 가로를 거리공원길이라고 하는데, 구로 거리공원을 따라 이어진 길이라는 의미에서 거리공원길이라고 이름 붙였다. 구로거리공원은 봄에는 벚꽃으로 만개하고 가을에는 단풍으로 물들어 계절마다 아름다운 풍경을 즐길 수 있는 곳이다. 구로구 시민들의 산책 명소로 알려져 있다.', '1', '7', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56660', '2758868', '거북선나루터', 'http://tong.visitkorea.or.kr/cms/resource/02/3446702_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/02/3446702_image3_1.jpg', '6', '37.5182707714', '126.9676539295', '', '서울특별시 용산구 이촌동 302-183', '', '', '서울 한강공원 이촌지구에 위치한 거북선 나루터는 도하[渡河] 체험장 및 수상레포츠 시설로 일반인들과 단체 생활의 팀워크 훈련장으로 이용되는 선착장이다. 거북선나루터는 1990년 10월 1일 국군의 날을 기념하여 180t 급 거북선 실물 크기로 제작된 거북선의 닻을 이곳에 내리고 유람선을 운영, 전시장으로 사용했었던 것에서 붙여진 이름으로 당시 거북선 유람선은 2005년부터 이순신 장군 한산대첩의 전승지인 통영시에서 운행 중이다. 주변을 따라 대나무숲, 갈대, 들꽃이 철 따라 피어나서 시민들의 산책과 운동코스로 자주 이용되고 있으며 매년 10월 열리는 불꽃축제 명당자리로도 유명하다. 4호선 이촌역 3-1번 출구로 나와 이촌한강공원 LG한강자이 아래 있다.', '1', '21', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56661', '1604652', '건청궁', 'http://tong.visitkorea.or.kr/cms/resource/10/2527110_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/10/2527110_image2_1.jpg', '6', '37.5774168614', '126.9779874266', '', '서울특별시 종로구 사직로 161', '(세종로)', '<a href=''https://royal.khs.go.kr/ROYAL/contents/menuInfo-gbg.do?grpCode=gbg''새창 : 경복궁 홈페이지로 이동''>https://royal.khs.go.kr/</a>', '건청궁은 경복궁 내 가장 깊숙한 곳에 자리한 궁궐로 1,873년(고종 10)에 지어졌다. 이곳은 명성황후가 일본인들에게 무참히 시해된 곳이다. 청일전쟁이 끝난 후 명성황후가 일본 세력을 배척하자 일본공사 미우라가 주동이 되어 고종 32년 (1895) 10월 8일(음력 8월 20일) 일본인들이 경복궁에 침입하여 많은 상궁과 명성황후를 시해하였다. 이 사건을 을미사변이라 한다.또한 건청궁은 우리나라 최초로 전깃불을 사용하여 어두운 밤을 밝혔던 곳이다. 우리나라 최초의 미국 유학생 유길준이 조선으로 돌아와 고종 임금에게 어두운 밤을 대낮같이 밝게 해주는 전구를 알려주고, 고종은 미국의 에디슨 전기회사에 전기공사를 맡겨 건청궁 앞에 있는 연못(향원지)의 물을 끌어들여 전기를 만드는 발전소를 지었다. 경복궁에 전깃불이 들어온 것은 우리나라에서 전기 사용의 최초이며 일본과 중국보다도 2년 정도 빠른 것으로, 이러한 사실을 알려주기 위해 ‘한국의 전기발상지’ 글로 새긴 돌이 건청궁에 있다.(출처 : 경복궁관리소 홈페이지 안내글 인용)', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56662', '294505', '경국사(서울)', '', '', '6', '37.6139242251', '127.0056310926', '', '서울특별시 성북구 보국문로 113-10 (정릉동)', '', '', '경국사는 1325년에 자정율사가 창건하였는데, 삼각산의 청봉 아래 있다고 하여 ‘청암사’라 하였다고 전한다. 창건주인 자정율사는 계율에 정통하였을 뿐만 아니라 법화 (法華), 유식(唯識) 등에서도 조예가 깊었으며, 계율과 법화경(法華經) 관음신앙을 승상하던 사찰이었다. 그리고 1349년 태고보우(太古普愚) 국사가 중국에 가서 석옥청공(石屋淸珙) 스님으로부터 법을 전해 받고 귀국하여 경국사에 공민왕의 청을 받아 금란가사와 주장자(拄杖子)를 하사 받고 국사가 되었다. 1545년, 명종조(明宗朝) 때 왕모(王母)인 문정왕후(文定王后)가 불사를 하면서 국가의 경사스러움을 끊어지지 않도록 기원하는 뜻에서 ‘경국사’로 개칭하였다. 그 후 1698년에는 연화승성 스님이 절을 중수하고 천태성전을 세웠다. 천태성전은 독성을 모신 전각으로 이때 기록한 『천태성전상량문』이 지금도 전한다. 전 대통령 이승만도 경국사에 주석하던 보경스님이 인품에 감화되어 전 닉슨 부통령과 함께 참배한 일화도 전한다. 1977년부터 1985년까지 보경 금어 큰스님이 뒤를 이어 주지 소임을 맡은 지관 대종사(전 조계종 총무원장)가 경국사를 현재의 모습으로 탈바꿈시켰으며, 2005년에 한국불교의 율풍 진작에 헌신했던 자운대율사의 계주원명사리탑(戒珠圓明舍利塔)도 이곳에 세워졌다.', '1', '17', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56663', '2930839', '경리단길', '', '', '6', '37.538452116', '126.987515439', '', '서울특별시 용산구 이태원동', '210-65', '', '경리단길은 국군재정관리단 정문으로부터 그랜드하얏트 호텔 방향으로 이어지는 길과 주변 골목길을 통칭하며, 과거 육군중앙경리단이 현 위치에 있어 경리단길이라 불리어왔다. 경리단길은 저마다 개성이 넘치는 식당과 카페의 다양하고 독특한 먹거리가 유명한 문화와 젊음의 공간이다. 또한, 서울의 평범한 동네 같으면서도 세계 각국에서 온 외국인들이 주민들과 자연스럽게 어울려 지내는 모습이 이국적이고 색다른 분위기를 자아낸다.', '1', '21', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56664', '126508', '경복궁', 'http://tong.visitkorea.or.kr/cms/resource/33/2678633_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/33/2678633_image3_1.jpg', '6', '37.5788222356', '126.9769930325', '', '서울특별시 종로구 사직로 161', '', '<a href=''https://royal.khs.go.kr/ROYAL/contents/menuInfo-gbg.do?grpCode=gbg'' target=''_blank'' title=''새창 : 경복궁 홈페이지로 이동''>https://royal.khs.go.kr/</a>', '경복궁은 1395년 태조 이성계에 의해서 새로운 조선왕조의 법궁으로 지어졌다. 경복궁은 동궐(창덕궁)이나 서궐(경희궁)에 비해 위치가 북쪽에 있어 ‘북궐’이라 불리기도 했다. 경복궁은 5대 궁궐 가운데 으뜸의 규모와 건축미를 자랑한다. 경복궁 근정전에서 즉위식을 가진 왕들을 보면 제2대 정종, 제4대 세종, 제6대 단종, 제7대 세조, 제9대 성종, 제11대 중종, 제13대 명종 등이다. 경복궁은 임진왜란 때 상당수의 건물이 불타 없어진 아픔을 갖고 있으며, 고종 때에 흥선대원군의 주도 아래 7,700여 칸에 이르는 건물들을 다시 세웠다. 그러나 또다시 명성황후 시해사건이 일어나면서 왕조의 몰락과 함께 경복궁도 왕궁으로서의 기능을 상실하고 말았다. 경복궁에는 조선시대의 대표적인 건축물인 경회루와 향원정의 연못이 원형대로 남아 있으며, 근정전의 월대와 조각상들은 당시의 조각미술을 대표한다. 현재 흥례문 밖 서편에는 국립고궁 박물관이 위치하고 있고, 경복궁 내 향원정의 동편에는 국립민속 박물관이 위치하고 있다.* 주요 문화재1) 사적 경복궁2) 국보 경복궁 근정전3) 국보 경복궁 경회루4) 보물 경복궁 자경전5) 보물 경복궁 자경전 십장생 굴뚝6) 보물 경복궁 아미산굴뚝7) 보물 경복궁 근정문 및 행각8) 보물 경복궁 풍기대◎ 한류의 매력을 만나는 여행 정보미국의 국민 TV 쇼 ‘더 투나잇 쇼 스타링 지미 팰런’에서는 ‘BTS위크’라는 이름을 붙여 닷새간 BTS 특별 방송을 진행했는데, 그중 BTS가 ‘맵 오브 더 솔 : 페르소나’ 미니앨범 수록곡 ‘소우주’와 ‘IDOL’을 부른 장소가 화제다. 그 장소는 바로 조선시대의 궁궐 중 하나인 ‘경복궁’의 경회루와 근정전이다. 보랏빛 조명에 아름답게 빛나던 경복궁에서 한국의 과거를 체험해 보길 추천한다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56665', '2373204', '경성 부민관 폭탄 의거지', 'http://tong.visitkorea.or.kr/cms/resource/30/3077530_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/30/3077530_image3_1.JPG', '6', '37.5675596477', '126.9765267272', '', '서울특별시 중구 세종대로 125', '(태평로1가)', '<a href=''http://mfis.mpva.go.kr/memorial/movementView.do?sid=703&memorialType=true&gubunCode=ALL&pageIndex=1&searchCondition=%EC%A0%9C%EB%AA%A9&searchKeyword=%EA%B2%BD%EC%84%B1'' target=''_blank'' title=''새창 : 홈페이지로 이동''>http://mfis.mpva.go.kr</a>', '경성 부민관 폭탄 의거지는 경성부민관에서 친일파가 주도하는 대회를 방해하기 위해 폭탄을 터트린 곳이다. 1945년 7월 24일 경성부민관 폭탄 의거 경성부민관은 1935년 12월 태평동 60번지에 세워진 경성부의 부립극장으로, 오늘날로 하면 시립극장이다. 경성부민관은 지하 1층, 지상 3층에 대강당·중강당·소강당·담화실 등을 갖춘 다목적 회관으로서, 당시로는 드물게 냉난방 시설과 조명·음향시설을 갖추어 각종 극단의 공연은 물론 전시 총동원 체제 아래 각종 관변 집회의 장소로 널리 이용되었다. 경성부민관 폭탄의거를 주도한 조문기·유만수·우동학·강윤국 등 당시 20세 안팎의 열혈 청년들은 1945년 5월 서울 종로구 관수동 13번지 유만수의 집에서 대한애국청년당을 결성하고 항일투쟁의 기회를 엿보았다. 1945년 7월 24일 저녁 경성부민관에서 친일파 거두인 대의당의 박춘금 일당이 주최하는 아세아민족분격대회가 열린다는 보도가 나오자, 조문기 등은 비밀회합을 갖고 대회장을 폭파할 계획을 세웠다. 이를 위해 유만수가 수색변전소 공사장 발파 인부로 침투해 빼낸 다이너마이트로 사제폭탄 두 개를 만들어 대회 전날 밤 대회장 뒤편 화장실 쪽에 설치하였다. 폭탄은 대회 당일인 7월 24일 밤 9시경 박춘금이 시국 강연을 위해 등단하고 얼마 뒤에 터졌고, 그것으로 대회는 중단되고 말았다. 경성부민관 폭탄의거는 해방을 한 달도 채 남겨두지 않은 시기에 꺾일 줄 모르는 민족의 독립 의지를 안팎에 떨친 사건이었다. 현재는 서울시의회 부지로 폭탄의거지임을 알리는 표지석이 설치되어 있다.', '1', '24', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56666', '264470', '경운동민병옥가옥', 'http://tong.visitkorea.or.kr/cms/resource/59/1568059_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/59/1568059_image3_1.jpg', '6', '37.5747089831', '126.9858034574', '', '서울특별시 종로구 인사동10길 23-9', '(경운동)', '<a href=''https://www.heritage.go.kr/heri/cul/culSelectDetail.do?pageNo=1_1_1_1&sngl=Y&ccbaCpno=2441100150000'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://www.heritage.go.kr</a>', '경운동 민병옥 가옥은 1930년대에 지은 것으로 보이는 개량한옥으로, 일제강점기의 대표적인 건축가 박길용(1898∼1943)이 설계했다고 전한다. 경운동 66-8번지에 있던 정순주 가옥과 같은 평면으로 정순주 가옥은 이 집의 뒤채에 해당한다. 원래 건축주가 두 아들에게 한 채씩 주기 위하여 지은 집으로 네모반듯한 집터 동쪽에 대문이 있고, 앞마당으로 들어가면 남향으로 H자형의 건물이 있다. 중심축에 부엌·대청·건넌방·사랑방이 있고, 부엌의 앞쪽으로 안방, 뒤쪽으로 뒷방이 있다. 반 칸 너비의 툇마루를 두어 동선을 연결하였다. 사랑방의 앞쪽에는 누마루를, 뒤쪽에는 현관마루를 두었다. 건넌방은 상당히 큰 방으로 꾸며 사랑방 구실을 하고, 현관마루와 연결되는 사랑방은 응접실 용도로 사용하도록 설계한 듯하다. 마당 남쪽 담장 밑에는 작은 화단을 가꾸어 놓았다. 현관과 화장실·목욕탕을 내부에 두고 이들을 연결하는 긴 복도를 둔 점과 유리창을 단 점 등 이 시기 개량한옥의 모습을 잘 보여주며, 주택의 변천을 알 수 있도록 해주는 중요한 자료이다.현재는 문화유산 보존을 위하여 출입은 불가하고 건물의 외부만 관람 가능하다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56667', '2500229', '경의선책거리', 'http://tong.visitkorea.or.kr/cms/resource/79/3381279_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/79/3381279_image3_1.jpg', '6', '37.5564989709', '126.9290369242', '', '서울특별시 마포구 와우산로37길 35', '', '<a href=''https://www.instagram.com/gbookst/'' target=''_blank'' title=''새창 : 경의선책거리 인스타그램으로 이동''>https://www.instagram.com/gbookst</a>', '경의선 책거리는 18세기 후반, 책을 통해 문치(文治)를 하려는 정조시대의 책(冊)가도 문화를 현대적 의미로 되살려, 시민들에게 책을 통한 복합문화공간으로 제공하고자 경의선 폐선부지인 홍대 복합역사에 조성한 책 테마거리다. 경의선 책거리를 찾는 시민들이 세상에 나온 책 한 권의 위대한 가치를 통해 건강한 삶의 지혜를 함께 나누고 꿈을 실현하며, 마포의 출판 산업 부가가치를 높이는 출발 거점으로 발전하길 바라는 의미로 2016년 10월 조성이 완료되었다.', '1', '13', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56668', '1826438', '경찰혼', 'http://tong.visitkorea.or.kr/cms/resource/27/3466927_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/27/3466927_image3_1.jpg', '6', '37.5263408571', '126.9006431406', '', '서울특별시 영등포구 국회대로 608', '(당산동3가)', '<a href=''http://mfis.mpva.go.kr/memorial/movementView.do?sid=1756&memorialType=true&gubunCode=ALL&pageIndex=1&searchCondition=%EC%A0%9C%EB%AA%A9&searchKeyword=%EA%B2%BD%EC%B0%B0%ED%98%BC'' target=''_blank'' title=''새창 : 국가보훈처 현충시설 홈페이지로이동''>http://mfis.mpva.go.kr</a>', '우리 사회의 안정과 치안을 위해 순국·순직한 영등포 경찰서 출신 경찰들의 숭고한 희생정신을 추모하고 그분들의 고귀한 업적을 후세에 널리 알리기 위하여 서울영등포재향경우회가 건립한 추모비이다. 6·25 전쟁 직후 전몰·순직한 경찰 62위 및 국내에서 발생한 각종 시위 진압 과정에서 순직한 경찰 16위 등 총 78위의 경찰들을 기리고 있으며 서울 영등포 경찰서 내에 위치해 있다. 규격은 높이 2m, 부지면적 33㎡이다.', '1', '20', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56669', '126484', '경희궁', 'http://tong.visitkorea.or.kr/cms/resource/21/2526521_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/21/2526521_image2_1.jpg', '6', '37.570442502', '126.9685079323', '', '서울특별시 종로구 새문안로 45', '', '국가유산청 <a href=''http://www.heritage.go.kr/heri/cul/culSelectDetail.do?pageNo=1_1_1_1&ccbaCpno=1331102710000'' target=''_blank'' title=''문화재청 사이트로 이동''>http://www.heritage.go.kr</a><br>서울역사박물관 <a href=''https://museum.seoul.go.kr/www/intro/annexIntro/annex_20/annex_20_01.jsp?sso=ok'' target=''_blank'' title='' 사이트로 이동''>https://museum.seoul.go.kr</a>', '경희궁은 새문안 대궐 또는 서쪽의 궁궐이라 해서 서궐이라 불렸고 영조 36년(1760)에 궁의 이름을 경희궁이라 개칭하였다. 경희궁에는 회상전, 흥정당, 집경당, 숭정전, 흥화문 등의 건축물이 있었으나, 순조 29년(1829)에 화재로 대부분이 소실되어 1831년에 중건하였다.이후 1987년에 서울시에서 경희궁지에 대한 발굴을 거쳐 숭정전 등 정전지역을 복원하여 2002년부터 시민들에게 공개하기 시작하였다. 현재 궁터에는 용비천(龍飛泉)이라는 샘터가 남아 있고, 숭정전 등 주요 전각의 기단이 그대로 남아 있다.이 지역은 한동안 학교로 이용되어 새로운 건물이 들어서고 주변의 조경이 변모되고 지하 방공시설이 구축되는 등 약간의 변화가 있었으나, 옛 건물의 기단이 일부 남아 있고 전체적으로 궁궐의 지형이 잘 남아 있으며, 뒤쪽에는 울창한 수림이 그대로 보존되어 있어 아직도 궁궐의 자취를 상당히 간직하고 있다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56670', '1606042', '경희궁 숭정전', 'http://tong.visitkorea.or.kr/cms/resource/95/1569195_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/95/1569195_image3_1.jpg', '6', '37.5577528954', '127.0011136538', '', '서울특별시 중구 장충동2가', '192-5', '<a href=''https://www.heritage.go.kr/heri/cul/culSelectDetail.do?ccbaCpno=2111100200000&pageNo=1_1_2_0'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://www.heritage.go.kr</a>', '숭정전은 경희궁의 정전으로 역대 국왕의 즉위식이나 대례 등 국가 의식을 행하던 곳이다. 경희궁은 원래 인조의 아버지인 원종의 사저가 있던 곳이다. 광해군이 이곳에 왕기가 서렸다는 풍수설을 믿고 왕기를 누르기 위해 궁궐을 건립하여 경덕궁이라 불렀던 곳이다. 숭정전은 1910년 일제가 경희궁을 철폐하고 일본인 자제들을 위한 학교인 경성중학교를 설립할 때도 남아 있었다. 그 후 1926년에 필동 남산 기슭, 지금의 동국대학교 자리인 조계사의 본전으로 사용되기 위해 이건되었다. 광복 후 그 자리에 동국대학교가 세워지면서 1976년 9월 현재 위치로 옮겨져 학교의 법당인 정각원으로 사용되고 있다.이곳은 정면 5칸, 측면 4칸 규모의 단층 건축물로, 일정한 길이로 가공한 장대석을 쌓아 2중 월대를 만들었다. 월대 정면에 3개의 계단을 설치하고 좌우측에 각각 1개의 계단을 놓았는데, 임금이 다니는 정면의 가운데 계단에는 소맷돌과 답도를 두어 다른 계단과 차별을 두었다. 원형의 주춧돌 위에 원형 기둥을 세우고 그 위에 주심포 형식의 공포를 올렸다. 지붕은 팔작지붕으로 용마루와 박공마루, 추녀마루에는 회반죽을 발라 양성바름을 하였고, 망새와 용두, 잡상 등을 설치하여 지붕 위를 장식하였다. 중화전의 내부는 한 공간으로 뚫려 있으며, 정면 가운데에 어좌가 설치되어 있다. 남쪽에는 숭정문, 동쪽에는 여춘문, 서쪽에 의추문, 북쪽에는 자정전의 정문인 자정문이 자리 잡고 있다.1980년 경희궁 터가 사적으로 지정되어, 1985년부터 경희궁 터의 발굴조사 및 복원 사업이 시행되었다. 이 과정에서 동국대학교의 정각원 건물을 다시 경희궁 터로 옮겨오려 했으나, 건물이 노후화되고 변형도 심해 새로 건물을 지어 복원하였다. 경희궁 발굴조사에서 출토된 숭정전 2중 기단을 근거로 1989년부터 6년에 걸쳐 경희궁의 본래 자리에 경희궁 숭정전이 복원되었다.', '1', '24', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56671', '1604784', '경희궁 흥화문', 'http://tong.visitkorea.or.kr/cms/resource/55/3384855_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/55/3384855_image3_1.JPG', '6', '37.5698981615', '126.9707771853', '', '서울특별시 종로구 새문안로 55 (신문로2가)', '', '<a href=''https://museum.seoul.go.kr/www/intro/annexIntro/annex_20/annex_20_01.jsp?sso=ok'' target=''_blank'' title=''새창 : 서울역사박물관 홈페이지로 이동''>https://museum.seoul.go.kr/</a>', '홍화문은 조선 광해군 8년(1616)에 세운 경희궁의 정문이다. 원래는 금천교 동쪽, 즉 현재의 구세군 빌딩자리에서 동쪽을 바라보고 있었다. 그러나 일제가 1910년에 경성중학교를 건립하고자 궁내의 많은 전각을 헐면서 그 기능을 상실했다. 그 후 일제는 1932년 남산 자락에 이등박문(伊藤博文)을 위한 사당인 박문사(博文寺)를 건립할 때 정문으로 사용하였다. 해방 후 박문사는 폐사되고 흥화문은 영빈관(迎賓館) 및 신라 호텔의 정문으로 사용되기도 했다. 1988년 서울시는 경희궁 복원 계획의 일환으로 흥화문을 지금의 자리로 이전(移轉) 복원하였다. 그러나 원 자리는 동향을 보고 배치했다고 하나, 그 위치는 현재 찾기 힘들다. 정면 3칸, 측면 2칸의 우진각 지붕의 다포 양식 건물로, 정전인 숭정전, 황학정과 더불어 건축적 성격을 이해하는데 중요한 자료이다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56672', '1604824', '경희궁공원', '', '', '6', '37.5707591425', '126.969105091', '', '서울특별시 종로구 새문안로 55', '', '', '종로구 신문로에 있으며 흥화문, 숭정전 등 문화재와 시립미술관, 서울 정도 600년 기념관 등이 있어 공원과 더불어 문화공간으로도 활용되고 있다. 경희궁은 원래 조선 시대 광해군 8년(1616)에 \'경덕궁\'이라는 이름으로 건립되었다가 영조 36년(1760)에 경희궁으로 개명되었다. 처음에는 회상전, 융복전, 집경당, 흥정당, 숭정전, 흥화문, 황학정 등의 건물이 있었으나 융복전과 집경 당은 없어지고 나머지 건물들은 1910년 경성중학교(지금의 서울고등학교)가 설립된 후 여기저기로 옮겨졌다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56673', '2611568', '계남공원(유아숲체험장, 우렁바위)', 'http://tong.visitkorea.or.kr/cms/resource/60/2611560_image2_1.bmp', 'http://tong.visitkorea.or.kr/cms/resource/60/2611560_image2_1.bmp', '6', '37.5127914822', '126.8474483699', '', '서울특별시 양천구 신정동', '', '', '1971년에 개원한 근린공원으로 다양한 운동시설과 야외무대, 약수터 등이 있다. 특히 무장애 숲길 1,023m를 포함한 둘레길 4km 구간이 개통되어 있다. 친환경 무장애 숲길은 아기자기하게 오르내리며 환상적인 단풍길을 즐길 수 있게 꾸몄고, 야간에도 안전하게 산책할 수 있도록 친환경 발광다이오드 조명등을 곳곳에 설치했다. 신정산 숲 속 여행은 숲해설가와 함께 체험활동 위주의 프로그램으로 자연과 호흡하고 심신을 재충전할 수 있다. 또한 신정산 초입에 아이들이 자연을 만끽할 수 있는 유아숲 체험장이 있어 생태교육 전문가의 친절한 설명은 숲 속 나들이의 즐거움을 더해준다. 이 외에도 숲 속 도서관, 모험놀이공간, 감성체험공간, 상상놀이공간, 숲 체험공간 등이 있다. 공원 내에는 사람들의 이목을 끄는 우렁 바위가 있다. 이 우렁 바위는 옛날에 울었다는 전설을 간직한 바위로 길마(소 등받이)처럼 생겼다 하여 길마 바위라 부르기도 한다.', '1', '19', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56674', '3428372', '계남근린공원', 'http://tong.visitkorea.or.kr/cms/resource/03/3432303_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/03/3432303_image3_1.jpg', '6', '19.69442748', '117.9925662504', '', '서울특별시 양천구 중앙로17길 21 (신정동)', '', '', '계남근린공원은 서울 구로구 고척동과 양천구 신정동에 걸쳐 있는 공원으로, 산책로, 인조잔디 축구장, 다목적 운동장, 숲 체험 공간 등 다양한 시설을 갖춘 근린공원이다. 자연 속에서 산책과 여가를 즐길 수 있도록 접근성이 좋은 나무 데크와 울창한 숲이 조성되어 있다.', '1', '19', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56675', '3045019', '계동배렴가옥', 'http://tong.visitkorea.or.kr/cms/resource/73/3045073_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/73/3045073_image3_1.jpg', '6', '37.5813498946', '126.9867059647', '', '서울특별시 종로구 계동길 89 (계동)', '', '<a href=''https://seoulbrhouse.com/'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://seoulbrhouse.com/</a>', '계동길에 자리한 계동배렴가옥은 서울의 공공 한옥이자 역사 가옥으로, 1930년대에 지어진 것으로 추정되는 튼 [ㅁ] 자 형태의 도시형 한옥이다. [ㄱ] 자의 안채와 [ㄴ] 자형 바깥채가 마주 보는 구조이다. 배렴은 이 집에서 1959년부터 1968년까지 살았으며 배렴이 살기 전에는 우리나라 최초의 영상 민속학자이자 국립민족박물관을 설립한 송석하가 살았다. 그림을 배우기 전부터 서예에 관심을 둔 배렴은 추사 김정희, 소전 손재형 등 조선시대부터 동시대에 활동한 서예가의 글씨를 수집하였다. 배렴이 세상을 뜬 뒤 2001년 에스에이치공사에서 매입 후 임대하였고, 숙박시설로 활용되면서 원형이 많이 훼손되었다. 2017년부터 서울시가 역사 가옥으로 개방하기 위해 공간 구성을 새롭게 하여 지금은 시민들을 위해 휴식공간이자 문화 예술공간으로 개방되고 있다. 또한, 계동배렴가옥의 다양한 전시와 문화 프로그램은 누구나 이용이 가능한 열린 공간이기도 하다.배렴가옥에서는 여러 프로그램들이 진행되는데, 매년 프로그램이 바뀌기 때문에 홈페이지를 통해 확인해야 한다. 지금까지 진행된 프로그램으로는 배렴 영화관, 일일 서재, 슬기로운 자아탐구 생활 등이 있다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56676', '1826457', '고당조만식선생동상', 'http://tong.visitkorea.or.kr/cms/resource/36/1984636_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/36/1984636_image3_1.jpg', '6', '37.5500801547', '127.0771625', '', '서울특별시 광진구 능동로 216', '(능동) 서울어린이대공원 내', '<a href=''http://mfis.mpva.go.kr/memorial/movementView.do?gubunCode=ALL&sid=430&pageIndex=1&searFacilgubun=&searTopicsort=&searAddr_sigun=&searMpvabranch=&searVR=&searInfo=&searGbn=tit&searTxt=%EC%A1%B0%EB%A7%8C%EC%8B%9D'' target=''_blank'' title=''새창 : 국가보훈처 현충시설 홈페이지로이동''>http://mfis.mpva.go.kr</a>', '1976년 12월 7일 서울 광진구 능동 18(서울 어린이대공원 내)에 조만식 선생의 숭고한 항일정신과 자유 수호를 기리기 위해 건립한 동상이다. 동상은 민복진이 작업을 했으며 동상 좌대의 전면 글씨는 고 박정희 대통령이 썼다. 고당조만식선생동상은 조선의 간디라 불리는 비폭력 무저항주의를 실천한 사상가이자 독립운동가이다. 평남 강서 출신으로 오산학교에서 민족교육에 힘쓰다 교장직을 사임하고, 1919년 3.1운동을 주도하다 1년간 옥고를 겪었다. 이후 1920년 조선 물산장려운동회를 조직하여 사회운동을 전개하였다. 조선 민립대학 설립 운동에 적극 참여하여 인재 양성에 힘쓰고, 1932년에는 조선일보사 사장으로 취임하여 언론 창달 및 민족 계몽에 앞장섰다. 조만식 선생은 좌·우파가 하나가 되는 신간회가 결성되도록 애썼으며, 광복 후 반탁운동과 민족자주 독립운동의 지도자로서 활동하다가 소련군에 의해 연행된 이후 공개적인 장소에서 선생의 모습을 다시는 볼 수 없게 되었다. 조만식 선생은 1970년 건국훈장 대한민국장이 추서되었다.', '1', '6', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56677', '814237', '고덕동 생태경관 보전지역', '', '', '6', '37.5670267098', '127.1493110052', '', '서울특별시 강동구 고덕동 396번지 일대', '', '<a href=''https://hangang.seoul.go.kr/www/contents/873.do?mid=867'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://hangang.seoul.go.kr</a>', '고덕동 생태경관 보전지역은 서울지역에서 자연성이 높은 지역으로 고덕수변생태복원지와 연접하고 있으며 고덕 지천부터 암사취수장 우측 산림지역 일부와 호안 및 한강 저수부지까지 포함하는 구역이다. 고덕수변생태복원지역 중 조류관찰소 등 일부 시설이 포함되어 있다.서울지역에서 자연성이 높은 지역으로 인근 산림지역과 연계하여 생물종 다양성을 증대하기 위하여 생태경관 보전지역으로 지정되어 있으며, 시민들에게 여가 및 생태체험 학습 장소로 제공되고 있다.', '1', '2', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56678', '2591797', '고척근린공원', 'http://tong.visitkorea.or.kr/cms/resource/27/3400227_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/27/3400227_image3_1.JPG', '6', '37.5066256365', '126.8533118977', '', '서울특별시 구로구 고척로45길 39', '(고척동)', '<a href=''https://www.guro.go.kr/www/contents.do?key=2996'' target=''_blank'' title=''새창 : 구로구청 홈페이지로 이동''>https://www.guro.go.kr/</a>', '고척 근린공원은 고척2동에 위치하며 자연적인 야산을 공원으로 조성한 구로구의 대표적인 공원이다. 도심 속에 자연과 휴식이 어우러진 공간이다. 높은 곳에 생긴 마을이라는 데에서 유래해서 고척이라는 지명을 갖게 되었다고 전해지고 있다. 은행나무와 벽화가 일대에 있어 산책하기에 적합하며, 다양한 운동기구와 산책로, 자전거도로, 편의시설 등이 설치되어 있다. 또한 아이들의 활동적이 놀이를 위한 시설들이 설치되어 있다. 안전하고 다양한 놀이기구들이 준비되어 있다.', '1', '7', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56679', '2591799', '고척동 고인돌', 'http://tong.visitkorea.or.kr/cms/resource/28/3401728_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/28/3401728_image3_1.JPG', '6', '37.5086169645', '126.8473068338', '', '서울특별시 구로구 고척동', '산12-1', '<a href=''https://www.guro.go.kr/www/contents.do?key=3008&'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://www.guro.go.kr</a>', '구로구 고척동에 있는 청동기시대 무덤이다. 고척고등학교 뒷산 삼국시대의 토성인 신정리 토성 경내에 있다. 이 고인돌은 1998년 서울대 조사단에 의해 처음 발견되었는데 그 후 실시된 관련분야 교수들의 조사에서 덮개돌의 주변에 덮개돌과 동일한 형태의 돌들이 존재하지 않는 것으로 보아 원래의 위치에서 벗어난 것으로 보인다는 의견이 제시되었다. 또한 규모는 크지 않지만, 돌방이 확인되었으며, 굄돌 등의 배치 상황으로 미루어 고인돌로 추정케 한다. 고척동 고인돌은 구로구에서 유일한 고인돌로서 주변의 고척동 유물산포지 등과 더불어 청동기시대 주변 지역 일대의 문화상 이해에 좋은 자료가 된다. 이곳은 구로올레길 코스에 있으므로 가볍게 산책을 하면서 함께 둘러보는 것도 좋다.', '1', '7', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56680', '2453179', '고촌홀(제약박물관)', 'http://tong.visitkorea.or.kr/cms/resource/51/3462951_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/51/3462951_image3_1.jpg', '6', '37.5597464264', '126.9631392793', '', '서울특별시 서대문구 충정로 8', '', '<a title=''새창: 고촌홀(제약박물관) 홈페이지로 이동'' href=''http://www.kochonhall.or.kr/'' target=''_blank''>http://www.kochonhall.or.kr/</a>', '‘고촌’은 종근당 창업자 고 이종근 회장의 호에서 따왔다. 종근당의 창업전시관에서 고촌의 일생, 종근당의 발생 과정, 국내외 의약사를 살펴보고, 도전과 나눔 정신을 체험해 보도록 구성되어 있다. 고촌 선생이 세계 각국에서 수집한 종들을 모티브로 만들어진, 종이미지의 체험 전시관이 독특하다.', '1', '14', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56681', '1827189', '고하송진우선생동상', 'http://tong.visitkorea.or.kr/cms/resource/97/3466797_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/97/3466797_image3_1.jpg', '6', '37.54883584', '127.0763931608', '', '서울특별시 광진구 능동로 216', '서울어린이대공원 내 (정문과 서문 사이)', '<a href=''http://mfis.mpva.go.kr/memorial/movementView.do?sid=428&memorialType=true&gubunCode=ALL&pageIndex=1&searchCondition=%EC%A0%9C%EB%AA%A9&searchKeyword=%EC%86%A1%EC%A7%84%EC%9A%B0'' target=''_blank'' title=''새창 : 국가보훈처 현충시설 홈페이지로이동''>http://mfis.mpva.go.kr</a>', '독립운동가, 교육자, 언론인, 정치가이자 민족지도자로 활동한 고하 송진우 선생을 추모하기 위해 건립되었으며 서울어린이대공원 내(정문과 서문 사이 위치)에 있다. 고하 선생은 3.1 운동을 배후 주동하여 옥고를 치렀고 1921년 동아일보 사장 취임 후 20여 년 간 신문을 통하여 일제 암흑기에 국내 독립운동을 주도하고 민족 민주 민생 민문주의를 구국의 기본사상으로 삼아 언론 교육산업 문화활동을 통하여 독립을 위한 민족의 힘과 얼을 고취하였다. 해방 직전 일제의 정권 인수제의를 거절하였고 광복 후 한국민주당을 창당하여 동아일보 사장으로서 민주건국과 신탁통치 반대에 진력하던 중 한현우 등의 총탄에 서울 원서동 자택에서 서거하였다.규격 : 좌대면적 9㎡, 좌대높이 3.9m, 동상높이 3.6m건립자 : 송진우선생동상건립위원회(1983.09.23. 건립)', '1', '6', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56682', '2713660', '공간 와디즈', '', '', '6', '37.5447330717', '127.0504178651', '', '서울특별시 성동구 연무장1길 7-1 (성수동2가)', '', '<a href=''http://gongganwadiz.com''target=''_blank'' title=''새창 : 공간 와디즈 홈페이지로 이동''>http://gongganwadiz.com</a>', '공간 와디즈는 와디즈에서 펀딩이 진행 중인 제품을 직접 보고 만지고 체험할 수 있는 공간이다. 지하 1층부터 3층 루프탑까지 총 4개의 층, 500평의 규모를 자랑하고 있다. 콘텐츠 및 행사 성격에 따라 최적화된 층별 공간을 선택하여 이용할 수 있다. 마당과 루프탑 등은 100평 규모의 야외 구좌도 보유하고 있다.야외 구좌 마당은 다양하게 교류하는 커뮤니케이션 장소이자 보다 액티브한 체험형 행사가 가능한 공간이다. 야외를 활용한 대규모 집객 이벤트 및 시음, 시식, 샘플링 등 F&B 프로모션이 열리고 있다. 오프닝은 공간 와디즈의 오프라인 경험을 시작하는 입구에 위치하여 독립성과 주목도가 매우 높은 공간이다. 팝업 행사의 시작을 알리는 포토 스팟으로 활용할 수 있다. 실내 구좌 지하1층은 다양한 프로그램을 진행할 수 있는 갤러리형 홍보, 전시 멀티 공간으로, 신제품 런칭쇼, 제품 설명회 및 영화 VIP 초대 행사 등을 진행할 수 있다. 지상 1층은 약 120평 규모로, 공간 와디즈의 메인 공간이다. 마당부터 1층까지 연결하여 대규모 팝업을 연출할 수 있고, 1층을 포함하여 전체 대관이 가능하다. 지상 2층은 약 75평으로, 층 전체를 브랜드 팝업으로 연출하기 용이한 구좌이다. 전면부 통창에 홍보 요소를 그래픽으로 노출하여 외부 유동 인구를 대상으로 압도적인 홍보 효과를 볼 수 있다. 야외 구좌 지상 3층 루프탑은 야외 공간을 활용한 대규모 이벤트가 가능하며, 야외용 테이블 및 파라솔, 무대 및 단상 등이 마련되어 있어 크리에이터, 콘텐츠 플랫폼, 아티스트 고객사의 만족도가 최상인 공간이다.', '1', '16', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56683', '749192', '공덕동 족발골목', 'http://tong.visitkorea.or.kr/cms/resource/23/3384423_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/23/3384423_image3_1.JPG', '6', '37.5445785695', '126.953343379', '', '서울특별시 마포구 만리재로 19 (공덕동)', '', '<a href=''https://korean.visitseoul.net/restaurants/%EC%A1%B1%EB%B0%9C%EA%B3%A8%EB%AA%A9_/28336'' target=''_blank'' title=''새창 : 비짓서울로 이동''>https://korean.visitseoul.net/</a>', '시장 골목 한쪽에서 시작한 족발집이 골목을 이루고 시장 상권보다 오히려 더 활발하게 손님을 모으고 있는 곳이 있다. 그곳이 바로 공덕동 족발골목이다. 30년 전 시장 한쪽에 자리 잡은 2평짜리 식당이 족발골목의 뿌리다. 당시 시장 사람들의 식사를 위해 순댓국을 끓였고 팍팍한 생활을 달랠 술안주로 족발을 만들어 냈다. 순댓국과 족발은 그렇게 시장 사람들의 속과 마음을 달래주면서 점차 식당이 잘 되자 시장 안의 가방 가게가 업종을 변경해 순대와 족발을 팔기 시작했다. 이렇게 주변에 족발집이 늘어나면서 어느덧 이 골목이 족발 골목으로 자리 잡았다. 세월이 흐르며 손님이 시장 사람들에서 학생과 주변 직장인들로 늘어가며, 손님들은 낮에는 나이 드신 분들이 많고 저녁에는 젊은 사람들이 많다. 나이 든 분들은 청춘 시절, 이 골목에서 술잔을 나누었던 추억을 찾아오고 젊은이들은 그들의 추억을 만들며 활기찬 밤 술자리를 만들어 간다. 손님도 세대가 바뀌었지만 이 골목 식당 가운데는 대를 이어 순댓국을 팔고 족발을 만드는 집도 있으니 오래된 옛 맛을 찾아가  보는 것도 괜찮다.', '1', '13', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56684', '776082', '관문사(서울)', 'http://tong.visitkorea.or.kr/cms/resource/03/3393103_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/03/3393103_image3_1.JPG', '6', '37.4737408098', '127.0222641043', '', '서울특별시 서초구 바우뫼로7길 111', '', '<a href=''https://www.gwanmunsa.org/'' target=''_blank'' title=''새창 : 관문사 홈페이지로 이동''>www.gwanmunsa.org</a>', '서울 우면산 아래 위치한 관문사는 수도권 천태종의 요람이자 포교의 중심 도량으로서 청계산, 우면산, 양재천 등의 천혜 자연 속에 자리 잡고 있다. 의천대사는 당시 혼란했던 고려불교를 하나로 통합하고 바로잡기 위해 6세기경 천태 지의가 개창한 천태종을 한국에 들여왔다.관문사는 현대와 전통적인 불교 건축양식이 병행되어 있다. 첨단의 설비를 갖춰 다양한 국내외 문화, 학술, 종교의식 등을 치르고 있다. 다양한 프로그램을 통해 마음이 힘들고 지친 현대인들을 도와 그 고통을 내려놓고 마음의 평온을 찾는데 도움을 주고 있다.', '1', '15', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56685', '3428259', '관악도시농업공원', 'http://tong.visitkorea.or.kr/cms/resource/77/3428277_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/77/3428277_image3_1.jpg', '6', '37.464923464', '126.9271202346', '', '서울특별시 관악구 광신길 142 (신림동)', '', 'cityfarmer.seoul.go.kr/', '서울특별시 관악구에 있는 도시농원업으로, 도시민들에게 농업 체험의 기회를 제공하고 자연과 교감을 느끼면서 보호적인 삶의 방식을 체험할 수 있는 공간이다. 이 곳은 도시안에서도 농업 활동을 체험할 수 있도록 다양한 시설과 프로그램을 공원에 있도록 하기 위해 나만의 링과 교육의 장으로 자리 잡고 있다.', '1', '5', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56686', '126480', '관악산', '', '', '6', '37.4484036407', '126.9540987991', '', '서울특별시 관악구 관악로', '(신림동)', '<a href=''https://www.gwanak.go.kr/site/gwanak/05/10503020100002016051205.jsp'' target=''_blank'' title=''새창 : 관악구청 홈페이지로 이동''>https://www.gwanak.go.kr</a>', '서울시 한강 남쪽에 솟아 있는 관악산(632.2m)은 산 정상부의 바위가 갓을 쓰고 있는 모습을 닮아 관악산이라고 부르게 되었다. 1973년 관악구가 영등포구에서 분구되면서 산 이름이 구의 명칭이 되었다. 1968년에 도시자연공원으로 지정되었으며 오늘날에는 수많은 서울 시민이 즐겨 찾는 휴식처로 서울의 명소가 되었다. 관악산 곳곳에 드러난 암봉들이 깊은 골짜기와 어울려 험준한 산세를 이루고 있는 관악산은 도심에서 가까워 누구나 하루 일정으로 산에 오를 수 있으며, 봄에는 관악산 입구 쪽으로 벚꽃이 만발하고, 철쭉이 필 때는 철쭉제가 열리기도 한다. 여름의 짙은 녹음과, 계곡 깊은 곳에 동폭포, 서폭포의 물소리가 장엄하고 가을에는 단풍, 겨울에는 설경이 명산 관악의 면모를 자랑하고 있다. 관악산 정상에는 조선 태조 이성계가 서울을 도읍지로 정할 때 연주사와 원각사 두 절을 지어 화환에 대처했다고 하며, 정상의 원각사와 연주암을 비롯하여 크고 작은 사찰과 암자가 있다. 아슬아슬한 벼랑 위에 자리 잡고 있는 연주대는 관악산의 모든 등산로가 집결하는 곳이다.관악산에는 무장애 숲길 등반코스가 조성되어 있고, 등산로도 여러 곳이 있다. 관악구 신림동 서울대학교 입구에서 시작하는 등산로가 대표적이고, 경기도 과천시 중앙동 과천중학교 뒤편으로 오르는 길이 있다.', '1', '5', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56687', '126513', '관악산 낙성대공원', '', '', '6', '37.4711316683', '126.9602475997', '', '서울특별시 관악구 낙성대로 77', '', '<a href=''http://www.gwanak.go.kr/site/gwanak/05/10503010100002016051205.jsp'' target=''_blank'' title=''새창 : 관악구 문화관광 홈페이지로 이동''>http://www.gwanak.go.kr/</a>', '낙성대공원은 고려의 명장 인헌공 강감찬 장군(948년∼1031년)을 기리기 위해 조성된 곳이다. 낙성대는 강감찬 장군이 태어난 곳으로 장군이 태어날 때 출생지에 큰 별이 떨어졌다는 전설에 따라 붙은 이름이다. 1973년 서울특별시에서 주변 지역을 정돈하여 사당과 부속건물을 조성하였다. 공원 동쪽에 사당을 지어 안국사라 하여 장군의 영정을 모셨고 정면에는 외삼문인 안국문과 내삼문을 세웠다. 안국사는 고려시대 목조 건축양식을 대표하는 영주 부석사 무량수전을 본떠 세웠으며 팔각 청기와 지붕이 올려져 있어 웅장한 느낌을 준다. 문 안에는 낙성대 3층석탑을 강감찬 장군 생가터에서 옮겨왔으며, 탑 맞은편에는 사적비를 세워놓았다. 낙성대공원은 울창한 숲을 이루고 계절 따라 꽃이 피고, 가을에는 단풍으로 물들어 풍광이 아름다운 곳이다. 자작나무숲, 소나무숲, 참나무숲, 사시나무 군락지, 전나무숲 등이 형성되어 있으며 산책로의 길이는 총 3km에 이른다. 그 외에도 연못과 야외놀이마당, 야외예식장도 있다. 위치가 서울대학교 후문으로 통하는 길과 인접해 있기 때문에 찾는 사람이 많아 서울 남부지역의 명소가 되고 있다.', '1', '5', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56688', '756580', '관악산 생태공원', '', '', '6', '37.4692949186', '126.9125881016', '', '서울특별시 관악구 문성로16다길 133-17 (신림동)', '', '', '2007년 12월 개원한 관악산 생태공원은 미성동 일대 76,521m²의 면적으로 조성되었다. 지형 여건을 최대한 살려 상부는 생태숲 구역으로, 물길이 모이는 하부 쪽은 생태연못 구역으로 각각 나누었다. 생태숲 구역은 활엽수림으로 복원했고, 생태연못 구역에는 수생식물과 다양한 수서생물이 살 수 있도록 하였다. 생태연못 및 야외학습장은 개구리, 청설모, 딱따구리 등 다양한 동, 식물을 관찰하고 느티나무, 자작나무, 낙우송 등 나무 그늘에서 쉴 수 있는 공간이 마련되어 있다. 또한, 생태학습장과 비를 피할 수 있는 정자를 만들어 다양한 생태학습이 가능하도록 조성하였다. 생태공원 옆으로 유아숲체험원이 있어 어린아이들이 숲속교실, 숲속동물원, 나무암벽, 통나무건너기 등 숲 속에서 다양한 경험과 놀이를 할 수 있다. 관악산생태공원안에는 선우체육관, 테니스장 등의 운동시설과 산책로가 있어 가벼운 산책과 운동을 즐길 수도 있다.', '1', '5', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56689', '756594', '관악산 호수공원', '', '', '6', '37.4585523416', '126.9473157972', '', '서울특별시 관악구 신림동', '203', '<a href=''https://www.gwanak.go.kr/site/gwanak/05/10503010300002016051205.jsp''target=''_blank'' title=''새창 : 관악구청 홈페이지로 이동''>https://www.gwanak.go.kr</a>', '관악산 호수공원은 관악산 제1광장에 닿기 전에 위치해 있다. 부지면적 6,406㎡, 담수 면적 2,485㎡으로 1997년 3월 준공되었다. 정자, 분수, 수변무대, 나무다리, 인공섬 등이 설치되어 있어 관악산을 찾는 많은 사람들이 휴식공간으로 활용하고 있다. 또한 인근에는 숲속 작은도서관과 야외 식물원이 조성되어 있어 주민들에게 큰 호응을 얻고 있다.', '1', '5', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56690', '756590', '관악산공원 야외식물원', 'http://tong.visitkorea.or.kr/cms/resource/45/755545_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/45/755545_image3_1.jpg', '6', '37.4585690064', '126.9472963541', '', '서울특별시 관악구 대학동 203번지 일대', '', '<a href=''https://www.seouldanurim.net/attractions/D/TOURINFOTYPE2/35253'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://www.seouldanurim.net</a>', '관악산공원 입구에서 5분 정도 걸어 들어가면 관악산공원 야외식물원을 만날 수 있다. 등산로 초입에 야생화 학습장이 있어 신기한 꽃들과 사진도 찍고 푯말에 표기된 꽃들의 이름을 외우는 재미도 있다. 계곡 옆 자연학습장에 조성된 1,000㎡ 규모 휴식 공간에는 19종의 농작물과 1,630본의 식재가 있어 농촌풍경을 그대로 옮겨 놓은 듯하다. 5월 말~6월에는 장미농원에서 장미 오감 체험도 할 수 있다. 식물원 중앙의 장미원을 비롯한 초화원, 관목원, 철쭉동산, 자연학습당, 벚나무쉼터, 물놀이데크, 간이퇴비장, 건강쉼터 등이 있다. 또 정자, 그늘막, 원두막 같은 조형물을 만들어 두었다. 상류 쪽으로 1km에 이르는 구간에는 어른 허리까지 물이 차는 자연형 수영장이 있으며, 관악산공원 정문을 통해 야외식물원 쪽으로 올라가면 신림계곡이 있다. 이곳에서 둘레길 2구간인 호압사 방향의 관악산 도란도란 걷는 길로 올라갈 수 있다.', '1', '5', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56691', '3081140', '관악산자연공원', 'http://tong.visitkorea.or.kr/cms/resource/38/3081138_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/38/3081138_image3_1.JPG', '6', '37.4663402951', '126.9455546273', '', '서울특별시 관악구 신림동', '산27-1', '<a href=''https://parks.seoul.go.kr/parks/detailView.do?pIdx=36'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://parks.seoul.go.kr</a>', '관악산자연공원은 서울특별시 관악구에 위치한 관악산에 조성된 공원이다. 1968년 도시자연공원으로 지정되었으며 수많은 서울 시민들이 즐겨 찾는 서울의 명소이다. 관악산자연공원은 정자, 분수 등이 설치되어 있는 휴식공간인 호수공원, 어린이도서뿐만 아니라 환경도서도 보유하고 있는 숲속 도서관, 우리 고유의 전통문화를 재현해 선보이는 전통 야외 소극장, 무장애숲길, 전망대 등으로 구성되어 있다. 식재된 주요 식물로는 소사나무, 산구절초, 산개나리, 할미밀빵 등이 있으며 천연기념물로는 회나무, 노거수 굴참나무 등이 있다. 체험 가능한 산림 프로그램으로는 치유 산책, 명상, 걷기 등을 즐길 수 있는 산림치유 프로그램과 숲을 이해하고 공감하며 관악산 숲의 동식물을 관찰해 보는 관악산 숲속생태체험관 프로그램 등이 있다.', '1', '5', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56692', '126916', '관음사(서울)', 'http://tong.visitkorea.or.kr/cms/resource/03/566503_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/03/566503_image3_1.jpg', '6', '37.4676601403', '126.9765239933', '', '서울특별시 관악구 승방1길 109-80', '(남현동)', '', '관음사는 관악산 주봉에서 북동쪽으로 뻗어있는 마지막 봉우리의 기슭에 있는 사찰이다. 대한불교조계종 직할교구 본사인 조계사의 말사이다. 신라 제51대 진성여왕 9년(895)에 도선국사가 창건한 비보사찰의 하나다. 비보사찰은 명당에 절을 세우고 기도하면 나라가 융성하게 된다는 불교의 호국 사상에서 비롯되었다. 천여 년 동안 기도의 도량으로 사용되어 오다가 1924년 주지 석주가 증축하였다. 1974년 진선당 박종하 스님이 주지로 부임하여 중건하기 시작하여 대웅전, 용왕각, 종각, 즉심시불의 비문석탑을 세웠다. 항간에 조선 고종 20년경에 봉은사의 스님들이 개창했던 것이라고 하지만 실제로는 그 당시 중건되었던 사실이 와전된 것으로 생각된다. 세간의 소리를 듣고 있다가 곧바로 구제해 준다는 관세음보살을 모시고 있기에, 관음사라고 부르게 되었다고 한다.', '1', '5', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56693', '2779550', '관음사국기봉', 'http://tong.visitkorea.or.kr/cms/resource/56/2796456_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/56/2796456_image2_1.jpg', '6', '37.4403515161', '126.9383143508', '', '서울특별시 관악구 신림동', '산 56-2', '<a href=''https://parks.seoul.go.kr/parks/detailView.do?pIdx=36'' target=''_blank'' title=''새창 : 서울의 공원 홈페이지로 이동''>https://parks.seoul.go.kr/parks/</a>', '서울 한강 남쪽에 우뚝 솟은 관악산은 관악구 신림동과 경기도 안양시, 과천시의 경계를 이룬다. 산꼭대기는 바위로 이루어져 있는데, 그 모습이 마치 갓을 쓰고 있는 것처럼 보여 관악산이란 이름이 붙었다. 대중교통으로도 쉽게 찾아갈 수 있을 만큼 접근성도 좋고 웅장한 바위 능선을 자랑해 등산객들에게 늘 인기 코스로 꼽힌다. 그중에서도 통일신라 때 창건한 사찰인 관음사를 끼고 국기봉으로 오르는 길은 짧고 굵게 관악산의 매력을 즐길 수 있는 코스로 통한다. 바위가 많고 일부 급경사를 이루는 구간도 있지만, 정상에 오르면 그동안의 수고로움을 잊을 만큼 탁월한 전망이 펼쳐진다. 우수경관 전망대란 이름으로 느긋하게 쉬어갈 수 있는 공간도 마련돼 있다. 관음사 국기봉은 성인 기준으로 2시간이면 넉넉하게 오르내릴 수 있다.', '1', '5', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56694', '1605990', '관훈동 민씨 가옥 (구 부마도위박영효가옥)', 'http://tong.visitkorea.or.kr/cms/resource/88/3077788_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/88/3077788_image3_1.JPG', '6', '37.5585082277', '126.9935754302', '', '서울특별시 중구 퇴계로34길 28', '', '▪ <a href=''https://www.heritage.go.kr/heri/cul/culSelectDetail.do?ccbaCpno=2441100180000&pageNo=1_1_1_1&sngl=Y'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://www.heritage.go.kr</a>', '관훈동 30-1에 위치한 민씨 가옥은 조선 말기의 정치인이자 친일인명사전에 등록된 민영휘 일가가 거주한 곳이다. 민영휘는 일제강점기 판서, 한일은행 은행장 등을 역임한 관료로 막대한 부를 누렸으며 1895년 안국동으로부터 교동으로 이주하면서 관훈동 일대의 토지를 매입해 일가를 거주하게 했다. 1936년 기준으로 총 6동의 목조와즙과 2층 양옥이 있었던 것으로 보이나 1976년 가옥 중 일부가 멸실되고 안채 일부와 문간채 등 목조와즙 2동만 남아있다. 1977년 서울시 민속자료로 지정되었다. 민영휘와 그 본처 대방마마 평산신씨와 입양한 장남 민형식 등은 경운동에, 첩인 해주마마 안유풍과 그 아들 민대식은 관훈동에 살았으며 그 자식들까지 경운동과 관훈동 일대에 한데 모여 살았다. 관훈동 30번지는 1970년대까지 민영휘의 자손들이 소유하고 있었다가 경매와 매매를 거친 끝에 1986년 경인미술관이 소유권을 갖게 되었다. 경인미술관 소유 당시인 1995~1998년 남산골 한옥마을로 안채 일부와 문간채가 이전 복원되어 오늘에 이르고 있다.', '1', '24', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56695', '130938', '광나루안전체험관', 'http://tong.visitkorea.or.kr/cms/resource/02/720502_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/02/720502_image3_1.jpg', '6', '37.5513105664', '127.077162465', '', '서울특별시 광진구 능동로 238', '', '<a href=''https://fire.seoul.go.kr/gwangnaru/main/main.do'' target=''_blank'' title=''새창 : 광나루안전체험관 홈페이지로 이동''>https://fire.seoul.go.kr</a>', '광나루안전체험관은 광진구 능동 서울어린이대공원 정문 옆에 있다. 이곳은 화재나 지진, 태풍 등 각종 재난 상황을 가상으로 설정하여 일반 시민들이 직접 체험하면서 안전교육을 받을 수 있는 국내 최초의 재난 체험관이다. 각종 재난·재해로부터 불특정 다수인들에 대하여 재난에 대처할 수 있는 능력을 기르고 안전 의식을 함양할 수 있는 가상 재난 체험 시설의 필요성이 제기되면서 광나루안전체험관이 설립되게 되었다. 광나루안전체험관은 지하 1층, 지상 3층 연면적 5,444.5㎡ 규모로서 각종 체험시설을 통하여 운영요원(소방공무원 등)에 의한 설명과 함께 직접 체험할 수 있다. 체험하기 위해서는 온라인 사전 예약은 필수이며, 긴바지와 양말을 착용해야 한다. 반바지, 치마, 맨발의 경우 체험이 불가하다.', '1', '6', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56696', '3065938', '광나루자전거공원', 'http://tong.visitkorea.or.kr/cms/resource/16/3065916_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/16/3065916_image3_1.jpg', '6', '37.5453008895', '127.1179978077', '', '서울특별시 강동구 선사로 83-19 (천호동)', '', '', '광나루자전거공원은 강동구에 위치한 광나루한강공원과 이어진 자전거 산책로이다. 한강을 바라보며 자전거를 탈 수 있으며, 보다 주변 공원과 함께 산책을 즐길 수 있다. 광나루자전거공원에는 한강 야경과 노을을 보면서 달릴 수 있다는 것이 가장 큰 장점이다. 자전거를 대여할 수도 있지만, 인근에 타이어 공기 주입기가 있어 개인 자전거를 가져와 타기에도 편하다. 광나루자전거공원에는 어린이 자전거 교육장과 자전거 트랙도 있어 가족과 연인끼리 여유롭게 탈 수도 있다. 광나루자전거공원의 입구는 수도권 지하철 암사역 3번 출구에서 약 900m 정도 떨어진 거리에 있다.', '1', '2', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56697', '250252', '광나루한강공원', 'http://tong.visitkorea.or.kr/cms/resource/11/3393811_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/11/3393811_image3_1.JPG', '6', '37.549969869', '127.1222583979', '', '서울특별시 강동구 선사로 83-66', '', '<a href=''https://hangang.seoul.go.kr/www/contents/645.do?mid=429#:~:text=%EA%B3%B5%EC%9B%90%EA%B0%A4%EB%9F%AC%EB%A6%AC-,%EA%B4%91%EB%82%98%EB%A3%A8%20%EC%86%8C%EA%B0%9C,-%EB%AF%B8%EB%9E%98%ED%95%9C%EA%B0%95%EB%B3%B8%EB%B6%80'' target=''_blank'' title=''새창 : 한강공원 홈페이지로 이동''>https://hangang.seoul.go.kr/</a>', '강동대교에서 잠실철교 사이에 있는 광나루 한강공원은 한강 상류로부터 유입된 토사가 퇴적되어 자연스럽게 형성된 모래톱과 대규모 갈대군락지로 자연 그대로의 한강의 모습을 가장 잘 유지하고 있는 공원이다. 서울시의 유일한 상수원보호구역으로 뱃놀이와 각종 수상 및 레저활동이 금지되어 있어 물이 맑고 깨끗하며, 북쪽 아차산 수목의 푸르름과 2㎞에 이르는 한강둔치의 갈대밭이 잘 조화되어 아름다운 경관을 자랑한다. 또한 광나루 한강공원은 철새들의 서식처이기도 하다. 근처에는 암사선사주거지 등 많은 문화유적지가 곳곳에 산재해 있어 자연과 문화가 잘 조화를 이루는 곳이다.', '1', '2', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56698', '814195', '광나루한강공원 암사생태공원', '', '', '6', '37.5500170857', '127.1222833943', '', '서울특별시 강동구 선사로 83-66', '', '한강사업본부 <a href=''http://hangang.seoul.go.kr/'' target=''_blank'' title=''새창 : 한강사업본부 사이트로 이동''>http://hangang.seoul.go.kr</a>', '암사동 한강변의 콘크리트 인공호안과 자전거 도로를 철거하고 갈대·물억새 군락지, 야생화, 산책로 등을 갖춘 자연형 호안과 생태공원을 조성하였다.* 면적 : 162,000㎡<br>', '1', '2', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56699', '3060896', '광진광장공원', 'http://tong.visitkorea.or.kr/cms/resource/11/3052711_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/11/3052711_image3_1.jpg', '6', '37.5477470211', '127.0732295916', '', '서울특별시 광진구 광나루로 389 (군자동)', '', '', '광진광장은 2006년 개장한 작은 공원으로, 어린이대공원역 바로 옆 광진광장 공영주차장 바로 위에 있는 공원이다. 넓은 광장과 옆으로는 나무그늘과 벤치가 있는 이곳은 아이들이 뛰어놀기 좋은 곳이다. 광진의 꿈이라는 조형물이 있는데, 사물놀이 상모 궤족을 띠 모양으로 표현하고, 광진구의 16개 동의 수만큼 16개의 물줄기가 있다. 여름이면 물기둥에서 시원하게 분수가 나온다. 광진광장의 그늘들은 지붕에 태양광 패널이 설치되어 있고, 생산된 전기는 광진광장 시설에 사용된다. 또한 재활용 캔, 페트와 같은 순환자원을 회수하는 로봇인 네프론이 있다. 투입구에 캔 이나 페트병을 넣으면 자동으로 감지해 수거되고 휴대폰 번호를 입력하면 1씨앗이 적립된다.', '1', '6', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56700', '1366921', '광진교 8번가', '', '', '6', '37.5444759163', '127.1141592915', '', '서울특별시 강동구 구천면로 77', '(천호동)', '<a href=''http://www.riverview8.co.kr'' target=''_blank'' title=''새창 : 광진교 8번가 홈페이지로 이동''>http://www.riverview8.co.kr</a>', '광진교의 상판 아래 8번째 교각에 위치해 있는 광진교 8번가는 최고의 절경에서 공연과 전시라는 소재를 통해 다양한 문화콘텐츠를 만들어 시민들의 문화 감성을 충전하고 편안한 휴식을 즐기기 위한 시민 문화공간이다. 전 세계적으로 3개밖에 존재하지 않는 교각 하부 전망대인 광진교 8번가는 좋은 경관의 공간을 문화 활동을 위한 공간으로 변모시켰다. 광진교 8번가에서는 공연과 미술을 즐길 수 있다. 확 트인 전망과 아름다운 공간의 분위기 속에서 재즈, 클래식, 힙합 등 테마에 어울리는 여러 공연을 볼 수 있다. 또한 미술관에서는 친숙한 그림과 액세서리를 중심으로 전시하고 프로뿐만 아니라 아마추어의 작품 또한 즐길 수 있다. 이 밖에도 일상생활의 소품 등 다양한 전시를 볼 수 있는 편안한 미술관이다. 그리고 이곳은 한강을 즐기는 시민을 위한 공간 역할도 하고 있다. 음악 감상이나 독서 등을 할 수도 있고 더불어 꽃꽂이, 카드 만들기 등의 문화강좌를 즐길 수 있는 곳이다.', '1', '2', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56701', '3094518', '광통교', 'http://tong.visitkorea.or.kr/cms/resource/11/3094511_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/11/3094511_image3_1.jpg', '6', '37.5691539885', '126.9811151529', '', '서울특별시 종로구 서린동', '', '', '광통교는 조선 시대 청계천에 놓인 다리 중 가장 규모가 컸으며, 도성 내 주요 도로를 연결하는 중요한 다리였다. 현재는 청계광장을 기준으로, 청계천의 2번째 다리다.  태조가 조선 초 도성을 건설할 때 흙으로 놓았는데 태종 10년(1410) 큰 비로 다리가 떠내려가자, 태조의 왕비 신덕왕후 강씨의 능인 정릉 옛터의 병풍석 등 석물을 가져다 돌다리로 만들었다. 광통교는 조선시대 육조거리-운종가-숭례문으로 이어지는 도성 안 중심 통로였으며, 주변에 시전이 위치하고 있어 도성에서 가장 많은 사람들이 왕래하던 다리였다. 특히 옛부터 서울에서는 큰 다리로 알려져 정월 대보름이 되면 도성의 많은 남녀가 이곳에 모여 답교놀이를 하던 곳으로 유명했다.광통교는 1958년 청계천 복개 공사로 인해 철거되었으나, 2005년 청계천 복원 사업에 따라 다시 복원되었다. 복원된 광통교는 원래의 모습과 유사하게 석조 다리로 만들어졌으며, 길이 30m, 폭 6m, 높이 4m 규모다. 지금도 청계천을 건너는 다리로 사용하는 서울의 대표적인 문화유산이며 청계천은 많은 시민들이 찾는 명소다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56702', '126512', '광화문', 'http://tong.visitkorea.or.kr/cms/resource/72/3069472_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/72/3069472_image3_1.JPG', '6', '37.576072552', '126.9768042386', '', '서울특별시 종로구 사직로 161', '', '<a href=''https://gwanghwamun.seoul.go.kr/main.do'' target=''_blank'' title=''광화문 광장 사이트로 이동''>https://gwanghwamun.seoul.go.kr/main.do</a>', '광화문은 경복궁의 남문이며, 궁성의 정문이다. 광화문은 국왕이 드나드는 정문이기도 했지만, 조선의 법궁인 경복궁의 정문이었기 때문에 다른 궁궐의 정문에 비해 그 규모와 격식 면에서도 매우 웅장하고 화려했다. 또한 광화문은 담장 끝 동쪽과 서쪽에 각각 동십자각과 서십자각을 두어 조선의 5대 궁궐 가운데 유일하게 궐문 형식을 갖추고 있기도 했다. 광화문은 중층으로 된 문루를 받치는 기단석축에 세 개의 홍예로 이루어져 있는데, 중앙의 홍예로는 왕이, 좌우의 홍예로는 왕세자와 신하들이 출입하도록 하였다. 문루(門樓)에는 종을 걸어 때를 알리는 데 사용하였다. 이처럼 경복궁이 조선왕조의 법궁이라는 점은 광화문을 비롯한 성문의 형태에서도 확인할 수 있다. 다른 궁의 문들은 낮은 단 위에 문을 세우지만 경복궁의 문은 돌로 높은 석축을 쌓고 중앙에 홍예문을 내서 마치 성곽의 문과 같은 모습을 취한다. 광화문은 그중에서도 홍예문을 셋이나 내서 가장 격식 높은 모습을 보여준다. 광화문이 법궁의 정문이라는 사실을 잘 드러내준다. 경복궁 창건 당시에는 특별한 이름이 없이 궁제에 따라 ‘오문(午門)’으로 부르다가, 태조 3년(1395) 정도전에 의해 ‘정문(正門)’으로 이름을 바꾸지만, 세종 8년(1426)에 경복궁을 수리하면서 집현전에서 ‘광화문(光化門)’이라 이름을 지어 올리면서 지금의 광화문이란 이름을 얻게 되었다.원래 광화문은 조선총독부 건물을 지으면서 궁성의 동문인 건춘문 북쪽으로 옮겼으나 한국전쟁 때 폭격을 맞아 문루가 부서지고 석축은 탄흔투성이가 되었다. 그런 가운데 광화문이 현재의 모습으로 재건된 것은 1968년 박정희 정권에 이르러서다. 파괴된 문루를 다시 짓고 광화문을 현재의 자리로 옮겨 놓았던 것이다. 그러나 새로 재건한 광화문은 철근 콘크리트로 지어졌으며, 재건 당시 광화문의 축을 경복궁의 중심축에 맞춘 것이 아니라 당시 중앙청으로 쓰이던 구 조선총독부 청사의 축에 맞추어, 그 결과 3.5도가량 본래의 축과 어긋나게 틀어지고 말았다. 그 뿐만 아니라 원래의 광화문 자리에서도 14.5미터가량 뒤로 물러나 지금의 자리에 서있게 되었다. 이렇게 원형을 잃어버린 광화문을 복원하고자 2006년부터 광화문 철거작업을 시작해 3년 8개월의 복원 공사를 마치고 2010년 8월 15일 완공되었다. 화강암으로 육중한 기단을 만들고 그 위에 이층의 문루를 얹어 멀리 조망하기 좋을 뿐 아니라 궁궐 정문으로서의 위엄을 나타내고 있다. 기단에 있는 3개의 아치형 출입문은 경복궁이 조선의 정궁이라는 위엄을 과시한다. 돌로 만든 기단 위에는 흙을 구워 벽돌처럼 만든 ‘전돌’로 나지막한 담을 둘렀는데, 여기를 장식한 팔괘 문양은 조선조 궁궐의 특성을 잘 반영하고 있다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56703', '775394', '광화문광장', 'http://tong.visitkorea.or.kr/cms/resource/24/3083624_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/24/3083624_image3_1.JPG', '6', '37.5727035021', '126.9769709861', '', '서울특별시 종로구 세종대로 172', '', '<a href=''https://gwanghwamun.seoul.go.kr/main.do''target=''_blank'' title=''새창: 광화문광장''>https://gwanghwamun.seoul.go.kr/main.do</a>', '새로운 모습으로 단장하여 시민들에게 개방된 광화문 광장은 600년 역사를 지닌 서울의 중심거리 세종로를 차량 중심의 거리에서 인간 중심의 공간으로 전환하고, 경복궁과 북악산 등 아름다운 자연경관을 조망할 수 있는 공간으로 새롭게 조성하여, 세종로의 옛 모습인 육조거리 복원을 통한 역사·문화 체험 공간으로 재탄생되었다.초입 구간인 ‘광화문의 역사를 회복하는 광장’에는 월대를 재현하고, 해태 상을 복원하였으며, 세종로 공원 주변 구간인 ‘육조거리의 풍경을 재현하는 광장’에는 과거 한양의 중심거리였던 육조거리의 흔적을 재현하고 이를 형상화한 축소모형도 설치하였다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56704', '2500218', '광흥당', 'http://tong.visitkorea.or.kr/cms/resource/35/3393035_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/35/3393035_image3_1.JPG', '6', '37.5486359893', '126.9286760682', '', '서울특별시 마포구 독막로21길 15', '', '<a href=''http://www.mapocc.or.kr/page/page5'' target=''_blank'' title=''새창 : 마포문화원 홈페이지로 이동''>http://www.mapocc.or.kr</a>', '마포 와우산 자락에 자리 잡고 있는 광흥당은 공민왕 사당 오른쪽에 있는 한옥 문화공간으로 전통문화의 소중함을 일깨우기 위해 다양한 프로그램을 운영하고 있다. 마포구의 향토 사료 발굴‧전시 및 청소년 등을 위한 교육 프로그램을 운영하고 있다. 정월대보름에는 근대 이전의 풍속과 생활 모습이 반영되어 민간에 전해져 오는 정월대보름 놀이, 연날리기, 지신밟기, 석전, 척사대회를 진행하고 있다. 성년의 날에는 전통 성년식이 진행되며, 한국의 전통 국악을 알리기 위한 국악한마당, 마포 국악울림 공연 등 다채로운 행사가 진행된다. 또한 공민왕 사당제를 구민 화합 및 축제의 장으로 발전시켰으며 장구반, 판소리, 국선도 등의 문화강좌가 유료로 열리고 있다.', '1', '13', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56705', '129502', '광희문', '', '', '6', '37.56437728', '127.0096987947', '', '서울특별시 중구 퇴계로 344', '', '<a href=''http://www.junggu.seoul.kr/tour/'' target=''_blank'' title=''새창 : 서울 중구 문화관광 홈페이지로 이동''>http://www.junggu.seoul.kr/tour/</a>', '광희문은 태조 5년(1396) 도성 창건 때 동남쪽에 세운 소문이다. 광희문은 실질적인 도성의 남소문으로 이를 흔히 수구문으로 불렀다. 청계천이 흘러 나가는 곳에 세워진 수구가 거리상으로는 광희문보다는 동대문이 가깝지만, 남소문이 장충단공원에서 한남동으로 넘어가는 언덕에 따로 있었기 때문에 편의상 수구문이라 부르게 된 것으로 보인다. 그리고 수구문은 실제로는 시구문으로 이용되었으니, 서쪽의 서소문과 함께 도성 내의 장례행렬이 동쪽 방향으로 지날 때 통과하는 문이었다.임진왜란으로 도성과 궁성이 파괴될 때 광희문도 파괴된 것으로 보인다. 숙종 때 기록을 보면 남소문과 광희문의 자리를 혼동하기도 하고, 성문 터과 군영의 위치 확인도 어려울 정도로 파괴된 도성을 수축하지 않았음을 알 수 있다. 광희문은 도성 수축이 이루어진 이듬해인 숙종 37년(1711)에 중건에 착수하는데, 수구문을 다시 쌓고 아울러 문루도 조성하게 하였다. 수구문은 심하게 파괴되어 석축을 새로 개수하는데 시일이 많이 걸리므로 서대문 지역을 담당하는 아문에 목재를 넘겨주어 돈의문 문루를 만들게 하였던 것이다. 이때에 성문이 수축되고 그 이후 언제 문류가 중건되었는지는 확인되지 않는다. 그런데 숙종 45년(1719)에 수구문은 옛 이름이 광희문이니, 해당 군문에서 액호를 써서 걸 것을 요청하자 대리청정하던 세자가 긍정적으로 받아들였다는 기록이 있다. 또 영조 때에 도성의 문루가 없는 성문을 중건하게 되었는데 그 대상에 광희문은 포함되지 않았다. 그리고 1759년대에 작성된 것으로 추정되는 도성도에는 당시 문류 유무에 따라 성문을 그렸는데 수구문에는 문루가 표현되어 있다. 따라서 광희문은 숙종 때에 재건된 것으로 확인된다.광희문은 일제가 전차궤도를 부설하면서 성곽을 철거할 때도 문과 문루는 그대로 남아 있었으나, 6.25 전쟁으로 문루가 파고 된 상태로 방치되었다. 그 후 1975년에 도로 중간에 위치하게 되어 원위치에서 남쪽으로 15m 옮기는 이전 복원공사가 시작되어 1975년 11월에 준공되었다. 광희문과 관련된 역사적 사실을 보면 인조 2년(1624)에 이괄이 난을 일으켰다가 수구문을 통하여 도망갔고, 병자호란(1636)때에는 임금이 역시 이문을 통과하여 남한산성으로 옮겼다고 한다. 그리고 조선시대 일본 사신들은 옥수동 두모포나루로 한강을 건너 광희문을 통하여 인현동에 있던 지정숙소인 동평관으로 향하였다. 성문은 서울성곽의 다른 소문과 같이 장방형 무사석으로 높게 축조한 육축을 마련하고 육축 가운데 홍예를 앞뒤로 설치하여 통로를 만들고, 그 위에 문루를 조성하였다.', '1', '24', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56706', '252528', '구 서울대학교 본관', 'http://tong.visitkorea.or.kr/cms/resource/18/3077518_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/18/3077518_image3_1.JPG', '6', '37.5798947977', '127.002854534', '', '서울특별시 종로구 동숭길 3', '', '<a href=''https://tour.jongno.go.kr/tour/tour/indvdlz/view.do?nttId=27016&menuNo=2009'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://tour.jongno.go.kr</a>', '구 서울대학교 본관은 일제강점기에 세워진 1931년 옛 경성제국대학의 본관 건물이다. 1930년 8월에 착공하여 1931년 10월에 완공한 벽돌조 3층 건물로, 최초의 한국인 건축가 박길룡이 설계하고, 일본인 미야가와구미가 시공했다.경성제국대학은 1924년 2년제의 예과를 설치하여 개교한 후, 법문학부, 의학부, 이공학부를 차례로 신설하며 대학의 모습을 갖추었다. 해방 이후 1946년 8월 미군정이 국립 서울대학교 설치령을 발표하면서 오늘날 서울대학교로 바뀌고 본관 건물로 사용되었다. 1972년 서울대학교가 관악구 신림동으로 캠퍼스를 옮기면서 유일하게 남아 있는 옛 서울대학교 시설물이 됐다.지하 1층, 지상 3층으로 외부에는 전체적으로 밝은 갈색 타일이 붙어 있다. 평지붕과 장식 없는 외관은 근대주의적 디자인을 추구하던 1930년대의 건축 경향이 잘 드러나 있다. 3층 외관 전체와 중앙 출입구 포치등에는 반원형의 아치가 있고 중앙 출입구 주변 벽면은 완만한 곡면을 이루어 세련되고 우아한 느낌을 준다. 서울대학교 이전 후에는 한국문화예술진흥원 청사로 사용되었는데 이 기관은 2005년 9월부터 한국문화예술위원회로 이름이 바뀌었다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56707', '2761661', '구강사', 'http://tong.visitkorea.or.kr/cms/resource/83/3463583_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/83/3463583_image3_1.jpg', '6', '37.5449150194', '127.1288607188', '', '서울특별시 강동구 천중로 64', '(천호동)', '', '서울 강동구 천호동에 있는 구강사는 서울 동부지역 천태법음 요람이자 포교도량으로 대한불교 천태종 사찰이다. 5층 단독 건물로 5층에 법당이 있으며, 3층에는 교육시설인  ‘금강유치원’이 있으며 옥상에는 정원이 있다. 정기법회는 매월 넷째 주 일요일 오전 11시에 열리고 있으며 불교대학도 운영되고 있다. 지하철 5호선 8호선 천호역 3번 출구 천인중학교 맞은편에 있다.', '1', '2', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56708', '250361', '구공업전습소본관', 'http://tong.visitkorea.or.kr/cms/resource/72/2031872_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/72/2031872_image3_1.jpg', '6', '37.5784255307', '127.0032406515', '', '서울특별시 종로구 이화장길 81', '(동숭동)', '<a href=''http://tour.jongno.go.kr/'' target=''_blank'' title=''새창 : 종로 문화관광 홈페이지로 이동''>http://tour.jongno.go.kr/</a>', '대학로에 자리한 한국 방송 통신대의 역사관 건물이 바로 구 공업 전습소의 역사관 건물이다. 이 건물은 탁지부 건축소에서 설계하고, 일본인 요시다 겐조(吉田謙造)가 시공을 맡아 1907년에 착공하여 1908년에 준공되었다.  공업 전습소는 대한 제국 시기 상공업 진흥정책의 일환으로 설치되어 염직, 직조, 제지, 금은세공, 목공 등의 근대 기술을 교육한 기관이다. 1906년에 발족되면서 원래 화폐를 인쇄하던 전환국 기계시험소가 있던 동숭동에 터를 잡아 본관을 비롯한 부속건물이 지어졌다. 공업 전습소는 1910년 일제강점기가 시작되면서 대한 제국의 통감부 소관에서 조선총독부 소관으로 이관되었고, 1912년 4월 다시 중앙 시험소 부설로 개설되었다가 1916년 4월 경성공업전문학교가 개설되면서 막을 내리게 된다. 이후 중앙 시험소 등으로 명맥을 유지하다가 현재 한국 방송통신대학의 본관 건물로 사용되고 있다. 이 건물은 2층의 르네상스식 건축으로 전체적으로는 좌우대칭을 이루는 H자형의 목조 건물이다. 대한제국 때 지은 목조건물로는 유일하게 그 원형을 잘 유지하고 있을 뿐만 아니라, 현재 대한제국 탁지부가 설계한 몇 안 되는 현존 건물로서 역사적 가치가 크다.* 면적 - 999㎡<br>', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56709', '3447964', '구로거리공원', 'http://tong.visitkorea.or.kr/cms/resource/36/3455136_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/36/3455136_image3_1.jpg', '6', '37.5030794513', '126.8883643588', '', '서울특별시 구로구 공원로 73-1 (구로동)', '', 'https://www.guro.go.kr/www/index.do', '지역 주민들의 휴식과 여가를 위한 공간이며, 구로구의 도시적 특성과 자연을 조화롭게 담아낸 공간이다. 공원에서는 종종 지역 축제, 공연, 전시 등 다양한 문화 행사가 열린다. 문화 행사로 지역 사회의 유대감을 강화시킨다는 장점이 있다.', '1', '7', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56710', '2553960', '구로공단 노동자생활체험관', 'http://tong.visitkorea.or.kr/cms/resource/88/3400288_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/88/3400288_image3_1.JPG', '5', '37.4828178318', '126.8836153607', '', '서울특별시 금천구 벚꽃로44길 17', '(가산동)', '<a href=''https://gchistory.kr/'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://gchistory.kr/</a>', '금천 순이의 집, 구로공단 노동자생활체험관은 구로공단 역사기념사업의 일환으로 구로공단의 역사와 노동자의 삶을 다룬 금천구의 근현대사 체험관이다. 1960년대 후반부터 80년대까지 구로공단에서 일을 했던 여공들이 기거했던 쪽방을 재현한 뒤, 관람객으로 하여금 당시 여공들의 퇴근 후 생활상을 직접 체험할 수 있도록 조성하였으며, 대한민국 산업화와 민주화에 크게 기여한 구로공단 노동자들의 공로를 기념하고 후대에 전할 목적으로 설립되었다. 상설체험 프로그램으로는 옛날과자 체험, 봉제 및 공예 체험, 컵 받침 만들기 등이 있으며, 특별체험 프로그램으로는 구로공단 역사해설 및 체험관 관람과 진로체험을 할 수 있는 금천 순이와 함께하는 진로체험과 금천 순이와 함께하는 역사투어 등이 있다.', '1', '8', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56711', '3112063', '구로구 반려견 놀이터', '', '', '6', '37.5082258503', '126.8757403642', '', '서울특별시 구로구 구로중앙로42길 27', '구로구 반려견 놀이터', '', '', '1', '7', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56712', '2591803', '구로기계공구단지', 'http://tong.visitkorea.or.kr/cms/resource/47/3392947_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/47/3392947_image3_1.JPG', '6', '37.5038652961', '126.8785568966', '', '서울특별시 구로구 구로중앙로 197 (구로동)', '', '<a href=''http://www.koreatools.co.kr/main.php'' target=''_blank'' title=''새창 : 서울구로기계공구상업단지조합 홈페이지로 이동''>http://www.koreatools.co.kr</a>', '서울시 구로구에 위치한 구로기계공구단지는 기계공구 및 산업용품 유통업체가 밀집된 지역이다. 이곳은 5만여 종의 산업용품을 블록별·동별·품목별로 나누어 배치한 국내 최초의 집단 산업용품 유통 상가 단지로 약 1,700여 개 업체가 입주해 있다. 지상 33개 동의 건물과 상가들이 줄을 지어 있으며, 상가는 4개의 블록, 1동~32동으로 이루어져 있다. A블록은 고무 제품, B블록은 전기 자재, C블록은 기계 공구, D블록은 철강 및 비철금속을 취급한다. 블록 안의 각 동은 서로 다리로 연결되어 있어 건물 간 접근성이 좋다. 단지 주변에도 기계와 공구 관련 상가들이 입주해 있으며 구로역과 서부간선도로가 인접하여 있어 교통 입지가 뛰어나다. 구로기계공구상업단지는 수도권 지하철 1호선 구로역 바로 앞에 위치해 있다.', '1', '7', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56713', '2733981', '구로리공원', 'http://tong.visitkorea.or.kr/cms/resource/43/3468043_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/43/3468043_image3_1.jpg', '6', '37.4904920825', '126.8916060834', '', '서울특별시 구로구 구로중앙로 13', '(구로동)', '<a href=''https://www.guro.go.kr/www/contents.do?key=2791&'' target=''_blank'' title=''새창 : 구로구청 홈페이지로 이동''>https://www.guro.go.kr/</a>', '구로리공원은 서울시 구로구 구로동에 위치한 어린이 공원이다.기존의 단조롭고 노후된 어린이 공원을 창의력을 키울 수 있는 공원으로 새롭게 단장하여 조성되었다. 어린이가 이용할 수 있는 다양한 시설과 조경을 갖추고 있다. 어린이전용 공원으로 운영되고 있어, 어린이 및 보호자 외 출입을 금지한다.', '1', '7', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56714', '2773272', '구룡사', 'http://tong.visitkorea.or.kr/cms/resource/53/3467253_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/53/3467253_image3_1.jpg', '6', '37.4713026828', '127.0489506338', '', '서울특별시 서초구 논현로5길 31-11 (양재동)', '', '<a href=''http://www.guryongsa.com/'' target=''_blank'' title=''새창 : 구룡사 사이트로 이동''>http://www.guryongsa.com/</a>', '서울 서초구 양재동에 있는 구룡사는 도심 지역에서 생활불교를 실천하는 통도사에서 운영하는 서울 포교당이다.대지 2,314㎡, 7층 건물로 지상 2, 3, 4층에 만불보전 법당이 있고 1층에는 문화회관과 유치원이 있다. 불교대학을 운영하고 있으며, 지하 160m에서 끌어올린 구룡토수 (九龍吐水)는 감로수로서 인근 주민들과 불자들에게 공양되고 있다.', '1', '15', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56715', '2357761', '구룡산(서울)', 'http://tong.visitkorea.or.kr/cms/resource/64/2357764_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/64/2357764_image3_1.jpg', '6', '37.4690781677', '127.0615995465', '', '서울특별시 서초구 염곡동', ', 강남구 개포동', '<a href=''https://www.seocho.go.kr/site/seocho/04/10405010800002015070703.jsp'' target=''_blank'' title=''새창 : 서초구청 홈페이지로 이동''>https://www.seocho.go.kr</a>', '구룡산(306m)은 대모산과 하나의 줄기를 가진 산으로 서초구 염곡동과 강남구 개포동 일대에 위치해 있다. 높거나 험하지 않아 부담 없이 등산하기에 좋다.구룡산은 옛날 임신한 여인이 용 10마리가 하늘로 승천하는 것을 보고 놀라 소리를 지르는 바람에 1마리가 떨어져 죽고 9마리만 하늘로 올라가 구룡산이라 불리게 되었고, 하늘에 오르지 못한 1마리는 좋은 재목, 좋은 재산인 물이 되어 양재천이 되었다는 전설이 있다. 정상보다 낮은 이 산의 주봉(主峰)은 국수봉(國守峰)이라고 하는데, 조선시대 전부터 정상에 봉수대(烽燧臺)가 있어 국가를 지킨다고 해서 붙여진 것으로 이곳에는 바위굴이 있어 봉수군(烽燧軍)이 기거했다고 한다.', '1', '15', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56716', '126824', '구암공원', 'http://tong.visitkorea.or.kr/cms/resource/22/3400722_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/22/3400722_image3_1.JPG', '6', '37.5676574399', '126.8537656954', '', '서울특별시 강서구 허준로5길 42', '(가양동)', '<a href=''https://korean.visitseoul.net/nature/%EA%B5%AC%EC%95%94%EA%B3%B5%EC%9B%90_/2027'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://korean.visitseoul.net/</a>', '구암공원은 허준의 출생지인 서울 강서구 가양동에 위치해 있다. 공원 뒤편에는 ‘동의보감’의 저자인 허준에 관한 박물관이 있다. 공원 내에 있는 허가바위는 성인 남성 스무 명이 들어갈 정도로 큰 바위로 임진왜란, 병자호란 등 난리 때 사람들의 피신처로 쓰이기도 했다. 또한 구암공원 내에  설치된 음악 분수는 가로 16m, 세로 3m 규모로 최고 15m 높이의 물줄기를 자랑한다. 음악과 조화를 이뤄 춤추는 분수는 공원의 으뜸 볼거리. 음악 분수가 가장 빛을 발하는 시간은 밤이다. 야간에는 282개의 조명이 켜지고 음악 분수는 화려한 조명 분수로 탈바꿈한다.', '1', '4', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56717', '127379', '구암서원 터(구암정)', 'http://tong.visitkorea.or.kr/cms/resource/20/3400820_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/20/3400820_image3_1.JPG', '6', '37.5678542251', '127.1401145966', '', '서울특별시 강동구 암사동', '', '<a href=''https://www.gangdong.go.kr/web/culture/contents/gdc020_050_060'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://www.gangdong.go.kr</a>', '강동구 암사동 산 1-1번지에 위치한 구암서원은 조선 중기 현종 8년(1667)에 건립된 구천면 최초의 사원이다. 이 지역의 유생들은 여기에서 유교적인 학식과 교양을 쌓아 과거에 응시하여 관료로 입신할 수 있었다. 여기에서 배출된 구천면 유생들은 둔촌 이 집(李集)을 향사하고 숙종 때 구암서원이라는 사액을 받았으며, 이 집(1314~1387)·이양중·어윤겸(1559~1625) · 임숙영(1576~1623) · 정선근(?~1504) 등이 배향되고 노론의 색목이 강하였다. 유생들은 구암서원 건립을 중심으로 하여 향약을 조직, 유교적 마을 공동체를 조직하였다. 하지만 구암서원은 1871년 고종 때 전국의 서원철폐와 함께 사라지고, 1898년 이를 기념하기 위한 유허비가 남아 있을 뿐이다. 원래 구암서원은 삼국시대 바위절터 위에 조성된 것이며, 오늘날 구암정을 건립하여 이를 기념하고 있다.(출처 : 강동구 문화관광)', '1', '2', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56718', '2452856', '구의 아리수 정수센터', '', '', '6', '37.5451087165', '127.0886504844', '', '서울특별시 광진구 광나루로 571', '', '<a href=''https://arisu.seoul.go.kr/c5/sub7_3_2.jsp''target=''_blank'' title=''구의 아리수 정수센터''>arisu.seoul.go.kr</a>', '구의 아리수정수센터에서는 우리나라 정수시설의 변천사를 한눈에 볼 수 있다. 구의아리수정수센터 제1, 2 정수장은 2007년 등록문화재로 지정되기도 하였다. 이곳에서는 서울의 수돗물인 아리수에 대해 구체적으로 알 수 있는 전시관과 아리수가 만들어지는 과정을 견학할 수 있으며, 어린이, 청소년, 성인 등 대상에 따라 맞춤 프로그램을 운영하고 있다. 유치원 단체 대상은 물환경전시관에서 수분 측정기 체험 및 수돗물 생산 과정을 배우는 애니매이션을 시청 후 체험 활동실에서 간이정수 실험, 수돗물 잔류염소 실험 등을 한다. 초등 고학년 이상 학생 대상은 옛날 정수장을 견학하고 체험활동실에서 실험을 진행한다. 성인 대상은 옛날 정수장 해설 투어와 수돗물 아리수 이야기 등을 현장 견학과 함께 체험할 수 있다. 자유 관람은 불가하며 월요일에서 금요일 10:00, 14:00 하루 2회 견학프로그램을 운영한다. 견학을 희망하는 시민들은 공공예약시스템(https://yeyak.seoul.go.kr)과 유선 전화(구의문화재 02-3146-5953, 수도박물관 02-3146-5921)를 통해 신청할 수 있다. 구의아리수정수센터는 수도권 지하철 2호선 구의역 또는 5호선 광나루역에서 도보로 약 15분 거리에 위치해 있다.', '1', '6', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56719', '126524', '국립4·19민주묘지', 'http://tong.visitkorea.or.kr/cms/resource/37/3393837_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/37/3393837_image3_1.JPG', '6', '37.6484480504', '127.0091159038', '', '서울특별시 강북구 4.19로8길 17', '', '<a href=''https://www.mpva.go.kr/419/index.do''target=''_blank'' title=''새창: 국립4.19민주묘지 공식 홈페이지로 이동''> www.mpva.go.kr/419</a>', '삼각산(북한산) 기슭의 국립 4·19 묘지는 1960년 3·15 부정선거를 자행한 권력에 맞서다 1960년 4·19 혁명 때에 희생된 224분이 안장되어 있으며, 이를 기리는 기념탑이 세워져 있다. 기념탑 중앙에는 의롭게 죽어간 이들을 기리는 탑문이 새겨져 있다. 135,901㎡의 묘지 경내에는 소나무와 향나무, 주목과 단풍나무 등으로 아담하게 꾸며져 있으며, 1,652.9㎡ 규모의 기념관과 영정을 모신 유영봉 안소는 한식 목조건물이 330.58㎡ 규모로 확장되어 있다. 그 외에 상징문, 민주의 뿌리, 정의의 불꽃 등 조각 작품과 연못이 있으며, 보도블록을 예쁘게 깔아 놓은 산책로와 야외 의자도 있다. 벚꽃이 피는 5월, 공원에서 바라보는 삼각산(북한산)은 어머니 품과 같은 아늑함과 화려함을 동시에 느낄 수 있으며, 일몰의 풍경은 장관이다.', '1', '3', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56720', '2608977', '국립민속박물관&국립민속박물관 어린이박물관', '', '', '5', '37.5816163907', '126.9789455538', '', '서울특별시 종로구 삼청로 37', '(세종로)', '<a href=''https://www.nfm.go.kr/home/index.do'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://www.nfm.go.kr/home/index.do</a>', '국립민속박물관은 우리나라 전통 생활양식과 관련된 4,000여 점의 민속자료가 전시되어 있는 국내 유일의 민속생활사 관련 국립박물관이다. 본관과 파주관이 있는데 본관은 경복궁 경내에 있다. 본관 안에는 어린이박물관이 있다.국립민속박물관은 민속문화의 연구·수집·보존과 문화교육 및 생활문화 전시로 전통문화에 대한 올바른 인식을 통한 민족적 자긍심을 일깨울 수 있는 문화교육의 현장이다. 전통과 현대가 어우른 문화의 전당으로 3개의 상설전시실과 야외 전시, 2개의 기획전시실이 있다. 상설 전시 [1전시실]에서는 한국인의 하루, [2전시실]에서는 한국인의 일 년, [3전시실]에서는 한국인의 일생에 대해서 전시한다. 야외 전시에는 열두 띠 동상과 연자방아, 장승 동상 등이 전시되어 있고, 1970~1980년대 서울 지역 동네 골목을 재현한 [추억의 거리]가 재현되어 있다.다양한 문화교육 프로그램과 행사는 홈페이지의 [교육안내∙신청]을 통해 선착순으로 참여할 수 있다. 설날이나 추석과 같은 전통 명절에도 전통 놀이와 공연 등이 다채롭게 진행된다. 주한 외국인을 위한 체험행사도 진행하여 한국문화알기에 도움을 준다.국립민속박물관 어린이박물관은 우리나라 전통생활양식에 관련하여 어린이들에게 알기 쉽게 설명해 주고 체험해 볼 수 있도록 전시해 놓은 박물관이다. 어린이박물관은 홈페이지를 통해 1인당 1일 1회만 전시예약이 가능하고 사전예약을 해야 관람할 수 있다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56721', '126521', '국립서울현충원', '', '', '6', '37.5013561691', '126.9732511193', '', '서울특별시 동작구 현충로 210', '', '<a href=''https://www.snmb.mil.kr/sites/snmb/index.do'' target=''_blank'' title=''새창 : 국립서울현충원 홈페이지로 이동''>https://www.snmb.mil.kr</a>', '국립현충원은 나라와 민족을 위해 순국한 영령들이 안장된 국립묘지이다. 국가유공자의 유골, 유해를 안장하고, 그 충과 의를 기리기 위해 설립되었다. 국립서울현충원은 관악산 기슭의 공작봉을 주봉으로 하여 동작의 능선이 병풍 치듯 3면을 감싸고 있으며 앞에는 한강이 굽이쳐 돌고 있다. 1955년 국군묘지로 창설되어 1965년 국립묘지로 승격되었으며, 기관 명칭은 1996.6.1 국립묘지관리소에서 국립 현충원으로 변경하였다.43만여 평의 이곳 성역에는 구한 말의 의병들을 위시하여 조국 광복을 위하여 투쟁하신 애국지사, 나라의 발전과 민족의 번영을 위해 평생을 바치신 국가 유공자, 위기에 처한 나라를 구하다 장렬히 산화하신 국군 장병과 경찰관, 예비군 등 165,000여 순국선열과 호국영령들이 잠들어 있다. 영령은 현충탑 내 위패 봉안관과  묘역에, 무명용사는 납골당에 안장되어 있다. 경내에는 현충탑, 현충문, 충성분수대, 현충지, 현충관, 사진 전시관, 유품전시관, 각 시도 공원 등이 설치되어 있다. 매년 6월 6일 현충일에는 거국적인 추념 행사가 거행되며, 2023년 3월부터 주중 및 토요일에 유가족과 참배객의 교통편의 제공을 위하여 현충원 주요 묘역 및 충혼당을 순환하는 셔틀버스를 운행한다.', '1', '12', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56722', '3448289', '국립중앙박물관전통염료식물원', 'http://tong.visitkorea.or.kr/cms/resource/93/3451093_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/93/3451093_image3_1.jpg', '6', '37.5211706397', '126.9791278024', '', '서울특별시 용산구 서빙고로 137 (용산동6가)', '', 'https://www.museum.go.kr/site/main/content/outdoor_facilities', '국립중앙박물관 전통염료식물원은 한국의 전통 염색 문화를 소개하고 체험할 수 있는 공간으로, 국립중앙박물관 내에 위치해 있다. 이 식물원은 염료로 사용되는 다양한 식물들을 전시하며, 그들이 한국 전통 염색에서 어떤 역할을 했는지를 설명한다. 전통 염색에 사용되는 식물들은 자연에서 자주 자생하는 것들로, 이곳에서는 그 식물들의 특성과 염색 과정, 역사적 배경을 배울 수 있다.', '1', '21', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56723', '1710166', '국제선센터', 'http://tong.visitkorea.or.kr/cms/resource/64/1984664_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/64/1984664_image3_1.jpg', '6', '37.5207742914', '126.8700849711', '', '서울특별시 양천구 목동동로 167', '(신정동)', '<a href=''http://www.seoncenter.or.kr/'' target=''_blank'' title=''새창 : 국제선센터 홈페이지로 이동''>http://www.seoncenter.or.kr</a> <br /><a href=''http://seoncenter.templestay.com'' target=''_blank'' title=''템플스테이 사이트로 이동''>http://seoncenter.templestay.com</a>', '대한불교조계종 국제선센터는 한국 불교와 전통문화의 세계화를 위한 국제적인 센터이다. 총면적 2,110㎡ , 연면적 10,600,34㎡의 지상 7층, 지하 3층 규모로 건립된 국제선센터는 선원, 템플스테이관, 큰 법당, 교육문화관 등의 시설을 갖추고 있다. 센터에서는 간화선 수행, 템플스테이 선문화 강좌, 사찰 요리 전수 등의 한국 전통문화 프로그램을 운영하고 있으며, 도심 한 가운데서도 템플스테이 및 템플라이프를 통하여 불교의 전통 수행법을 배우고 체험할 수 있는 곳이다. 매주 토요일 영어로 진행하는 Dharma Talk는 [외국인들을 위한 명상 교실]로 참선 수행을 배우고자 하는 외국인 뿐만 아니라 재미교포와 영어에 관심있는 내국인들도 자유롭게 참석할 수 있다.수도권 지하철 5호선 오목교역 8번 출구에서 650m에 위치하고 있다.', '1', '19', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56724', '2591805', '국제음식문화거리(INTERNATIONAL FOOD STREET)', 'http://tong.visitkorea.or.kr/cms/resource/28/3467228_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/28/3467228_image3_1.jpg', '6', '37.5058319638', '126.8914195585', '', '서울특별시 구로구 새말로 102', '(구로동)', '', '국제음식문화거리가 조성되는 곳에는 신도림역, 대형 쇼핑몰, 먹자골목 등이 위치해 있어 하루 약 2만 명 이상이 왕래하고 있다.연간 1만 5,000여 명의 교육생이 방문하는 유네스코 아태 교육원이 위치한 지역이기도 하다. 다양한 국가의 음식을 판매하고 있어 골라 먹는 재미가 쏠쏠하다. 또한 문화축제, 플리마켓 등 다양한 문화행사도 진행하고 있다.', '1', '7', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56725', '126827', '국회의사당', '', '', '6', '37.5319710006', '126.9142167446', '', '서울특별시 영등포구 의사당대로 1', '(여의도동)', '<a href=''https://www.assembly.go.kr/portal/main/contents.do?menuNo=600164'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://www.assembly.go.kr</a>', '국회의사당은 대한민국 국회가 위치한 여의도의 중심적인 건물로, 한강과 아름다운 풍경을 배경으로 현대적이고 독특한 디자인의 건물로 알려져 있다. 국회 의원들이 정책과 법률을 토의하고 결정하는 중요한 장소로 활용되며, 그 주변에는 광장과 정원이 조성되어 일반 시민들에게도 공개되어 있다. 국회의사당은 국가의 중요한 의사결정과 역사적인 순간들이 일어나는 곳으로, 일반인들도 특별한 행사나 투어를 통해 이곳을 방문할 수 있다. 현대와 전통이 조화를 이룬 건물은 국가의 정치와 역사를 체험할 수 있는 상징적인 장소로 자리잡고 있다.국회의사당의 회녹색 돔은 처음에는 동판의 붉은 색을 띠고 있었지만, 시간이 지나 동판이 녹슬어 현재의 녹색으로 변했다. 이 육중한 돔은 밑지름이 64m이고 무게가 1,000톤으로 매우 무거우며, 회백색의 처마와 파라펫, 높은 기단, 8각 기둥의 24개 각주로 이루어져 있다. 높이 32.5m인 24개의 각주는 24절기를 상징하고, 전면의 기둥 8개는 우리나라 전국 8도를 나타낸다. 또한, 둘러싸고 있는 24개의 기둥은 국민의 다양한 의견을 상징하며, 돔 지붕은 의회민주정치의 본질을 나타내어 국민의 의견이 토론을 거쳐 모아지는 것을 상징한다.국회의사당에서는 대한민국 국회를 더 가깝게 이해하고 체험할 수 있도록 다양한 서비스가 제공된다. 본회의장 참관, 국회 체험, 어린이박물관 관람 등을 예약하여 국회의 역할과 기능을 체험할 수 있다.', '1', '20', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56726', '2591809', '궁동저수지 생태공원', '', '', '6', '37.5012175047', '126.8293197915', '', '서울특별시 구로구 궁동', '45', '<a href=''https://www.guro.go.kr/www/contents.do?key=2980&'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://www.guro.go.kr</a>', '궁동저수지생태공원은 옛 궁동저수지의 열악한 환경 개선과 더불어 친환경적인 공간을 조성하여 구로구 지역 주민에게 휴식 공간을 제공하고자 조성되었다. 이곳은 생태탐방로, 산책로, 전통 한식 정자, 휴게시설, 체육시설 등이 있어 산책하기 좋으며 25,000여 본의 다양한 꽃과 나무가 식재되어 있다. 동식물을 관찰할 수 있는 공간과 어린이놀이터도 조성되어 있다. 생태연못에는 100여 마리의 비단잉어들이 저수지에 노닐고 있는 등 서울에서 보기 드문 아름다운 풍경과 생태 환경을 자랑한다. 궁동저수지생태공원에는 봄에는 철쭉이 피고 여름에는 연꽃이, 가을에는 갈대가, 겨울에는 얼어버린 저수지의 풍경이 사계절 내내 아름다운 곳이다. 이곳과 인접하여 정선옹주묘역이 있으므로 연계해서 둘러보는 것도 좋다.', '1', '7', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56727', '3456212', '그라운드서울', 'http://tong.visitkorea.or.kr/cms/resource/11/3455811_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/11/3455811_image3_1.jpg', '6', '37.5734729769', '126.9836425781', '', '서울특별시 종로구 인사동9길 26 (견지동)', '', 'http://www.groundseoul.com/', '그라운드서울은 인사동 한복판에 위치한 복합문화공간으로, 다양한 예술 전시와 문화 행사를 한자리에서 만나볼 수 있는 곳이다. 특히, 전통과 현대가 어우러진 독특한 공간 구성과 다채로운 프로그램으로 많은 사람들에게 사랑받고 있다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56728', '1606052', '극락사(서울)', '', '', '6', '37.6047383453', '127.1133520931', '', '서울특별시 중랑구 망우로91길 98', '(망우동)', '', '1968년 4월 10일 건립된 극락사는 태조 이성계가 \'절골\' 이라 이름 붙인 곳에 위치하고 있는데, 공기 좋고 경치 좋은 생활명소로 연혁은 97년 화재로 소실되어 기록으로 남아있는 것이 없다. 하지만, 화재에도 불구하고 신도들이 끊이지 않고 있으며 인근에 있는 약수터나 동래 정씨 묘역 등을 사이에 두고 있다.', '1', '25', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56729', '2756120', '금빛공원', '', '', '5', '37.4527050325', '126.9044198668', '', '서울특별시 금천구 금하로 668', '(시흥동)', '', '서울 금천구 시흥 1동 주민센터 옆에 조성된 근린공원으로 휴식을 취하거나 문화예술 공연을 관람할 수 있는 넓은 공원이다. KBS 전국노래자랑, 금천구 주부가요제 등 각종 문화행사가 많이 열리는 곳이다. 공원 숲속 사이로 ‘찬란한’ 조깅 코스도 만들어 놓아 주민들에게 인기가 많다. 공원 내 피트니스센터에서는 헬스, GX, 골프 등 다양한 프로그램을 진행하고 있어 공원에서 다채로운 시간을 보낼 수 있다.', '1', '8', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56730', '298109', '금선사(서울)', '', '', '6', '37.6207780461', '126.9534467684', '', '서울특별시 종로구 비봉길 137', '', '금선사 <a href=''http://www.geumsunsa.org'' target=''_blank'' title=''새창 : 금선사 홈페이지로 이동''>http://www.geumsunsa.or</a><br /><a href='' https://www.templestay.com/temple_info.asp?t_id=geumsunsa '' target=''_blank'' >☞ 템플스테이 보러가기 </a>', '금선사는 아름다운 자연환경과 솔 내음이 가득한 아름다운 사찰이다. 청와대와 경복궁이 위치한 인왕산이 바로 보이는 종로구 구기동에 소재하고 있다. 이북5도청을 지나서 북한산국립공원 비봉코스를 따라 올라가다 보면 첫 번째 관문인 삼각산 금선사의 일주문을 만나게 된다. 도량의 전각은, 법회를 보는 반야전을 지나면 200년이 넘은 소나무가 중앙에 있으며, 소나무를 지나서 108계단을 오르면 금선사의 주불이 모셔진 대적광전이 사찰의 가장 중심부이면서 가장 높은 곳에 그 위용을 과시하고 있다. 우측으로는 삼성각이 있고, 북한산의 일급수가 모였다가 흘러내리는 홍예교가 자리하고 있다.대적광전 아래에 있는 미타전과 연화당은 정면 3칸, 측면 1칸의 콘크리트 2층 건물로 2004년 법안 스님이 신축했다. 1층은 연화당이라는 편액이 붙은 건물로, 영가들의 유골(영옥 처리)과 위패를 봉안하는 추모시설로 활용되고 있다. 금선사 신도를 중심으로 한 소량(약 600기)의 추모관이며 냉난방을 겸비한 최신형 공간으로 아미타원불과 지장보살원불이 있는 장엄한 공간으로 꾸며져 있다. 2층은 제사 의식과 각종 강의를 위한 미타전이다.홍예교에서 바라보면 맞은편에 앉아있는 적묵당은 5칸 전통 목조 건물로 원형 초석을 두고 그 위로 두리기둥을 세워 익공을 받친 모습으로, 여느 사찰의 요사채처럼 중앙 3칸은 툇마루가 있는 다담방을, 좌우 협칸은 요사채로 구성되어 있고 아래에는 대중공양간인 심검당이 위치한다. 반야전의 아래엔 농산 스님이 300일 기도 후 순조 대왕으로 환생한 기도 성지인 목정굴이 위치하고 이 목정굴 수월관세음 보살님이 바라보시는 정면에는 인왕산이 위치한다.금선사는 템플스테이 정기프로그램을 운영한다. 매주 월요일은 휴무이고 평일 휴식형과 금토 체험형, 당일 단체형 등 다양한 프로그램 중 선택하여 체험할 수 있다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56731', '3044625', '금성전파사 새로고침센터', 'http://tong.visitkorea.or.kr/cms/resource/17/3044617_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/17/3044617_image3_1.jpg', '6', '37.5789844381', '127.0387485112', '', '서울특별시 동대문구 고산자로36길 3 (제기동)', '본관 3층, 4층', '홈페이지 : <a href=''https://goldstar.lge.co.kr/'' title=''새창: 금성전파사 새로고침센터 홈페이지로 이동'' target=''_blank''>https://goldstar.lge.co.kr/</a><br>인스타그램 : <a href=''https://www.instagram.com/goldstar_since1958/'' title=''새창: 금성전파사 새로고침센터 인스타그램으로 이동'' target=''_blank''>https://www.instagram.com/goldstar_since1958/</a>', '전통시장 및 지역경제 활성화를 위해 경동시장, 스타벅스와 함께 LG전자가 만든 상설 고객경험공간으로 제품을 수리하는 대신, 고객분들의 고민을 접수하고 스타일부터 마음까지 새로고침 할 수 있는 경험을 선사해 드리고 있다. LG전자의 전신인 금성사 시절의 최초 제품을 통해 중장년 세대에게는 과거의 추억들을 소환시키고, ThinQ방탈출, 금성오락실 등의 프로그램을 통해 MZ세대에게는 즐거움과 새로움을 선사하고 있다.', '1', '11', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56732', '3082221', '금암문화공원', 'http://tong.visitkorea.or.kr/cms/resource/15/3082215_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/15/3082215_image3_1.JPG', '6', '37.64440244', '126.9179965551', '', '서울특별시 은평구 진관동', '53-5', '', '금암문화공원은 은평구 진관동 구파발에 있는 공원으로 조선 후기를 빛냈던 할아버지와 손자의 이야기가 있는 은평의 역사 문화가 깃든 곳이다.이곳은 조선시대 군사정보를 전달하기 위해 파발꾼이 말을 바꿔타는 금암참이 있던 자리로, 왕이 능행 시 중간에 쉬어가는 길목이었다. 조선 22대 임금인 정조가 조부 숙종의 명릉을 참배하러 다녀오던 길에 휴식 차 이곳 금암발참에 들러, 조부 영조의 선정을 회상하면서 지은 글을 비문에 새긴 금암기적비가 있다. 조선시대 이곳을 지나는 사람은 누구든지 말에서 내려야 한다는 글이 새겨져 있는 하마비도 있다.금암문화공원에는 LED 조명, 멀티미디어, 센서 등 첨단 IT 기술을 다양하게 적용하여 놀이와 게임 교육을 통해 흥미를 주는 어린이 놀이터가 있다. 모래 놀이터도 있는데, 옆에 수돗가도 있어 놀이 후 손을 씻을 수 있다. 어린이 놀이터 건너편에는 어른들 운동기구가 있어서 아이와 어른들이 같은 공간에서 운동을 즐길 수 있다. 운동하면 동력에 의해 불이 들어오는 운동기구가 있어서 야간에 운동하면 재미가 더해진다. 공원 안에는 관리사무실 건물이 있어서 공원 관리가 잘 이루어지고 건물 안에 화장실도 있어서 이용하기에 편리하다.', '1', '22', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56733', '2756126', '금천 체육공원', 'http://tong.visitkorea.or.kr/cms/resource/36/3467636_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/36/3467636_image3_1.jpg', '6', '37.4683755096', '126.9085579394', '', '서울특별시 금천구 독산로54길 102-27', '(독산동)', '<a href=''https://www.geumcheon.go.kr/portal/contents.do?key=2122'' target=''_blank'' title=''새창 : 금천구청 홈페이지로 이동''>https://www.geumcheon.go.kr/</a>', '서울 금천구 주민들의 건강을 위해 체육시설을 잘 갖추어 놓은 공원이다. 하루 이용객이 약 1,300여 명으로 금천구에서 가장 활성화된 대표적인 다목적 공원으로, 농구장, 배드민턴장, 게이트볼장, 조합놀이대 시설과 광장, 야외무대, 정자 등이 설치되어 있다. 새벽에는 운동하는 사람들이, 낮에는 어린이 놀이터에서 뛰어노는 어린이들이, 휴게공간에는 휴식을 취하는 노인층이, 저녁 시간에는 에어로빅 및 저녁 운동을 하는 사람들이 이곳에 와서 다양한 연령층이 공원을 애용하고 있다. 또한 다목적구장은 각종 행사 및 운동 공간으로 활용되기도 한다. 공원 옆에는 어린이들의 놀이터 겸 체험장인 ‘베짱이 유아 숲’이 있다.', '1', '8', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56734', '2756134', '금천 폭포공원', 'http://tong.visitkorea.or.kr/cms/resource/14/3467814_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/14/3467814_image3_1.jpg', '6', '37.4464696458', '126.9044617046', '', '서울특별시 금천구 시흥대로38길 61', '(시흥동)', '<a href=''https://www.geumcheon.go.kr/portal/contents.do?key=2123'' target=''_blank'' title=''새창 : 금천구청 홈페이지로 이동''>https://www.geumcheon.go.kr/</a>', '금천 폭포공원은 서울시 서남부에 있는 총 4,835㎡ 규모의 공원이다. 경기도에서 서울 도심으로 진입하는 관문 성격의 교통 요충지로 박미 고갯길의 자연 지형을 이용하여 경사면에 인공폭포를 조성했으며 상부는 문화회관과 공원시설을, 하부는 연못 형태의 수조가 있는 공원이다. 인공폭포의 3개 물줄기는 금천구의 가산동, 독산동, 시흥동 3개 동을 상징한다.', '1', '8', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56735', '2553946', '금천예술공장', 'http://tong.visitkorea.or.kr/cms/resource/77/3400277_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/77/3400277_image3_1.JPG', '6', '37.4688834875', '126.8919733235', '', '서울특별시 금천구 범안로15길 57', '', '<a href=''https://www.sfac.or.kr/artspace/artspace/geumcheon_main.do''target=''_blank'' title=''새창: 금천예술공장 홈페이지로 이동''>https://www.sfac.or.kr/artspace</a>', '금천예술공장은 2009년 인쇄공장을 리모델링한 시각예술 전문 국제 창작 레지던시이다. 국내‧외 예술가에게 24시간 사용 가능한 창작스튜디오를 제공하며, 오픈스튜디오, 다빈치 크리에이티브 페스티벌 등의 프로그램을 통하여 시민과 예술가의 접점을 만들어가고 있다. 예술가들을 위한 창작스튜디오 19실과 대형 작업을 위한 창고동, 전시실(PS333) 및 워크숍룸 등을 갖추고 있다.', '1', '8', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56736', '2993536', '기기국 번사창', '', '', '6', '37.5859909364', '126.9819618211', '', '서울특별시 종로구 삼청로 118 (삼청동)', '', '<a href=''https://www.heritage.go.kr/heri/cul/culSelectDetail.do?pageNo=1_1_1_1&sngl=Y&ccbaCpno=2111100510000'' target=''_blank'' title=''새창 : 국가유산청 홈페이지로 이동''>https://www.khs.go.kr/</a>', '조선 말기 근대식 무기를 제작하던 기기창 건물로, 강화도 조약이 이루어지고 8년 후인 고종 21년(1884)에 지어졌다. 당시는 무기의 근대화를 위해 새 제도를 마련하고 근대식 군사훈련과 무기제조에 힘쓰던 때이다. 건물의 이름인 ‘번사(飜沙)’라는 말의 뜻은 흙으로 만든 틀에 금속용액을 부어 만드는 것을 말한다. 검은 회색 벽돌로 벽을 쌓았으며 붉은 벽돌로 띠를 두른 후, 지붕 옆면이 사람 인(人) 자 모양인 맞배지붕을 올렸다. 문은 무지개모양이며 정문은 화강암으로 만들고 옆문은 붉은색 벽돌로 띠를 넣어 장식하였다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56737', '2655928', '기억의 터(일본군 위안부)', 'http://tong.visitkorea.or.kr/cms/resource/67/3412267_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/67/3412267_image3_1.JPG', '6', '37.5591608706', '126.9902812673', '', '서울특별시 중구 퇴계로26가길 6', '(예장동)', '<a href=''https://korean.visitseoul.net/attractions/%EC%9C%84%EC%95%88%EB%B6%80-%EA%B8%B0%EC%96%B5%EC%9D%98-%ED%84%B0_/19540'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://korean.visitseoul.net/</a>', '일본군 위안부 기억의 터는 서울특별시 중구 예장동 남산에 있는 공간으로 2016년 개장했다. \'기억의 터\'는 위안부 피해자 할머니들을 기리는 추모공원으로 그들의 증언록과 피해기록, 일제의 만행 등을 기록한 내용을 중심으로 조성되었고 인권과 평화의 의미를 담고 있는 공간이다. 기억의 터·기림비 상설 해설프로그램이 있어서 남산 일본군 위안부 기림비(정의를 위한 연대)와 기억의 터를 잇는 남산 둘레길을 함께 걸으며 일본군 위안부 문제에 대해 올바른 이해를 하고 전문 강사와 함께 눈높이 체험활동, 인권과 평화 이야기를 나눌 수 있다.', '1', '24', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56738', '2756575', '긴고랑 공원', 'http://tong.visitkorea.or.kr/cms/resource/37/3468037_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/37/3468037_image3_1.jpg', '6', '37.5589071568', '127.0910359861', '', '서울특별시 광진구 중곡동 68-19', '', '<a href=''https://www.gwangjin.go.kr/portal/bbs/B0000081/view.do?nttId=139477&menuNo=200280&pageIndex=3'' target=''_blank'' title=''새창 : 광진구청 홈페이지로 이동''>https://www.gwangjin.go.kr/</a>', '서울 광진구 아차산 인근에 위치한 근린공원으로 긴고랑길, 주몽교 등 여러 산책코스로 구성되어 있다. 아차산 안심 둘레길 구간으로 인근 주민 및 등산객들이 많이 이용하는 공원이다. 아차산과 용마산 계곡 길이 만나는 곳의 중곡동 언덕진 동네에서 벽화를 감상하며 걸을 수 있는 긴고랑길을 걷다 보면 산 입구에 다다르게 된다. ‘용마산·아차산 종합안내’와 ‘긴고랑길 아트투어’ 안내도가 설치되어 있어 이용하기 편리하다. 공원 전 구역에서 오토바이와 자전거는 출입을 금지하고 있다. 5호선 아차산역 1번 출구에서 중곡4동 주민센터 근처까지 도보로 17분 정도 걸리고, 5호선 군자역 4번 출구에서 도보로 18분 정도 소요된다.', '1', '6', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56739', '3060915', '긴고랑계곡', 'http://tong.visitkorea.or.kr/cms/resource/48/3052748_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/48/3052748_image3_1.jpg', '6', '37.5619402315', '127.0962659796', '', '서울특별시 광진구 중곡동', '중곡4동 143-130', '', '긴고랑계곡은 서울특별시 광진구와 경기도 구리시에 걸쳐있는 아차산에 위치한 계곡이다. 긴고랑이라는 이름은 아차산의 가장 높은 봉우리인 용마봉에서 내려오는 골짜기가 길어서 [긴골] 혹은 [진골]이라고 부른 데서 유래됐다고 한다. 덥고 습한 날씨에도 청량감이 더하는 곳으로, 도심 속 피서지로 손색없는 곳이다. 계곡은 상류부터 하류까지 모두 이용할 수 있는데, 계곡의 하류도 계곡물의 깊이가 깊지 않아 위험하지 않다. 계곡의 중류는 계단식으로 되어 있고, 발만 담그기에는 좋게 조성되어 있다. 계곡의 상류는 물이 깨끗하고 더 시원하며, 그늘이 많다.', '1', '6', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56740', '127375', '길동생태공원', '', '', '6', '37.5413187204', '127.1568388307', '', '서울특별시 강동구 천호대로 1291', '(길동)', '길동생태공원 <a href=''http://parks.seoul.go.kr/gildong'' target=''_blank'' title=''길동 생태공원 홈페이지로 이동''>http://parks.seoul.go.kr/gildong</a>', '길동생태공원은 여의도 샛강생태공원에 이어 국내에서 두 번째로 조성된 생태공원이다. 도심 속 생물에게 서식처를 제공하고 종 다양성을 높이기 위한 생태공간이자 자연 체험을 통해 생태계를 이해하며 환경보전의 중요성을 느낄 수 있는 곳이다. 천연기념물 7종, 멸종 위기 야생동물 6종, 특산 및 희귀식물 11종 등 2,800여 종의 다양한 생물들이 서식하고 있으며 환경지표종인 반딧불이를 직접 만나 볼 수 있는 반딧불이 체험관, 교육 프로그램 운영을 위한 탐방객 안내센터 등이 위치하고 있다.전체 80,683㎡(24,407평)에 습지지구, 산림지구, 초지지구광장지구를 조성하였으며 야외 강의장, 야외 전시장, 야외 관찰대, 파고라, 조류 관찰대, 수중 섬, 자연초지와 초가집, 움집, 인공 벌통, 돌담, 버섯재배대, 새집, 조류먹이공급대 등이 설치 조성되어 있다. 특히, 조류 관찰대는 나무로 높이 칸막이를 만들고 사방 30cm 정도의 관찰창을 뚫어 새들이 놀라지 않게 세심하게 배려하였다. 계절에 따라 물총새, 왜가리, 원앙, 흰뺨검둥오리, 꼬마물떼새 등을 관찰할 수 있다. 습지지구에서는 수생식물과 수서곤충 개구리 등을 볼 수 있고, 수련 노랑어리 연꽃 갈대창포와 하늘의 사냥꾼 잠자리, 물방개 물자라 등을 볼 수 있다.체험 프로그램으로는 농촌체험학습장이 있다. 계절별로 논밭에서 작물을 재배하는 모습을 관찰할 수 있으며, 프로그램을 통해 재배 과정을 직접 체험할 수 있다. 체험을 통해 볼 수 있는 작물은 수박, 앵두, 오디 등이 있다.', '1', '2', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56741', '2946113', '길상도예', 'http://tong.visitkorea.or.kr/cms/resource/05/2947505_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/05/2947505_image3_1.jpg', '6', '37.48583681', '127.0324406141', '', '서울특별시 서초구 서초동 1362-9 브니엘빌딩', '101호', '<a href=''https://cafe.naver.com/gilsangdoye''target=''_blank'' title=''새창 : 업체카페 이동''>https://cafe.naver.com/gilsangdoye</a><br><a href=''https://www.instagram.com/gilsang_ceramics''target=''_blank'' title=''새창 : 업체인스타그램 이동''>https://www.instagram.com/gilsang_ceramics</a>', '길상도예공방은 일반인을 대상으로 하는 취미반과 전문가 과정으로 나뉘어있다. 취미반 수업 중 도자기 원데이 클래스는 총 2시간 정도 작업이 진행된다. 5인 이상의 단체체험은 따로 문의를 해야 하며 대부분 4인 이하의 인원은 소수정예 클래스로 진행된다. 1:1 또는 1:2~4로 진행되기 때문에 처음 도자기를 접해보더라도 어렵지 않게 최대한 원하는 형태로 제작해 볼 수 있다. 제작기법은 물레성형과 핸드빌딩이 있으며 원하는 도자기의 디자인에 따라 소요되는 시간이 다르므로 개수는 1인당 1개에서 최대 2개까지 가능하다. 완성품은 4주 정도의 기간이 소요되고 직접 가져가거나 택배발송도 가능하다. 길상도예공방은 ‘달려라 방탄’에서 BTS가 체험한 곳으로 아미들에게 유명한 도예공방이다. 지민은 뷔에게, 진은 정국에게, 뷔는 슈가에게 컵을 만들어주었다. 물레와 핸드빌딩으로 컵, 화병, 접시, 물병 등을 디자인하고 만들 수 있다. BTS 멤버들의 작품을 따라 만들어 보는 것도 재미있다.', '1', '15', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56742', '128866', '길상사(서울)', 'http://tong.visitkorea.or.kr/cms/resource/72/3376572_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/72/3376572_image3_1.jpg', '6', '37.5985980194', '126.9939575925', '', '서울특별시 성북구 선잠로5길 68 (성북동)', '', '<a href=''https://www.gilsangsa.or.kr/'' target=''_blank'' title=''새창 : 길상사 공식 홈페이지로 이동''>https://www.gilsangsa.or.kr/</a>', '길상사는 서울 성북구 성북동 삼각산 자락에 위치한 대한불교조계종 사찰이다. 1987년에 김영한 여사가 법정스님의 무소유 청빈 사상에 감동받아 7,000여 평의 대지와 40여 동의 부동산을 기증하며 길상사의 역사가 시작되었다. 법정스님은 1995년에 대법사로 등록하고, 1997년 12월 14일에 길상사로 개명하여 창건되었다.길상사는 극락전, 설법전, 지장전, 범종각, 관세음보살상, 길상보탑, 길상화 공덕비, 법정스님 진영각 등 다양한 건축물과 시설을 자랑한다. 극락전에는 아미타부처님을 주불로 두고, 관세음보살과 지장보살을 협시보살로 봉안하고 있다. 2005년에 옛 건물을 재정비하여 지장전과 선열당(공양간)을, 2008년에는 설법전과 종각을 새로 지어 자연과 조화를 이룬 수행 공간으로 꾸몄다. 사찰 내에서는 불교와 가톨릭 신자인 최종태 조각가의 만남이 빚어낸 섬세하고 오묘한 관세음보살상이 많은 사람들을 온화하게 맞이하고 있다. 2013년에는 법정스님의 유품과 저서를 전시하는 진영각을 개원하였다. 설법전에서 불교대학, 불교입문, 요가, 사경, 선명상 등의 교육 프로그램을 운영하여 교육도량으로서의 신행을 실천하는 역할을 하고 있다.', '1', '17', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56743', '3451976', '길음역롯데캐슬트윈골드상가', 'http://tong.visitkorea.or.kr/cms/resource/65/3451865_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/65/3451865_image3_1.jpg', '6', '37.6038534948', '127.0234880845', '', '서울특별시 성북구 길음로 11 (길음동, 길음역롯데캐슬트윈골드아파트)', '', 'https://www.youtube.com/watch?v=xVxVxZZZYYY', '다양한 노르웨이의 상가가 입점해 있고, 대형 수족관, 카페, 레스토랑, 병원, 생활 편의점 등이 주를 이루고있다. 외부와 반대되는 플라스틱형 상가 구조로, 시각적인 이점을 극대화한다.', '1', '17', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56744', '1827190', '나석주의사동상', 'http://tong.visitkorea.or.kr/cms/resource/66/3077666_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/66/3077666_image3_1.JPG', '6', '37.5655100385', '126.9850482312', '', '서울특별시 중구 을지로 66', '(을지로2가) 외환은행 본점 앞', '<a href=''http://mfis.mpva.go.kr/memorial/movementView.do?sid=1228&memorialType=true&gubunCode=ALL&pageIndex=1&searchCondition=%EC%A0%9C%EB%AA%A9&searchKeyword=%EB%82%98%EC%84%9D%EC%A3%BC'' target=''_blank'' title=''새창 : 홈페이지로 이동''>http://mfis.mpva.go.kr</a>', '나석주의사동상은 조국을 위해 기꺼이 목숨을 바쳤던 나석주 의사의 숭고한 희생정신을 추모하기 위해 현재 외환은행 본점이 위치한 옛 동양척식주식회사 자리에 세워진 동상이다.나석주의사는 동양척식주식회사 농장이 있었던 황해도 재령 출신으로, 3.1운동이 일어나자 겸이포에서 독립 만세 시위에 참여한 뒤 결사대를 조직해 독립군 자금 모집과 친일파 처단 활동을 펼치다가 1920년 9월 중국 상해로 망명하였다. 중국에서 나석주는 임시정부 경무국 경호원과 한단 군관학교를 거쳐 중국군 부대에서 활동하였다. 그 뒤 천진에서 의열단에 입단해 한층 적극적인 독립투쟁을 강구하던 중, 1926년 6월 유림 대표 김창숙을 만나 조국의 강토와 경제를 착취하는 동양척식주식회사와 조선은행, 식산은행 등을 폭파할 계획을 세웠다. 1926년 12월 폭탄과 권총을 휴대하고 서울에 잠입한 나석주는 먼저 조선식산은행에 들어가 대부계 철책 너머로 폭탄 한 개를 던지고 큰길 건너편 동양척식주식회사 경성지점으로 달려가 총격과 함께 또 폭탄 한 개를 던졌다. 그러나 폭탄은 모두 불발에 그치고 말았다. 이어 동양척식주식회사 바로 옆의 조선철도 회사로 들어가 한 차례 더 총격을 가하고 길거리로 나왔는데, 황급히 출동한 경찰대와 기마대의 추격을 받자, 권총으로 자결하였다.수도권 지하철 2호선 을지로입구역 5번 출구에서 120m에 있다.', '1', '24', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56745', '129501', '낙산공원', 'http://tong.visitkorea.or.kr/cms/resource/09/3095709_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/09/3095709_image3_1.jpg', '6', '37.5805725621', '127.0065125148', '', '서울특별시 종로구 낙산길 41', '(동숭동)', '<a href=''https://parks.seoul.go.kr/template/sub/naksan.do'' target=''_blank'' title=''새창 : 낙산공원 홈페이지로 이동''>https://parks.seoul.go.kr</a>', '낙산공원은 대학로와 동대문으로부터 이어지며 역사와 문화를 함께 즐길 수 있는 공원이다. 낙산은 지형이 낙타의 등처럼 생겨 낙타산이라고도 했고, 궁에 우유를 보급하던 왕실 목장이 있어 타락산이라고도 불렀다. 1960년대 이후 시민아파트와 밀집된 주택으로 낙산 본래의 모습이 사라지게 되었고, 서울시가 낙산 복원계획을 수립하였다. 이 계획의 일환으로 공원화 사업이 진행되어 2002년 7월 낙산공원으로 개원하였다. 낙산공원 중앙광장에는 낙산전시관과 중앙무대, 매점 등이 있어서 쉬어가기 좋고 광장 바로 아래 주차장이 있어서 중앙광장으로 오기도 편하다. 낙산전시관은 낙산공원 입구에 있는 시설물로 낙산의 유래, 역사적 인물, 한양도성의 사진 자료 및 모형이 전시되어 낙산공원을 방문하는 시민들에게 다양한 정보를 제공한다. 주말에는 낙산과 서울한양도성에 관한 공원 이용 프로그램이 운영되며, 누구나 무료로 이용할 수 있다. 광장 한쪽으로는 낙산정이 있다. 낙산정은 낙산을 복원하면서 만든 정자로 서울의 전경이 한눈에 보이는 전망 좋은 곳이다.  서울한양도성과 푸른 숲이 어우러져 아름다운 정취를 느낄 수 있는 시민의 휴식 공간으로 자리매김하였으며, 서울에서 가장 아름다운 야경을 볼 수 있는 곳으로 각광받고 있다. 언덕 정상에서 주택과 벽화가 있는 이화동 벽화마을로 갈 수 있고 흥인지문과 한양도성길로 가는 동대문 코스, 외부 성곽 벽면 전체를 보면서 걸을 수 있는 한성대 입구 방면 코스 등 다양한 코스가 있다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56746', '1604941', '낙선재', 'http://tong.visitkorea.or.kr/cms/resource/93/1568293_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/93/1568293_image3_1.jpg', '6', '37.5786003366', '126.993544308', '', '서울특별시 종로구 율곡로 99', '(와룡동)', '<a href=''https://tour.jongno.go.kr/tourMain.do'' target=''_blank'' title=''종로 문화관광 사이트로 이동''>https://tour.jongno.go.kr/tourMain.do</a>', '낙선재는 본래 창경궁에 속해 있던 건물이었으나 근래에 창덕궁에 속한 것으로 취급하게 되었다. 이 건물은 헌종 12년(1846)에 건립한 것으로 본 건물에 접속된 석복헌(錫福軒)과 수강재(壽康齋) 등 3개의 건물을 총칭하여 낙선재(樂善齋)라 부르고 있다.  정문은 장락당(長樂堂)으로 낙선재 남행각(南行閣) 12칸에 설치되었다. 이 문을 들어서면 넓은 마당 뒤로 낙선재가 있다. 정면 6칸 중 우측 한 칸이 전면으로 돌출되어 누마루가 되었고 몸채는 장대석 기단 위에 방형(方形) 초석과 기둥을 한 일반적인 주택풍으로 세워졌다.  누마루의 아래 분구(焚口) 있는 주변은 불규칙한 선의 모자이크 장식이 독특하며 주간의 문짝의 무늬가 다양하다. 집 뒤로는 큰 석재로 쌓아 만든 화계(花階)가 있고 굴뚝과 각 단의 꽃나무, 괴석들이 조화를 이루고 있어 조원(造園)이 뛰어나다. 낙선재에 연접한 석복헌은 역시 정면 6칸, 측면 2칸으로 안사랑에 해당하는 건물이다. 3급 장대석 기단에 계단이 둘 설치되어 있다. 사다리꼴 초석 위에 방주가 섰다. 전면의 주간에는 세장(細長)한 장지문이 달렸는데 그중 중앙 1칸은 머름을 하여 낮고 문살 모양도 다른 문과 다르다. 건물 주위로 행각이 둘렸으며 밖으로 외행각(外行閣)이 나 있고 이것이 수강재에 이어진다. 수강재는 15칸 규모이며 5량 가구(架構)의 홑처마 단층기와지붕을 한 건물이다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56747', '699222', '낙원동 아구찜 거리', 'http://tong.visitkorea.or.kr/cms/resource/02/2526402_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/02/2526402_image3_1.jpg', '6', '37.5732314221', '126.9883060491', '', '서울특별시 종로구 삼일대로 428', '', '<a href=''https://tour.jongno.go.kr/tour/tour/indvdlz/view.do?nttId=34057&menuNo=2226'' target=''_blank'' title=''종로구 문화관광 사이트로 이동''>http://tour.jongno.go.kr</a>', '1972년 낙원동 거리에 서울에서 처음으로 아구찜을 하는 식당이 등장했다. 당시만 해도 아구를 찜으로 해 먹는 것은 저 먼 마산에서만 듣던 소리다. 서울에서 가장 가까운 바다인 인천에서도 아구를 찜으로 해 먹지는 안았다고 한다. 아구찜 거리에 처음으로 문을 연 식당은 그래서 아구찜을 만드는 데 온갖 정성을 다 들였다. 물에 끓으면 흐물흐물해지는 아구의 살을 꼬들꼬들하게 만드는 게 최우선 과제였다. 산에서 말려도 보고 인천 앞바다 섬에 들어가 모래사장에서 말려보기도 했으나 파리가 꼬이는 등 위생상 문제가 심했다. 갖은 방법을 동원했지만 그때마다 아구의 살을 유지하기 어려웠다. 결국 터득한 방법은 찬얼음물과 끓는 물을 번갈아 가며 끓이고 식히는 식이었다. 서울에서 아구찜을 맛볼 수 있다는 말에 소문은 퍼져나갔고 소문 따라 주변에 아구찜을 하는 식당도 늘어났다. 한때 십여 집을 넘던 아구찜 식당이 어떤 이유에서인지 몰라도 문을 닫고 다른 곳으로 떠나는 시기가 있었다. 한 번 그렇게 시련을 겪고 난 뒤 다시 아구찜 거리에 아구찜 식당이 들어차게 됐고 그 결과 지금의 거리 모습을 갖추게 된 것이다. 지금 이 거리에는 불과 3~4년 전에 문을 연 집부터 30년을 훌쩍 넘긴 집까지 사이좋게 식당문을 열고 성업 중이다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56748', '3056376', '낙원동 포장마차거리', 'http://tong.visitkorea.or.kr/cms/resource/57/3056357_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/57/3056357_image3_1.jpg', '6', '37.5726898327', '126.989114324', '', '서울특별시 종로구 종로3가', '종로3가역 5번 출구 낙원악기상가 앞 거리', '', '낙원동 포장마차 거리는 밤이 되면 열리는 낙원상가 앞 포장마차 거리이다. 야외 테이블이 식당 밖에 주르륵 늘어서 있는데 어둑해지기 시작하면 자리들이 하나둘씩 채워진다. 주변에 익선동, 인사동 등 관광지가 많고 광화문, 종각, 시청 근처의 회사들이 많이 모여 있어서 이른 저녁 시간에도 만석이 되는 곳들이 많으니 서둘러 자리를 잡는 것이 좋다. 대부분 음식의 가격이 저렴하지만 현금으로만 결제할 수 있다. 여러 가지 꼬치구이. 산낙지, 멍게, 해삼 등 해산물과 탕, 볶음 종류까지 다양한 메뉴가 있으며, 포차마다 메뉴가 다르기 때문에 취향이나 인원에 따라 원하는 곳을 선택할 수 있다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56749', '2784060', '난지천공원', '', '', '6', '37.5725158027', '126.8870625217', '', '서울특별시 마포구 월드컵로 365 (상암동)', '', '<a href=''https://parks.seoul.go.kr/template/sub/worldcuppark.do'' target=''_blank'' title=''새창 : 서울의 공원 홈페이지로 이동''>https://parks.seoul.go.kr</a>', '난지천공원은 월드컵공원의 5대 테마공원 중 하나이다. 난지천공원에선 연못과 천이 들과 어우러진 멋진 광경을 감상할 수 있다. 다양한 수생식물, 꽃 등이 잘 보존되어 있어서 도심에서는 쉽게 볼 수 없는 식물군이 자주 보인다. 아름다운 자연을 배경으로 아이들이 신나게 뛰어놀 수 있어 가족 단위의 나들이 장소로 제격이다. 봄이면 봄날의 향기를 가득 머금은 유채꽃과 알록달록한 튤립 등 다채로운 꽃도 만날 수 있다. 탐스러운 꽃송이를 자랑하는 겹벚꽃도 피어 걷는 재미를 더한다.', '1', '13', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56750', '127859', '난지한강공원', 'http://tong.visitkorea.or.kr/cms/resource/15/2659815_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/15/2659815_image3_1.jpg', '6', '37.562819678', '126.8854129455', '', '서울특별시 마포구 한강난지로 162', '', '<a href=''https://hangang.seoul.go.kr/www/contents/672.do?mid=478'' target=''_blank'' title=''새창 : 한강사업본부 홈페이지로 이동''>http://hangang.seoul.go.kr</a>', '난지한강공원은 수변공간으로서의 공원 기능은 물론 여가·레저 및 습지생태공원 기능을 골고루 갖추고 있는 공원이다. 공원의 상류부는 수변 활동 구역으로 캠핑장, 선착장, 수변 광장으로 조성하고, 중류부는 완충 녹지 지역으로 다목적 초지광장, 잔디마당, 하류부는 기존 수목을 이용하여 습지생태공원을 이루고 있다. 홍제천교에서 난지천교 사이에 있는 난지한강공원은 길이 4.02㎞(약 776,000㎡)로 마포구 상암동에 있다. 한강르네상스 사업으로 새롭게 조성된 난지한강공원은 노을공원, 하늘공원과 연결되는 중앙연결브릿지, 평화의 공원 연결브릿지, 복합연결통로가 완공되어 공원 이용 시민들의 접근성과 편리성을 더하고 있다. 평화의 공원 연결브릿지 앞에 설치된 거울분수는 난지한강공원의 랜드마크로, 물을 뿜을 때마다 아름다운 풍경을 연출하여 일곱빛깔무지개처럼 다양한 볼거리와 즐길 거리를 제공하고 있으며, 여름철 이용할 수 있는 강변물놀이장은 과거 강변에서 물놀이하던 추억을 되살리는 공간으로 어린아이를 동반한 온 가족이 함께 즐길 수 있도록 조성되어 있다. 또한 생태습지원에 보행연결브릿지가 설치되어 자연생태의 초지 및 식물군락 등 수변에 서식하는 동물을 관찰할 수 있으며, 주변에 야생탐사센터, 수변문화센터가 설치되어 있어, 이곳을 찾는 어린이들이 자연과 더불어 마음껏 뛰어놀 수 있는 자연학습장으로 이용할 수 있다. 인근에는 난지캠핑장이 조성되어 있어 바비큐를 즐기며 야영을 할 수 있어 더더욱 인기가 좋다.', '1', '13', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56751', '1827191', '남강이승훈선생동상', 'http://tong.visitkorea.or.kr/cms/resource/87/1984687_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/87/1984687_image3_1.jpg', '6', '37.54883584', '127.0763931608', '', '서울특별시 광진구 능동로 216', '(능동) 서울어린이대공원 내', '<a href=''http://mfis.mpva.go.kr/memorial/movementView.do?gubunCode=ALL&sid=431&pageIndex=1&searFacilgubun=&searTopicsort=&searAddr_sigun=&searMpvabranch=&searVR=&searInfo=&searGbn=tit&searTxt=%EC%9D%B4%EC%8A%B9%ED%9B%88'' target=''_blank'' title=''새창 : 국가보훈처 현충시설 홈페이지로이동''>http://mfis.mpva.go.kr</a>', '일제 강점기 시절 교육자이자 애국 계몽가인 남강 이승훈 선생의 공적을 기리기 위해 건립되었으며 어린이대공원 내 남문과 동문 사이 위치에 있다. 서울 오산 중·고등학교를 설립한 남강 이승훈의 공로를 표창하고 시은을 보답하기 위해 동교 관계자와 졸업생들이 이승훈 선생의 동상 건립을 위해 동상 건설 준비회를 조직하고 기부금을 모집하였다. 박정희 전 대통령의 하사금과 각계 성금 1,500만 원으로 민복진 조각가에 의해 1974년 10월 3일에 완공되었다.남강 이승훈 선생(1864~1930)은 정주의 가난한 가정에서 태어나 40대에 큰 부자가 되었다. 일본의 침략으로 나라가 위기에 처하자 1907년 재산을 털어 오산학교를 세우고 인재를 기르는 데 힘썼고 독립운동에 몸 바쳐 1922년부터 세 차례 수년간의 옥고를 겪었다. 출옥 후 기독교 신앙에 몰입하여 복음 전도와 복지 운동, 민립대학 설립 추진, 언론 창달 등에 힘을 쏟았다. 평생을 말보다 실천을 앞세우는 교육 운동에 헌신한 그는 내 유해를 땅에 묻지 말고 골격표본으로 만들어 학생들이 공부하는 데 쓰게 하라 유언하고 눈을 감았다. 1962년 이승훈 선생에게 건국공로훈장 대한민국장이 추서되었다.', '1', '6', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56752', '748018', '남대문 갈치조림골목', 'http://tong.visitkorea.or.kr/cms/resource/33/3082833_image2_1.png', 'http://tong.visitkorea.or.kr/cms/resource/33/3082833_image3_1.png', '6', '37.5595328096', '126.9769046979', '', '서울특별시 중구 남대문시장길 16-17', '', '', '남대문 갈치조림 골목은 1988년 전후로 형성되어 그 당시 가격이 저렴했던 갈치를 상인들의 입맛에 맞춰서 매콤하고 얼큰하게 조려 내놓았던 것이 매스컴과 입소문을 타면서 맛거리로 유명해졌다. 본래 이 일대는 갈치조림을 하는 골목이 아닌 식당가였다. 가장 먼저 갈치조림으로 메뉴를 특화한 곳은 희락식당과 왕성식당이었는데 갈치조림이 유명해지면서 주변의 가게들이 하나둘씩 갈치조림으로 간판을 바꿔 달게 되었다.원재료부터 상인들이 직접 구매해 사용하며 밑반찬도 직접 만들어 제공한다. 원재료 중에서도 갈치를 고르는 것이 가장 중요하다고 하는데 식당마다 사용하는 갈치 산지는 다소 차이가 있지만 여수 갈치, 제주 갈치, 목포 갈치, 부산 갈치와 같은 최고 품질의 국산 갈치를 사용한다. 또한 갈치를 조리하는 방법에도 다소간 차이가 있어 같은 갈치조림이라 하더라도 식당마다 조금씩 다른 고유의 맛을 제공한다. 우리나라에서 가장 큰 재래시장인 남대문시장에 대표 골목으로 꼽히는 갈치조림골목은 남대문시장의 주변 직장인들과 시장 상인들에게 필수 점심 코스가 되었고, 한번 다녀간 이들에겐 다시 찾는 곳이 되었다. 일본 언론에서 소개가 되면서 일본 관광객들의 발길도 늘었는데 갈치조림을 맛본 관광객이 그 맛에 반해 명함을 챙겨가서 한국을 다시 방문했을 때도 찾아올 정도라고 한다. 갈치조림이 매운 사람은 생선구이를 같이 판매하는 곳으로 가면 된다.', '1', '24', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56753', '1603482', '남산 야외식물원', '', '', '6', '37.5428517517', '126.9957145677', '', '서울특별시 용산구 이태원동', '', '<a href=''https://parks.seoul.go.kr/template/sub/namsan.do'' target=''_blank'' title=''새창 : 서울의 공원으로 이동''>https://parks.seoul.go.kr</a>', '남산 야외식물원은 남산제모습 가꾸기 사업에 의해 994년에 철거된 용산구 한남동 외인주택 터에 만들어진 곳이다. 1997년 2월 18일에 개원되었으며, 13개 테마로 나누어서 서울지방에서 자랄 수 있는 식물이 총 269종 117,132주가 심어져 있다. 이 중 나무 종류는 소나무 등 129종 60,921주이고, 풀 종류는 할미꽃 등 140종 56,220본이 있다. 서울 남산에 위치한 자연 친화적인 공간으로, 다양한 식물과 수목이 조화롭게 어우러져 사계절 내내 아름다운 풍경을 자랑한다. 시민들에게 휴식과 학습의 기회를 제공하며, 산책로를 따라 다양한 식물 종을 관찰할 수 있다. 특히 계절별로 변화하는 꽃과 나무들이 자연의 아름다움을 더욱 돋보이게 한다. 도심 속에서 자연을 가까이 느낄 수 있는 장소로, 가족 단위 방문객과 자연을 사랑하는 사람들에게 인기가 많다. 남산의 풍경과 함께 힐링할 수 있는 공간이다.', '1', '21', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56754', '128776', '남산 케이블카', '', '', '6', '37.556997122', '126.9841041137', '', '서울특별시 중구 소파로 83', '', '남산 케이블카 <a href=''http://www.cablecar.co.kr/'' target=''_blank'' title=''남산케이블카 홈페이지로 이동''> http://www.cablecar.co.kr</a><br />서울 관광 <a href=''https://bit.ly/3eXbely'' target=''_blank'' title=''서울관광 홈페이지로 이동''>http://www.visitseoul.net</a>', '남산케이블카는 회현동에서 남산 꼭대기까지 서울의 풍광을 한눈에 조망하며 오를 수 있는 시설이다. 1962년 5월 12일 운행을 시작하여 오늘날까지 역사를 이어가고 있다. 케이블카가 운행하는 거리는 회현동 승강장에서 남산 꼭대기에 있는 예장동 승강장까지 약 600m이다. 케이블카는 평균 초속 3.2m로 운행하며 편도 운행 시간은 약 3분 정도이다. 지상과의 높낮이 차는 약 138m이다. 케이블카는 산 정상으로 올라가는 것과 본관 승강장을 향해 내려오는 것 두 대가 동시에 운행되며 케이블카의 수용 정원은 48명이다. 정원이 꽉 찼을 경우 왕복 92명을 안전하게 모실 수 있는 시설을 완비하고 있다.명동역 1번 출구나 서울역 서울스퀘어 정문 앞에서 셔틀버스를 타면 남산케이블카 승강장으로 갈 수 있다.', '1', '24', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56755', '1603336', '남산 팔각정', '', '', '6', '37.5516394747', '126.9876206116', '', '서울특별시 중구 예장동 8-1', '', '<a href=''http://parks.seoul.go.kr/template/sub/namsan.do'' target=''_blank'' title=''새창: 남산공원''>http://parks.seoul.go.kr/template</a>', '남산 팔각정은 남산타워와 함께 남산의 대표적 명소로 꼽히는 정자이다. 1959년 초대 대통령 이승만 대통령의 호를 따서 우남정이라는 이름으로 지어졌다가 1년 뒤인 4·19혁명 때 철거되고 1968년 새로 건립했다. 남산 정상에 세워져 있어 서울 시내가 한눈에 들어오며 멋진 야경을 감상할 수 있어 시민들과 관광객들이 많이 찾는다. 걸어서 올라가거나 남산 순환버스인 셔틀버스 또는 남산 케이블카를 타고 올라갈 수 있다.', '1', '24', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56756', '126747', '남산골한옥마을', 'http://tong.visitkorea.or.kr/cms/resource/62/1946562_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/62/1946562_image3_1.jpg', '6', '37.5591248302', '126.9940059289', '', '서울특별시 중구 퇴계로34길 28', '(필동2가)', '<a href=''http://hanokmaeul.or.kr'' target=''_blank'' title=''새창: 남산골 한옥마을 홈페이지로 이동''>http://hanokmaeul.or.kr</a>', '1998년 조성된 남산골한옥마을은 남산 북측 옛 수도방위사령부 부지 총 79,934m²(24,180평)에 한옥 5동, 전통공예관, 천우각, 전통정원, 서울남산국악당, 새천년타임캡슐 광장으로 구성되어 시민과 관광객을 맞이하고 있다. 번화한 도심 한가운데에서 한국 전통문화를 소재로 한 다양한 체험거리를 만나볼 수 있으며, 남산 자락을 따라 전통조경 양식으로 조성된 계곡과 정자, 각종 화초가 만들어내는 아름다운 풍경을 만날 수 있다. 전통정원 가장 높은 곳에는 서울정도 600년을 기념하고 400년 후 서울정도 천년을 기대하며 1994년 당대의 기억을 매설한 서울천년타입캡슐 광장이 조성되어 있다. 옛 가옥을 복원해 놓은 남산골한옥마을 남산골한옥마을 안으로 들어서면 연못 청학지와 그 곁으로 천우각이 있다. 천우각 광장을 지나 돌계단을 오르면 서울 곳곳에 있던 한옥이 이전·복원되어 옛사람들의 생활방식을 볼 수 있게 한 예스러운 공간이 나타난다.모두 다섯 채의 한옥으로서 삼각동 도편수 이승업 가옥, 삼청동 오위장 김춘영 가옥, 관훈동 민 씨 가옥, 제기동 해풍부원군 윤택영 재실, 옥인동 윤 씨 가옥이 있다. 이 중 건물 이전이 불가능할 정도로 낡은 옥인동 윤 씨 가옥만 새 자재를 사용해 복원했고 나머지 건물은 종래의 집 부재를 그대로 이전했다. 남산골한옥마을에서 즐기는 다양한 체험거리 남산골한옥마을에는 다양한 체험거리가 마련되어 있다. 한옥 다섯 채를 활용하여 한옥실내 공간을 체험하면서 동시에 한국 전통문화프로그램을 접할 수 있다. 한복 입기, 한지 접기, 한글 쓰기, 전통차 마시기를 체험할 수 있고, 전통예절학교와 한방체험도 운영하고 있다. 토요일과 일요일 양일에는 전통혼례가 실제로 치러지고 전통혼례체험도 운영하고 있다. 그 밖에도 천우각 맞은편 원두막에서는 짚공예 시연이 이루어지고 있으며, 천우각 무대에서는 태권도 시범공연과 체험 행사에 참여할 수 있다. 피금정 마당에서는 제기차기, 윷놀이, 비석치기 등과 같은 민속놀이를 즐길 수 있어 가족이 함께 즐거운 시간을 보낼 수 있다. 또한 남산골한옥마을 곳곳을 동행하며 설명해주는 전통문화유산해설사가 상주해 있어 도움을 받을 수도 있다.', '1', '24', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56757', '126485', '남산공원(서울)', '', '', '6', '37.5557113068', '126.99225063', '', '서울특별시 중구 삼일대로 231', '', '<a href=''https://parks.seoul.go.kr/parks/detailView.do?pIdx=1'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://parks.seoul.go.kr/parks/</a>', '남산공원은 서울 도심에 위치하여 시민에게 맑은 공기를 제공하는 자연 휴식처이다. N서울타워와 남산케이블카 등의 시설 이용과 산책, 운동, 휴식 등 다양한 활동이 가능한 여가 생활의 중심지이기도 하다. 남산공원의 정식 명칭은 남산도시자연공원이다. 남산은 서울의 중심부에 있는 서울의 상징으로 본래 이름은 인경산이었으나 조선조 태조가 도읍지를 개성에서 서울로 옮겨 온 뒤에 남쪽에 있는 산이므로 ‘남산’으로 지칭되었다. 그리고 풍수지리상 안산으로 중요한 산이기도 하다.1991년부터 8년간 ‘남산제모습가꾸기’ 사업으로 공원 내 부정적 시설 89동을 이전하고 야외식물원을 조성하는 등 중구 예장동, 회현동, 용산구 한남동 일대를 대대적으로 복원, 정비하여 시민들에게 휴식과 맑은 공기를 제공하며 여가생활의 중심지로 거듭난 공원이다. 남산타워 전망대를 오르는 길에 남산공원의 명물 ‘사랑의 자물쇠’가 있다. 연인들의 사랑을 지켜준다는 의미로 이미 많은 자물쇠가 걸려있다. 수많은 자물쇠에 적혀있는 문구들을 읽으면서 가는 것도 소소한 재미가 있다.2005년 5월 1일부터 남산공원에 일반승용차, 택시 통행이 금지되었다. 남산 서울타워까지 명동, 충무로, 동대입구역에서 순환버스나 남산케이블카를 이용하거나 산책 겸 도보로 오르는 방법도 있다.', '1', '24', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56758', '126535', '남산서울타워', 'http://tong.visitkorea.or.kr/cms/resource/70/3089670_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/70/3089670_image3_1.jpg', '6', '37.5511089858', '126.9878983791', '', '서울특별시 용산구 남산공원길 105', '', '<a href=''http://www.seoultower.co.kr/'' target=''_blank'' title=''새창: 남산서울타워 홈페이지로 이동''>http://www.seoultower.co.kr</a>', '남산서울타워는 효율적인 방송전파 송수신과 한국의 전통미를 살린 관광 전망시설의 기능을 겸비한 국내 최초의 종합전파탑으로 방송문화와 관광산업의 미래를 위해 건립되었다. 세계 유명한 종합 탑들이 그 나라 또는 그 도시의 상징적인 존재가 된 것처럼 ‘남산서울타워’ 역시 지난 40여 년간 대한민국의 대표적인 관광지이자 서울의 상징물 역할을 해왔다. 남산서울타워는 서울 시내 전 지역에서 바라보이는 탑의 높이와 독특한 구조, 형태 등으로 인하여 시민의 관심과 사랑의 대상이 되었고, 내외국인들이 즐겨 찾는 제1의 관광 명소로서의 위치를 확고히 하고 있다. 최근에는 한류 바람을 몰고 온 각종 예능, 드라마의 촬영지로 이름이 높아지면서 내외국인 관광객들이 발길이 끊이지 않는 곳이다.◎ 한류의 매력을 만나는 여행 정보TXT 자체 콘텐츠 <TALK X TODAY>에서 막내 휴닝카이와 태현이 교복 나들이를 즐긴 장소다. 두 사람은 케이블카를 타고 정상에 올라 함께 사진을 찍고, 소원 자물쇠를 채우며 시간을 보냈다. 자물쇠는 케이블카 상류 정류장에 위치한 기념품점에서 구매할 수 있고, 소원을 적을 때 쓰는 네임펜도 대여 가능하다.', '1', '21', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56759', '2435104', '남산서울타워 한복문화체험관', 'http://tong.visitkorea.or.kr/cms/resource/12/2435112_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/12/2435112_image3_1.jpg', '6', '37.5511312042', '126.9878594933', '', '서울특별시 용산구 남산공원길 105', '(용산동2가)', '<a href=''https://namsanhanbok.modoo.at/''target=''_blank'' title=''한복문화체험관''>https://namsanhanbok.modoo.at/</a>', '서울의 랜드마크인 남산서울타워 로비층(지하1층)에 위치한  남산서울타워 한복문화체험관은 조선시대의 대표적인 공간을 테마로 한 포토존과 다채로운 디자인 및 색상의 한복을 테마별 (전통, 개량, 혼례 등)로 구비하고 있다. 특히 체험관 내 대표 포토존은 경복궁 근정전의 어좌, 경복궁 교태전 중전의 방, 한옥의 사랑방에서 원형을 가져왔다. 한복과 더불어 사진이라는 매개체를 통하여 왕, 중전, 사대부를 어우르는 조선시대 사람들의 삶을 체험하고 공감하여, 한국 전통 미학의 가치를 국내외 다양한 사람들에게 알리는 것을 목적으로 하기 때문이다. 또한 한국영화 속 전통사극의 세트공간을 간접적으로 체험할 수 있도록 하였다. 한복 디자이너, 포토그래퍼를 비롯한 전문 스탭이 한복체험은 물론 한국의 혼례식을 현대적으로 재해석한 혼례체험 및 기념촬영 등을 진행하고 있다.', '1', '21', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56760', '2783557', '남산순환나들길', 'http://tong.visitkorea.or.kr/cms/resource/60/3458860_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/60/3458860_image3_1.jpg', '6', '37.5549112997', '126.9866844934', '', '서울특별시 중구 남산공원길 609 (예장동)', '', '<a href=''https://www.durunubi.kr/4-2-1-1-walk-mobility-view-detail.do?crs_idx=T_CRS_MNG0000003086'' target=''_blank'' title=''새창 : 두루누비 홈페이지로 이동''>https://www.durunubi.kr/</a>', '도시 한복판에 우뚝 솟은 남산은 서울의 랜드마크이자 사계절 시민들의 휴식처로 사랑받는다. 남산둘레길은 서울타워를 기점으로 남산을 한 바퀴 돌아볼 수 있는 길로 걷기 좋은 산책 길이다. 총길이 7.3㎞로 2시간 30분 정도 걸린다. 누구나 걷기 좋은 북측순환로, 이야기가 있는 역사문화길, 남산의 자연환경을 엿볼 수 있는 자연생태길과 야생화원길, 그리고 산림숲길로 이어지는 코스이다. 그중에서도 북측순환로는 휠체어나 유아차 사용자와 시각장애인 등 보행 약자가 동행자 또는 보호자의 큰 도움 없이 다닐 수 있어 무장애산책로로 선정됐다. 남산둘레길 북측순환로는 남산케이블카 입구에서 시작하여 국립극장까지 이어지는 3.4km의 산책로로, 자동차와 자전거 통행이 금지되어 안전하게 걸을 수 있다. 모든 길의 가운데에는 점자 블록이 설치되어 있고, 점자 블록이 설치된 중간 길을 기준으로 비장애인은 우측으로 통행하며 질서를 지키자는 의미로 ‘배려의 길’이라고도 불린다.남산둘레길의 관광포인트로는 안중근 의사가 남긴 붓글씨, 관련 사진, 건국공로 훈장과 서한, 공판 당시 신문 보도 내용, 유명 인사 휘호 등이 전시되어 있는 ‘안중근 의사 기념관’, 누에의 머리를 닮았다 하여 이름 붙여진 잠두봉의 포토존 잠두봉 ‘포토아일랜드’, 남산서울타워 광장에서 볼 수 있는 ‘사랑의 자물쇠’, 남산 정산 부근 서울 시내를 내려다볼 수 있는 전파 송출 및 관광용 타워 ‘남산서울타워’ 등이 있다.', '1', '24', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56761', '3067314', '남산예장공원', 'http://tong.visitkorea.or.kr/cms/resource/98/3067298_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/98/3067298_image3_1.JPG', '6', '37.5605217984', '126.9888869118', '', '서울특별시 중구 주자동', '5-22', '', '조선시대 군사들의 훈련장이었던 남산예장자락을 복원한 공원이다. 2만 2,833㎡ 규모로, 지상과 지하로 나뉘어 있다.지상에는 중앙정보부의 고문실을 재현한 ‘기억6’, 조선총독부 관사 터를 그대로 보존한 ‘유구터’, 남산의 역사와 문화를 소개하는 ‘남산이야기’ 등이 있다. 또한, 푸른 소나무 숲과 잔디밭, 산책로 등이 조성되어 있어 시민들의 휴식처로 사랑받고 있다.지하에는 이회영 기념관, 친환경 버스환승센터, 녹색에너지센터 등이 있다. 이회영 기념관은 독립운동가 이회영 선생의 삶과 업적을 기리는 공간으로, 선생의 유품과 기록 등이 전시되어 있다.남산예장공원은 과거와 현재, 역사와 문화가 공존하는 공원으로 조선시대부터 일제강점기, 군사독재 시절까지 다양한 역사적 사건이 있었던 곳이다. 이러한 역사적 의미를 되새기고자 공원 조성 과정에서 다양한 역사적 유적과 시설물을 복원했다. 또한, 공원을 통해 시민들이 역사를 배우고, 평화와 인권의 가치를 되새길 수 있도록 다양한 전시와 교육 프로그램도 운영하고 있다.', '1', '24', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56762', '3447859', '남산인권숲', 'http://tong.visitkorea.or.kr/cms/resource/70/3455270_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/70/3455270_image3_1.jpg', '6', '37.5588942261', '126.9898035408', '', '서울특별시 중구 퇴계로26가길 6 (예장동)', '', '', '남산인권숲은 본래 중앙정보부가 위치하던 곳이다. 지금은 터만 남기고 숲으로 되어 있으며 민주주의를 상징하는 기념비와 안내판이 설치되어 있다. 단순히 휴식 공간을 넘어 교육에 도움이 되는 장소이다.', '1', '24', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56763', '3082015', '남태령고개', 'http://tong.visitkorea.or.kr/cms/resource/11/3082011_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/11/3082011_image3_1.JPG', '6', '37.4598001285', '126.9869510439', '', '서울특별시 관악구 남현동', '', '', '남태령고개은 경기도 과천시와 서울특별시 관악구, 서초구 사이에 위치한 고개이다. 관악산 능선에 자리하고 있으며 높이는 해발 183m이다. 과거 여우고개라고 불렸던 이곳은 과거 정조대왕이 사도세자의 능원이 있는 수원으로 행차할 때 이 고개에서 쉬면서 과천형 이방 변 씨에게 이 고개의 이름을 묻자, 여우고개라고 할 수 없어 둘러댄 것이 남태령이었고 지금까지 내려왔다는 설이 있다. 남태령은 과거 서울과 수원을 연결하는 유일한 길로 현재의 남태령은 큰 도로이지만 옛날에는 사람 한 명 지나기 어려운 아주 좁은 길이었다. 조선시대에 선조들에게는 한양에서 삼남(충청, 전라, 경상)으로 통하는 중요한 관문이었다. 일제 강점기에 이 길을 넓히면서 서울의 반쪽은 없어지고, 지금은 과천의 일부만 남아있다.남태령고개에는 둘레길이 조성되어 있으며, 관악산 둘레길 중 과천 구간이다. 선바위역에서 출발해 남태령 옛길 쪽으로 다시 돌아오는 코스로 과천 쪽에서 관악산 정상 연주대를 오르는 주된 들머리인 과천향교와 조선시대 삼남지방으로 가는 길이었던 삼남길 표지판을 지나면 정조가 잠시 쉬었다는 남태령 옛길을 따라 남태령 망루에서 6.6km 구간이 끝난다. 다른 구간에 비해 오르막 내리막 지점이 적어 걷기 수월한 편이다.', '1', '5', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56764', '2946242', '내맘대로폰케이스 홍대점', 'http://tong.visitkorea.or.kr/cms/resource/69/2947669_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/69/2947669_image3_1.jpg', '6', '37.5512466761', '126.9218988345', '', '서울특별시 마포구 와우산로19길 21 (서교동)', '빅마마건물 3층', '<a href=''https://blog.naver.com/decodencafe''target=''_blank'' title=''새창 : 업체블로그 이동''>https://blog.naver.com/decodencafe</a>', '‘내맘대로폰케이스 홍대점’은 많은 양의 파츠를 구비하고 있으며 본드의 컬러, 모양 등 선택이 가능하다. 모든 파츠를 제한 없이 원하는 만큼 사용할 수 있는 것이 장점이다. 폰케이스 꾸미기 뿐만 아니라 요즘 유행 중인 모루인형 만들기, 데코덴, 탑꾸, 폴꾸 등의 체험도 인기가 높다. 기본 체험 소요시간은 약 1시간 정도 소요되며 자신의 취향껏 만들기가 가능하기 때문에 여행 기념품 혹은 선물용으로도 제격인 곳이다. 또한 이곳은 V LIVE(브이앱)에서 제니와 지수가 휴대폰 케이스를 만들었던 곳으로 제니와 지수의 디자인을 따라 만들어보는 것도 좋다.', '1', '13', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56765', '298341', '내원사(서울)', 'http://tong.visitkorea.or.kr/cms/resource/10/3465410_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/10/3465410_image3_1.jpg', '6', '37.6266725544', '126.9951622409', '', '서울특별시 성북구 보국문로 262-151 내원사', '', '<a href=''http://www.naewonsa.net/'' target=''_blank'' title=''내원사 사이트로 이동''>http://www.naewonsa.net</a>', '내원사는 삼각산 자락에 자리하고 있는 사찰로, 정릉 골짜기를 지나 1㎞ 정도 오르면 멀리 목멱산을 바라보는 곳에 자리하고 있다.  언제 누가 창건했는지는 정확하게 밝혀지지 않고 있지만 터전이 예사롭지 않음을 느낄 수 있다. 역사적으로 내원사의 존재를 밝혀주는 것은 현재로서는 김정호가 제작한 「수선전도(首善全圖)」와 1859년(철종 10) 만들어져 사찰에 전하는「백의대사불도(白衣大士佛圖)」라는 목판이 유일하다. 수선전도에는 현재 내원사가 자리한 곳과 같은 위치에 내원암이 표시되어 있고, 「백의대사불도(白衣大士佛圖)」목판에는 삼각산내원암(三角山內院庵)이라는 기록이 있어 19세기 전반만 해도 법등을 이어오고 있었던 것을 알려준다. 그러던 내원사가 언제 어떠한 이유로 역사 속으로 사라져 버렸는지 의문이 아닐 수 없다. 그래서 더욱 세월의 무상함이 절로 느껴진다. 오늘도 무상함 속에 범종루 아래 길게 이어진 이끼 낀 석축에서는 세월의 흔적이 배어나고, 바위틈에서 솟는 감로수는 사찰의 흥망성쇠를 잊은 듯 말없이 흐르고 있다.출처 : 내원사', '1', '17', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56766', '2763685', '노들나루공원', 'http://tong.visitkorea.or.kr/cms/resource/38/3458538_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/38/3458538_image3_1.JPG', '6', '37.5146931319', '126.9521887724', '', '서울특별시 동작구 노량진로 247', '(본동)', '', '서울 동작구 노량진에 있는 노들나루공원은 노량진 배수지공원이라 불리다가 지금은 노들나루공원으로 명칭이 바뀐 근린공원이다. 바닥분수와 야외무대, 여러 가지 운동 시설이 갖춰져 있으며, 푸른 잔디와 함께 장미꽃 광장도 조성되어 있고, 여러 개의 산책코스도 마련되어 있다. 6.25 전쟁 초기, 서울이 함락된 직후 북한군의 한강 도하를 6일간이나 저지시킨 한강 방어선 전투의 전사자 명비를 볼 수 있다. 공원과 붙어 있는 풋살장, 족구장은 서울특별시 공공서비스예약(yeyak.seoul.go.kr)에서 예약 후, 사용할 수 있다. 여의도 불꽃축제 기간에 불꽃놀이를 감상할 수 있는 명소이기도 하다. 지하철 9호선 노들역 2번 출구와 연결되어 있다.', '1', '12', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56767', '2638475', '노들섬', '', '', '6', '37.5177178854', '126.9580520415', '02-749-4500', '서울특별시 용산구 양녕로 445 (이촌동)', '', '<a href=''https://nodeul.org/'' target=''_blank'' title=''새창 : 노들섬 공식 홈페이지로 이동''>https://nodeul.org/</a>', '노들섬은 한강대교 중간에 위치한 타원형 모양의 땅으로 ‘백로가 놀던 돌’이라는 뜻의 ‘노돌’에서 이름이 유래한 것으로 알려져 있다. 2012년 한강에서 휴식과 만남의 공간으로 사람들을 이어주던 문화 공간으로 복원하기 위한 고민이 시작되었고, 2019년 9월 노들섬은 ‘음악을 매개로 한 복합문화기지’를 모토로, 자연과 쉼, 문화가 있는 공간으로 재탄생하였다.', '1', '21', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56768', '3067187', '노량진컵밥거리', 'http://tong.visitkorea.or.kr/cms/resource/86/3067186_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/86/3067186_image3_1.jpg', '6', '37.5134598263', '126.9457088419', '', '서울특별시 동작구 노량진로 178 (노량진동)', '', '', '노량진컵밥거리는 노량진 명물로 알려진 컵밥 포장마차가 모여있는 길이다. 컵밥은 동작구 노량진 학원가에 줄지어 서 있는 포장마차에서 개발된 거리 음식 중 하나로 주머니 사정이 넉넉하지 않은 수험생과 입시생을 배려해 만들어졌다. 처음에는 포장마차에서 파는 밥이라고 하여 포차밥이라고 불리다가 2011년쯤부터 컵밥이라는 용어가 본격적으로 사용됐다. 김치볶음밥을 일회용 그릇에 담고 그 위에 계란, 소시지, 햄, 참치 등 각종 토핑을 얹은 한 끼 식사이며 평균 4,000원에서 5,000원 사이의 가격으로 원하는 토핑을 선택해서 먹을 수 있다.동작구청이 2018년 컵밥거리를 찾는 관광객과 시민들에게 편의를 위해 안내판을 갖추고 이후 가게 사이사이 쉼터를 조성해 편하게 컵밥을 즐길 수 있도록 정비했다. 컵밥 외에도 분식, 쌀국수, 핫도그, 와플 등 먹거리 포장마차가 있어 다양한 거리 음식을 먹고 돌아다닐 수 있다.', '1', '12', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56769', '2774274', '노원 불빛정원', '', '', '6', '37.6243079306', '127.0932978062', '', '서울특별시 노원구 화랑로 610 (공릉동)', '', '<a href=''https://www.nowon.kr/www/info/info1/info1_06/info1_06_08/info1_06_08_01.jsp'' target=''_blank'' title=''새창 : 노원구청 홈페이지로 이동''>https://www.nowon.kr</a>', '노원 불빛정원은 화랑대역 철도공원에 야간 경관 조성으로 빛의 향연이 펼쳐지는 서울시 최초 불빛정원이다. 비밀의 정원과 생명의 나무, 음악의 정원, 환상의 기차역, 은하수 정원, 숲 속 동화 나라를 주제로 꾸며진 이곳은 시시각각 다양한 색깔을 뿜어낸다. 낮에는 정겨운 간이역 풍경으로 마음을 따스하게 어루만졌던 공간이 밤이 되면 화려한 꽃과 푸른 바닷속 돌고래 떼, 아기자기한 기차 카페로 색다른 매력을 더한다.', '1', '9', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56770', '2733971', '노을공원', 'http://tong.visitkorea.or.kr/cms/resource/09/2780909_image2_1.png', 'http://tong.visitkorea.or.kr/cms/resource/09/2780909_image2_1.png', '6', '37.5694354462', '126.8781800855', '', '서울특별시 마포구 하늘공원로 108-2', '(상암동)', '<a href=''https://korean.visitseoul.net/nature/%EB%85%B8%EC%9D%84%EA%B3%B5%EC%9B%90/KOP027691'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://korean.visitseoul.net/</a>', '90년대까지 난지도란 이름의 쓰레기 매립장이었던 노을공원은 환경 재생사업을 통해 월드컵공원으로 탈바꿈하면서 자연 식생지, 운동시설 및 산책로 등 시민들이 여가를 즐길 수 있는 공간으로 재탄생했다. 전망대는 가양대교와 양화대교 주변의 한강, 올림픽대로, 난지한강공원의 캠프장이 어우러진 풍경을 사계절 내내 감상할 수 있는 명소가 되었다. 맹꽁이 전기차도 운행해 전망대까지 올라가기도 편하다. 노을공원 총면적의 57% 정도는 골프장으로 만들어지고 나머지 부분은 자연 식생지, 운동시설 및 산책로 등 시민들이 여가를 즐길 수 있는 공간이다. 드넓은 잔디밭에 조각예술품과 전망데크가 있어 여유를 느낄 수 있다.', '1', '13', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56771', '3007012', '노해체육공원', 'http://tong.visitkorea.or.kr/cms/resource/07/3007007_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/07/3007007_image3_1.jpg', '6', '37.6205219892', '127.0783159455', '', '서울특별시 노원구 공릉로22길 1 (공릉동)', '', '', '노해근린공원은 1989년에 조성된 근린체육공원으로 노원구의 주거 밀집 지역의 중심에 있어 구민들의 활용도가 놓은 공원이다. 작은 면적에 운동장 조깅트랙, 인라인트랙, 농구대, 족구장, 배드민턴장, 산책로, 어린이 놀이시설, 웰빙 체육시설 등이 갖춰져 있으며 노원구민체육센터가 인접해 있다. 노해근린공원 어린이 놀이터는 흙놀이가 가능하고 중앙에 넓은 대형 운동장이 있다. 공원 한쪽 숲속에는 족구장이 자리잡고 있고 건강 지압길도 있으며 다양한 조각 작품도 전시되어 있다. 아침에 노원구민을 위한 아침체조교실과 택견교실이 열리는데 누구나 별도 신청없이 참가가 가능하다.', '1', '9', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56772', '3453040', '녹번서공원', 'http://tong.visitkorea.or.kr/cms/resource/96/3452496_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/96/3452496_image3_1.jpg', '6', '37.6055181816', '126.9239887769', '', '서울특별시 은평구 서오릉로 52 (녹번동)', '', '', '녹번서공원은 서울 은평구 녹번동에 위치한 자연친화적인 공원으로, 다양한 식물과 나무들이 자생하는 공간입니다. 특히 봄과 가을에는 꽃과 단풍을 감상할 수 있으며, 자연학습장, 운동기구, 도서관 등 다양한 시설이 마련되어 있다.', '1', '22', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56773', '1603149', '능인선원', 'http://tong.visitkorea.or.kr/cms/resource/72/1567772_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/72/1567772_image3_1.jpg', '6', '37.4717304764', '127.0526058449', '', '서울특별시 강남구 양재대로 340', '(개포동)', '<a href=''https://www.nungin.net/'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://www.nungin.net/</a>', '구룡산 아래에 있는 능인선원은 부처님의 가르침인 지혜와 자비를 바탕으로 불자 양성과 포교 및 사회복지 증진을 위한 수행도량으로 설립되었다. 불교 포교와 공부하는 불자 양성으로 일관해 온 능인선원은 이제 대한민국 최고의 포교 사찰이 되었다. [능인(能仁)]이란 [능히 남을 교화하여 이롭게 하는 이]라는 뜻을 지닌 석가모니 부처님의 다른 이름이다. 능인선원은 생활 불교를 선언하여 기도의 생활화, 지역 법회의 활성화 및 각종 봉사활동을 통해 한국 불교를 현대화하는 데 기여하였다. 또한 체계적인 불교 교육을 통해 불교의 과학성과 우수성을 널리 펼쳐, 우수한 불자를 양성함으로써 불법 중흥에 이바지하고 있다.', '1', '1', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56774', '2764354', '다락원체육공원', '', '', '6', '37.6914909527', '127.0472806389', '', '서울특별시 도봉구 창포원로 45 (도봉동)', '', '<a href=''https://www.dobongsiseol.or.kr/contents/culture8.html?menu1=3&menu2=1'' target=''_blank'' title=''새창 : 다락원체육공원 홈페이지로 이동''>https://www.dobongsiseol.or.kr</a>', '서울 도봉산 자락에 위치한 서울 동북권 최대 규모의 체육공원이다. 테니스장, 배드민턴장, 축구장 등의 시설이 있다. 테니스장은 실내와 실외로 구분되어 있으며, 실내는 총 3면을 갖추고 있다. 배드민턴장은 14면으로 대형 실내에 마루 시설이 잘 되어 있다. 축구장에는 야간 조명까지 갖추고 있어 밤에도 축구를 즐길 수 있다.', '1', '10', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56775', '2392535', '다이나믹 메이즈 (서울 인사동점)', '', '', '6', '37.5718843365', '126.9872034096', '', '서울특별시 종로구 인사동길 12', '(인사동)', '<a href=''http://www.dynamicmaze.com/main/main.php#&slider1=2''새창 : 다이나믹 메이즈 홈페이지로 이동''>http://www.dynamicmaze.com</a>', '다이나믹 메이즈는 여럿이서 협동하여 장애물을 뛰어넘고, 순발력과 집중력을 필요로 하는 미션을 수행하면서 미로를 탈출하는 실내 놀이 공간이다. 커다란 미로 공간 속에서 벽을 타고 올라가고, 촘촘한 밴드를 탈출하는 등 다양한 장애물을 넘어 탈출해야 한다. 2015년 7월, 인사동에 선보인 이후 수많은 국내외 고객들로부터 사랑을 받은 다이나믹 메이즈는 강원도 속초와 제주도 성읍에도 문을 열었다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56776', '299978', '달마사(서울)', 'http://tong.visitkorea.or.kr/cms/resource/74/2690574_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/74/2690574_image3_1.jpg', '6', '37.4985229748', '126.9626521644', '', '서울특별시 동작구 서달로 50-26', '', '<a href=''http://www.dalmasa.org/'' target=''_blank'' title=''새창: 달마사 홈페이지로 이동''>http://www.dalmasa.org/</a>', '한강, 그리고 강 건너 남산을 바라보는 서달산 중턱에 작은 사찰이 있다. 낮이면 흐르는 강물을 멀리서 지켜볼 수 있고, 밤이면 작은 불빛을 발하는 남산의 정경을 바라볼 수 있는 열린 사찰이다. 서달산을 남산으로 자연과 함께하는 맑은 도량으로 알려져 있는 곳으로 하루가 다르게 변하는 도심 속에서 찌든 혼탁한 마음을 바로 세울 수 있는 도심 속 전통사찰이다. 이른 아침 새벽예불의 소리와 함께 지역주민과 하나 되고, 하루라는 인고의 시간을 지는 해와 같이하는 그런 포근한 사찰이다. 한강을 바라보는 녹지 속의 열린 공간이며, 부처님의 가르침과 참된 수행의 정신이 깃든 곳, 또 작지만 마음으로 넓힌 지역주민의 공간이다.', '1', '12', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56777', '2741117', '달맞이봉공원', '', '', '6', '37.5433327144', '127.0214816511', '', '서울특별시 성동구 금호동4가 산 27', '(금호동4가)', '<a href=''https://parks.seoul.go.kr/parks/detailView.do?pIdx=1011'' target=''_blank'' title=''새창 : 서울의 공원으로 이동''>https://parks.seoul.go.kr</a>', '달맞이봉공원은 옥수동 미타사와 현대아파트가 있는 뒷산으로 예전부터 정월 보름에 주민들이 이곳에 올라가 달을 맞이하였다 하여 붙여진 이름이다. 이 공원은 해돋이 명소이자 서울 야경 명소로 많은 시민들이 찾고 있다. 한강변에 우뚝 선 바위산으로 한강을 가깝게 바라볼 수 있다. 영화 <ing>의 촬영지로도 유명하지만, 뚝섬부터 한남대교가 내려다 보이는 야경이 일품인 곳이다.  또한, 멋진 한강의 다리를 배경으로 찍을 수 있는 포토존도 있고, 누워서 달과 별을 볼 수 있는 벤치도 설치되어 있다.', '1', '16', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56778', '282011', '답십리 고미술상가', 'http://tong.visitkorea.or.kr/cms/resource/98/3055898_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/98/3055898_image3_1.JPG', '6', '37.5682581489', '127.0522448086', '', '서울특별시 동대문구 고미술로 39', '', '', '답십리 고미술상가는 고서화, 고가구, 도자기 등의 고미술품과 독특하고 다양한 생활용품을 판매하는 상점이 밀집한 곳이다. 답십리 고미술상가는 청계천 8가, 이태원, 아현동 등지에 흩어져 있던 고미술상점들이 1980년대 중반부터 답십리 일대에 모여들기 시작하여 형성되었으며 현재 140여 개의 점포가 밀집해 있다. 고미술품은 물론 나무 촛대, 괘종시계, 오래된 액자 등과 같은 오래되고 독특한 소품들이 많아 볼거리가 풍성하다. 일반 상점에서는 보기 힘든 특이한 물건들이 많아 한국인은 물론 외국인들도 자주 찾는다. 일반적으로 인사동이 질 좋고 값비싼 고미술품들을 주로 판매한다면 답십리 고미술 상가는 몇천원부터 시작되는 작은 소품들이 많은 것이 특징이다.수도권 지하철 5호선 답십리역 1, 2번 출구 바로 옆에 있으며 답십리 로데오거리와도 인접해 있어 같이 둘러보기 좋다.', '1', '11', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56779', '3057901', '답십리 영화의 거리', 'http://tong.visitkorea.or.kr/cms/resource/64/3057864_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/64/3057864_image3_1.jpg', '6', '37.5718468286', '127.0637714345', '', '서울특별시 동대문구 답십리로 210-9 (답십리동)', '', '', '답십리 영화의 거리는 구 서울 동대문구 답십리에 있던 촬영소 고개(촬영소 사거리)를 영화를 주제로 한 문화예술 복합공간으로 탈바꿈한 곳이다. 이 일대는 1964년부터 1969년까지 약 80편의 영화가 촬영되며 한국 영화의 전성기를 이끌었던 곳이다. 거리의 양쪽 벽에는 과거 촬영된 영화 작품들과 옛 영화관인 답십리 극장의 모습을 재현한 포토존들이 있다. 영화 제목, 감독, 배우, 스태프들의 이름이 새겨진 디자인의 보도블록과 시대별 대표 영화배우들의 핸드프린팅 등 볼거리가 많다.길의 끝에는 동대문구문화회관을 리모델링한 답십리 영화미디어아트센터가 있다. 이곳에서는 무료로 영화를 상영하고 한국 영화의 역사를 담은 전시관을 구경할 수 있고, 직접 영화를 찍을 수 있는 스튜디오 체험이 가능하다.', '1', '11', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56780', '126803', '당고개순교성지', '', '', '6', '37.5355770817', '126.9669951674', '', '서울특별시 용산구 청파로 139-26', '', '<a title=''새창 : 당고개순교성지 홈페이지로 이동'' href=''http://www.danggogae.org/'' target=''_blank''>http://www.danggogae.org</a><br /><a title=''새창 : 서울 용산구 문화관광'' href=''http://www.yongsan.go.kr/'' target=''_blank''>http://www.yongsan.go.kr/</a>', '당고개 순교성지는 용산전자상가에서 도보로 불과 5분 거리인 작은 언덕에 있다.  9분의 성인과 1분의 복자를 배출한 성지로, 기해박해가 끝날 무렵 1839년 열 명의 남녀 교우들이 장렬히 순교한 곳이다. 이들은 본래 서소문 밖 네거리에서 처형되기로 되었으나 서소문 밖 상인들이 설 대목장을 보아야 함으로 형장을 다른 곳으로 옮겨 줄 것을 요청하였기 때문에 이곳 당고개로 옮겨 사형을 집행함으로써 순교자들을 모시게 되었다.당고개 순교성지는 [찔레꽃 아픔 매화꽃 향기]를 주제로, 박해의 고통을 찔레꽃 가시로, 하느님의 은총을 매화꽃 향기로 표현하여 조성되었다. 당고개 성지의 십자가의 길 14처는 복자 이성례 마리아가 예수님의 길을 따라가는 구성으로 이루어져 고난의 길과 순교의 영광을 함께 느낄 수 있게 했다.순교한 애절한 사연과 서울 시내를 한눈에 내려다볼 수 있는 전망 때문에 당고개 순교성지는 많은 순례객들의 발길이 끊이지 않고 있다. 순교 성지인 새남터 성당과 용산가족공원 등이 인접해 있다. 수도권 지하철 6호선 효창공원역 5번 출구에서 도보 15분에 위치해 있다.', '1', '21', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56781', '3060934', '당산공원', 'http://tong.visitkorea.or.kr/cms/resource/87/3054287_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/87/3054287_image3_1.jpg', '6', '37.5252714388', '126.8951603366', '', '서울특별시 영등포구 당산로27길 12 (당산동3가)', '', '', '당산공원은 영등포구청역 바로 앞에 자리한 다양한 편의시설을 갖춘 공원이다. 기억생생길과 지압보도, 야외서가, 무장애 놀이터를 갖추고 있어 남녀노소 누구나 이용할 수 있는 공원이다. 기억생생길은 치매인식개선 및 치매 친화적 환경 조성을 위해 만들어진 산책로이다. 치매예방수칙과 치매 조기검진의 중요성 등 치매와 관련된 정보가 담긴 안내판을 산책로 곳곳에 담아 놓아서 산책을 하면서 정보도 볼 수 있다. 지압보도는 자연친화적인 형태로 발 지압을 하며 산책할 수 있다. 야외 오픈 서가도 있어서 누구나 자유롭게 책을 읽을 수 있다. 무장애 놀이터는 바닥면에 턱이 없어 휠체어를 탄 아이도 이용할 수 있도록 민들었다.', '1', '20', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56782', '299980', '대각사', '', '', '6', '37.5736481075', '126.9920473628', '', '서울특별시 종로구 율곡로10길 87', '(봉익동)', '<a href=''http://daegaksa.kr/'' target=''_blank'' title=''새창 : 홈페이지로 이동''>http://daegaksa.kr/</a>', '대각사는 종로3가역에서 창경궁으로 가는 길에 있는 작은 절로 재단법인 대각회의 사찰이다. 민족대표 33인이며 3.1운동을 주도했던 백용성 스님이 창건한 절이다. 대각교가 대각사를 중심으로 민족 자주성을 일깨운다고 판단한 일제의 탄압을 받았다. 하지만 탄압 속에서도 1945년 12월 12일에 김구 선생과 임시정부의 귀국을 환영하는 귀국봉영회를 마련한 역사적 장소이다. 일제에 맞서 나라의 독립을 소망했던 자취가 남아 있는 절로서 일주문 단청에는 용성스님의 독립운동 발자취와 3.1독립만세운동에 대한 그림이 그려져 있다.1969년 동헌 완규 대선사를 비롯한 용성 스님의 제자들이 스님의 대각교 창립 정신을 기리고 대각 사상을 널리 드러내기 위하여 재단법인 대각회를 대각사에 설립하였으며, 1986년 대각사 경내에 있던 건물을 모두 철거하고 지하 1층, 지상 3층의 신축 건물을 지었다. 일주문을 지나면 바로 왼편에 범종각이 자리하고 있고 경내 중심에는 팔작지붕 구조로 된 대각성전이 자리하고 있다. 또, 대각성전 건립을 기념하기 위한 대각사 대각회사적비가 세워져 있고 그 뒤로는 심검당이 있다. 2층 법당의 바깥쪽에서는 북한산 보현봉이 보인다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56783', '2402981', '대림동 차이나타운', 'http://tong.visitkorea.or.kr/cms/resource/89/2029889_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/89/2029889_image3_1.jpg', '6', '37.4924530714', '126.8975138236', '', '서울특별시 영등포구 대림동', '1050-43', '', '대림동 차이나타운은 2002년을 전후로 대림역을 중심으로 재중동포들이 이주하여 모여 살게 된 밀집 주거지로, 전통적으로 개항장을 중심으로 형성된 인천, 부산 등의 차이나타운과는 다른 형태를 보인다. 대림동 중앙시장의 상점 중 40% 정도를 재중동포들이 운영하고 있으며, 대림역 근처에는 재중동포를 대상으로 운영되는 은행, 숙박업소, 식당 등 각종 편의 시설이 밀집되어 있다. 가까이에 대림중앙시장이 있으며, 이곳의 풍경은 우리나라 여느 재래시장과는 다르게 생소한 중국식 재료와 중국 향기가 물씬 풍기는 길거리 음식들을 판매하고 있다.대림동 차이나타운에는 마라탕이나 훠궈 등 전통식부터 퓨전식까지 다양한 종류의 중국 음식을 판매하는 유명한 중국음식점들이 많다. 많은 식당들이 한국어 메뉴를 갖추고 있지만, 한국어가 전혀 통하지 않는 곳도 있다.', '1', '20', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56784', '2753103', '대림어린이공원', 'http://tong.visitkorea.or.kr/cms/resource/93/3399393_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/93/3399393_image3_1.JPG', '6', '37.491122772', '126.9032244523', '', '서울특별시 영등포구 대림로 106 (대림동)', '', '', '영등포구 대림동에 위치한 공원으로, 어린이 놀이시설과 다양한 운동시설, 휴식공간이 갖춰져 있다. 또한 다양한 문화, 여가를 즐길 수 있는 공간으로 많은 시민들이 찾는 곳이다. 지역단체와 연계한 활동, 마을축제 등을 진행하기도 하는 소통과 화합의 장이다. 산책로도 잘 조성되어 있어 가족 나들이로 좋고 인조잔디가 조성된 곳도 있어 안전하게 운동하기에도 좋다. 공원 지하에 공영주차장이 있어 이용하기 편리하다.', '1', '20', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56785', '1602451', '대모산도시자연공원', '', '', '6', '37.4775857564', '127.0750897453', '', '서울특별시 강남구 광평로10길 30-71', '(일원동)', '<a href=''https://visitgangnam.net/%ec%97%ac%ed%96%89%ec%a7%80/%ea%b0%95%eb%82%a8/%eb%8c%80%eb%aa%a8%ec%82%b0-%ec%82%b0%ea%b8%b8/'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://visitgangnam.net</a>', '강남구 개포동과 일원동 남쪽에 있는 대모산(해발 293m)은 산의 모양이 늙은 할머니를 닮았다고 해서 할미산으로 불리다가 조선 태종 이방원과 그의 비인 원경왕후 민씨 묘인 헌릉이 대모산 남쪽에 조성되면서 왕명에 의해 대모산을 불리게 되었다고 한다. 대모산에 조성된 대모산도시자연공원은 강남 일대에 거주하는 서울 시민들이 즐겨 찾는 도시자연공원이다. 대모산은 도시민이 즐겨 찾는 산인만큼 등산로가 많이 발달하였는데, 산행의 들머리는 강남구 개포동과 일원도, 서초구 내곡동 세 곳을 이용하면 된다. 대모산 둘레길에 무장애 나눔길이 조성되어 아이들이나 노약자들도 어렵지 않게 산책을 즐길 수 있다. 대모산 입구에 경작으로 훼손된 대모산을 토지 보상하고, 기존 지형을 이용한 다랑이 특성을 살려 돌담 사이로 다양한 야생화가 피어나는 야생화원도 둘러볼 수 있다. 또한, 공원 내에는 11종의 체력단련시설이 있으며, 주위의 산림과 조화를 이루도록 대부분 목재로 설치되었다. 이 외에도 임간교육장, 파고라 등의 편의시설이 있고, 다양한 나무 종이 심어져 있어 삼림욕에도 좋다. 약수터와 등산로가 있어 쉼터로도 좋으며, 수도권 지하철 일원역에서 약 630m 정도 떨어진 곳에 위치하고 있다.', '1', '1', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56786', '3076460', '대모산숲속야생화원', '', '', '6', '37.4804771469', '127.0783976958', '', '서울특별시 강남구 일원동', '산63-57', '<a href=''https://www.gangnam.go.kr/contents/Daemosan/2/view.do?mid=ID02_011107'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://www.gangnam.go.kr</a>', '대모산 숲속 야생화원은 경작 등으로 황폐해진 대모산을 친환경적으로 복구하고 경관을 개선하고자 조성된 곳으로, 대모산의 특성에 맞는 산딸나무, 구절초 등 토착수종과 관찰데크와 쉼터, 숲길 등이 어우러지는 숲속의 명소이다. 계절정원, 자연정원, 바람정원, 느림보숲길, 향기정원으로 이루어진 야생화원은 기존 지형을 이용한 다랑이 특성을 살린 돌담 사이로 다양한 야생화가 피어나 계절에 따라 변화하는 풍경과 자연 속에서 휴식을 즐길 수 있는 공간이다.대모산 숲속 야생화원의 근처에는 불국사를 비롯하여 수질 좋은 약수터가 있고, 산 입구 쪽에는 각종 희귀 나무 등을 심어 놓은 자연 학습장이 있어 학생들의 야외 교육장 및 주민들의 산책, 등산로로 사랑받고 있으며 무장애 나눔길로 휠체어로 편하게 이동이 가능하다.', '1', '1', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56787', '300076', '대성사(서울)', 'http://tong.visitkorea.or.kr/cms/resource/68/2360968_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/68/2360968_image3_1.jpg', '6', '37.4763181306', '127.0134731649', '', '서울특별시 서초구 남부순환로328길 49 (서초동)', '', '<a href=''http://www.daeseongsa.org/'' target=''_blank'' title=''새창: 대성사 홈페이지로 이동''>http://www.daeseongsa.org/</a>', '기록상 백제는 384년인 침류왕 원년에 고승 마라난타가 동진으로부터 바다를 건너서 서울인 한산으로 들어오자 왕은 그를 궁 안에 머물도록 하였고, 이듬해 10명의 백제인을 출가시켜 승려로 만들었다고 한다. 마라난타는 백제로 오는 동안 풍토병으로 고생을 하였는데, 이곳에 와서 우면산 물을 마셔 병이 완쾌하였고, 이에 우면산에 대성초당을 짓게 되는데 이것이 바로 대성사의 전신이라 전해진다. 이후 알려진 연혁은 없지만 근대에 들어 3·1독립만세운동 때 불교계 대표로 참가했던 용성스님을 탄압하는 과정에서 대성사가 소실되었다고 전해진다. 그 후 한국전쟁 때 다시 한번 피해를 입었다고 전하며, 전쟁이 끝난 1954년에 중창된다. 현재 대성사에는 백용스님이 만들었다고 알려진 대성사 목불좌상(서울특별시 유형문화유산)이 모셔져 있다.', '1', '15', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56788', '1605948', '대원군별장 (석파정)', '', '', '6', '37.5991223247', '126.9586719057', '', '서울특별시 종로구 창의문로11길 4-1', '', '<a href=''https://tour.jongno.go.kr/tour/tour/indvdlz/view.do?nttId=26975&menuNo=2010''종로 역사 문화 관광 사이트로 이동''>https://tour.jongno.go.kr/tour</a>', '대원군 별장은 원래 창의문(彰義門) 밖 종로구 부암동 산 16-1번지에 있었던 건물이다. 1958년 서예가 소전(素筌) 손재형(孫在馨)이 이곳에 집을 지으면서 뒤뜰 바위 위 지금의 위치로 이전한 것이다. 부암동 산 16-1번지에는 조선말 흥선대원군 이하응(李昰應)의 별장인 석파정(石坡亭, 서울특별시 유형문화유산 석파정 별당)이 지금도 보존되어 있다. 이 대원군 별장 건물은 그 곳 사랑채에 부속되어 있던 건물이었다. 동북향을 하고 있는 현재의 건물은 정면 5칸, 측면 3칸 규모의 역(逆)\'ㄱ\'자형 평면으로, 3량 가구의 맞배기와지붕을 하였다. 건평 10평정도 규모의 건물로 가운데에 1칸 대청방을 설치하고, 그 왼쪽인 동쪽에 2칸 반 크기의 큰방, 오른쪽에 1칸 반 크기의 건넌방을 꾸며놓았다. 큰방은 다시 앞으로 2칸 꺾여 나와 \'ㄱ\'자를 형성하였는데, 이 방은 흥선대원군이 사용하였다. 건넌방은 손님 접대용이었으며, 대청방은 흥선대원군이 즐겨 그리던 사군자의 난초를 그릴 때만 사용하던 방이라고 한다. 회색 전벽돌로 쌓은 큰방 측벽에는 반원형의 창을 내었고, 건넌방 측벽에는 원형의 창을 내어 중국풍의 정취가 나도록 하였다. 대청방과 안방, 그리고 건넌방 사이에는 분합문을 달았고, 큰방 동측면 벽에는 벽장을 설치하여 공간의 이용 밀도를 높였다. 건물 정면에는 \'ㄱ\'자로 꺾인 곳을 따라 툇마루를 설치하였고, 그 양 끝은 전벽돌로 쌓은 측벽으로 막았으며, 건물 뒷면 중앙 3칸에는 쪽마루를 놓았다. 건물 앞퇴에는 대청마루 앞부분을 제외하고 모두 난간을 설치하여 고급스런 분위기로 장식하였으며, 기둥에는 주련(柱聯)을 달았다. 양측면 박공벽에는 전벽돌을 처마가 없이 지붕 밑까지 쌓아 중국 건축의 \'경산(硬山)\'식 지붕으로 처리하였으며, 후면 외벽도 전벽돌로 쌓고 처마를 내었다. 큰방의 아궁이는 동쪽에 두었다. 이 건물은 평면 구성, 공간과 벽면 사용, 창호 디자인, 재료 마감 등에서 빈틈 없이 탄탄하게 처리한 점이 돋보인다. 대청을 중심으로 하는 정면 부분과 내부 구조, 그리고 창살은 전통 한옥형식을 갖추었으나, 외형을 이루는 전반적인 모습은 조선말기에 전래된 중국 청(淸)나라 건축의 영향을 받았다. 이건하면서 원 모습대로 복원하지 않고 평면과 구조를 부분적으로 바꾸었다. 손재형은 일본으로 유출된 김정희의 &lt;세한도(歲寒圖)&gt;를 천신만고 끝에 한국으로 가져온 인물로 유명하다. 그는 말년에 이곳에서 작품활동을 하였다. 이 별장 아래에 있는 손재형이 살던 건물들은 현재 전통음식점 \'석파랑\'으로 사용되고 있다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56789', '2733970', '대치유수지체육공원', 'http://tong.visitkorea.or.kr/cms/resource/48/3467748_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/48/3467748_image3_1.jpg', '6', '37.5016327582', '127.0635595572', '', '서울특별시 강남구 역삼로90길 43', '(대치동)', '<a href=''http://www.xn--vk1b79znxd34c61h.kr/'' target=''_blank'' title=''새창 : 대치유수지 홈페이지로 이동''>http://www.대치유수지.kr/</a>', '각종 체육시설과 운동기구, 산책로, 휴게공간, 음악분수, 상징조형물 등 여러 시설물을 갖추어 체력 증진과 생활 체육의 활성화를 위해 지역 주민이 편리하게 이용할 수 있도록 조성한 체육공원이다. 축구장, 테니스장, 풋살구장 대관이 가능하며 예약은 사용일 기준 1일 전까지 선착순으로 받는다. 당일 예약은 불가하며 지역주민 우선 접수로 진행된다.', '1', '1', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56790', '126534', '대학로', 'http://tong.visitkorea.or.kr/cms/resource/22/3384822_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/22/3384822_image3_1.JPG', '6', '37.5805669329', '127.0023878907', '', '서울특별시 종로구 대학로 104 (동숭동)', '', '<a href=''https://tour.jongno.go.kr/tour/main/contents.do?menuNo=400165'' target=''_blank'' title=''새창 : 서울 종로구 문화관광 홈페이지로 이동''>https://tour.jongno.go.kr/tour/</a>', '젊음의 열기가 가득한 대학로는 대한민국의 예술, 공연, 자유 등을 대표하는 문화 집결지라 할 수 있다. 대학로가 젊은이들에게 이처럼 아낌없는 사랑을 받았던 것은 서울대학교 문리대와 법대가 자리한 시절부터이다. 서울대학교 학생들을 중심으로 주변 대학 학생들과 젊은이들이 모여들었고, 자연스럽게 다른 어떤 장소와도 비교 불가한 대학로만의 개성을 만들어갔다. 1975년 서울대학교는 관악산 아래로 캠퍼스를 이전했다. 그러나 그 자리에는 아름드리 마로니에 나무가 있어 ‘마로니에공원’이라 이름 붙은 공원이 조성되었다. 이후 젊은이들과 방문자들을 위한 연극, 뮤지컬 등의 크고 작은 문화시설들이 하나 둘 들어서게 되면서 비로소 오늘날의 대학로가 완성되었다. 서울시는 1985년 5월 대학로를 ‘문화예술의 거리’로, 인사동에 이어 두 번째로 2004년에 ‘문화지구’에 지정했다. 문화지구는 문화자원이 밀집된 장소를 선별해 시장뿐 아니라 정부 차원에서도 보호하고 관리할 필요성을 느껴 선정한다. 대학로의 공연 예술을 활성화시키고 방문객을 늘면서도 상업 관련 시설이 주가 되는 것은 지양하고 소극장 및 문화시설을 보호하기 위함이다. 앞으로도 대학로만의 순수한 낭만과 예술이 번창하는 곳이기를, 대학로의 더욱 빛나는 내일을 소망한다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56791', '127015', '대한성공회 서울주교좌성당', '', '', '6', '37.5671457978', '126.9760490054', '', '서울특별시 중구 세종대로21길 15', '(정동)', '<a href=''http://www.cathedral.or.kr/'' target=''_blank'' title=''대한성공회 서울주교좌성당 사이트로 이동''>http://www.cathedral.or.kr</a>', '대한성공회 서울주교좌성당은 친교의 신앙으로 선교하는 제자공동체이다. 이 건물은 대한성공회 서울교구 주교좌성당으로서 조선 성공회 3대 주교인 마크트롤로프 주교가 영국인 아더 딕슨의 설계대로 착공하여 헌당식을 했다. 이후 1993년에 원 설계도를 영국의 한 도서관에서 찾게 되어 본래 모습대로 1996년에 완공하였다. 이 성당 건물은 로마네스크 양식에 한국 전통 건축 기법을 조화시킨 아름다운 건물이다. 외형은 십자가 형태로서 다양한 선이 조화를 이루고 있고 성당 내부에는 좌우로 12사도를 상징하는 돌기둥이 서 있고 전면 반원형 벽면에 예수 그리스도의 모자이크상이 새겨져 있으며 지하 성당에는 트롤로프 주교의 유해가 안치된 동판이 새겨져 있다. 주일감사성찬례는 성인과 학생, 어린이에 따라 여러 시간대가 있고, 평일에는 오전 7시와 낮 12시에 있다.성공회는 나라와 문화마다 다양한 색깔을 지니며, 한 교회 안에서도 신앙과 신학의 흐름이 매우 다양하다. 다른 교단과 다르게 성공회는 ‘개혁된 가톨릭’,  ‘교황 없는 천주교’,  ‘교리에 너그러운 정교회’,  ‘가톨릭 전통을 유지하는 개신교’이다. 수도권 지하철 1, 2호선 시청역 3번 출구에서 200m에 있다.', '1', '24', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56792', '2373223', '대한제국군 서울시가 전투지', 'http://tong.visitkorea.or.kr/cms/resource/30/3412230_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/30/3412230_image3_1.JPG', '6', '37.5607020615', '126.97375773', '', '서울특별시 중구 세종대로 39', '', '<a href=''http://mfis.mpva.go.kr/memorial/movementView.do?sid=742&memorialType=true&gubunCode=ALL&pageIndex=1&searchCondition=%EC%A0%9C%EB%AA%A9&searchKeyword=%EB%8C%80%ED%95%9C%EC%A0%9C%EA%B5%AD%EA%B5%B0'' target=''_blank'' title=''새창 : 홈페이지로 이동''>http://mfis.mpva.go.kr</a>', '대한제국군 서울시가 전투지는 대한제국군의 신념이 있고 항일투쟁이 시작된 곳이다. 1907년 통감부가 대한제국 군대해산을 통고하자 제1연대 제1대대장 박승환이 그 소식에 격분해 ＂군인으로서 나라를 지키지 못하고 신하로서 충성을 다하지 못하였으니 만 번 죽은들 무엇이 아깝겠는가＂라는 말을 남기고 자결을 택했다. 이를 본 장병들은 군대해산에 반대하는 장교들의 지휘에 따라 무기고를 부수고 총을 꺼내 일본군과 교전을 시작하였다. 대한제국군 장병들이 분투했으나 전력의 차이로 인하여 시가전은 일본군의 승리로 끝나고 말았다. 이 전투에서 대한제국군은 장교 11명 등 68명이 전사하고 100여 명이 부상을 입었다. 해산된 대한제국의 군인들은 의병에 합류했고 항일 의병의 전력이 예전과는 비교할 수 없을 만큼 강해졌다. 현재는 서울대한상공회의소 부지로, 건물 오른쪽 화단에 현충 시설 안내판이 설치되어 있다.', '1', '24', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56793', '3456362', '대현문화공원', 'http://tong.visitkorea.or.kr/cms/resource/80/3456380_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/80/3456380_image3_1.jpg', '6', '37.5576991161', '126.9449298864', '', '서울특별시 서대문구 이화여대1길 10 (대현동)', '', '', '서울에 있는 대현문화공원은 서울특별시 서대문구 대현동에 위치한 공원이다. 이 공원은 지역 주민들에게 자연과 여가 공간을 제공하며, 주변 도심 속에서 휴식을 취할 수 있는 장소로 사랑받고 있다.<br />대현문화공원은 다양한 산책로와 조경이 잘 어우러진 녹지 공간을 갖추고 있으며, 지역 주민들의 건강과 여가 활동을 지원하기 위한 운동기구와 휴식 공간이 마련되어 있다. <br />특히, 근린공원이라는 특성상 지역 주민들이 자주 찾는 친근한 장소로 알려져 있다.', '1', '14', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56794', '3352052', '대현산배수지공원·대현산배수지공원 모노레일', 'http://tong.visitkorea.or.kr/cms/resource/05/3351805_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/05/3351805_image3_1.jpg', '6', '37.5573533154', '127.021914548', '', '서울특별시 성동구 금호동1가', '1-72', '', '응봉은 산 모양이 매처럼 보이고 조선시대 왕들이 매사냥을 하였다 하여 한자어로 붙여진 이름이다. 옛날에는 하나의 줄기였으나 도시개발로 인해 응봉산, 대현산, 대현배수지, 금호산, 매봉산 5개 지역으로 나뉘어 각 자치구별로 관리되고 있다.		서울시에서 관리하는 대현산배수지공원은 배수지 상부에 조성된 공원으로 잔디광장, 걷기 트랙, 테니스장, 게이트볼장 등 다양한 운동시설과 편의시설이 설치되어 있어 많은 시민들이 즐겨 찾는 곳이다.가파른 경사로 인해 보행이 힘든 교통약자를 포함한 지역 주민들이 대현산배수지공원을 편리하게 이용할 수 있도록 모노레일이 설치되어 있다. 총 110m로, 왕복 6~7분 정도 소요된다. 승강장을 시작으로 신당현대아파트를 지나 공원에 도착하게 된다.', '1', '16', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56795', '3043811', '더 페이퍼랩', '', '', '6', '37.557751523', '127.0792426486', '', '서울특별시 광진구 천호대로 549 (중곡동)', 'G-TOWER 지하 1층', '<a href=''https://www.samwonpaper.com/paperLab/overview'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://www.samwonpaper.com</a>', '삼원특수지에서 운영하는 더 페이퍼랩은 종이 복합문화공간으로 군자역과 G-TOWER 지하 1층으로 연결되어 있다. 제품 패키지 디자인에 있어서 다채로운 종이의 중요성을 알 수 있는 곳으로, 전 세계 5,000여 가지의 종이를 직접 보고 구매뿐 아니라, 출력, 샘플 목업 패키지 제작까지 합리적인 비용으로 가능하며 완성된 모형을 촬영할 수 있다. 페이퍼 지니어스(전문가)가 상주하고 있어서 제작물을 구현, 제작하는데 필요한 실질적인 도움을 받을 수 있다.메인 공간인 [페이퍼 라이브러리]에서는 삼원 특수지에서 취급하는 모든 종이를 만날 수 있으며. [디자인 레퍼런스]에서는 국내외 다양한 패키지 제작물을 직접 확인할 수 있다. 더페이퍼랩은 디자이너의 정보, 지식 등을 공유하는 소통 공간으로 전 세계 제지사의 다양한 샘플북과 잡지 등 무료 열람이 가능하며 디자인, 인쇄 등 관련 교육을 제공한다. THE BUNDER에서는 전시회, 세미나가 가능한 공간으로 무료 관람이 가능하다.체험 프로그램으로는 더 페이퍼랩 투어가 있는데 더 페이퍼랩의 공간의 주요 기능들을 체험할 수 있는 프로그램이다. 다양한 종이를 직접 만져볼 수 있고, 모든 공간 설명과 다양한 인쇄법, 후가공 체험 등을 할 수 있다. 투어 프로그램은 무료로 사전예약이 필수이다.', '1', '6', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56796', '2794931', '더벨스파 동대문현대시티아울렛점', 'http://tong.visitkorea.or.kr/cms/resource/06/2796106_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/06/2796106_image2_1.jpg', '6', '37.5687878415', '127.0076821872', '', '서울특별시 중구 장충단로13길 20 (을지로6가)', '지하 1층', '<a href=''http://www.bellespa.co.kr/'' target=''_blank'' title=''새창 : 더벨스파 홈페이지로 이동''>http://www.bellespa.co.kr/</a>', '더벨스파에서는 1:1 맞춤형 관리로 고객의 피부 상태 및 신체에 맞도록 서비스를 제공한다. 웨딩부터 산전산후 안티에이징까지 최고의 제품과 전문적 프로그램으로 여성의 생애관리를 책임지는 곳이다. 5성급 호텔 스파에서 사용되는 프랑스 상위 1%의 에스테틱 제품을 사용하고 전문교육을 이수하여 고객들에게 최상의 관리와 결과를 위해 노력한다. 美 FDA의 승인을 받은 최신 장비도 보유하고 있다.', '1', '24', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56797', '126509', '덕수궁', 'http://tong.visitkorea.or.kr/cms/resource/05/3092905_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/05/3092905_image3_1.jpg', '6', '37.5661486714', '126.9749296905', '', '서울특별시 중구 세종대로 99', '', '<a href=''https://royal.khs.go.kr/ROYAL/contents/menuInfo-dsg.do?grpCode=dsg'' target=''_blank'' title=''새창 : 덕수궁 홈페이지로 이동''>https://royal.khs.go.kr/</a>', '사적 덕수궁은 본디 왕궁이 아니었다. 원래 성종의 형인 월산대군(1454∼1488)의 집이었으나, 1592년 임진왜란 때 왕궁이 모두 불타서 1593년 행궁으로 사용하기 시작하였다. 선조임금은 의주로 피난 갔다가 한양으로 환도한 뒤 덕수궁에서 머물렀다. 선조의 뒤를 이은 광해군은 1608년 이곳 행궁에서 즉위한 후 1611년 행궁을 경운궁이라 고쳐 부르고 7년 동안 왕궁으로 사용하다가 1615년에 창덕궁으로 옮기면서 이곳에는 선왕인 선조의 계비인 인목대비만을 유폐시켰다. 1618년에는 인목대비의 존호를 폐지하고, 경운궁을 서궁이라 낮추어 부르기도 하였다. 1623년 인조반정으로 광해군이 폐위되고 인조가 이곳 즉조당에서 즉위하고 창덕궁으로 옮긴 이후 270년 동안은 별궁으로 사용되었다. 1897년 고종황제가 러시아 공관에 있다가 환궁하면서 이곳을 다시  왕궁으로 사용하게 되었는데, 그때부터 다시 경운궁이라 부르게 되었고 그 규모도 다시 넓혔다. 그리고 고종 황제는 1907년 순종에게 양위한 후, 왕궁을 창덕궁으로 옮긴 후에도 이곳에 거처하였는데, 이때부터 고종 황제의 장수를 비는 뜻에서 덕수궁이라 부르게 되었다. 이런 까닭에 요즘 와서는 경운궁이라는 본래의 명칭을 사용해야 한다는 주장도 제기되고 있다. 덕수궁엔 지난날 많은 건물이 있었으나 현재  18,635평의 경내에 남아 있는 것은 대한문, 중화전, 광명문, 석어당, 준명당, 즉조당, 함녕전, 덕홍전 및 석조전 등에 불과하다. 덕수궁은 특히 구한말 비운의 황제 고종이 일제의 압박으로 양위를 강요당하고, 한 많은 여생을 보내다가 1919년 1월 22일 덕수궁 함녕전에서 일제에 의해 독살당하여 돌아가신 것으로 알려져 3·1 독립운동의 직접적인 계기가 되었던 곳이다.* 대한문 - 대한문은 현재 덕수궁의 정문이다. 이문의 원래 이름은 대안문(大安門)이고, 궁궐의 동문이었다. 대안문은 1906년에 다시 지으면서 ‘대한문’이라고 이름을 붙였다. 현판 글씨는 당시 한성부 판윤을 지냈던 남정철이 썼다.* 광명문 - 이 문은 왕의 침전인 함녕전의 정문으로 광무 8년(1904)에 큰불이 나서 타 버린 것을 같은 해에 다시 지었다. 1938년 석조전을 미술관으로 개관하면서 정동에 있던 흥천사(興天寺)의 범종과 창덕궁 보루각에 있던 자격루를 지금의 자리로 옮길 때 같이 옮겨 세웠다.* 석조전 - 대한제국때 외국 사신들을 접견하던 곳이다. 광무 4년(1900)에 착공하여 융희 3년(1909)에 준공한 석조 건물로, 조선왕조에서 마지막으로 지은 큰 규모의 건물이었다. 건물의 외관은 19세기 초 유럽에서 유행했던 신고전주의 양식을 따른 것으로 정면에 있는 기둥의 윗부분은 이오니아식으로 처리하고 실내는 로코코풍으로 장식하였다. 전체는 3층인데 1층은 접견 장소로, 반지하층은 시종인들의 대기 장소로 사용되었고, 2층에는 황제가 거처하였다. 광복 후에는 미소공동위원회 회의장, 국립박물관,국립현대미술관, 궁중유물전시관 등으로 사용하였다. * 함녕전 - 보물로 지정된 함녕전은 1897년 건축하여 고종황제가 거처하던 건물로, 1904년 화재로 인해 소실된 것을 같은 해에 복원하여 오늘에 이르고 있다. 고종황제는 1919년 1월 21일 이 함녕전에서 돌아가셨다. 그 후 비어 있던 이곳에 당시에 사용하던 가구, 서화, 집기 등을 배치하여 100여년 전의 궁중 생활상을 새롭게 재현하였다. * 중화전 - 대한제국(大韓帝國)의 정전으로 조선 제26대 왕 고종(高宗, 1852~1919 재위 1863∼1907)의 근대 국가 건설에 대한 자신감을 엿볼 수 있는 곳이다. 특히 정전 안에서 어좌(御座) 바로 위의 닫집을 보면 용을 한 쌍 볼 수 있는데 이는 정전 천정의 용과 같은 문양으로 덕수궁이 대한제국 황제의 황궁이었음을 보여 주고 있다. 지금 남아 있는 중화전은 1902년에 중층으로 건립되었으나 1904년 화재로 소실된 후 1906년 단층으로 중건된 것이다.* 중명전 - 중명전(重明殿)은 러시아 건축가 사바틴(Seredin Sabatin)의 설계로 1897년부터 1901년에 걸쳐 건립된 황실도서관이다. 지상 2층, 지하 1층의 서양식 건물로 처음에는 수옥헌(漱玉軒)이라 불렀다. 2009년 12월 복원을 거쳐 2010년 8월부터 전시관(‘대한제국의 운명이 갈린 곳, 덕수궁 중명전’)으로 일반에 공개하고 있다.* 그 밖에 석어당, 준명당, 즉조당, 정관헌, 덕홍전, 보루각 자격루 등이 있다.', '1', '24', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56798', '1605981', '덕수궁 대한문', 'http://tong.visitkorea.or.kr/cms/resource/91/3384991_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/91/3384991_image3_1.JPG', '6', '37.5651071556', '126.9765906796', '', '서울특별시 중구 세종대로 99 (정동)', '', '<a href=''https://royal.khs.go.kr/ROYAL/contents/menuInfo-gbg.do?grpCode=dsg'' target=''_blank'' title=''새창 : 궁능유적본부 홈페이지로 이동''>https://royal.khs.go.kr/</a>', '대한문(大漢門)은 덕수궁의 정문으로 ‘대한’은 “한양이 창대해진다”라는 뜻이다. 원래 경운궁의 정문은 남쪽에 있었던 인화문(仁化門)이었으나, 동문인 대안문(大安門) 주변이 환구단을 비롯하여 새로운 도심의 역할을 하게 되면서 정문의 역할을 하게 되었다. 지금의 이름은 1906년(광무 10) 문을 수리하면서 바꾸게 되었다. 원래 대한문은 약 33m 앞에 있었으나 1970년대 태평로를 확장하면서 현재의 자리로 옮겼다. 대한문을 지나서 있는 금천교는 1986년에 발굴하여 복원하였다.', '1', '24', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56799', '129186', '덕수궁 돌담길', 'http://tong.visitkorea.or.kr/cms/resource/50/2658350_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/50/2658350_image3_1.jpg', '6', '37.5660820124', '126.9748935853', '', '서울특별시 중구 세종대로 지하 101', '', '<a href=''https://royal.khs.go.kr/ROYAL/contents/menuInfo-gbg.do?grpCode=dsg'' target=''_blank'' title=''덕수궁 사이트로 이동''>https://royal.khs.go.kr/</a>', '기존의 도로를 보행자 중심의 도로로 재정비하고, 보행자를 위해 보도와 차도 공존도로 및 푸르름이 가득한 가로공간인 녹도의 개념을 복합적으로 도입한 우리나라 최초의 사례로 길이는 900m이다. 자연친화적인 점토블록 및 보행자의 안전한 보행권 확보를 위해 차도에 석고석 포장(페이빙스톤) 실시하였고, 시민들의 휴식 공간을 위해 느티나무 외 2종 130주 식재, 평의자 20개 설치하였으며 주변의 역사적 시설인 덕수궁과 연계한 역사적 가로 경관의 창출 및 가로환경 개선하였다. 낭만이 가득한 덕수궁 돌담길은 연인들이 걷기에 더없이 좋은 곳이며, 주변에 서울시립미술관과 서울역사박물관이 있어 문화산책 코스로도 제격이다.', '1', '24', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56800', '126481', '도봉산', '', '', '6', '37.6987534162', '127.0151139227', '', '서울특별시 도봉구 도봉동', '', '<a href=''https://tour.dobong.go.kr/Contents.asp?code=10001582'' target=''_blank'' title=''새창 : 도봉구청 홈페이지로 이동''>https://tour.dobong.go.kr</a>', '도봉산은 서울특별시 도봉구와 경기도 의정부시, 양주시 장흥면에 걸쳐 있는 산이다. 백두대간의 분수령에서 서남쪽으로 뻗은 한북정맥의 연봉을 따라 운악산과 불곡산을 거쳐 남서쪽으로 내려오다가 서울 동북쪽에서 우뚝 솟아 우이령을 경계로 북한산에서 이어진다. 최고봉인 자운봉을 비롯하여 남쪽으로 만장봉, 선인봉이 있고, 서쪽으로 오봉이 있는데, 암봉이 걸출하고 문사동계곡, 원도봉계곡, 무수골, 오봉계곡 등 수려한 계곡을 품고 있어 실로 금강산을 빚어 놓은 것 같아 일찍부터 서울의 금강이라 불렸다.자운봉은 해발 739.5m로 깎아지른 듯한 예봉은 전부가 암석으로 수천, 수만 성상이 풍우에 깎이고 씻겨 그 형상은 모두가 기암이고 묘이다. 용이 승천하는 형태가 있는가 하면 거북이 모양도 있다.천축사의 후봉인 만장봉은 1만 길이 되는 석벽으로 도봉산 봉우리 중에서도 정수라 할 수 있다. 도봉산의 산계는 크게 사패산, 만장봉, 오봉산, 우이암을 주봉으로 하여 이를 잇는 사패능선, 포대능선, 오봉능선, 도봉 주능선으로 이루어져 있다.', '1', '10', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56801', '1955511', '도봉서원', 'http://tong.visitkorea.or.kr/cms/resource/35/3401335_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/35/3401335_image3_1.JPG', '6', '37.6883382147', '127.0291407181', '', '서울특별시 도봉구 도봉산길 90', '(도봉동)', '<a href=''https://tour.dobong.go.kr/Contents.asp?code=10008364'' target=''_blank'' title=''새창 : 도봉구 문화관광 홈페이지로 이동''>http://tour.dobong.go.kr/</a>', '도봉서원은 조선 전·후기의 가장 대표적인 성리학자인 조광조와 송시열을 배향(配享:위패를 모시고 제사를 지냄)한 사액서원(賜額書院:조선시대 국왕으로부터 편액·서적·토지·노비 등을 하사 받아 그 권위를 인정받은 서원)으로 백사 이항복 등 저명한 시인 묵객들이 시문을 남긴 오늘날 서울 지역 내 가장 대표적인 서원이다. 비록 1871년 서원철폐령에 의해 훼철되어 유적의 대부분이 멸실되었으나 서울 지역 내 다른 서원과 달리 사당의 기단과 옛 사료상의 도봉서원 유적으로 소개된 각석군(刻石群)이 서원 터 앞 계곡에 대부분 원형대로 남아 있어 유적의 경계를 비교적 명확히 확인할 수 있다. 또한 이들 유적들을 차치하고서라도 각종 문헌이나 시에서 오랫동안 경치가 아름다운 것으로 손꼽히던 경승지(景勝地)에도 해당하므로 보존가치가 크다.(출처 : 국가유산청)', '1', '10', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56802', '126486', '도산공원', '', '', '6', '37.5214632538', '127.0338117034', '', '서울특별시 강남구 도산대로45길 20', '', '<a href=''https://visitgangnam.net/%ec%97%ac%ed%96%89%ec%a7%80/%ec%95%95%ea%b5%ac%ec%a0%95/%ec%95%95%ea%b5%ac%ec%a0%95%ec%9d%98-%eb%85%b9%ec%83%89-%ed%9c%b4%ec%8b%9d%ed%84%b0-%eb%8f%84%ec%82%b0%ea%b3%b5%ec%9b%90/'' target=''_blank'' title=''새창 : 비짓강남 홈페이지로 이동''>https://visitgangnam.net</a>', '도산공원은 서울 강남구 신사동에 있는 공원으로, 자주와 독립을 위해 일생을 바친 도산 안창호 선생의 고결한 삶을 기리기 위해 설립된 공원이다. 규모는 아담하지만 선생의 동상과 기념관, 부인 이혜련 여사와 합장된 묘소 등이 조성되어 있다. 기념관에서는 기념관에는 임시정부 사료집과 도산일기 등이 전시되어 있으며, 터치스크린을 이용해 도산어록을 볼 수 있다. 또한 이곳에서 매년 3월 10일 추모기념행사가 열린다. 현재 도산공원을 중심으로 많은 레스토랑과 카페들, 갤러리, 의류 상가 등이 들어서면서 강남에 간다면 꼭 들려야 하는 곳이 되었다. 또한 도산공원 근처에 해외 유명 브랜드의 플래그십 스토어들이 위치하고 있어 쇼핑을 즐기기에도 좋다.', '1', '1', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56803', '126502', '도선사(서울)', '', '', '6', '37.6552274686', '126.989734146', '', '서울특별시 강북구 삼양로173길 504', '(우이동)', '<a href=''http://www.doseonsa.org'' target=''_blank'' title=''새창 : 도선사 사이트로 이동''>http://www.doseonsa.org</a>', '강북구 북한산 능선에 자리 잡은 도선사는 대한불교조계종 직할교구 본사인 조계사의 말사이다. 호국참회 본찰 도선사는 경문왕 2년(862) 도선국사가 창건하였다고 전한다. 신라말의 도선국사가 명산승지를 답사하다가 산세가 절묘하고 풍경이 청수하여 1,000년 뒤의 말법시대에 불법을 다시 일으킬 곳이라고 예견하고 건립한 사찰이다. 그 뒤 조선 후기까지의 중건·중수에 관한 기록은 전하지 않으나 북한산성을 쌓을 때는 승병들이 이 절에서 방번(보초 임무)을 서기도 한 기록이 전해지고 있다.도선사 명부전에는 생전 인연이 깊었던 고 박정희 대통령과 고 육영수 여사(대덕화), 고 정주영 현대그룹 회장의 영정이 봉안되어 있으며, 명부전 앞뜰에는 200여 년 전 어떤 고승이 인도에서 가져와 심었다는 보리수가 고목이 되어 있고, 12세 소년이 썼다는 기록이 새겨진 대웅전 현판 글씨는 보는 이들의 감탄을 자아내게 한다. 천불전으로 오르는 길옆 연못 청담 심지에서는 참배객들이 동전을 던지며 소원을 빌기도 한다. 도선사까지 도착하는 셔틀버스를 운영하므로 지하철을 이용하여 이곳을 방문할 경우 홈페이지>종무행정>오시는길 내용을 참조하면 된다.', '1', '3', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56804', '2611730', '도시농업공원', 'http://tong.visitkorea.or.kr/cms/resource/18/2611718_image2_1.bmp', 'http://tong.visitkorea.or.kr/cms/resource/18/2611718_image3_1.bmp', '6', '37.5128105466', '126.8262419697', '', '서울특별시 양천구 신월동', '산174-1', '<a href=''http://www.yangcheon.go.kr/YangcheonUrbanAgriculturePark/html/index.html'' target=''_blank'' title=''새창: 양천 도시농업공원 ''>http://www.yangcheon.go.kr</a>', '2019년 4월에 개장한 서남권 최초의 도시농업공원인 ‘양천도시농업공원’은 신월동 산 174-1 일대에 위치하고 있다. 도시농업공원은 882㎡ 규모의 노지텃밭 및 어르신과 장애인을 위한 책상형 텃밭과 비닐하우스, 옥외샘터 등으로 구성된 도시농업존과 야외학습장, 숲 속마당, 힐링숲 등으로 구성된 산림복원존으로 총면적 24,078㎡의 ‘도시농업’ 특화 공원이다. 또한 공원 내 도시농업교육센터에서는 농사에 관심 있는 구민 누구나 쉽게 체험할 수 있는 도시농부학교와 어린이 도시농부체험교실을 운영하고 있다. 앞으로도 도시농업을 통해 몸과 마음의 건강, 가꾸는 재미, 나누는 행복, 뿌듯한 자부심을 느낄 수 있도록 다양한 행사와 볼거리를 제공할 예정이다.', '1', '19', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56805', '750863', '도안사', 'http://tong.visitkorea.or.kr/cms/resource/73/739073_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/73/739073_image3_1.jpg', '6', '37.6838730841', '127.0854384871', '', '서울특별시 노원구 덕릉로145길 99', '(상계동)', '<a title=''새창 : 도안사 홈페이지로 이동'' href=''http://www.doansa.or.kr'' target=''_blank''>http://www.doansa.or.kr</a>', '도안사는 서울 노원구와 경기 의정부시, 남양주시 별내면(別內面) 경계에 위치한 수락산(水落山) 내에 있는 사찰이다. 수락산 뒤쪽 외딴 곳에 아늑하게 자리 잡은 자그마한 산사로 화려하진 않지만 자연과 잘 조화된 경내의 풍경은 마음 속의 근심과 걱정을 씻겨준다.', '1', '9', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56806', '2946238', '도자기카페 줄', 'http://tong.visitkorea.or.kr/cms/resource/43/2947643_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/43/2947643_image3_1.jpg', '6', '37.5573821924', '126.9285063921', '', '서울특별시 마포구 동교동 179-38', '세광빌딩 3층', '<a href=''https://cafejool.modoo.at/''target=''_blank'' title=''새창 : 업체홈페이지 이동''>https://cafejool.modoo.at/</a><br><a href=''https://blog.naver.com/cafe_jool''target=''_blank'' title=''새창 : 업체블로그 이동''>https://blog.naver.com/cafe_jool</a><br><a href=''https://www.instagram.com/cafe_jool/''target=''_blank'' title=''새창 : 업체인스타그램 이동''>https://www.instagram.com/cafe_jool/</a>', '도자기 카페 줄에서는 원데이 클래스를 위주로 진행하고 있는데 클래스 종류로는 한번 구워진 컵이나 접시에 도자기전용물감, 연필로 그림을 그리는 ‘그림 그리기 클래스’와 직접 흙을 만지면서 비정형적인 형태의 머그컵, 접시, 그릇을 만들고 간단하게 채색까지 할 수 있는 ‘손빚기 클래스’, 물레라는 기계를 이용하여 정형적인 원형의 그릇을 만들어보는 ‘물레작업 클래스’가 준비되어 있다. 도자기 완성 기간은 그림 그리기는 2주 정도의 예상기간이 걸리며 손빚기와 물레작업 클래스는 총 6주 정도의 기간이 소요된다. 그리고 도자기 카페라는 이름에 걸맞게 음료 주문이 가능하고 음료를 마시며 여유롭게 수업을 들을 수 있다. 도자기 카페 줄은 블랙핑크 단독 리얼리티 콘텐츠인 \'24/365 with BLACKPINK\'에서 지수와 로제가 방문해 도자기를 만들었던 공방이다.', '1', '13', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56807', '1828571', '독립관', 'http://tong.visitkorea.or.kr/cms/resource/28/2413928_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/28/2413928_image3_1.jpg', '6', '37.5746723659', '126.9573421635', '', '서울특별시 서대문구 통일로 251 (현저동)', '', '<a href=''https://parks.seoul.go.kr/parks/detailView.do?pIdx=30&tabTarget=1'' target=''_blank'' title=''새창 : 서울의 공원으로 이동''>https://parks.seoul.go.kr</a>', '서대문 독립공원에 있는 독립관은 일제강점기에 철거되었던 옛 독립협회 사무실로 쓰던 독립관을 고증에 따라 복원한 건물이다. 이 건물은 원래 중국 사신 방문 시 접대를 하던 공간인 영빈관으로 사용하던 모화관 건물을 청·일 전쟁 이후 독립협회에서 사용하던 건물이다. 1894년 이후 서재필이 발의하고 독립협회에서 주도하여 개수한 후 독립관으로 개칭하였다. 이후 이곳에서 애국토론회를 개최하여 자주·민권·자강 사상을 고취하는 장소로 사용하였으며, 독립문과 함께 독립사상을 표현하였으나 일제에 의하여 철거되었다.독립관은 지상 1층 한식 목조건물로서 정면 6칸, 측면 4칸, 7량 팔작지붕 구조였으며, 원래 위치는 동남쪽으로 약 350m 지점이었다. 서대문독립공원 조성사업계획에 의거 전문가의 고증 자문에 따라 지상층은 당초대로 한식 목조건물로 복원하여 순국선열들의 위패봉안 및 전시실로 사용하고, 지하층은 행사 및 유물 보관을 위해 사용하고 있다. 1996년 서울시에서 건립하였다.', '1', '14', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56808', '127425', '독립문', '', '', '6', '37.5746640029', '126.955642319', '', '서울특별시 서대문구 통일로 251', '(현저동)', '<a href=''https://www.heritage.go.kr/heri/cul/culSelectDetail.do?pageNo=1_1_1_1&sngl=Y&ccbaCpno=1331100320000'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://www.heritage.go.kr</a>', '서대문독립공원에 있는 독립문은 갑오개혁 이후 자주독립의 의지를 다짐하기 위해 중국 사신을 영접하던 영은문을 헐고 서재필과 독립협회의 주도로 세워진 기념물이다. 내정개혁과 제도개혁을 추진하였던 개혁운동인 갑오개혁이 외국 세력의 간섭으로 성공하지 못하였고 나라의 자주독립 또한 이루지 못하자, 국민들은 민족의 독립과 자유를 위해서는 어떠한 간섭도 허용하지 않겠다는 다짐으로 세웠다. 독립문은 높이 14.28m, 넓이 11.48m로 프랑스 개선문을 모방하여 만든 건축물이다. 화강석을 쌓아 만든 이 문의 중앙에는 무지개 모양의 홍예문이 있고 왼쪽 내부에는 정상으로 통하는 돌층계가 있다. 정상에는 돌난간이 둘러 져 있으며, 홍예문의 가운데 이맛돌에는 조선왕조의 상징인 오얏꽃무늬가 새겨져 있다. 그 위의 앞뒤에는 한자로 한글로 [獨立門, 독립문]이라는 글자와 그 양옆에 태극기가 새겨져 있다. 문앞에는 영은문 주초 2개가 남아있다.원래 남동쪽으로 70m 떨어진 길 가운데에 있었는데, 1979년 성산대로 공사에 따라 지금의 위치로 옮기게 되었다. 바로 인근에는 서대문형무소가 있어 독립문과 더불어 민주주의와 자유의지에 대한 갈망을 되새겨 보는 역사 여행으로 둘러보면 좋겠다.', '1', '14', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56809', '2783547', '독립선언문 배부 터', 'http://tong.visitkorea.or.kr/cms/resource/09/3412009_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/09/3412009_image3_1.JPG', '6', '37.5754005765', '126.9862006239', '', '서울특별시 종로구 삼일대로 457', '(경운동)', '', '서울 종로구에 있는 운현궁에서 길 하나만 건너면 ‘독립선언문 배부 터’라고 적힌 작은 표지석이 눈에 들어온다. 1919년 3·1 운동 당시, 독립운동가들이 모여 독립선언문을 검토하고 배부했던 위치다. 지금도 표지석 앞에 민족대표 33인과 위안부 피해자 여성들의 이름을 기록한 돌이 자리해 보는 이들을 숙연하게 한다. 3.1 독립선언문은 이곳에서 멀지 않은 보성인쇄소에서 찍어낸 후 신축 중이던 천도교 대교당 마당(현재의 배부 터 바로 옆)에 갈무리해 두었다가 종교계, 학생 대표들에게 나누어주었다고 한다. 사람들이 무심하게 지나는 길옆에 자리해 그냥 지나치기 쉽지만, 나라의 독립을 위해 목숨을 걸고 싸웠던 1919년 봄의 자긍심을 느끼고 되돌아볼 수 있는 특별하고도 소중한 장소라 하겠다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56810', '2756142', '독산 근린공원', 'http://tong.visitkorea.or.kr/cms/resource/59/3467759_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/59/3467759_image3_1.jpg', '6', '37.4633256748', '126.8849491194', '', '서울특별시 금천구 범안로 1096', '(독산동)', '<a href=''https://www.geumcheon.go.kr/portal/contents.do?key=2123'' target=''_blank'' title=''새창 : 금천구청 홈페이지로 이동''>https://www.geumcheon.go.kr/</a>', '서울 금천구 독산1동에 있는 근린공원으로, 산책로와 운동 시설이 갖추어진 소규모 공원이다. 안양천이 길 건너에 바로 있어 함께 이용해도 좋다. 공원 중앙에는 2017년에 조성된 16,528㎡ 규모의 대형 인조잔디축구장이 울창한 나무들로 둘러싸여 축구 동호회 인들에게 인기가 높다. 대관 예약 시간 이후에는 주민들에게 개방되어 어린이들도 안전하게 뛰어놀 수 있다. 축구장 외에도 게이트볼장과 배드민턴장 5면을 비롯해 안양천변을 낀 산책로와 운동 시설을 갖추고 있다.', '1', '8', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56811', '3023349', '돈덕전', '', '', '6', '37.5651071556', '126.9765906796', '', '서울특별시 중구 세종대로 99 (정동)', '', '<a href=''https://royal.khs.go.kr/ROYAL/contents/R104020000.do?groupCode=dsg&programCode=exhibition5'' target=''_blank'' title=''새창 : 궁능유적본부 홈페이지로 이동''>https://royal.khs.go.kr/</a>', '대한제국 외교 공간으로 기획되었던 돈덕전은 덕수궁 내에 있는 서양식 2층 건물이다. ‘돈덕(惇德)’의 뜻은 ‘덕 있는 이를 도탑게 해 어진 이를 믿는다’라는 의미로 ‘덕이 있는 자’는 교류하며 신뢰를 쌓아가야 할 여러 국가를 가리킨다. 돈덕전은 고종이 즉위 40주년 기념행사장으로 사용하려고 세운 서양식 건물로 일제에 의해 헐렸다가 2017년 발굴조사를 시작으로 약 6년 만에 완성하여 2023년 9월 정식으로 일반에 공개되었다. 새롭게 개관한 돈덕전은 대한제국의 외교 장소라는 역사적 의미를 살리면서 내부 공간을 대한제국 외교사 중심 전시와 기록보관, 도서 열람, 국내외 문화교류와 예술행사를 위한 공간으로 꾸며졌다.', '1', '24', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56812', '3060927', '돈암동아리랑고개', 'http://tong.visitkorea.or.kr/cms/resource/42/3053742_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/42/3053742_image3_1.jpg', '6', '37.5951904732', '127.0157195941', '', '서울특별시 성북구 정릉동', '', '', '돈암동 아리랑고개는 성북구 돈암동에서 정릉동으로 넘어가는 고개를 뜻한다. 서울시내 전체 가로명 가운데 대로나 로, 길이 아닌 아리랑고개로 불리는 유일한 도로로서, 유서 깊고 친근한 고개이다. 정릉으로 가는 고개라서 정식 명칭은 정릉 고개였으나 1926년 나운규가 우리나라 최초의 영화 [아리랑]을 이 고개에서 촬영하고 폭발적인 인기를 얻으면서 아리랑 고개로 불리었다. 한국 영화의 선구자인 춘사 나운규를 기리기 위해 지금은 돈암동 아리랑고개 일대를 아리랑 영화의 거리로 명칭을 바꾸었다. 길을 따라 걷다 보면 나운규 공원과 아리랑 시네센터를 둘러볼 수 있다.', '1', '17', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56813', '3043990', '돈암장', 'http://tong.visitkorea.or.kr/cms/resource/83/3043983_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/83/3043983_image3_1.jpg', '6', '37.5914074625', '127.0091897415', '', '서울특별시 성북구 동소문로3길 84 (동소문동4가)', '', '<a href=''https://www.heritage.go.kr/heri/cul/culSelectDetail.do?pageNo=1_1_1_1&sngl=Y&ccbaCpno=4411100910000'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://www.heritage.go.kr</a>', '1945년 이승만이 한국에 돌아와 처음 기거했던 사저로 당시 서울시 돈암동 인근에 있다 하여 [돈암장]이라 하였다. 원래 조선시대 의료기관인 동활인서가 있었던 곳으로 일제강점기에 송성진의 의뢰로 중요무형문화재 대목장 배희한이 궁궐 양식으로 돈암장을 지었다. 쇠못을 전혀 쓰지 않고 나무를 짜서 지었으며 3채의 건물과 700평에 이르는 정원이 조화를 이룬 빼어난 주택이다. 해방 후에는 조선타이어주식회사 사장이었던 장진영이 거주하고 있었는데 미국에서 귀국한 이승만에게 2채를 빌려주어 2년간 거주하게 하였기 때문에 이승만의 거처로 알려진 한국 근대 정치의 역사적 현장이라고 할 수 있다. 돈암장은 주인이 바뀌고 세월이 흐르면서 증축과 개축으로 원형을 잃었다. 그러나 대한민국 정부 수립의 역사적 현장으로 가치를 인정받아 목조팔작지붕의 한옥 본채 한 동은 2004년 등록문화대로 지정되었다. 현재는 개인 소유로 소유주가 거주하고 있어 방문은 어렵다.', '1', '17', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56814', '1187220', '동대문 문구완구거리', '', '', '6', '37.5712043487', '127.0144065057', '', '서울특별시 종로구 종로52길 36', '(창신동)', '', '서울 지하철 1호선 동대문역 4번 출구로 나와 독일약국 옆 골목으로 들어서면 120곳의 문구점이 있는 동대문 문구거리가 나온다. 동대문 문구거리는 1960년대에 생겨나 오랜 전통을 자랑하는 국내 학용품의 중심지로 자리 잡고 있다. 이곳은 주로 공책류와 크레파스, 연필, 실내화, 가방, 스케치북 등의 학용품과 어린이 선물용품이나 팬시용품, 파티 용품 등 서울시내 학교 앞 문방구에 있는 문구가 이곳에 다 있다. 가격은 소비자 가격의 30~40%까지 싸게 살 수 있고 한 번에 구입해야 하는 단위가 클수록 가격이 저렴하다. 또한 작년에 팔다 남은 이월 상품을 정가의 절반 이하로 파는 곳도 많다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56815', '3111840', '동대문구 반려견 놀이터', '', '', '6', '37.5798461158', '127.0757923339', '', '서울특별시 동대문구 장안동 316-15(중랑천 장안교 하부)', '', '', '지역주민들이 반려견을 목줄 없이 산책시킬 수 있는 소형놀이터이다', '1', '11', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56816', '2470006', '동대문디자인플라자(DDP)', 'http://tong.visitkorea.or.kr/cms/resource/51/3083051_image2_1.png', 'http://tong.visitkorea.or.kr/cms/resource/51/3083051_image3_1.png', '6', '37.5675991318', '127.009948681', '', '서울특별시 중구 을지로 281', '(을지로7가)', '<a href=''http://www.ddp.or.kr'' target=''_blank'' title=''새창 :  DDP 홈페이지로 이동''>http://www.ddp.or.kr</a>', '꿈꾸고(Dream), 만들고(Design), 누린다(Play)는 뜻을 가지고 있는 DDP(동대문 디자인 플라자)는 서울특별시 동대문에 위치한 복합문화공간이다. 2014년 3월 21일 개관한 이래 굵직한 전시와 패션쇼, 신제품 발표회, 포럼과 콘퍼런스를 개최해오고 있다. 알림터, 배움터, 살림터, DDP 디자인 뮤지엄, 디자인 장터와 어울림 광장, 동대문 역사 문화공원으로 나뉘었다. 알림터에서는 서울패션위크, MAMA(Mnet Asian Music Awards), 반클리프 아펠전, BMW 신차 발표회 등을 개최했고, 다목적 연회 공간인 국제회의장 시설을 갖추었다. BTS 주제전과 서울도시건축 비엔날레 등이 열렸던 배움터, 누구나 디자이너가 되는 곳을 주제로 2019년 개장한 시민 라운지 ‘D-숲’과 DDP 관련 기념품을 소개하는 DDP 디자인 스토어를 아우르는 살림터까지, 규모에 걸맞은 내실로 관람객의 흥미를 북돋운다. 식당과 베이커리, 카페가 들어선 디자인 장터 역시 감성 충전을 기대하는 가족이나 연인을 만족시키고도 남을 산책 코스다.SNS 상에 동대문디자인플라자의 야경 사진이 범람하는 데는 이유가 있는 법이다. 건축가 자하 하디드(Zaha Hadid)가 동대문디자인플라자를 설계하며 “이른 새벽부터 밤이 저물 때까지 쉴 새 없이 변화하는 동대문의 역동성에 주목해 곡선과 곡면, 사선과 사면으로 이뤄진 특유의 건축 언어로 자연물과 인공물이 이음매 없이 이어지는 공간을 만들어냈다''라고 밝혔는데, 해 질 녘 조명이 켜진 후 건축물에 오롯이 집중할게 될 때 곡선 부가 주는 역동성의 진가가 드러난다. 야경 사진을 찍기 좋은 지점은 어울림 광장 진입로로, 내부의 웅장함과 건축물의 신비로운 소재가 연출하는 분위기를 담을 수 있다. 알림터와 배움터를 나누는 살림터 문 앞은 내외부의 현란한 곡선과 조명이 정점을 이룬다.', '1', '24', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56817', '1906334', '동대문역사문화공원', 'http://tong.visitkorea.or.kr/cms/resource/04/3064104_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/04/3064104_image3_1.JPG', '6', '37.567068642', '127.0101708978', '', '서울특별시 중구 을지로 281', '(을지로7가)', 'DDP <a title=''새창 : DDP 사이트로 이동'' href=''http://www.ddp.or.kr'' target=''_blank''>http://www.ddp.or.kr</a><br />VISITSEOUL <a title=''새창 : 서울시 문화관광 홈페이지로 이동'' href=''http://www.visitseoul.net'' target=''_blank''>http://www.visitseoul.net</a><br />', '동대문역사문화공원은 서울특별시 중구의 옛 동대문운동장 자리에 조성한 공원이다. 서울의 옛 역사와 문화를 보여주는 테마공원이자, 현대의 디자인과 문화를 경험할 수 있는 전시관 및 행사장, 시민들의 휴식공간이다. 2008년 동대문운동장을 철거하고 공원화하는 과정에서 조선시대의 서울성곽인 이간수문과 치성, 하도감과 훈련도감 터를 비롯한 건물지 유구 44기, 조선백자와 분청사기 등의 도자류를 포함해 주요 유물 1,000여 점이 출토되었다. 발굴된 유물은 공원 곳곳에 보존해 전시하고 있다. 공원 입구에는 동대문운동장 기념관이 있다. 옛 동대문운동장의 추억과 역대 스포츠 경기 정보, 각종 스포츠 관련 유물과 옛 동대문 운동장의 모형을 전시하고 있다. 수도권 지하철 2, 4, 5호선 동대문역사문화공원역 1번 출구에서 26m에 있다. 동대문디자인플라자(DDP)와 신당동 떡볶이거리가 도보 10분 거리에 있어 함께 둘러보기 좋다.', '1', '24', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56818', '1603677', '동십자각', '', '', '6', '37.576133703', '126.9794373301', '', '서울특별시 종로구 삼청로 1', '', '<a href=''https://www.heritage.go.kr/heri/cul/culSelectDetail.do?pageNo=1_1_2_0&VdkVgwKey=21,00130000,11'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://www.heritage.go.kr</a>', '동십자각은 광화문에서 연결되어, 경복궁 외궁성이 건춘문을 향하여 꺾이는 부분에 세운 망루이다. 석단은 조선시대 초기의 유구로 추측되나 누각은 흥선대원군 시대의 건립으로 추측된다. 규모는 작지만 조화로운 예술적 조형미로 경복궁 내 뛰어난 건축물 중의 하나라고 할 수 있다. 평면은 정면과 측면이 모두 3칸 방형이며 축부 구성은 하방과 창방만을 짜 올리고 벽 없이 모두 개방하였다. 주간에는 무벽의 개방식이며 바닥은 흙바닥으로 되었다. 동십자각은 궁성의 동남 모퉁이에 세웠던 망루와 같은 돈대 건물의 기능을 갖고 있던 것인데 궁성 원장이 헐리면서 현재와 같이 길가에 독립건물의 모습을 갖게 됐다. 서십자각은 궁성 전면 양 모서리에서 궁 내외를 감시할 수 있게 하였던 것인데 헐리어 현재는 볼 수 없다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56819', '2764187', '동작청소년센터', 'http://tong.visitkorea.or.kr/cms/resource/94/3397194_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/94/3397194_image3_1.jpg', '6', '37.5041023568', '126.9332602397', '', '서울특별시 동작구 상도로15바길 5 (상도동)', '', '<a href=''http://www.djyc.or.kr/'' target=''_blank'' title=''새창 : 동작청소년센터 홈페이지로 이동''>http://www.djyc.or.kr/</a>', '서울 동작구 상도동에 있는 청소년을 위한 공연, 밴드 연습, 댄스 연습 등 무대와 음악 활동을 할 수 있는 공간이다. 2005년에 개관하여 통기타, 드럼 레슨 프로그램과 자기표현 스피치 창의 창작 미술 프로그램도 운영하고 있으며, 자유롭게 장소를 빌려 이용할 수 있고, 스터디, 소모임도 가능하다. 청소년 활동 프로그램 모범 청소년 수련 시설로 평가받고 있으며, 2020년 11월에는 신대방동 청소년아트아지트를 개소하여 운영 중이다.', '1', '12', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56820', '3006915', '등나무근린공원', 'http://tong.visitkorea.or.kr/cms/resource/00/3006900_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/00/3006900_image3_1.jpg', '6', '37.6407111019', '127.0668719687', '', '서울특별시 노원구 동일로 1238 서울시립 북서울미술관', '', '<a href=''https://www.nowon.kr/www/info/info4/info4_01/info4_01_13.jsp'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://www.nowon.kr</a>', '등나무근린공원은 서울시립 북서울미술관 바로 옆에 위치해 있다. 무지개 모양 다리를 건너면 차도 건너의 중계근린공원으로 넘어갈 수 있다.공원 산책로를 따라 걷다 보면 다양한 조각상을 만나볼 수 있는데, 작품에 대한 설명이 있어 이해를 돕는다. 공원 내에 설치되어 있는 수도꼭지 마저도 아름다운 예술품이다. 공원 내에 운동시설과 팔각정이 설치되어 있어서 운동 후 잠시 쉬어갈 수도 있다. 여름에는 바닥분수도 운영하며, 뒤편에 나무그늘이 있고 벤치도 여러 개 있어서 쉴 수 있는 공간도 많다. 근처에는 아울렛과 대형 마트가 위치해 있어 쇼핑과 함께 그 곳의 다양한 푸드코트도 이용할 수 있다.', '1', '9', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56821', '2452881', '디지털 파빌리온', 'http://tong.visitkorea.or.kr/cms/resource/70/2502570_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/70/2502570_image3_1.jpg', '6', '37.5794706086', '126.890367579', '', '서울특별시 마포구 월드컵북로 396 (상암동)', '', '<a href=''https://kovac.modoo.at/?link=5asvc6jl''target=''_blank'' title=''디지털 파빌리온''>kovac.modoo.at</a>', '디지털 파빌리온은 IT 기업의 신제품과 기업 홍보 공간이다. 미래도시 I City에서는 미래의 거리, 학교, 문화, 생활이 펼쳐지고, ICT 탐구관에서는 놀이와 체험을 통해 ICT 기술 원리를 이해한다. 또한 2017년 2월에 ‘VR DEVELOPING BED’를 개관하여 국내 유명 중소기업의 실감 콘텐츠 관련 기술 및 제품을 지원하고 있다. ICT 산업 중 VR을 특화한 다양한 프로그램이 준비되어 있으니, 예약 후 이용해 보는 것을 추천한다.', '1', '13', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56822', '3448217', '디큐브시티 해바라기 공원', 'http://tong.visitkorea.or.kr/cms/resource/42/3452942_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/42/3452942_image3_1.jpg', '6', '37.5062901367', '126.8854062045', '', '서울특별시 구로구 경인로 624 (신도림동)', '', '', '신도림역과 직접 연결되어 있어 접근성이 우수하며, 도심 속에서 자연과 휴식을 즐길 수 있는 공간이다. 약 6,000평 규모로, 해바라기 광장, 야생화 계곡, 장미터널, 770석 규모의 야외극장 등 다양한 시설을 갖추고 있다. 특히 밤에는 화려한 조명과 분수가 어우러져 가족, 친구, 연인들에게 인기 있는 장소이다.', '1', '7', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56823', '2757145', '뚝섬 전망 문화 콤플렉스', '', '', '6', '37.5309294', '127.0659973939', '', '서울특별시 광진구 강변북로 2202', '(자양동)', '<a href=''http://www.j-bug.co.kr/'' target=''_blank'' title=''새창 : 뚝섬 자벌레 서울생각마루 사이트로 이동''>http://www.j-bug.co.kr/</a>', '강변대로 구리 방향을 가다 보면 우주선 모양의 건물을 발견할 수 있는데, 자벌레처럼 가늘고 긴 원통형 모양의 건물이 뚝섬 전망 문화 콤플렉스이다. 실내 총면적은 2,476㎡이며 약 700명의 관람객을 수용할 수 있다. 내부에는 한강을 내려다볼 수 있는 전망 공간이 마련되어 있어 휴식과 힐링의 장소로 인기를 얻고 있다. 1층은 한강이야기전시관이 있어 역사전시 관람과 문화체험을 할 수 있고, 3층은 한강이야기라운지 공간으로 휴게공간이 구비되어 있다. 한강시민공원 주차장과 7호선 뚝섬유원지역 3번 출구와 연결되어 있어 접근성이 편리하다.', '1', '6', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56824', '2756578', '뚝섬 한강공원 벽천마당', '', '', '6', '37.529151924', '127.0713164044', '', '서울특별시 광진구 자양동', '', '<a href=''https://hangang.seoul.go.kr/www/contents/654.do?mid=449'' target=''_blank'' title=''새창 : 미래한강본부 한강공원 홈페이지로 이동''>https://hangang.seoul.go.kr/</a>', '뚝섬 한강공원에는 바닥에서 솟아오르는 시원한 물줄기를 감상할 수 있는 분수와 암벽 등반을 즐길 수 있는 벽천마당이 있다.  여의도 한강공원 수상 분수, 난지 한강공원 거울 분수와 함께 서울 3대 한강 분수공원 명소 중의 한 곳으로, 특히 밤에 보는 야간조명 분수는 화려한 한강대교들과 강남 빌딩 숲들의 조명이 어우러져 장관을 이룬다. 공원에 앉아 윈드서핑, 수상 스키, 모터보트 등의 수상스포츠를 즐기는 모습을 감상하며 도심 속 지친 일상을 날려볼 수 있다.', '1', '6', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56825', '2756583', '뚝섬 한강공원 엑스게임장', 'http://tong.visitkorea.or.kr/cms/resource/02/3467602_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/02/3467602_image3_1.jpg', '6', '37.5298324112', '127.0718996655', '', '서울특별시 광진구 자양동 427-27', '', '<a href=''https://hangang.seoul.go.kr/www/contents/654.do?mid=449'' target=''_blank'' title=''새창 : 뚝섬한강공원 홈페이지로 이동''>https://hangang.seoul.go.kr</a>', '뚝섬 한강공원 엑스게임장은 인라인, 스케이트보드, BMX 등의 익스트림 스포츠를 초보자부터 프로들까지 즐길 수 있으며 프로 대회까지 열려 연중 스포츠 열기로 가득한 곳이다. 각종 시설물이 갖추어져 있어 아찔한 스릴을 즐길 수 있다. 어린이와 초급자를 위한 평지 공간부터 초·중급자를 위한 소형 기물, 중·상급자를 위한 대형 기물까지 공간구성도 다양하다. 이용 시 헬멧과 무릎, 팔꿈치, 손목 보호장구를 반드시 착용해야 하며, 청소년 이상(만 9세 이상)만 이용할 수 있다.', '1', '6', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56826', '1030763', '뚝섬한강공원', '', '', '6', '37.5293518579', '127.0689166174', '', '서울특별시 광진구 자양동', '427-1', '<a href=''https://hangang.seoul.go.kr/www/contents/654.do?mid=449'' target=''_blank'' title=''새창 : 한강공원 홈페이지로 이동''>http://hangang.seoul.go.kr</a>', '뚝섬한강공원은 한강공원으로 새 단장하기 이전부터 강변유원지로 유명했던 곳이다. 공원 내에는 수변광장, 장미정원, 자연학습장, 어린이 놀이터 등이 조성되어 있다. 봄과 가을에는 카페테리아와 계절 꽃전시장으로, 여름에는 시원한 바람을 맞으며 낭만과 젊음을 만끽할 수 있는 윈드서핑, 수상스키, 모터보트 등 수상스포츠가 활발하게 이루어진다. 겨울철에는 눈썰매장과 스케이트장으로 사계절 내내 시민들에게 많은 볼거리와 즐길거리를 제공한다. 이 외에도 X게임장, 인공암벽장, 유람선 선착장, 토요 나눔 장터 운영 등의 시설들이 있어 시민들의 많은 사랑을 받고 있다. 뚝섬한강공원에는 몸은 가늘고 긴 원통형인 자벌레 형태의 길이 243m 규모의 예술과 휴식이 함께하는 공간이 있다. 이곳은 한강의 역사와 문화적 가치를 일상에서 누리고 체험할 수 있는 오직 한강만을 주제로 한 유일한 전시관이다. 뚝섬유원지역에서 연결되어 있어 누구나 이곳을 통해 편리하게 공원으로 진입할 수 있으며, 자벌레통로에는 미디어아트 작가들의 작품전시 감상을 할 수 있도록 꾸며놓았다.', '1', '6', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56827', '3074841', '라움아트센터', '', '', '6', '37.5081871185', '127.0394226796', '', '서울특별시 강남구 언주로 564 (역삼동)', '', '<a href=''http://www.theraum.co.kr/'' target=''_blank'' title=''새창 : 홈페이지로 이동''>http://www.theraum.co.kr/</a>', '라움아트센터는 사회적 교류의 장소라는 의미의 소셜베뉴(Social Venue)의 가치를 실현하고자 하는 복합문화공간이며, 품격 높은 공연과 아카데미 그리고 연회와 웨딩을 주로 하고 있다.전체적인 디자인은 스코틀랜드의 스키보 캐슬(Skibo Castle)을 모티브로 건축된 고성을 연상시키며, 하우스, 가든, 스몰, 채플의 다양한 분위기를 조성할 수 있다. 지하 1층부터 4층까지로 이루어진 라움아트센터는 파티와 콘서트 그리고 웨딩을 위한 넓고 럭셔리한 분위기의 홀을 구비하고 있다.  마제스틱 볼룸, 갤러리 홀, 챔버홀, 가든 4개의 홀로, 이 공간에서 페스티벌, 테마 콘서트, 클래식  연주회, 파티, 아카데미, 세미나, 초청행사 등 다양한 행사를 개최한다.라움아트센터의 아카데미는 플라워클래스와 아트클래스를 주로 운영하고 있다. 정기적으로 운영되는 플라워클래스는 입문자부터 전문가까지 다양한 수준의 꽃꽃이를 배울 수 있다. 선정릉역 4번 출구 근처에 있어 쉽게 방문이 가능하다.', '1', '1', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56828', '2992824', '락고재 컬쳐 라운지 애가헌', '', '', '6', '37.5823525048', '126.983914524', '010-3882-3106', '서울특별시 종로구 가회동', '31-6', '<a href=''http://rkj.co.kr/ko-culturelounge '' target=''_blank'' title=''새창 : 락고재 컬쳐 라운지 애가헌 홈페이지로 이동''>http://rkj.co.kr/ko-culturelounge </a>', '락고재 컬쳐 라운지 애가헌은 조선시대 사랑방을 재해석한 아름다운 한옥에서 락고재가 큐레이션 한 우리 차 3가지와 계절별로 준비하는 프리미엄 디저트 페어링 코스를 즐길 수 있다. ‘옛 즐거움 누리는 곳’이라는 의미의 락고재(樂古齋)가 ‘가족을 사랑하는 집(애가헌愛家軒)’이라고 이름 붙인 만큼 옛 선조의 지혜와 사랑이 녹아 있는 따뜻하고 즐거운 시간을 이미 약속받은 기분. 소중한 사람들과 오붓한 시간을 즐기기 위해 이곳의 문을 두드리는 사람이 유독 많은 이유이기도 하다. 스몰 웨딩은 물론 프라이빗 티파티, 스페셜 이벤트 등 저마다의 사연이 있는 특별한 날 특별한 추억을 만들고자 하는 이들에게 든든한 히든 스페이스 역할을 자처하고 있다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56829', '2440925', '레일크루즈 해랑열차', 'http://tong.visitkorea.or.kr/cms/resource/43/2440943_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/43/2440943_image2_1.JPG', '6', '37.5548277077', '126.9717164149', '', '서울특별시 용산구 한강대로 405', '', '<a href=''https://www.railcruise.co.kr/website/infault.asp''새창 : 국립무형유산원 홈페이지로 이동''>https://www.railcruise.co.kr/<br>', '호텔과 열차가 결합된 레일크루즈 해랑은 2008년 11월 국내최초로 편의시설이 갖춰진 호텔식 관광전용열차로 출발하였다. 해랑은 해(태양)와 함께 아름다운 금수강산을 유람하는 최고의 열차라는 순우리말의 의미와 함께 봉황을 형상화한 독특한 로고와 열차 전면에 도색된 파란색과 금빛 라인이 매력적이다. 한국적인 인테리어와 함께 장거리 여행객을 위해 맞춤형으로 제작된 객실에는 침대, 소파, 화장실(비데), 샤워실, TV 등 여행과 휴식에 필요한 모든 편의시설을 갖췄다. 객실 또한 스위트룸(2인기준), 디럭스룸(2인기준), 패밀리룸(3인기준), 스탠더드룸(4인기준)으로 객실이 구분되어 있어 부부나 연인, 혹은 가족 여행 등 여행 콘셉트에 맞춰 객실을 선택할 수 있다.달리는 열차 내에서 창밖으로 펼쳐지는 풍경을 감상하며 와인과 케이크 등을 비롯해 다양한 식음료 서비스를 받을 수 있는 고급 레스토랑 칸과 아카펠라, 난타, 악기연주 등 해랑 승무원들의 특별공연과 다양한 문화이벤트가 펼쳐지는 이벤트 라운지칸도 해랑만의 또 다른 매력이다. 해랑은 이러한 상품의 우수성을 인정받아 , KATA(한국여행업협회)에서 주관하는 2014 / 2015 / 2016 우수여행상품 선정되었다. 해랑코스는 효도상품으로 인기인 화요일 출발 2박 3일 전국일주 코스와 자녀를 둔 가족단위의 여행객이 주로 이용하는 토요일 출발 1박 2일 동부권, 서부권 코스(격주운행)로 나뉜다. 해랑은 시즌 별로 고객의 만족도가 가장 높은 코스로 변화시켜 재이용하는 고객들도 꾸준히 증가하고 있는 추세이며, 정기코스 외에 스페셜코스와 단체 맞춤형 코스로도 운행 가능하다.', '1', '21', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56830', '2500214', '로렌스길', 'http://tong.visitkorea.or.kr/cms/resource/37/3464837_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/37/3464837_image3_1.jpg', '6', '37.5484886553', '126.9214545083', '', '서울특별시 마포구 상수동', '', '<a href=''https://www.mapo.go.kr/site/culture/home''target=''_blank'' title=''새창: 마포관광''>https://www.mapo.go.kr/site/culture/home</a>', '서울시 마포구 상수역 1번 출구로 나와 쭉 내려오면 만날 수 있는 로렌스 길은 한국에서 가장 오래된 시계회사인 로렌스 뒤쪽으로, 개성 있는 식당과 카페들이 위치한 골목이다. 시계회사 로렌스 옆쪽 벽에 아기자기한 로렌스 길 지도가 벽화처럼 그려져 있어, 로렌스 길을 찾는 관광객들이 원하는 곳을 쉽게 찾아갈 수 있다.', '1', '13', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56831', '3090533', '롯데몰 김포공항점스카이파크', 'http://tong.visitkorea.or.kr/cms/resource/23/3090523_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/23/3090523_image3_1.jpg', '6', '37.5651123866', '126.8030065089', '', '서울특별시 강서구 하늘길 38 (방화동)', '김포공항 롯데몰 스카이시티', '', '김포공항점 스카이파크는 김포공항과 연결되어 공항이용객은 물론 모든 시민들이 자유롭게 이용할 수 있는 자연친화적 문화휴식 공간이다. 김포공항 입구 오른쪽에 위치한 스카이파크는 대형 녹지공간으로 6개의 테마로 구성된 곳이다. 전통공원과 국제선 하늘숲 정원, 썬큰광장과 레이크 파크로 구성되어 있으며 공원 내 다양한 조각상과 핑크퐁과 같은 캐릭터 동상이 많이 있어 다양한 볼거리가 있는 곳이다. 또한 전통공원에는 연못과 정자가 있어 휴식하기에 좋다. 오솔길을 따라 산책을 하고, 놀이터에서 아이들이 놀 수 있는 공간이 마련되어 있다.', '1', '4', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56832', '2476731', '롯데월드 아쿠아리움', 'http://tong.visitkorea.or.kr/cms/resource/45/2384845_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/45/2384845_image3_1.jpg', '6', '37.5141653654', '127.1040749597', '', '서울특별시 송파구 올림픽로 300 (신천동)', '지하 1층', '<a href=''https://www.lotteworld.com/aquarium/index.asp'' target=''_blank'' title=''새창 : 롯데월드 아쿠아리움 공식 홈페이지로 이동''>https://www.lotteworld.com/aquarium</a>', '전 세계 5대양을 그대로 옮겨놓은 13개의 다양한 테마존에서 해양생물과의 특별한 만남을 가질 수 있다. 강에서 연안으로, 연안에서 바다로 생태계의 흐름을 따라 걸으며 자연의 경이로움을 경험할 수 있다. 25m 넓이의 국내 최대 서식의 수조로, 집이 되어 살아갈 해양생물을 위해 바다를 담은 수조를 설계했다. 또한 다양한 해양생물들이 더불어 사는 자연과 동일한 서식환경을 만들었으며, 국내 최고 수준의 생명유지장치를 통해 청정지역 바닷물을 그대로 구현하고 있다. 아쿠아리움에서는 국내 최다 14종의 체험 교육프로그램을 통해 해양생물과 더불어 사는 즐거움을 전하고 있다.', '1', '18', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56833', '126498', '롯데월드 어드벤처', 'http://tong.visitkorea.or.kr/cms/resource/00/3304300_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/00/3304300_image3_1.jpg', '6', '37.5113516917', '127.0979006014', '', '서울특별시 송파구 올림픽로 240', '', '<a href=''https://adventure.lotteworld.com/kor/main/index.do'' target=''_blank'' title=''새창 : 롯데월드 홈페이지로 이동''>http://www.lotteworld.com</a>', '롯데월드는 롯데 그룹이 국민들의 여가 선용과 외국인 관광객 유치를 위해 설립하고 운영하고 있는 테마파크이다. 모험과 신비를 주제로 한 실내 주제공원인 롯데월드 어드벤처, 호수공원인 매직아일랜드, 쇼핑몰, 민속박물관, 아이스링크, 호텔, 백화점 등으로 구성되어 있으며, 관광, 레저, 쇼핑, 문화를 한 곳에서 해결할 수 있는 대단위 복합생활공간이다. 롯데월드 어드벤처는 세계에서 가장 큰 실내 테마파크로, 최첨단 탑승시설과 환상적인 퍼레이드, 각종 영상 시스템, 레이저쇼, 공연, 각국의 음식을 연중무휴로 즐길 수 있다. 민속박물관에는 한국의 오천 년 역사와 민속 문화가 역사전시관, 모형촌, 놀이마당, 저잣거리로 나누어져 쉽고 재미있게 재현되어 있다. 롯데월드 가든스테이지는 롯데월드 공연의 중심무대로 각종 시즌 뮤지컬 쇼, 공개방송 등의 다양한 이벤트가 진행되며, 롯데월드 스타에비뉴는 한류스타 조성거리로 체험형 엔터테인먼트 시설이다.', '1', '18', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56834', '2492348', '롯데월드타워 서울스카이', '', '', '6', '37.5136209882', '127.1042832896', '', '서울특별시 송파구 올림픽로 300', '', '<a title=''새창 : 롯데월드타워 서울스카이 홈페이지로 이동'' href=''http://seoulsky.lotteworld.com'' target=''_blank''>http://seoulsky.lotteworld.com</a>', '세계 5위, 국내 최고 높이 롯데월드타워 117층~123층에 위치한 서울 스카이는 500m 상공에서 환상적인 서울의 풍경과 야경을 360도 뷰를 통해 즐길 수 있는 서울의 새로운 명소이다.스카이데크, 스카이테라스, 123라운지 등으로 구성되었으며, 전시존은 지하 1층, 2층에 위치해있다. 전시층 및 전망대 로비에는 한국의 탄생과 세계 속의 한국을 보여주고 한국의 자부심을 상징하는 곳으로 다양한 미디어 아트들이 전시되어 있어 볼거리를 제공한다. 또한 서울스카이에서 자체적으로 개발한 캐릭터 상품과 롯데월드타워 관련 다양한 기념품점이 있어 서울스카이의 추억을 오래도록 기억할 수 있는 기념품을 구매할 수 있다. 전망층에서는 건물 2층 높이의 통유리를 통해 서울 시내를 한눈에 내려다 볼 수 있다. 지상 478m 높이에 시공한 \'스카이데크\'는 세계에서 가장 높은 유리 바닥 전망대로 기네스 월드 레코드에 등재됐다.120층으로 올라가면 고도 486m의 바람을 피부로 느낄 수 있는 스카이 테라스가 있다. 123층에는 높고 웅장한 커튼월을 통한 최고층의 전망과 함께 다양한 메뉴를 즐길 수 있는 프리미엄 라운지가 있다. 서울스카이를 향해 올라가는 더블데크 엘레베이터 \'스카이 셔틀\'도 빼놓을 수 없는 볼거리이다. 단순한 엘리베이터가 아닌 분속 600m로 운행하는 엘리베이터로 세계 최초로 천장과 벽체 3면에 모니터 화면이 설치되어 있어 한국의 탄생과 발전 모습을 담은 영상을 상영한다. 전망대로 오르는 1분간 시공간을 초월한 황홀함을 느껴볼 수 있다.', '1', '18', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56835', '2003909', '롯데월드타워&롯데월드몰', 'http://tong.visitkorea.or.kr/cms/resource/91/2938991_image2_1.bmp', 'http://tong.visitkorea.or.kr/cms/resource/91/2938991_image3_1.bmp', '6', '37.5126349603', '127.1025918048', '', '서울특별시 송파구 올림픽로 300', '', '<a href=''http://www.lwt.co.kr'' target=''_blank'' title=''롯데월드타워&롯데월드몰 홈페이지로 이동''>http://www.lwt.co.kr</a>', '한국적 곡선의 미를 지닌 한국 전통의 도자기와 붓의 형상을 모티브로 설계된 롯데월드타워는 총 123층, 555M 높이로 세계 5위의 초고층 빌딩이다. 대지면적은 축구장의 12.2배, 전체 총 면적은 축구장의 110배에 달하는 거대한 수직도시이다. 2016년 12월 마침내 준공되었으며, 특급 호텔 ‘시그니엘 서울’과, 화려한 서울의 야경을 볼 수 있는 전망대인 ‘서울스카이’, 최고급 레지던스와 오피스 공간, 복합기능공간 등으로 구성되어있다. 롯데월드몰은 국내 최대의 명품 백화점인 ‘에비뉴엘’과 국내 시내 면세점 중 최대 면적을 자랑하는 ‘롯데면세점’, ‘롯데 하이마트’, ‘쇼핑몰’, ‘롯데마트’ 등이 위치해있는 쇼핑 관광의 명소로 다양한 국내외 브랜드를 만날 수 이는 공간이다. 쇼핑공간 외에도 문화·예술공간으로 아시아 최대규모의 멀티플렉스인 ‘롯데시네마 월드타워관’, ‘롯데월드 아쿠아리움’, 국내 최초 빈야드 스타일의 ‘롯데 콘서트홀’이 있어 쇼핑과 엔터테인먼트를 함께 즐길 수 있는 복합 쇼핑몰이다.<br>', '1', '18', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56836', '2794933', '롯데호텔 서울 설화수 스파', 'http://tong.visitkorea.or.kr/cms/resource/57/2796257_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/57/2796257_image2_1.jpg', '6', '37.5654016461', '126.981045821', '', '서울특별시 중구 을지로 30 (소공동)', '본관 7층', '<a href=''https://www.sulwhasoo.com/kr/ko/spa/programs/programs.html'' target=''_blank'' title=''새창 : 설화수 홈페이지로 이동''>https://www.sulwhasoo.com/</a>', '설화수 스파의 공간 디자인은 테라피스트의 지속적인 관리와 서비스를 경험하며 “기억에서 자신만의 이야기가 만들어진다”라는 의미를 고객에게 전달하고자 시작되었다. 스파를 들어선 시작의 순간부터 스파 케어의 마지막까지, 브랜드 스토리와 장인정신 등 다채로운 표현 기법을 느낄 수 있으며, 공간 곳곳에서 현대적으로 재해석한 한국의 미를 경험할 수 있다. 스파 내 대기 공간에서는 20세기 초 작품 ‘궁중자수매화도 10곡 병풍’을 전통 자수 장인과 협업하여, 한국 전통 자수만의 특별한 기법인 자릿수를 표현한 작품을 경험할 수 있다. 밀랍을 이용해 제작한 ‘윤회매’ 매화 작품 또한 예부터 이어져 내려온 전통 방식으로 만든 공예품으로, 디스플레이 연출로 재탄생해 설화수가 추구하는 브랜드 철학과 헤리티지를 깊이 있게 느낄 수 있다. 스파 공간은 서울의 중심이자 역사가 살아있는 1979년에 지어진 롯데호텔 객실 구조의 흥미로운 동선 구획과 은유적으로 표현되는 한국 전통의 미(美), 포근하면서 아늑한 분위기를 느낄 수 있다.', '1', '24', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56837', '2591813', '류순정·류홍 부자 묘역', 'http://tong.visitkorea.or.kr/cms/resource/81/3392981_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/81/3392981_image3_1.JPG', '6', '37.4887161799', '126.8293840248', '', '서울특별시 구로구 오류동', '산43-31, 산43-32', '<a href=''https://www.guro.go.kr/www/contents.do?key=3003&'' target=''_blank'' title=''새창 : 구로구청 홈페이지로 이동''>www.guro.go.kr</a>', '조선 전기의 문신 류순정(1459~1512)과 무신인 그의 아들 류홍(1483~1551)의 묘역이다. 류순정과 류홍 부자는 공을 세운 공신으로, 이 묘역은 16세기에 조성되었다. 오늘날 서울 지역에서 유일한 부자(父子) 2대 공신묘역으로, 돌로 만들어 묘 앞에 세워 둔 석물들의 양식과 묘역의 조성 형태로 조선시대 공신의 묘를 세우던 관습이나 제도 등 묘제의 특징을 파악할 수 있는 가치를 지닌다. 류순정의 묘 형태는 부인과 상하 합장으로, 위는 류순정의 묘이고 아래는 부인 안동 권씨의 묘이다. 부부의 묘역 왼쪽에는 류순정의 생애와 업적을 기리기 위해 1567년(명종 22)에 세운 신도비가 있다. 비문은 한성부 판윤이었던 강혼이 지었고, 글씨는 이름난 서예가였던 송인이 썼다.류순정의 묘에서 서남쪽으로 약 150m 떨어진 언덕에 아들 류홍을 비롯한 후손의 묘역이 있다.', '1', '7', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56838', '2946527', '리얼 이스케이프 챌린지', 'http://tong.visitkorea.or.kr/cms/resource/75/2945575_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/75/2945575_image3_1.jpg', '6', '37.5542047587', '126.9271149424', '', '서울특별시 마포구 서교동 339-5', '4층', '<a href=''https://www.rec-escape.com/''target=''_blank'' title=''새창 : 업체홈페이지 이동''>https://www.rec-escape.com/</a><br><a href=''https://www.instagram.com/rec_escape''target=''_blank'' title=''새창 : 업체인스타그램 이동''>https://www.instagram.com/rec_escape</a>', '‘리얼 이스케이프 챌린지’는 스릴 넘치는 게임, 신선한 테마, 놀라운 스토리가 가득하며 테마에 따라 제한 시간 안에 방에서 탈출해야 하는 방탈출카페이다. 이곳은 러블리즈 멤버 ‘미주’ 등 연예인들도 즐겨 찾는 곳이다. 주기적으로 테마가 교체되며 방마다 영어로 표기되어 있는 외국인을 위한 안내서비스가 제공되고 있다. 그리고 100% 예약제로 운영되기 때문에 네이버 예약 또는 홈페이지로 예약 후 방문을 해야 한다. 게임 시작 전 휴대폰과 스마트워치 등의 전자기기는 사물함에 보관해야 하며 사전 동의서 작성 후 게임을 시작하게 된다. 게임이 진행되는 60분간 타당한 이유가 없는 한 퇴장이 불가하다. 빨리 탈출하게 된다면 방탈출 랭킹에도 올라갈 수 있다.', '1', '13', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56839', '2850356', '리얼월드 성수', '', '', '6', '37.5422307285', '127.0584227632', '', '서울특별시 성동구 연무장13길 8', '', '<a href=''http://seongsu.realworld.to/'' target=''_blank'' title=''새창 : 홈페이지로 이동''>http://seongsu.realworld.to/</a>', '놀이와 문화가 공존하는 도심 속 신개념 놀이 문화 공간이다. 미스터리 호텔 컨셉의 공간을 돌아다니면서 이색 체험을 할 수 있고 카페와 게임을 동시에 즐길 수 있다. 몽환적인 호텔을 배경으로 다양한 인터렉티브 게임을 경험하며 스토리 속 진짜 주인공이 되어볼 수 있다. 1층 매장에서 다양한 음료를 즐기는 것은 물론, 다양한 인터렉티브 게임을 구매할 수 있다. 그리고 포토존도 준비되어 있어 인생샷도 찍을 수 있다.', '1', '16', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56840', '3027054', '리홀뮤직갤러리', '', '', '6', '37.5946345287', '126.990480244', '', '서울특별시 성북구 성북로31길 9 (성북동)', '', '<a href=''https://www.instagram.com/rheehallmusicgallery/'' target=''_blank'' title=''새창 : 인스타그램으로 이동''>https://www.instagram.com/rheehallmusicgallery/</a>', '리홀뮤직갤러리에 대중교통을 이용하여 방문한다면, 4호선 한성대입구역 6번 출구로 나와 초록버스 1111, 2112번을 타고 종점 [서울다원학교]에서 하차 후 성북동 누룽지백숙건물 3층으로 올라오면 된다. 이곳은 12만여 장의 레코드판과 진공관시스템 그리고 1930년도에 제작된 빈티지스피커를 통해 다양한 장르의 음악을 즐길 수 있는 음악감상 전용공간이다.  리홀뮤직갤러리에서 다루는 장르는, 클래식, 팝 그리고 재즈이다.원하는 음악을 신청곡 종이에 적어 DJ에게 전달하면 장르에 따라 스피커를 바꿔가며 음악을 맞춤 제공해 준다. 양쪽의 벽면을 가득 채운 음반들이 주는 향수와 최초의 진공관 사운드가 주는 편안함 속에서 일상의 부산스러움을 잊고 오롯이 음악을 감상할 수 있는 공간이다.비정기적으로 음악회나 인문학 강연도 진행하니 전화 문의 혹은 인스타그램을 참고해 방문하는 것도 감성을 채우는 또하나의 즐거움이다.', '1', '17', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56841', '2720441', '립바이 메이크업 스튜디오', '', '', '6', '37.521938082', '127.0273206149', '', '서울특별시 강남구 논현로155길 8 (신사동)', '한주빌딩 지하 1층', '<a href=''http://www.lipby.com'' target=''_blank'' title=''새창: 립바이 메이크업 스튜디오 홈페이지로 이동''>http://www.lipby.com</a>', '립바이 메이크업 스튜디오는 메이크업 클래스 전문 서비스 브랜드이다. 나에게 어울리는 맞춤형 1:1 메이크업 레슨을 통해 개성에 맞는 화장 방법을 알려준다. 또한 생활 속 나만의 메이크업 방법을 차차 경험하고 즐길 수 있도록 서포터 해준다. 한국의 뷰티 스타일로 나에게 어울리는 화장법을 배워보자.', '1', '1', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56842', '3458417', '마곡광장', 'http://tong.visitkorea.or.kr/cms/resource/81/3458381_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/81/3458381_image3_1.JPG', '6', '37.56606548', '126.8269404101', '', '서울특별시 강서구 마곡중앙5로 지하9 (마곡동)', '', '', '마곡광장은 서울특별시 강서구 마곡동에 위치한 복합 문화 공간이다. 이곳은 지하철 9호선과 공항철도 마곡나루역 사이에 위치하며, 면적은 약 12,979㎡이다. 광장은 지상 1층부터 지하 2층까지 구성되어 있으며, 지상부는 공원으로 조성되어 시민들의 휴식 공간을 제공한다. 지하 1층에는 선큰광장과 상가, 그리고 지하철 9호선 마곡나루역이 위치해 있어 편리한 교통 접근성을 갖추고 있다. 마곡광장은 마곡지구의 중심부에 위치하여 업무지구를 관통하는 보행축과 페스티벌이 이루어지는 가로축이 교차하는 지점에 자리하고 있다. 이로 인해 교통의 중심지이자 문화 활동의 거점으로 기능하고 있다. 또한, 광장 주변에는 스마트 이끼 타워, 스마트 모빌리티, 미세먼지 저감 수직 정원 등 스마트 시티 서비스가 구현된 첨단 시설들이 설치되어 있어 방문객들에게 미래 지향적인 도시 환경을 체험할 수 있는 기회를 제공한다. 마곡광장은 도심 속에서 자연과 첨단 기술이 어우러진 공간으로, 시민들에게 다양한 문화와 휴식의 기회를 제공하는 서울의 새로운 랜드마크로 자리매김하고 있다.', '1', '4', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56843', '3331253', '마곡안전체험관', 'http://tong.visitkorea.or.kr/cms/resource/46/3331246_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/46/3331246_image3_1.JPG', '6', '37.5553222488', '126.8249436714', '', '서울특별시 강서구 마곡중앙로 13 (내발산동)', '', '<a href=''https://magoksafety.kr/'' target=''_blank'' title=''새창 : 마곡안전체험관 홈페이지로 이동''>https://magoksafety.kr/</a>', '마곡안전체험관은 자치구 주도로 건립된 최초의 대형 안전체험시설이다. 교통안전, 학생안전, 재난안전, 보건안전 등 6개 분야, 12개 프로그램을 통해 다양한 재난 상황을 체험하고 사고를 대응할 수 있는 방법을 배울 수 있다. 다양한 체험형 교육 프로그램을 통해 안전에 대한 의식을 새롭게 하고 실제 현장에서의 대응과 대처 요령도 몸으로 습득하게 될 것이다. 마곡안전체험관은 재난체험을 통해 시민들이 안전의식을 생활화하고 어떤 재난 상황에서도 대처할 수 있기를 희망한다.', '1', '4', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56844', '126487', '마로니에공원', '', '', '6', '37.5802419773', '127.0027239807', '', '서울특별시 종로구 대학로 104', '', '<a href=''http://parks.seoul.go.kr/parks/detailView.do?pIdx=72&tabTarget=1'' target=''_blank'' title=''서울의 공원 사이트로 이동''>http://parks.seoul.go.kr</a>', '마로니에공원은 서울 시민들을 위한 문화예술의 터전으로 옛 서울대학교 문리대, 법대 자리에 있다. 이곳에는 마로니에나무가 자라고 있는데 1929년 4월 5일 서울대학교의 전신인 경성제국대학 시절에 심은 것으로, 이 나무 이름을 따서 마로니에공원이라는 이름이 붙었다.어린이놀이터가 있고 야외무대와 분수공원, 조각품 등이 전시되어 있다. 야외무대는 가수들의 공연장과 각종 문화행사장으로 활용되고 있다. 이 공원 주위로 문예회관 대극장과 바탕골소극장, 동숭아트센터 등의 소극장이 자리하고 있다. 유명한 맛집과 다양한 컨셉의 카페들이 주위에 많다. 이 공원에서는 연중무휴 무료로 개방되는 공연과 행사들에 많아서 언제 방문하더라도 참여하고 즐길 거리가 가득하다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56845', '2752525', '마루공원', 'http://tong.visitkorea.or.kr/cms/resource/61/3467561_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/61/3467561_image3_1.jpg', '6', '37.4965531025', '127.0808164645', '', '서울특별시 강남구 일원동', '', '', '강남구 일원동에 있는 마루공원은 주차가 가능한 공원으로 반려견과 함께 산책하기 좋은 곳이다. 날씨가 좋을 때는 다수의 방문객이 공원에 설치된 그늘막 아래쪽에서 텐트와 의자를 펼치고 쉬는 모습을 볼 수 있다. 공원 내에는 2개의 놀이터 구역이 있는데 한 곳은 미끄럼틀과 그네가 있는 일반 놀이터이고, 또 다른 구역은 여러 종류의 도로 교통 표지판들이 나열되어 있어 교통안전 교육과 더불어 기차, 자동차 등을 타고 놀 수 있는 공간으로 구성되어 있으며, 놀이터 바닥이 푹신푹신하게 되어 있어 어린이들이 넘어져도 다치지 않고 안전하게 뛰어놀 수 있다.', '1', '1', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56846', '2757653', '마천공원', 'http://tong.visitkorea.or.kr/cms/resource/52/3401452_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/52/3401452_image3_1.JPG', '6', '37.501795325', '127.1454548806', '', '서울특별시 송파구 성내천로23길 12', '(마천동)', '', '마천공원은 서울 송파구 마천동에 있는 근린공원으로 주택가에 자리 잡고 있다. 공원의 규모는 작은 편이지만 관리사무실, 화장실, 어린이놀이터, 산책로 등 내부에 시설들이 잘 갖추어져 있다. 운동장과 놀이터를 중심으로 공원 내 아스팔트 길로 걷기 운동을 하기 좋고, 옆쪽으로 올라가면 나무들이 많이 있는 풍경을 보며 산책할 수 있다. 온동장은 인조잔디로 꽤 넓은 편이다. 옆쪽으로는 경로당이 있고 앞쪽에 운동기구들이 있다. 마천공원은 지역 어린이와 어른 모두에게 휴식과 건강 증진에 도움을 주는 공간이다.', '1', '18', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56847', '753558', '마포대교', 'http://tong.visitkorea.or.kr/cms/resource/61/758561_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/61/758561_image3_1.jpg', '6', '37.5364709722', '126.9418446559', '', '서울특별시 마포구 마포동', '', '<a href=''https://www.mapo.go.kr/site/culture/content/culture01020604''마포 문화관광 사이트로 이동''>https://www.mapo.go.kr</a>', '서울 마포구 용강동과 영등포구 여의도동을 잇는 마포대교는 길이 1,400m, 너비 25m의 6차선 도로이며 한남대교에 이어 한강에서는 4번째로 가설된 교량이다. 1968년 2월에 착공하여 1970년 5월에 준공하였으며 준공 당시에는 서울대교라고 부르다가 1984년에 마포대교로 이름을 바꾸었다. 마포대교는 차도 외에도 자전거 전용도로와 보행자 전용도로가 나뉘어 있어서 안전하다. 대교 양쪽에는 앉아서 쉴 수 있는 의자가 마련되어 있어서 잠깐 앉아서 쉴 수 있다. 특히 마포대교의 조명은 다른 한강 다리 중 야경이 특히 볼만하다. 마포대교에서 여의도 한강공원이 가깝고 밤이 되면 한강 다리마다 조명이 켜져 한강을 유유히 떠다니는 유람선과 조화를 이루어 아름답다. 한강공원 쪽으로 건너가면 더현대백화점과 IFC몰이 있어서 함께 둘러보기에 좋다. 마포대교는 수도권 지하철 5호선 마포역 4번 출구에서 964m에 위치하고 있다.', '1', '13', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56848', '3385841', '마포음식문화거리', 'http://tong.visitkorea.or.kr/cms/resource/58/3383058_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/58/3383058_image3_1.JPG', '6', '37.5418536613', '126.9408140425', '', '서울특별시 마포구 용강동', '', '', '마포음식문화거리는 마포용강 맛깨비길이라고도 불리는 먹자골목이다. 마포역 1번 출구부터 대로변 양쪽으로 식당들이 쭉 늘어서 있다. 주로 주물럭, 껍데기, 돼지갈비 등의 고깃집이 있으며, 총 37개의 식당 골목이 형성되어 있다. 거리에는 가게를 찾기 쉽게 안내판도 설치되어 있다. 또한 이곳은 토정 이지함 선생이 살던 동네라 거리 곳곳에 선생의 동상과 운세에 관한 물고기 조형물들이 설치된 것을 볼 수 있다.', '1', '13', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56849', '2373247', '마포전차종점 3·1운동 만세 시위지', 'http://tong.visitkorea.or.kr/cms/resource/62/3401362_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/62/3401362_image3_1.JPG', '6', '37.5384152466', '126.9449665392', '', '서울특별시 마포구 마포대로 20', '(마포동)', '<a href=''http://mfis.mpva.go.kr/memorial/movementView.do?sid=655&memorialType=true&gubunCode=ALL&pageIndex=1&searchCondition=%EC%A0%9C%EB%AA%A9&searchKeyword=%EB%A7%88%ED%8F%AC%EC%A0%84%EC%B0%A8%EC%A2%85%EC%A0%90'' target=''_blank'' title=''새창 : 국가보훈처 현충시설 홈페이지로이동''>http://mfis.mpva.go.kr</a>', '마포 전차 종점은 현재는 서울 지하철 5호선 마포역에서 마포대교 진입 구간을 이른다. 일제강점기부터 1960년대 말까지 서울 중심부를 오가던 전차의 종점이 있던 곳이다. 이 장소는 역사적으로 중요한 지점으로 1919년 3월 1일 오후 8시경 약 1,000명의 군중이 모여 독립 만세 시위를 벌인 3·1 운동 만세 시위지이다. 1919년 3월 1일, 태화관에서 독립선언식을 거행하고 오후 2시 탑골공원 팔각정에서 독립선언서가 낭독되며 수천 명의 학생들이 대한독립만세를 외치며 시위행진을 벌였다. 종로에서 광교, 시청 앞, 남대문 등지를 거쳐 해가 질 무렵 오후 8시에 이곳 마포 전차 종점에 집결하여 다시 한번 크게 독립만세를 외쳤고 연희전문학교 부근에서는 오후 11경까지 해산하지 않고 시위행진은 계속되었다. 단 한 건의 폭동도 발생하지 않고 평화적이고 비폭력적인 방법으로 우리 민족의 독립 의지를 표명했던 곳이다. 현재는 불교방송국이 있는 다보빌딩이 자리하고 있다. 만세 시위지임을 알리는 표지석이 다보빌딩 건물 화단에 설치되어 있다. 수도권 지하철 5호선 마포역 4번 출구에서 112m에 위치하고 있다.', '1', '13', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56850', '2759682', '만남의광장', 'http://tong.visitkorea.or.kr/cms/resource/43/3467843_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/43/3467843_image3_1.jpg', '6', '37.4682678469', '126.9454518049', '', '서울특별시 관악구 신림로 23', '(신림동)', '', '관악구 만남의 광장은 서울 남쪽 경계의 명산 관악산으로 가는 사람들을 위한 집결지이다. 서울대 입구 정류장 관악산공원 현판이 있는 관악산공원이 제1 광장이며, 관악산과 서울대 사이 숲길을 따라 만나는 수중동산 인근에 제2 광장이 있다. 제2 광장까지는 휠체어 규격이나 회전 시 소요 공간 등을 고려한 무장애 숲길이 마련되어 있다. 걷는 거리는 총 1.3km이다.', '1', '5', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56851', '1395466', '만해한용운심우장', 'http://tong.visitkorea.or.kr/cms/resource/93/2622293_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/93/2622293_image3_1.jpg', '6', '37.5936180016', '126.991669053', '', '서울특별시 성북구 성북로29길 24', '(성북동)', '<a href=''https://www.sb.go.kr/tour/PageLink.do?link=forward:use/PageContent.do&menuNo=11000000&subMenuNo=11010000&thirdMenuNo=11010800&fourthMenuNo=''target=''_blank'' title=''새창 : 홈페이지 이동''>https://www.sb.go.kr/</a> <br><a href=''https://www.heritage.go.kr/heri/cul/culSelectDetail.do?ccbaCpno=1331105500000&pageNo=1_1_1_1&sngl=Y'' target=''_blank'' title=''문화재청 사이트로 이동''>http://www.cha.go.kr</a>', '이 집은 3.1 운동 대 민족대표 33인 중 한 분이자 님의 침묵의 시인인 만해 한용운 선생이 1933년부터 1944년까지 살았던 곳이다. 동쪽으로 난 대문을 들어가면 북쪽으로 향한 기와집인 심우장과 양옥의 관리사 그리고 만해가 직접 심었다는 향나무 등이 있다. 심우장은 전체 규모가 5칸에 불과한 작은 집으로, 이곳에는 가운데 대칭을 중심으로 왼쪽에 온돌방, 오른쪽에 부엌이 있다. 부엌 뒤로는 식사 준비는 하는 공간인 천마루방이 있다. 한용운의 서재였던 온돌방에는 심우장이라는 현관이 걸려있는데, 이는 근대의 대표적인 서화가인 위창 오세창이 쓴 것이다. 이 이름은 깨우침을 찾아 수행하는 과정을 소를 찾는 일에 비유한 불교 설화에서 따온 것이다. 심우장이 있는 성북동 일대는 1930년대 서울이 확장되면서 주거지로 개발되었는데, 이 집은 당시의 여느 집과는 다르게 검소하고 소박한 구성을 보여준다. 한용운은 조선의 불교를 개혁하려고 했던 승려이자 조국의 독립에 힘쓴 독립운동가이며 근대 문학에 큰 업적을 남긴 시인이었다. 한용운이 만년을 보낸 심우장은 그의 이 같은 면모를 살필 수 있는 역사의 현장이다.', '1', '17', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56852', '2650756', '망우 역사문화공원', 'http://tong.visitkorea.or.kr/cms/resource/24/2006124_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/24/2006124_image2_1.JPG', '6', '37.5984057744', '127.114832694', '', '서울특별시 중랑구 망우로 570', '(망우동)', '<a href=''https://www.jungnang.go.kr/manguriPark/main.do''target=''_blank'' title=''새창: 망우리 공원''>https://www.jungnang.go.kr</a>', '망우리공원은1933년 2월 2일자 동아일보 ＂미아리 공동묘지가 조만간 가득 찰 것에 대비하여 경기도 망우리의 임야 70여 만 평을 경성부의 공동묘지로 결정하였다.＂ 1935년 10월 24일자 동아일보 ＂이태원의 공동묘지를 택지화하기 위하여 37,000餘基 중 연고자가 있는 묘는 망우리로 이장하고 무연고 묘는 신사리(新寺里)에서 화장하기로 하였다.＂라는 기록에서 알 수 있듯이 망우리 망우산을 1933년부터 서울시의 공동묘지로 사용하기 시작하였다. 이후 40년이 지난 1973년 3월 25일에는 28,500여기의 분묘가 가득 차 더 이상의 묘지 쓰는 것이 금지된 이후 현재는 이장과 납골을 장려하면서 2018년 12월 기준 7,425基가 있으며, 앞으로도 계속해서 줄어들 전망이다. 이 시기에 도산 안창호, 고하 송진우, 명창 임방울, 애국지사 조종완, 박찬익, 백대진 등의 묘소도 이장하여 국립묘지와 사립공원 묘지에 안장 되어 있다.1992년 2월에는 이곳에 안장 되어 있는 방정환, 오세창, 한용운, 조봉암, 지석영, 문명훤, 장덕수 등 7명의 연보비를 중랑구에서 산책로를 중심으로 조성했으며, 1998년 2월에는 추가로 박인환, 문일평, 서병호, 서동일, 오재영, 서광조, 유상규, 오긍선 등 8명에 대한 연보비가 추가로 5.2km의 산책로에 조성하였다.그 동안 망우리 공원에는 3.1 독립 운동을 주도했던 萬海 한용운 등을 비롯한 17인의 유명 인사가 안장 되어 있는 것으로 알려졌으나, 각 언론사 인명록 및 독립유공자 공훈록을 중심으로 서울시 시설관리공단 장묘문화센터의 고인 검색을 통해 묘역을 확인하고 현장 답사를 통해 소설가 계용묵을 비롯한 10인의 유명 인사가 추가로 안장 되어 있음이 밝혀졌다.<출처 : 서울시 중랑구청>', '1', '25', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56853', '1115203', '망우산(서울)', 'http://tong.visitkorea.or.kr/cms/resource/89/1902989_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/89/1902989_image3_1.jpg', '6', '37.594281087', '127.1056030968', '', '서울특별시 중랑구 망우동 산 69-1', '일대', '<a href=''http://www.jungnang.go.kr/portal/main/contents.do?menuNo=200598'' target=''_blank'' title=''중랑구 문화관광 사이트로 이동''>http://www.jungnang.seoul.kr</a>', '망우산은 서울 중랑구 망우동과 면목동, 경기도 구리시에 걸쳐 있는 해발 281.7m의 산이다. 망우산 일대에는 1933년 공동묘지로 지정된 서울시립장묘사업소 망우묘지와 아동문학가인 소파 방정환과 민족 대표 33인의 한 분인 독립운동가인 오세창, 한용운 그리고 천연두 보급의 선구자로 의학자이며 국어학자인 지석영 등이 안장되어 있는 망우리공원 등의 시설이 있다. 공원에는 시인 박인환, 문일평, 서병호, 서옹일, 오재영, 서광조, 유상규, 교육가 오긍선 등 여덟 분의 연보비가 추가로 설치되어 역사의 교육장으로 이용되고 있다. 망우리공원에는 5.2km의 산책로가 있으며 1998년 산책로의 이름을 공모하여 사색의 길로 부르기 시작했다. 문화재로는 거북비로 불려 오던 조선 중기의 문신인 신경진의 신도비(서울특별시 유형문화재)가 있다. 도시환경과 자연관찰로, 나무 정자, 약수터 등의 시설이 있으며, 수목 관리가 잘 되어있고, 공기가 맑아 휴식 및 자연공원으로 많은 시민들이 찾고 있다.', '1', '25', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56854', '3062089', '망원유수지체육공원', 'http://tong.visitkorea.or.kr/cms/resource/29/3062029_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/29/3062029_image3_1.jpg', '6', '37.556817805', '126.8969787272', '', '서울특별시 마포구 월드컵로25길 190 (망원동)', '', '<a href=''https://www.mapo.go.kr/site/main/content/mapo05040501'' target=''_blank'' title=''새창으로 이동''>https://www.mapo.go.kr/</a>', '망원유수지체육공원은 수해 조절용 유수지를 정비해서 2004년에 문을 연 망원동에 있는 체육공원이다. 2008년 인라인스케이트 전용 트랙과 보행자용 우레탄 육상트랙, 국제경기장 규격의 축구장, 야간 조명시설 등을 추가로 설치했다. 현재 축구장 2면, 족구장 3면, 농구장 3면, 인라인연습장, 다목적 트랙, 게이트볼장, 체력단련 운동기구 56점 등의 시설을 보유하고 있다. 야간 조명시설이 있어서 저녁 운동이 가능하다. 또, 체육공원 사이로 작은 오솔길이 나 있어 산책 중인 시민들도 많이 볼 수 있다.', '1', '13', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56855', '127307', '망원정지', 'http://tong.visitkorea.or.kr/cms/resource/43/720543_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/43/720543_image3_1.jpg', '6', '37.549871528', '126.9046921083', '', '서울특별시 마포구 동교로8안길 23', '(합정동)', '<a href=''http://culture.mapo.go.kr/'' target=''_blank'' title=''새창 : 마포문화체육관광 홈페이지로 이동''>http://culture.mapo.go.kr</a> <br /><a href=''http://www.cha.go.kr/'' target=''_blank'' title=''새창 : 문화재청 홈페이지로 이동''>http://www.cha.go.kr</a>', '합정동에 위치하고 있는 정자로서 서울시 기념물로 1989년 10월 복원되어 관리하고 있다. 세종6년(1424년)에 효령대군이 별서를 지었는데 이듬해 세종이 서교에 농정을 살피러 왔다가 정자에 들렀을 때 단비가 내리어 ''희우정''으로 이름지었다고 하며, 성종 15년(1484년)에 월산대군이 정자를 크게 고치고, 망원정으로 이름했다. 연산군 12년 7월 1천여명이 앉을 수 있게 확장하고 수려정으로 고쳤으나, 그해 9월 중종반정으로 다시 망원정으로 변경하였다. 1925년 을축대홍수과 한강개발로 자취를 감추었던 것을 한강변 문화 유적 복원 계획의 일환으로 1988년 6월에 기공하여 1989년 10월 준공하였다. 근처의 망원동은 바로 이 정자에서 딴 동네 이름이다.<br>', '1', '13', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56856', '1131275', '매헌시민의 숲', 'http://tong.visitkorea.or.kr/cms/resource/66/2668466_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/66/2668466_image3_1.jpg', '6', '37.4691610476', '127.0368379074', '', '서울특별시 서초구 매헌로 99 (양재동)', '', '<a href=''https://parks.seoul.go.kr/template/sub/citizen.do'' target=''_blank'' title=''새창 : 서울의 공원으로 이동''>https://parks.seoul.go.kr</a>', '매헌시민의 숲은 우리나라 최초로 숲 개념을 도입한 공원으로 도심에서 보기 힘든 울창한 숲을 이루고 있는 공원이다. 86아시안 게임과 88올림픽을 앞두고 당시 서울의 관문이었던 양재 톨게이트 주변 환경을 개선하고자 1986년에 공원으로 조성되었다. 공원에는 소나무, 느티나무, 당단풍, 칠엽수, 잣나무 등 70종 25만 주의 수목이 식재되어 있다. 매헌로를 기준으로 북측 구역에는 매헌윤봉길의사기념관, 바닥분수, 어린이놀이터 등이 있어 아이들과 나들이를 하러 가족들이 많이 찾는다. 남측 구역에는 유격 백마부대 충혼탑과 1987년 미얀마 안다만해협 상공에서 북한의 테러로 폭파된 대한항공 858편의 위령탑, 1995년 삼풍백화점 붕괴로 사망한 희생자와 유족들을 위로하기 위해 세운 위령탑, 우면산 산사태 희생자 추모비 등이 있다. 특히, 매헌시민의 숲 제3구역은 서초구에서 문화예술공원으로 조성하였는데, 놀이마당, 야외공연장, 각종 기획전시장, 조각공원, 만남의 광장 등으로 구성되어 있다. 이곳에서 공연과 전시회등이 열리고 있어 문화자치구를 자랑하는 서초구의 새로운 명소로 각광받고 있다. 이렇게 공간마다 색다른 분위기를 연출하고 있어 시민들에게 많은 사랑을 받고 있는 공간이다.', '1', '15', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56857', '2373320', '맥아더 사령관 한강방어선 시찰지', 'http://tong.visitkorea.or.kr/cms/resource/48/3384448_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/48/3384448_image3_1.JPG', '6', '37.5162061316', '126.9122840318', '', '서울특별시 영등포구 신길로 275 (영등포동)', '', '<a href=''http://mfis.mpva.go.kr/memorial/movementView.do?gubunCode=ALL&sid=581&pageIndex=1&searFacilgubun=&searTopicsort=&searAddr_sigun=&searMpvabranch=&searVR=&searInfo=&searGbn=tit&searTxt=%EB%A7%A5%EC%95%84%EB%8D%94'' target=''_blank'' title=''새창 : 현충시설정보서비스 홈페이지로 이동''>http://mfis.mpva.go.kr/main/main.do</a>', '북한군의 남침 직후 맥아더 사령관이 미군의 참전 결정에 앞서 한강방어선 전황을 확인하기 위해 시찰한 장소이다. 1950년 6월 29일 수원비행장에 도착한 미 극동군 사령관 맥아더는 전쟁 상황에 대한 보고를 받았다. 이어 전선을 직접 확인하기로 하고, 시흥을 거쳐 한강 전선을 마주한 영등포 동양맥주공장(현 영등포공원) 옆 언덕으로 이동하여 전황을 관찰하였다. 이때 언덕의 진지를 방어하던 한 병사가 “상관이 철수 명령을 내릴 때까지 지키겠다”라고 말하여 맥아더를 크게 감동시켰다고 한다. 이날 맥아더의 전선 시찰을 미군의 6·25전쟁 참전에 중요한 계기가 되었다.', '1', '20', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56858', '264311', '명동', '', '', '6', '37.5651434137', '126.9850038013', '', '서울특별시 중구 을지로 66', '', '<a href=''http://www.junggu.seoul.kr/tour/content.do?cmsid=14818&sf_category1=CWS1325&mode=view&page=&cid=98087638'' target=''_blank'' title=''새창 : 서울 중구 문화관광 홈페이지로 이동''>http://www.junggu.seoul.kr/tour/</a>', '명동은 거대 쇼핑도시를 연상케하는 공간이다. 일반적으로 명동 거리는 지하철 4호선 명동역에서 을지로, 롯데백화점으로 이어지는 약 1km 정도의 거리를 말한다. 이곳에는 각종 브랜드매장, 백화점,보세가게 등이 밀집되어 있다. 유행의 메카라는 표현이 어울릴 정도로 의류,신발,액세서리 등의 다양한 제품을 구입할 수 있다. 남대문이나 동대문보다는 질이 좋은 브랜드가 많이 모여 있는 것이 특징이다.우선 백화점으로는 가까이에 롯데백화점, 신세계백화점이 있으며, 명동거리에는 눈스퀘어(Noon Square), 명동밀레오레, 엠플라자(M Plaza)와 같은 쇼핑몰이 있다. 각종 브랜드숍은 중앙거리를 비롯해 사이드 골목에 밀집되어 있다.명동에는 쇼핑과 함께 먹을거리, 즐길거리가 많다.먹을거리로는 패밀리레스토랑, 패스트푸드점은 물론 한식, 양식, 일식으로 다양하다. 이중에서 명동 돈까스와 칼국수(명동교자)는 유명하므로 한번 먹어보는 것이 좋다. 그 외에도 명동에는 헤어샵, 은행, 극장 등 많은 편의시설이 있다.* 명동 주요 관광지- 명동성당 : 한국 천주교 서울대교구 본당 건물인 명동성당은 우리나라 최초의 본당이며 한국 천주교회의 상징으로, 고종 29년(1892)에 착공, 광무 2년(1898)에 준공된 순수한 고딕양식의 건물이다. 명동성당은 우리나라 기독교 역사뿐 아니라 정치, 사회, 문화 전반에 걸쳐 큰 영향을 미친 터전이다.- 명동예술극장 : 명동 옛 국립극장을 복원하여 2009년 6월 개관한 명동예술극장은 완성도 높은 연극작품을 만날 수 있는 연극예술전문 공연장이다. 1934년 바로크 양식으로 건축된 극장외형과 현대적인 내부공연 시설이 잘 조화되는 곳이다.- 명동 재미로 : 명동과 남산의 연결지점이지만 명동의 화려함에 비해 특색 없이 밋밋하고 가파르기만 했던 오르막길이 2013년 12월, 만화의 거리 \'재미로\'라는 이름으로 새롭게 태어났다. 이곳에서는 원로부터 신인 작가까지 약 70명의 대한민국 대표 작가의 다양한 만화 캐릭터들과 콘텐츠들을 만날 수 있다.', '1', '24', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56859', '1957600', '명동 남대문 북창동 다동무교동 관광특구', 'http://tong.visitkorea.or.kr/cms/resource/42/3077642_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/42/3077642_image3_1.JPG', '6', '37.5599771733', '126.9753048316', '', '서울특별시 중구 세종대로 40', '(남대문로4가)', '<a href=''https://www.junggu.seoul.kr/tour/content.do?cmsid=14994'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://www.junggu.seoul.kr</a>', '명동, 남대문, 북창동, 다동무교동 관광특구는 서울 중심부에 있는 명동과 남대문, 북창동, 다동 무교동 일대의 외국인이 많이 찾는 쇼핑과 먹거리 명소를 말한다.대한민국의 대표적인 상업 및 금융 중심지이며 쇼핑과 문화, 예술과 패션의 1번지로 롯데백화점, 신세계백화점, 명동 밀리오레, 삼익패션타운 등의 쇼핑몰이 자리하고 있다. 이곳에서 고급 명품에서부터 보세 옷에 이르기까지 다양한 쇼핑을 즐길 수 있다. 한국에 온 관광객이라면 누구나 들르는 랜드마크로 외국인 방문율이 가장 높은 지역이기도 하다. 한국 천주교회의 메카인 명동성당에서부터 남대문로에 이르는 명동 길을 중심으로 크고 작은 골목마다 상점들이 늘어서 있다. 최신 유행의 의류와 액세서리를 파는 상점뿐 아니라 수많은 노점까지 더해져 사는 재미, 구경하는 재미를 느낄 수 있다.전통을 자랑하는 음식점과 휴식 공간인 카페도 구석구석 자리를 잡았다. 남대문시장은 대한민국 최대의 전통 시장이다. 노상과 건물을 아우르는 대단지 쇼핑타운이 밀집해 저렴한 가격에 각종 기념품과 토산품을 구매할 수 있다. 의류부터 패션 소품, 생활용품, 먹을거리 등 없는 것이 없는 곳으로 구석구석 뒤지면 신기한 것들이 쏟아져 나온다. 북창동과 소공동 일대는 롯데호텔, 웨스틴조선호텔 등 고급 숙박 시설과 면세 상점 등이 몰려 있으며 한국 전통 음식점들이 많아 ‘한국의 맛’을 찾는 외국인 관광객의 발길이 끊이지 않는다. 무교동과 다동 일대 역시 한국의 직장인들도 즐겨 찾는 한국의 다양한 먹거리로 유명한 지역이다.', '1', '24', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56860', '2583158', '명동관광정보센터', 'http://tong.visitkorea.or.kr/cms/resource/65/2713665_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/65/2713665_image2_1.jpg', '6', '37.5651545234', '126.9849815808', '', '서울특별시 중구 을지로 66', '(을지로2가)', '<a href=''http://korean.visitseoul.net/index'' target=''_blank'' title=''새창: 명동관광정보센터 홈페이지로 이동''>http://korean.visitseoul.net/index</a>', '명동 관광정보센터는 서울 중구 을지로 2가에 위치해 있는 관광안내소이다. 서비스 언어는 영어, 중국어, 일어이다. 외국인뿐만 아니라 서울시민에게도 관광, 축제, 쇼핑, 공연 등에 관한 서울의 정보를 빠르고 정확하게 제공한다.', '1', '24', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56861', '2761662', '명락사', 'http://tong.visitkorea.or.kr/cms/resource/83/2790183_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/83/2790183_image2_1.jpg', '6', '37.4814635656', '126.9470207355', '', '서울특별시 관악구 청룡1길 11', '', '', '서울 관악구 청룡동에 있는 명락사는 관악산 줄기의 끝자락 도심 상업지역에 있다. 40여 년 넘게 서울 서남권의 포교 활동과 다문화가정을 위한 포교에 관심을 기울이고 있는 사찰이다. 청년회가 활성화되어 있으며, 토요일 저녁 8시 30분부터 정기 법회를 갖는다. 2호선 봉천역과 서울대입구역 중간지점에 있다.', '1', '5', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56862', '3457082', '모두예술극장', 'http://tong.visitkorea.or.kr/cms/resource/83/3456983_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/83/3456983_image3_1.jpg', '6', '37.5601463655', '126.9623226761', '', '서울특별시 서대문구 충정로 7 (충정로3가)', '', 'https://www.moduarttheater.or.kr', '모두예술극장은 장애 예술인들의 꿈을 담은 국내 최초의 장애인 전문 공연장이다. 2023년 10월에 문을 열어, 장애 예술인들이 자신의 작품을 마음껏 펼칠 수 있는 공간이자, 누구나 예술을 향유할 수 있는 문화 공간으로 자리매김하고 있다. 또한 장애에 대한 사회적 인식 개선에도 큰 역할을 하고 있으며, 모두가 함께 누리는 문화 공간이라는 비전을 실현하고 있다.', '1', '14', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56863', '2753094', '목5동성당', 'http://tong.visitkorea.or.kr/cms/resource/17/3468117_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/17/3468117_image3_1.jpg', '6', '37.5333896158', '126.8770200955', '', '서울특별시 양천구 목동동로 353', '(목동)', '<a href=''http://www.mok5.com/'' target=''_blank'' title=''새창 : 목5동성당 사이트로 이동''>http://www.mok5.com/</a>', '서울 양천구 목동, 파리공원 인근에 있는 성당으로 건축미가 아름답기로 유명하다. 1987년 설립된 성당은 높은 천장과 국내에 몇 안 되는 파이프 오르간 등을 갖추고 있어 고풍스러운 분위기를 느끼게 한다. 영화 속에서 결혼식이 열리는 장소로도 등장했으며,  지하 1층에 폐백실이 따로 마련돼 있을 만큼 가톨릭 신자들에게는 예식을 올리기 좋은 성당으로 인기 있는 곳이다.', '1', '19', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56864', '250350', '목멱산봉수대터', 'http://tong.visitkorea.or.kr/cms/resource/15/3477015_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/15/3477015_image3_1.JPG', '6', '37.552053313', '126.9875150542', '', '서울특별시 중구 예장동', '8-1', '<a href=''https://www.heritage.go.kr/heri/cul/culSelectDetail.do?pageNo=1_1_1_1&sngl=Y&ccbaCpno=2331100140000'' target=''_blank'' title=''새창 : 국가유산청 홈페이지로 이동''>https://www.khs.go.kr/</a>', '봉수대는 횃불과 연기를 이용하여 급한 소식을 전하던 옛날의 통신수단을 말한다. 높은 산에 올라가서 불을 피워 낮에는 연기로 밤에는 불빛으로 신호를 보냈다. 남산 봉수대는 조선 태조 3년(1394)에 도읍을 한양으로 옮긴 뒤 설치하여, 갑오경장 다음 해까지 약 500년간 사용되어 왔다. 남산의 옛 이름을 따서 ‘목멱산 봉수대’라 하기도 하고, 서울에 있다 하여 ‘경봉수대’라 부르기도 하였다. 평상시에는 1개, 적이 나타나면 2개, 적이 경계에 접근하면 3개, 경계를 침범하면 4개, 경계에서 아군과 전투를 벌이면 5개의 불을 올리도록 하였다. 남산 봉수대는 전국의 봉수가 도달하게 되는 중앙 봉수대로서 중요한 역할을 담당하던 곳이다. 동쪽에서부터 서쪽으로 향하여 5개소가 있었다 하는데, 현재 봉수대는 없어지고 터만 남아있다. 전해지는 기록이 없어 정확하 자리를 확인할 수는 없는데, 「청구도」등의 관련자료를 종합하여 1개소를 복원하였다.', '1', '24', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56865', '753981', '몽마르뜨공원', 'http://tong.visitkorea.or.kr/cms/resource/07/3456307_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/07/3456307_image3_1.jpg', '6', '37.4955435016', '127.0037401743', '', '서울특별시 서초구 반포대로37길 59', '(반포동) 부근', '', '몽마르뜨공원은 서래마을 윗쪽에 위치한 공원으로 인근 서래마을에 프랑스인들이 많이 거주하고 있어  이 공원의 이름을 몽마르뜨공원으로 명명하게 되었고 이곳은 공원의 명칭만큼이나 예술과 낭만이 가득한 공원이다.비록 규모는 크지 않지만, 몽마르뜨공원에는 프랑스 시인들이 남긴 시와 조형 예술작품들이 가득하다. 이 밖에도 빈센트 반고흐, 폴 고갱, 파블로 피카소 등 프랑스에서 활동한 대표적인 예술가들의 흉상 조형물도 볼 수 있다. 또한 대중가수로 유명한 이미자 씨의 기념 공간 이미자 뜰이 조성되어 있고 놀이터 시설도 있어서 가족와 함께 피크닉을 즐기기에도 좋다. 몽마르뜨공원 끝에는 서리풀 근린공원을 잇는 누에다리가 있다. 누에다리를 건너서 계절에 따라 다양한 꽃들을 감상할 수 있는 서리풀근린공원까지 산책을 즐길 수 있다.수도권 지하철 2호선 서초역 6번 출구에서 600m에 위치하고 있다.', '1', '15', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56866', '128872', '묘각사(서울)', 'http://tong.visitkorea.or.kr/cms/resource/08/1927908_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/08/1927908_image3_1.jpg', '6', '37.5754261586', '127.0187670996', '', '서울특별시 종로구 종로63가길 31', '(숭인동)', '<a href=''http://www.myogaksa.net/'' target=''_blank'' title=''새창 : 묘각사 홈페이지로 이동''>http://www.myogaksa.net/</a><br /><a title=''새창 : 템플스테이 홈페이지로 이동'' href=''http://www.templestay.com''target=''_blank''>http://www.templestay.com</a>', '묘각사는 서울시 종로구 숭인동 낙산 동쪽에 있는 사찰이다. 1930년 5월에 태허 홍선 대종사가 창건하였고, 그 뒤 두 차례의 증축을 거쳐서 오늘에 이르고 있다. 창건 이래 현재까지 법화경을 중심으로 수행과 대중 불교 운동을 지향하는 법화 신앙의 중심 사찰이다. 현 위치에 절을 세우면 서울이 편안하고 시민 생활에 안정을 가져온다는 풍수지리설에 의해 터를 잡았다고 전하고 있다. 낙산묘각사에는 대광명전과 천수관음이 봉안된 원통보전, 마애관세음보살, 석굴암, 산신당, 낙가선원이 보존되어 있으며, 법당의 본존불은 청동불로서 합장한 특이한 모습이 인상적이다. 도심 속에 위치하여 교통이 편리한 관계로 많은 관광객이 찾고 있으며, 명상 및 참선 프로그램을 중점으로 사찰의 일상을 체험할 수 있는 산사체험 프로그램 템플스테이를 운영하고 있다. 수도권 지하철 1, 6호선 동묘앞역 2번 출구에서 도보 5분 거리에 있다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56867', '3013272', '무계원', 'http://tong.visitkorea.or.kr/cms/resource/12/3013012_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/12/3013012_image3_1.jpg', '6', '37.5918454822', '126.9636244375', '', '서울특별시 종로구 창의문로5가길 2 (부암동)', '', '<a href=''https://www.jfac.or.kr/site/main/content/moogw01'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://www.jfac.or.kr</a>', '무계원은 종로구 부암동에 개원한 도심 속 전통문화 공간이다. 고즈넉한 풍광 속에서 한옥을 체험하며 전통과 문화를 통해 한국의 아름다움을 느낄 수 있다.무계원의 건물은 과거 종로구 익선동에 있었던 서울시 등록 음식점 1호 오진암의 건물 자재를 사용하여 지어졌으며 무계원의 대문을 비롯해 기와, 서까래, 기둥 등에 쓰였다. 무계원이 위치한 무계정사지는 안평대군이 꿈을 꾼 도원과 흡사해 화가 안견에게 3일 만에 몽유도원도를 그리게 했고 정자를 지어 시를 읊으며 활을 쏘았다고 전해지는 유서깊은 장소이다. 무계원의 안채와 사랑채, 행랑채는 세미나, 강연, 회의실 등으로 사용 가능하며 별채, 안채마루, 안마당과 뒷마당 등 부대시설에서는 다양한 전통문화 행사를 진행한다.부암동은 조선시대부터 있던 곳으로 고즈넉한 분위기와 구도심의 느낌이 많아 산책하 듯 걸어도 좋은 곳이다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56868', '1603543', '무궁화동산', 'http://tong.visitkorea.or.kr/cms/resource/24/3399924_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/24/3399924_image3_1.JPG', '6', '37.5843076223', '126.9721572068', '', '서울특별시 종로구 궁정동 55-5', '', '', '무궁화동산은 옛 중앙정보부의 궁정동 안전가옥 터에 마련된 시민휴식공원이다. 본래 이곳은 청와대 구내로 출입이 금지되었던 곳이나 1993년 청와대 앞길이 개방된 뒤 시민공원으로 조성되었다. 태극무늬로 무궁화를 심었으며, 중앙에 궁정동을 상징하는 ‘우물 정’ 자의 분수대가 놓여 있다. 주변에 자연석으로 성곽을 만들고 240m의 산책로 주위에는 화단을 만들어 놓았다. 화단에는 전국 각지의 야생화 7,700그루를 심었으며, 무궁화와 소나무·느티나무 등 수목 13종 1,500여 그루를 심어 놓았다. 군데군데 벤치가 있어 인근 주민들의 휴식처로도 이용된다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56869', '2900390', '무대륙', 'http://tong.visitkorea.or.kr/cms/resource/83/2900383_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/83/2900383_image3_1.jpg', '6', '37.5460194431', '126.9183854133', '', '서울특별시 마포구 토정로5길 12 (합정동)', '', '', '무대륙은 서울시 마포구 합정동에 있는 전시와 인디밴드 공연이 있는 복합 문화공간이다. 지하철 2호선 합정역 7번 출구나 6호선 상수역 4번 출구에서 당인리 화력발전소 방향으로 있다. 대표 메뉴인 타이 프라이드치킨 외에도 타바스코 치킨, 피쉬 앤 칩스, 오픈샌드위치 등과 커피, 음료수를 즐기며 각종 전시와 인디밴드의 공연을 관람할 수 있다. 저녁에는 와인과 칵테일 등의 주류도 판매한다. 반려동물과 동반 입장이 가능하고 매장 앞에 약 2~3대의 주차 공간도 있다. 단체석이 있고 매장이 커서 모임을 갖기에도 편리하다. 근처에 다양한 디저트 맛집, 카페, 식당이 있어서 데이트하기 좋다.※ 반려동물 동반 가능', '1', '13', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56870', '2759649', '무수골계곡', '', '', '6', '37.6766535473', '127.0338156248', '', '서울특별시 도봉구 도봉동', '468-1', '', '경기도 의정부와 맞닿은 서울의 최북단 도봉산은 자운봉 740m와 오봉산 683m의 높은 봉우리 아래로 용어천계곡, 문사동계곡, 원도봉계곡 등이 있다. 산 정상에서 마을로 내려가는 마지막 자락에 길게 뻗은 계곡이 있는데, 그곳이 도봉계곡 또는 무수골계곡이라고도 불린다. 이곳은 많은 바위와 물줄기를 만날 수 있는 곳이다. 여름철 더위를 피해 산과 물을 찾는 사람들에게 사랑받는 인기가 많은 계곡이다.', '1', '10', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56871', '126531', '무악산 동봉수대 터', '', '', '6', '37.5756692729', '126.9462487025', '', '서울특별시 서대문구 봉원동', '(봉원동)', '<a href=''https://www.heritage.go.kr/heri/cul/culSelectDetail.do?culPageNo=1&region=1&searchCondition=%EB%AC%B4%EC%95%85%EC%82%B0+%EB%8F%99%EB%B4%89%EC%88%98%EB%8C%80+%ED%84%B0&searchCondition2=&ccbaKdcd=23&ccbaAsno=00130000&ccbaCtcd=11&ccbaCpno=2331100130000&ccbaCndt=&ccbaLcto=&stCcbaAsdt=&endCcbaAsdt=&header=view&returnUrl=%2Fheri%2Fcul%2FculSelectViewList.do&pageNo=1_1_1_0&p=multiSch&sortType=&sortOrd=&sngl=Y&s_kdcdArr=00&s_ctcdArr=00&ccbaPcd1Arr=99&ccbaGcodeArr=00'' target=''_blank'' title=''문화재청 사이트로 이동''>https://www.khs.go.kr</a>', '봉수대는 횃불과 연기를 이용하여 급한 소식을 전하던 옛날의 통신수단을 말한다. 높은 산에 올라가서 불을 피워 낮에는 연기로 밤에는 불빛으로 신호를 보냈다. 조선시대에 전국적으로 5군데 있었는데, 그중에서 2군데가 안산(무악산) 정상에 있었다. 평안북도 강계에서 시작하여 황해도, 경기도 내륙, 고양 해포나루, 무악동봉, 남산 훈도방으로 연결되는 봉수를 남산에 최종적으로 연락하는 곳이다. 무악산에 있는 이 봉수대터는 조선시대 봉수체제가 확립되었던 1438년(세종 24)에 무악산 동·서에 만든 봉수대 가운데 동쪽 봉수대터이다. 터만 남아있던 것을 서울정도 600년 기념사업의 하나로 1994년에 복원하였다.', '1', '14', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56872', '2762874', '묵정어린이공원', 'http://tong.visitkorea.or.kr/cms/resource/86/3463686_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/86/3463686_image3_1.jpg', '6', '37.5633050369', '127.0016134507', '', '서울특별시 중구 동호로31길 21', '(충무로5가)', '', '서울 중구 충무로 위치한 묵정어린이공원은 4,003㎡ 규모의 공원이다.1992년 개장해 2007년 개보수를 거쳐 인근 도시민들에게 휴식처를 제공하고 있다. 어린이 놀이기구, 운동 시설, 벤치, 조각품 등이 설치되어 있으며, 지하에는 공영주차장 시설이 있다. 소나무 등 여러 종류의 나무와 꽃들이 잘 심어져 있는 경관이 아름다운 공원이다.', '1', '24', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56873', '2751348', '문래근린공원', 'http://tong.visitkorea.or.kr/cms/resource/35/2779835_image2_1.png', 'http://tong.visitkorea.or.kr/cms/resource/35/2779835_image2_1.png', '6', '37.5167529614', '126.8939134684', '', '서울특별시 영등포구 문래동3가', '66', '<a href=''https://www.ydp.go.kr/tour/viewTnTursmResrceU.do?resrceNo=6&sc1=%EA%B3%B5%EC%9B%90&rcpp=9&key=4008'' target=''_blank'' title=''새창 : 영등포구 문화관광 홈페이지로 이동''>https://www.ydp.go.kr/tour/</a>', '서울시 영등포구 문래동에 있는 근린공원으로, 주변에 문래역 1번 출구와 아파트 단지들이 있다. 공원 내에는 문래동의 유래가 되었던 물레의 조형물이 있으며, 건너편에는 있는 문래예술창작촌이 있다. 1961년 5.16 군사 정변의 발상지인 육군 제6관구 사령부가 주둔했었던 역사가 남아있는 장소로, 공원 동편에는 당시 육군 소장이었던 박정희 前 대통령의 흉상이 있다.', '1', '20', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56874', '2650230', '문래예술공장', 'http://tong.visitkorea.or.kr/cms/resource/16/3466116_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/16/3466116_image3_1.jpg', '6', '37.5132812541', '126.900160219', '', '서울특별시 영등포구 경인로88길 5-4', '(문래동1가)', '<a href=''https://www.sfac.or.kr/artspace/artspace/mullae_main.do'' target=''_blank'' title=''새창 : 문래예술공장 홈페이지로 이동''>http://www.sfac.or.kr/</a>', '서울특별시 창작공간 문래예술공장은 자생적 예술 마을인 문래창작촌을 포함하여 국내외 다양한 예술가들을 위한 창작지원센터로 2010년 1월 28일에 개관했다. 문래동 철공소 거리의 옛 철재 상가 자리에 전문 창작공간으로 새롭게 건립되었으며, 공동작업실, 다목적 발표장을 비롯해 녹음실, 세미나실 등 창작 및 발표 활동을 위한 다양한 지원시설을 갖추고 있다.', '1', '20', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56875', '2781318', '문래창작촌', '', '', '6', '37.5143449757', '126.8976881922', '', '서울특별시 영등포구 도림로128가길 13-8 (문래동3가)', '', '<a href=''https://www.ydp.go.kr/tour/contents.do?key=5313&'' target=''_blank'' title=''새창 : 영등포구 문화관광 홈페이지로 이동''>https://www.ydp.go.kr/tour</a>', '서울 영등포구 문래동 예술촌은 한때 서울에서 가장 큰 철강공단지대로 불리기도 했던 철강소 골목이 현재는 예술가들이 이곳에 몰리면서 예술과 철공소가 공존하는 공간으로 거듭나고 있는 곳이다. 2010년 서울문화재단이 예술가들을 위해 지은 창작공간 ‘문래 예술공장’을 세우면서 저렴한 임대료와 독특한 분위기에 이끌린 예술가들이 하나둘씩 모여 둥지를 틀었다. 문래 창작촌으로 불리는 이곳은 버려진 철과 낡은 연장들을 활용해 재탄생한 작품들이 거리 곳곳에 배치돼 있다. 철공소와 예술인 공방 사이사이로 벽화, 조형물이 있어 구경할 것도 많고 빈티지한 감성 사진을 찍기에 좋은 장소로 급부상했다. 당산로와 도림로 128길이 만나는 지점의 문래창작촌 부스는 이곳의 랜드마크로 활용된다. 평일보다는 주말에 내려진 셔터에 그려진 다양한 그림 작품들을 볼 수 있다. 영화 <어벤저스 2 : 에이지 오브 울트론>, <아저씨>, <특별시민>과 KBS2 드라마 <추리의 여왕 시즌2>, MBC 예능프로그램 <놀면 뭐하니>의 촬영지로 더욱 알려졌다. 현재 문래동에는 100여 개의 작업실이 있으며 약 200명의 예술가가 활동하고 있지만 재개발이라는 이슈가 문래동을 달구면서 예술가의 활동영역이 줄어들고 있다.', '1', '20', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56876', '250364', '문화역 서울 284', 'http://tong.visitkorea.or.kr/cms/resource/89/3413089_image2_1.png', 'http://tong.visitkorea.or.kr/cms/resource/89/3413089_image3_1.png', '6', '37.5560136788', '126.9716025029', '', '서울특별시 중구 통일로 1', '(봉래동2가)', '<a href=''http://www.seoul284.org'' target=''_blank'' title=''문화역서울284 사이트로 이동''>http://www.seoul284.org</a><br>', '문화역 서울 284는 한국 근현대사의 주요 무대이자 교통과 교류의 관문이었던 구 서울역사의 원형을 복원해 만든 복합문화공간이다. 문화·예술의 창작과 교류가 이루어지는 플랫폼으로서 전시, 공연, 워크숍 등 다채로운 프로그램을 진행하고 있다.1900년 남대문정차장을 시작으로 경성역을 거쳐 서울역이 됐다가 2004년 구역사가 폐쇄되며 새로운 공간으로 변신했다. 건물 외관은 1925년 경성역을 복원해 재현한 것이다. 유럽 르네상스식의 이국적인 외관으로 붉은 벽돌, 화강암 바닥, 인조석을 붙인 벽, 박달나무 바닥으로 이루어져 있어 인기를 끌었다.◎ 한류의 매력을 만나는 여행 정보 드라마 <무인도의 디바> 속 목하(박은빈 분)가 꽃을 들고 기호(채종협 분)를 기다리던 곳이다. 옛 서울역사를 복원한 만큼, 드라마 <응답하라 1994>에서는 삼천포(김성균 분)가 상경한 장소로 등장한다.', '1', '24', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56877', '2592089', '문화철도959', 'http://tong.visitkorea.or.kr/cms/resource/85/2592085_image2_1.bmp', 'http://tong.visitkorea.or.kr/cms/resource/85/2592085_image3_1.bmp', '6', '37.5090871374', '126.8912028193', '', '서울특별시 구로구 경인로 688 (신도림동)', '', '구로문화재단 <a href=''https://guroartsvalley.or.kr/user/overall/overallArt9.do'' target=''_blank'' title=''새창 : 구로문화재단 홈페이지로 이동''>https://guroartsvalley.or.kr</a><br>공식 인스타그램 <a href=''https://www.instagram.com/grcf2007/'' target=''_blank'' title=''새창 : 구로문화재단 홈페이지로 이동''>https://www.instagram.com/grcf2007</a>', '구로문화재단 신도림 문화철도959는 신도림선상역사 1호선 3층에 위치해 있는 복합문화공간이다. 뛰어난 접근성과 안전성을 활용하여 문화 예술을 통해 다양한 문화를 향유할 수 있는 공간으로 만들었다. 유휴공간 및 지역 경제활성화를 목적으로 구로구와 구로문화재단, 예술가 단체 토카아트가 함께 만들어 가고 있다. 문화철도959는 청년작가 15인의 창작활동을 펼치고 있는 959아트플랫폼과 전시공간 아트갤러리, 그리고 매 분기별로 진행되는 다양한 문화교실을 개최하여 문화예술 체험프로그램을 배울 수 있는 기회를 제공하고 있다. 또한 청년작가들과 함께 하는 문화예술체험프로그램과 구로구 지역주민, 각 단체 및 동아리 행사를 진행할 수 있는 공간도 마련되어 있다.', '1', '7', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56878', '2733852', '물빛광장', 'http://tong.visitkorea.or.kr/cms/resource/42/2779842_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/42/2779842_image2_1.jpg', '6', '37.5313018929', '126.9286987769', '', '서울특별시 영등포구 여의도동 84-9', '', '<a href=''https://hangang.seoul.go.kr/archives/639'' target=''_blank'' title=''새창 : 한강공원 홈페이지로 이동''>https://hangang.seoul.go.kr/</a>', '물빛광장분수는 여의도 공원에서 한강으로 통하는 길목에 위치한 수경 공간이다. 지형을 이용하여 물의 흐름을 시각화·청각화 하였다. 야간 경관조명을 물속 바닥에 비추어 별빛을 형상화하여 다양한 볼거리를 제공한다. 또한 물소리를 듣고 느끼고 만지며 바라보는 친수공간도 제공한다. 지하철 여의나루역과 연계되어 각종 공연과 한강조망 등을 볼 수 있는 가족나들이 장소로도 손색이 없다.', '1', '20', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56879', '2469939', '뮤지엄 김치간', '', '', '6', '37.5736063169', '126.9848813503', '', '서울특별시 종로구 인사동길 35-4 (관훈동)', '4, 5, 6층', '<a href=''http://www.kimchikan.com'' target=''_blank'' title=''새창: 뮤지엄 김치간 홈페이지로 이동''>http://www.kimchikan.com</a>', '뮤지엄 김치간은 서울 중구 필동에서 김치박물관이라는 이름으로 처음 문을 열었다. 1987년에 풀무원이 인수하여 운영하기 시작했다. 한국 최초의 김치박물관이었으며, 이후 오랫동안 김치뿐만 아니라 식품을 주제로 삼은 유일한 박물관이었다. 전통 사회에서는 반찬을 만드는 곳은 찬간(饌間), 임금의 식사를 준비하는 곳은 수라간(水刺間), 양식을 보관하는 곳은 곳간(庫間)이라고 하였다. 이런 ‘-간(間)’처럼 김치의 다채로운 면모와 사연이 흥미롭게 간직된 곳, 김치를 느끼고 즐기는 체험공간이 되겠다는 다짐을 담아 ‘김치간(間)’이라고 이름 지었다.88서울올림픽 때문에 수많은 외국 관광객이 김치박물관을 찾아 김치의 독창성과 다양한 가치에 눈뜨게 되었다. 김치로부터 점점 멀어져 가는 아이들을 대상으로 김치 과학 전시 등 흥미로운 전시와 다양한 김치 체험 프로그램을 지속적으로 진행함으로써 김치와의 친화력을 높이는 데 이바지했다. 2013년 12월에 ‘김장 문화’가 유네스코 인류무형문화유산에 지정되었으며, 2015년 3월에는 김치박물관이 한국에서는 유일하게 CNN이 뽑은 세계 11대 음식 박물관에 선정되었다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56880', '3056596', '미어캣파크', 'http://tong.visitkorea.or.kr/cms/resource/91/3056591_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/91/3056591_image3_1.jpg', '6', '37.5816751457', '127.0028211535', '', '서울특별시 종로구 대학로10길 12 (동숭동)', '6층', '<a href=''https://www.instagram.com/meerkatpark/'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://www.instagram.com/meerkatpark/</a><br><a href=''https://blog.naver.com/meerkatpark''target=''_blank'' title=''새창 : 업체블로그 이동''>https://blog.naver.com/meerkatpark</a>', '미어캣파크는 미어캣, 강아지, 고양이, 앵무새 등의 이색 동물들과 함께 하는 체험 카페이다. 동물과 사람의 안전을 위해 5세 미만은 체험이 불가하다. 각 동물마다 체험방이 따로 있으며, 미어캣 체험은 1팀당 15분만 가능하다. 앵무새 체험의 경우 시간제한 없이 자유롭게 할 수 있다. 카운터에서 먹이를 사서 줄 수 있다. 체험방마다 동물의 이름을 소개하고 주의해야 할 부분들을 적어놓은 안내문이 있다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56881', '309300', '미타사(강서)', 'http://tong.visitkorea.or.kr/cms/resource/92/2671592_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/92/2671592_image3_1.jpg', '6', '37.5805689272', '126.8033171574', '', '서울특별시 강서구 개화동로13길 56-33 (개화동)', '', '', '미타사는 서울시 강서구 개화동 개화산 서남 기슭에 자리한 대한불교조계종 소속 사찰이다. 서울의 서쪽 끝 김포평야와 행주나루 끝에 걸쳐 있는 미타사는 도심 속에 있는 사찰이면서도 평온함과 한적함을 느낄 수 있는 곳이다. 아기자기하게 꾸며놓은 동산에 자연스럽게 다듬어진 나무들과 앙증맞게 피어 있는 들꽃들, 하나같이 예쁘게만 보인다. 그러나 이곳은 한국전쟁 때 육군과 인민군이 치열한 교전을 벌이던 격전지로 김포공항을 사수하던 육군 1,000여 명이 전사했으며, 그로 인해 절의 모든 당우들이 전소되는 비운을 맞이하기도 했다.미타사에서 가장 눈길을 끄는 것은 단연 법당 옆 커다란 바위 위에 우뚝 솟아 있는 미륵불입상이다. 절의 창건과도 관련이 있을 것 같은 이 불상은 무수한 일화를 남기고 있는데, 이야기 속에 이 미륵부처님은 하나 같이 땅에서 솟아났다고 한다.', '1', '4', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56882', '309302', '미타사(성동)', '', '', '6', '37.5435714979', '127.0171792585', '', '서울특별시 성동구 독서당로40길 21', '(옥수동)', '성동구 문화관광 <a href=''http://www.sd.go.kr/tour/index.do'' target=''_blank'' title=''새창: 성동구 문화관광 홈페이지로 이동''>http://www.sd.go.kr/tour/index.do</a>', '옥수동에 자리한 미타사는 대한불교 조계종 조계사의 직할 사찰로 달맞이봉공원 아래 위치해 있다. 또, 두뭇개에 있으므로 두뭇개승방이라고도 한다.888년 신라 진성여왕 2년에 창건된 미타사는 금호동 부근에 대원 비구니 스님의 원력으로 창건되었으며 1115년 봉적 두 비구니 스님이 현재의 옥수동으로 옮겨 극락전을 건립하고 아미타불을 모신 후 국사로부터 미타사라는 사명을 받았다. 미타사의 극락전과 관음전은 불심 깊은 조대비의 보시로 건립되었으며 극락전의 삼존불과 6개의 탱화, 관음전의 천수 탱화, 7층 석탑 등은 불교예술의 전통미를 함축하고 있다. 미타사는 역사를 이어오며 많은 비구니 스님이 운집하여 미타사 내에 각기 암자를 건립하게 되는데 현재는 대승암, 칠성각, 금보암, 금수암, 정수암, 관음암, 용운암의 7개 암자가 있는 서울 도심 속의 특별한 전통 사찰이다. 1988년 천불전과 미타유치원을 건립하여 지역주민과 불자들의 친행처와 지역 어린이 보육전당으로 운영하고 있다. 수도권 지하철 옥수역 7번 출구에서 아주 가깝다.', '1', '16', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56883', '309299', '미타사(성북)', '', '', '6', '37.5850249996', '127.0163337157', '', '서울특별시 성북구 보문사길 6-16 (보문동3가)', '', '', '보문사와 담장 하나 사이에 있으며, 탑골승방으로 잘 아려진 미타사는 여승들의 거처이다. 이 사찰은 950년(고려 광종 원년)에 혜거국사가 처음 창건하고 미타사라는 이름을 지었다고 전해진다. 1047년(문종 원년)에는 석탑을 조성하여 탑골승방이라 하였다. 석탑은 대웅전 뒤 단하각 왼쪽 언덕 위에 위치해 있다.이후 몇 차례의 중수 사실이 전해지지만 의미 있는 것은 1457년(세조 3)에 단종비였던 정순왕후 송씨에 의해 중수되었다는 것이다. 이는 미타사 서쪽, 숭인동의 정업원, 동망봉, 자지동천(자줏빛 물이 흘러나오던 샘) 등에 정순왕후와 관련된 사실이 있음으로 보아 이 절과 연관되었을 가능성이 높다고 할 수 있다.', '1', '17', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56884', '605961', '민영환 자결터', 'http://tong.visitkorea.or.kr/cms/resource/62/3050162_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/62/3050162_image3_1.JPG', '6', '37.5718842715', '126.9835815178', '', '서울특별시 종로구 공평동', '', '<a href=''http://tour.jongno.go.kr'' target=''_blank'' title=''새창 : 종로 역사 문화 관광 홈페이지로 이동''> http://tour.jongno.go.kr </a>', '민영환 자결터는 민영환이 을사늑약에 항거하다 자결하여 순국한 곳이다. 조선말의 충신 민영환은 을사늑약의 폐기를 궁궐 앞에서 주장하다가 일본 헌병의 강제해산으로 실패하자 1905년 11월 30일 새벽 6시에 청지기의 집에서 국민, 외교사절, 황제에게 보내는 유서 3통을 남기고 자결하였다. 순국 후 피 묻은 옷과 칼을 산청 마루방에 걸어두었는데 이듬해 5월 산청의 문을 열어보니 대나무 네 줄기가 마룻바닥과 피 묻은 옷을 뚫고 올라왔다고 한다. 사람들은 그의 충정이 혈죽으로 나타났다고 하여 이 나무를 절죽이라 하였다. 종로구 인사동 하나빌딩 앞 민영환의 자결터에 김충헌이 글씨를 쓰고 백문기가 조각한 추모 조형물이 세워져 있다. 이 조형물에는 그의 충정이 나타났다고 하는 혈죽을 상징하는 대나무와 창호문이 조각되어 있고 자결할 때 사용한 단검과 유서 등을 배치했다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56885', '2373326', '반공 청년운동 순국 열사 기념비', 'http://tong.visitkorea.or.kr/cms/resource/35/3064135_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/35/3064135_image3_1.JPG', '6', '37.552231068', '126.9873511751', '', '서울특별시 중구 남산공원길 125-72', '', '<a href=''http://mfis.mpva.go.kr/memorial/movementView.do?sid=35&memorialType=true&gubunCode=ALL&pageIndex=1&searchCondition=%EC%A0%9C%EB%AA%A9&searchKeyword=%EB%B0%98%EA%B3%B5%EC%B2%AD%EB%85%84%EC%9A%B4%EB%8F%99'' target=''_blank'' title=''새창 : 홈페이지로 이동''>http://mfis.mpva.go.kr</a>', '반공청년운동 기념비 건립위원회가 반공청년운동을 전개하다 순국한 애국열사들의 넋을 기리고 그들의 호국정신을 길이 계승하고자 1968년 10월 9일 중구 예장동 산5-6에 건립한 기념비이다. [이 비석은 돌이 아니고 반공투사들의 심장입니다.] 라는 문구가 비석에 새겨져 있다. 앞으로 살아갈 세월이 많았던 어린 애국열사 청년들이 짧은 일생을 영원한 조국에 바친 것에 대해 감사를 표하는 비석으로 의미가 있다. 기념비 주변에 남산 팔각정과 봉수대가 가까이에 있어서 함께 둘러보기 좋다.', '1', '24', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56886', '1828631', '반공순국용사 위령탑', '', '', '6', '37.5171533568', '126.9186112066', '', '서울특별시 영등포구 신길1동 20-4', '(신길동)', '<a href=''http://mfis.mpva.go.kr/memorial/movementView.do?gubunCode=HG&sid=1555&pageIndex=1&searFacilgubun=&searTopicsort=&searAddr_sigun=&searMpvabranch=&searVR=&searInfo=&searGbn=tit&searTxt=%EB%B0%98%EA%B3%B5%EC%88%9C%EA%B5%AD'' target=''_blank'' title=''새창 : 홈페이지로 이동''>http://mfis.mpva.go.kr/</a>', '반공순국용사 위령탑은 약 15m 높이로 조성된 탑으로, 나라를 위해 투쟁하다가 목숨을 잃은 호국 용사들을 기리기 위해 세워졌다. 1945년 8월 15일 조국 광복 이후의 혈전과 1950년 6월 25일 북한의 남침으로 인한 국가 위기 상황에서 영등포구 관내의  대동청년단, 대한청년단, 대한노총, 반공연맹, 민보단 서북청년회 등 애국우익 단체소속 동지들과 경찰, 소방관, 학생 등 많은 애국 동지들이 구국의 신념으로 목숨을 바쳐 순국한 사람들을 기리기 위해 건립되었다.위령탑은 신길역 옆의 여의도 샛강을 따라 놓인 노들길변에 위치하고 있으며, 매년 6월에는 순국용사들의 희생을 기리는 위령제가 열리고 있다. 이 탑은 영등포구 재향군인회와 반공순국용사위령탑 건립위원회에 의해 세워졌으며, 그 헌신적인 행동과 희생에 각종 애국 단체와 국민이 각별한 경의를 표하는 곳이다.', '1', '20', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56887', '753983', '반포 서래섬', '', '', '6', '37.5077391108', '126.9927658294', '', '서울특별시 서초구 신반포로11길 40 (반포동)', '', '<a href=''http://hangang.seoul.go.kr/archives/46727'' target=''_blank'' title=''새창: 한강공원으로 이동''>http://hangang.seoul.go.kr</a>', '서래섬은 반포대교와 동작대교 사이에 위치한 인공섬이다. 1982년부터 1986년까지 올림픽대로 건설 및 한강종합개발을 통해 조성되었다. 3개의 다리(서래교)로 반포한강공원과 연결되어 있어 편리하게 접근이 가능하다.서래섬은 계절에 따라 유채, 메밀 등의 작물을 식재되어 있어 꽃이 피는 시기가 되면 사진 찍는 사람들, 산책을 하는 사람들로 붐빈다. 또한 매년 봄과 가을이 되면 유채꽃축제와 메밀꽃축제가 개최된다.', '1', '15', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56888', '729167', '반포대교 달빛무지개분수', '', '', '6', '37.5077391108', '126.9927658294', '', '서울특별시 서초구 신반포로11길 40 (반포동)', '', '<a href=''https://hangang.seoul.go.kr/www/contents/856.do?mid=849'' target=''_blank'' title=''새창: 한강공원으로 이동''>http://hangang.seoul.go.kr</a>', '서울 반포대교에 설치된 분수인 달빛무지개분수는 한강르네상스의 핵심적인 사업으로 2007년 6월 시작하여 2009년 4월 개막식을 통해 유유히 흐르는 한강의 정적인 이미지에 웅장하고 역동적인 이미지를 담은 교량분수로 화려하게 탄생했다. 서울 한강만이 가지고 있는 약 700m의 넓은 강폭 전체를 연출하기 위해 반포대교 구간의 상류 측과 하류 측 1,140m 구간에 380개 노즐을 설치해 낙하용 수중펌프로 끌어올린 한강물을 약 20m 아래 한강 수면으로 떨어뜨리는 새로운 개념의 분수이다. 단순히 물만 내뿜는 것이 아니라 배경음악과 분수연출이 아름답고 조화롭게 어우러지도록 프로그램을 구성하고, 야간에는 경관조명을 이용하여 환상적인 한강의 야경을 보여준다. 또한 2008년 11월 7일 ‘세계에서 가장 긴 교량분수’로 기네스북에 등재되면서 최고의 위용을 세계에서도 인정받았으며, 시향연주회, 무용발표회, 창장음악회 및 개막식, 그랜드오픈행사 시 분수연출을 선보이기도 했다. 달빛무지개분수는 낮과 밤에 다른 모습을 즐길 수 있다. 낮에는 분수에 떨어지는 물결의 모양에 따라 휘날리는 버들가지와 버들잎을 형상화한 모양 등 100여 가지의 다양한 모습의 분수를 만들어내며, 밤에는 긍정과 희망의 이미지를 상징하는 무지개 색깔의 분수로 화려하게 변신한다. 설치된 조명 200개는 아름다운 무지개 모양의 야경을 선사하고 음악에 맞춰 춤추는 분수는 시민들에게 색다른 즐거움을 선사한다.', '1', '15', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56889', '970138', '반포한강공원', 'http://tong.visitkorea.or.kr/cms/resource/73/2933473_image2_1.bmp', 'http://tong.visitkorea.or.kr/cms/resource/73/2933473_image3_1.bmp', '6', '37.5077391108', '126.9927658294', '', '서울특별시 서초구 신반포로11길 40 (반포동)', '', '<a href=''http://hangang.seoul.go.kr/archives/46727'' target=''_blank'' title=''새창: 한강공원으로 이동''>http://hangang.seoul.go.kr</a>', '반포한강공원은 반포대교(잠수교)를 중심으로 상류는 한남대교, 하류는 동작대교 사이의 한강변 남단에 위치해 있다. 특히, 한강르네상스 사업으로 조성된 반포한강공원에서 반포대교 다리 양쪽에 설치된 달빛무지개분수는 총길이 1,140m로 2008년에 세계에서 가장 긴 교량분수로 세계기네스협회에 등재되었다. 야간에는 200여 개의 조명과 함께 물을 뿜을 때마다 일곱 빛깔 무지개처럼 아름다운 풍경을 연출한다. 또한 물방울놀이터, 인라인 전용트랙, 축구장, 농구장 등의 체육시설과 더불어 동작대교 남단에 설치된 전망대인 노을카페와 구름카페에서 탁 트인 한강수면과 그 위로 펼쳐진 넓은 하늘과 강변 빌딩숲을 감상할 수 있다. 그리고 반포한강공원과 연결된 서래섬은 도심 속 휴식과 놀이공간을 제공하는 인공섬으로 반포대교와 동작대교 사이에 위치해 있다. 계절에 따라 유채, 메밀 등의 작물을 식재하여 꽃이 피는 시기에는 서래섬을 일주하는 산책코스로 인기가 높다. 봄이 되면 노란 유채꽃이 만발하여 ‘서래섬 나비·유채꽃 축제’로 나들이와 산책을 즐기는 사람들이 많다. 이 외에도 반포한강공원에는 생태학습장, 보트장, 자전거도로, 피크닉장 등 볼거리와 즐길거리가 무궁무진하다.', '1', '15', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56890', '2763875', '반포한강공원 달빛광장', '', '', '6', '37.5077752176', '126.9927436077', '', '서울특별시 서초구 신반포로11길 40', '(반포동)', '<a href=''https://korean.visitseoul.net/gangnam/%EB%B0%98%ED%8F%AC%20%ED%95%9C%EA%B0%95%EA%B3%B5%EC%9B%90_/3968'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://korean.visitseoul.net/</a>', '서울 반포대교와 잠수대교 한강 남단에 있는 반포한강공원의 중심 광장으로, 달의 모습을 형상화하여 만든 테마 광장이다. 잠수교를 사이에 두고 동쪽에 2개, 서쪽에 3개의 초승마루가 있다. 주변에는 교량 양쪽에 설치된 달빛무지개분수를 비롯해 보행자 중심 다리로 거듭난 잠수교 등 재미있는 아이템이 가득하다. 달빛무지개분수는 총길이가 1,140m로 2008년 세계에서 가장 긴 교량 분수로 기네스협회에 등재되었다. 세빛섬, 가빛섬, 예빛섬과 한강 주변의 빌딩 숲 야경이 달빛공원을 아름답게 만든다. 지하철 3호선, 9호선, 7호선 고속터미널역에서 가까워 접근성도 좋다.', '1', '15', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56891', '2763871', '방배배수지체육공원', 'http://tong.visitkorea.or.kr/cms/resource/93/3467593_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/93/3467593_image3_1.jpg', '6', '37.4727848312', '126.9924835083', '', '서울특별시 서초구 남부순환로296길 4-9', '(방배동)', '<a href=''https://www.seocho.go.kr/site/seocho/04/10405020600002015070703.jsp'' target=''_blank'' title=''새창 : 방배배수지체육공원 사이트로 이동''>https://www.seocho.go.kr/</a>', '서울 서초구 사당역 방배래미안 아파트 건너편에 있는 방배체육공원은 앞으로는 매봉재산과 뒤로는 우면산을 사이에 두고 울창한 숲 속에 있는 30,353㎡ 규모의 체육공원이다. 방배체육공원으로도 불리며, 공원 전체가 금연/금주 구역이다. 인조 잔디가 깔린 축구장, 풋살구장, 실내 배드민턴 코트도 있으며 야간 조명시설도 갖췄다. 시설은 서울시 공공서비스예약(yeyak.seoul.go.kr) 사이트를 통해 예약, 이용할 수 있다. 여성축구교실, 유아축구교실, 서초홍명보어린이축구교실, 서초리틀야구단 프로그램이 운영되고 있다.', '1', '15', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56892', '3043977', '방우산장', 'http://tong.visitkorea.or.kr/cms/resource/68/3043968_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/68/3043968_image3_1.jpg', '6', '37.5932682184', '127.00147928', '', '서울특별시 성북구 성북동', '142-1', '', '방우산장은 조지훈 기념 건축 조형물이다. 방우산장은 성북동에서 활동하던 시인 조지훈이 자신이 살던 집에 붙인 이름으로 [마음속에 소 한 마리를 키우면 직접 소를 키우지 않아도 소를 키우는 것과 다름없다]는 [방우즉목우]의 사상을 담은 것이라고 한다. 이 조형물은 시인 조지훈의 집터 근처에 [시인의 방]이라는 콘셉트으로 조성된 것으로, 조지훈 시인의 삶과 흔적을 만날 수 있는 공간이다. 전통 한옥의 처마와 마루를 모티브로 삼아 벽과 바닥을 만들었다. 현무암을 써서 독특한 느낌을 풍기며, 열린 공간에는 잔디를 깔고 청동주물로 만든 의자를 들여놨다. 의자는 정해진 방향 없이 자유롭게 배치하였다. 그리고 시인의 집터 방향으로 문을 내고 바깥벽에는 시인이 가장 아끼던 작품 [낙화]가 새겨졌다.', '1', '17', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56893', '814203', '방이동 생태경관 보전지역', '', '', '6', '37.5121856929', '127.1403189408', '', '서울특별시 송파구 동남로 397 (방이동)', '', '', '방이동 생태경관 보전지역은 하천 사이에 발달한 충적토로서 과거에는 논농사로 이용되었다. 1970년 초반 벽돌생산을 위해 토사를 채취하면서 자연스럽게 웅덩이가 형성된 대규모 인공습지이다. 1980년대 이후 소규모 양어장으로 이용되다 폐쇄된 후 사람들의 출입이 제한되면서 본격적으로 생물의 다양성이 풍부한 서식처로 자리 잡았다. 이후 습지식물의 자연스러운 번성과 2차 천이 발생으로 안정된 소생물권을 형성하여 현재 희소성과 보전가치가 매우 큰 도심 속 습지로서의 제 기능을 다 하고 있다.  습지를 찾아오는 다양한 새들도 있어서 물억새, 애기부들 등의 습지식물과 청둥오리, 물총새 등의 야생조류가 관찰된다. 풀벌레소리와 새소리가 들리는 한적한 숲길을 걸으며 곳곳에 적혀있는 동식물에 대한 설명을 읽다 보면 유익한 산책시간이 된다. 이곳은 보전지역이기 때문에 동식물 보호를 위해 채취행위는 엄격히 금지되며 반려동물 동반도 불가하다.', '1', '18', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56894', '3076147', '방화근린공원', '', '', '6', '37.5817161925', '126.8132078888', '', '서울특별시 강서구 방화동', '855', '<a href=''https://www.gangseo.seoul.kr/kids/kids030303'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://www.gangseo.seoul.kr/kids/kids030303</a>', '방화근린공원은 1990년 12월에 공원으로 지정되어 방화 택지 개발 지구 개발과 함께 조성되었다. 공원 안에 다목적 운동장 등의 체육시설과 원형광장, 연못, 분수대 등이 있다. 산책로에는 벚꽃나무를 비롯한 다양한 수목이 있는데, 벚꽃이 잘 조성되어 있어 봄에는 봄꽃 축제가 열리고, 여름에는 바닥분수를 그리고 겨울에는 원형광장에 얼음 썰매장을 운영한다. 이 밖에도 초가로 지은 집과 정자가 있는 민속놀이마당이 있으며, 이곳에서는 매월 두 번째, 넷째 주 토요일에 학생들을 대상으로 전래놀이와 짚공예체험교실이 열린다. 주변에는 개화산과 치현산이 있고, 꿩고개 근린공원까지 함께 있어 자연과 함께 하기에 좋은 공원이다.', '1', '4', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56895', '250166', '방화대교', '', '', '6', '37.5660513299', '126.8122361958', '', '서울특별시 강서구 방화동로 61 (방화동)', '', '', '방화대교는 인천국제공항이 21세기 동북아시아 허브 공항의 역할을 수행할 수 있도록 건설된 한강대교 중 하나이다. 길이 2.6㎞로 한강 교량 중 가장 긴 교량이며, 국내 최초로 장경간 아치 트러스 구조로 설계, 시공된 한강의 27번째 교량으로 예술성과 기능성이 우수하다. 다리의 아치는 전통 사찰과 기와집 처마의 부드러운 이미지를 차용하여 개화산 봉우리, 덕양산 봉우리에 이어 인위적인 세 번째 봉우리가 되었다. 이륙하려는 듯한 거대한 항공기를 연상시키는 기하학적인 형상과 밤이면 한강 물결에 비치는 황홀한 야경이 보는 이로 하여금 감탄을 자아내게 한다. 다리의 조명이 켜지는 시간은 대략 저녁 19:00에서 23:30분까지이므로 다리와 한국의 야경을 감상하며 드라이브를 즐기기에 시간적 여유가 있다. 야경도 아름답지만, 해 질 녘 방화대교 너머로 지는 태양이 방화대교의 아치와 어우러지는 모습은 절로 감탄이 나오는 절경이다.방화대교 자체가 서울의 외곽에 위치해 있어 답답한 도심에서 벗어나 여유로운 기분으로 자전거길이나 산책길을 즐길 수 있다.', '1', '4', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56896', '2661483', '배봉산', '', '', '6', '37.5807819121', '127.0643739039', '', '서울특별시 동대문구 전농동', '', '* <a href=''https://parks.seoul.go.kr/parks/detailView.do?pIdx=82''target=''_blank'' title=''새창: 서울의 공원''>https://parks.seoul.go.kr/</a><br>* <a href=''http://korean.visitseoul.net/nature/%EB%B0%B0%EB%B4%89%EC%82%B0%EA%B7%BC%EB%A6%B0%EA%B3%B5%EC%9B%90_/29459''target=''_blank'' title=''새창: 배봉산 근린공원''>http://korean.visitseoul.net</a>', '배봉산은 서울특별시 동대문구 전농동, 휘경동에 있는 해발 108m의 나지막한 산이다. 배봉산에 조성된 배봉산근린공원은 산지형 공원으로 비탈졌으나 무장애 둘레길이 있어 수월하게 오를 수 있다. 보행로 또한 걷기에 최적화된 길로서 완만한 오르막과 내리막으로 되어있고 중간에 벤치들이 적절하게 배치되어 있어 쉬어가기 좋다. 보행 데크가 잘 정비되어 있어 보행 약자에게도 좋은 산책길이자 운동길이다. 배드민턴장, 약수터, 운동기구 시설, 숲속도서관과 맨발 황토길도 조성되어 있으며 맨발로 걸은 뒤에는 발을 닦을 수 있는 수도 시설도 갖추어져 있다. 야트막한 등산로를 따라 봄에는 갖가지 야생화와 때죽나무꽃이 여름에는 아카시아 나무 꽃이 핀다.배봉산 숲해설 프로그램과 주말가족 프로그램이 있고 \'서울공공서비스예약\'을 통해 신청하면 된다.1992년에 공원으로 지정되었고 인근에 서울시립대학교, 서울삼육의료원 등이 있다. 배봉산 정상부에 위치한 배봉산 보루는 2017년 2월9일 서울특별시 기념물로 지정되었다.', '1', '11', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56897', '128028', '백련사(서울)', 'http://tong.visitkorea.or.kr/cms/resource/12/3401412_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/12/3401412_image3_1.JPG', '6', '37.5862038214', '126.9273751252', '', '서울특별시 서대문구 백련사길 170-43', '', '', '서대문 백련산 자락에 있는 천년고찰 백련사는 신라 경덕왕 6년(747)에 진표율사가 창건하였다. 당시는 정토사라 하였는데 조선조 정종 원년에 백련사라 개칭하였다. 조선 2대 임금인 정종이 왕위를 태종에게 물려주고 난 뒤에 요양차 이곳에서 머무르기도 했다. 조선 임금 세조의 딸 의숙옹주가 20세에 남편을 잃고 비통함을 달래기 위해 돌아다니다가 여기서 해동묵(엄나무)을 보고 인생의 참뜻을 깨달았다고 한다. 그 후 의숙옹주의 원당으로 정하면서 이름을 백련사로 바꾸었다고 한다. 일설에 의하면 경복궁에서 볼 때 서쪽에 있어서 서방정, 정토사라고 하였는데 어느 여름날 연못에서 갑자기 하얀 연꽃이 피어올라 백련사라고 명칭을 바꾸었다고 전한다. 선조 때 임진왜란으로 소실되었으나 바로 재건하였고, 그 뒤로도 여러 차례 중창을 거듭했다. 1965년 준공한 극락전과 아미타불이 있는 무량수전, 관세음보살이 있는 원통전을 비롯해 약사전, 관음전, 명부전, 삼성각, 독성각, 벙종각, 해탈루가 있고 정포대화상과 정토관세음 보살상이 있다.', '1', '14', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56898', '309307', '백련사(서울)', 'http://tong.visitkorea.or.kr/cms/resource/98/2356798_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/98/2356798_image3_1.jpg', '6', '37.6470369314', '126.9995223749', '', '서울특별시 강북구 4.19로20길 142', '(수유동)', '', '백련사(白蓮寺)은 서울시 강북구 수유 4동 산 127-1번지 삼각산에 자리한 한국불교 태고종 소속 사찰이다. 북한산 첩첩산중에는 많은 사찰들이 자리하고 있다. 유구한 역사를 지니고 있는가 하면 터만 남아 이름을 전하기도 한다. 그러나 사찰의 역사가 오래되었건 되지 않았건 모두 부처님의 연화도량임에는 변함이 없음이다. 4·19 국립묘지에서 대동문으로 오르는 등산로 가에는 작은 가람이 자리하고 있다. 백련사는 산을 오르는 사람들은 그냥 스쳐 지나가는 작은 사찰이지만 80여 년 전인 1930년 이은순여사가 지극한 신심으로 창건한 곳이다. 이후 많은 사람들이 머무르면서 수행과 포교의 도량으로서 자리매김하게 하였다. 그리고 오늘날에는 있는 듯 없는 듯 항상 그 자리에서 부처님의 법 등을 이어가고 있다.', '1', '3', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56899', '1828914', '백마고지 3용사의 상', 'http://tong.visitkorea.or.kr/cms/resource/77/1985177_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/77/1985177_image3_1.jpg', '6', '37.54883584', '127.0763931608', '', '서울특별시 광진구 능동로 216', '', '<a href=''http://mfis.mpva.go.kr/memorial/movementView.do?gubunCode=ALL&sid=1559&pageIndex=1&searFacilgubun=&searTopicsort=&searAddr_sigun=&searMpvabranch=&searVR=&searInfo=&searGbn=tit&searTxt=%EB%B0%B1%EB%A7%88%EA%B3%A0%EC%A7%80+3'' target=''_blank'' title=''새창 : 백마고지3용사의상 홈페이지로 이동''>http://mfis.mpva.go.kr</a>', '1973년 어린이대공원 내(동문 인근 위치)에 건립한 동산이다. 6.25전쟁 당시 가장 치열했던 백마고지 전투에서 육탄으로 적의 진지를 공격해 승리로 이끌었던 강승우 소위와 오규봉 일병, 안영권 일병을 기리기 위해 건립하였다. 이들 3용사는 국군 제9사단 제30연대 예하 제1중대 제1소대 소속으로 1952년 10월 철원평야 일대 중부 전선의 전략 요충지인 백마고지에서 TNT와 박격포탄, 수류탄 등을 휴대하고 육탄으로 돌진하여 적의 기관총 특화점을 격파한 후 장렬히 산화함으로써 고지를 탈환하는 데 결정적인 기여를 하였다. 정부에서는 이들의 고귀한 희생정신을 기려 1953년 7월 1일 을지무공훈장을 수여하였고 강승우 소위는 중위로, 오규봉·안영권 일병은 하사로 추서했다.', '1', '6', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56900', '129418', '백범김구선생상(백범 광장)', 'http://tong.visitkorea.or.kr/cms/resource/66/2947266_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/66/2947266_image3_1.jpg', '6', '37.5549250652', '126.9798934455', '', '서울특별시 중구 회현동', '남산공원 백범광장 내', '<a href=''https://www.junggu.seoul.kr/tour/content.do?cmsid=15027&contentId=3469''target=''_blank'' title=''새창 : 중구 문화관광 이동''>https://www.junggu.seoul.kr/tour/</a>', '백범김구선생상(백범 광장)은 백범김구선생기념사업회가 독립운동가이자 교육자이자 정치가인 김구의 항일구국운동과 통일국가수립을 위해 노력한 애국정신을 기리기 위해 1969년 8월 남산공원 백범광장 안에 설립한 동상이다.그가 암살당한 뒤 2달 만에 김구선생동상건립추진위원회가 설립되면서 동상 건립논의가 시작됐다. 장소는 일제강점기 조선신궁 터였던 남산공원으로 정했다. 동상은 국내외 각계에서 보내온 찬조금으로 생존 시 만들어진 석고 흉상을 바탕으로 조각가 김경승과 민복진이 조각하여 높이 10m로 만들었다. 1960년에 결성된 백범김구선생기념사업회의 주도하에 1969년 4월 8일 동상 건립기공식을 열고, 그가 태어난 날인 8월 23일 동상 제막식을 가졌다.', '1', '24', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56901', '809372', '백사실계곡', '', '', '6', '37.6004834158', '126.9663489448', '', '서울특별시 종로구 부암동 115', '', '<a href=''http://tour.jongno.go.kr'' target=''_blank'' title=''종로 문화관광 사이트로 이동''>http://tour.jongno.go.kr</a>', '백사실계곡은 자연환경과 문화사적이 잘 어우러진 우수한 자연생태 지역이다. 깊은 숲을 따라 이어지는 오솔길과 걸음을 막아서는 크고 작은 계곡의 정취가 아름답고 계곡에는 도롱뇽, 개구리, 버들치, 가재 등 다양한 생물체들이 서식하고 있다. 서울시 보호야생동물로서 1급수 지표종인 도롱뇽이 집단 서식하여 그 보존 가치가 매우 높은 지역이다.문화사적으로서 조선시대부터 아름다운 경관을 갖춰 별장시설이 있던 곳임을 알리는 백석동천 각자바위가 있고 절경을 즐기기 위해 지어졌던 별서 터에는 안채와 사랑채의 터가 남아 있다. 한쪽에는 우물이었던 곳과 연못도 자리하고 있다.백사실계곡은 세검정로에서 현통사 방면으로 올라가는 방법과 자하문로에서 응선사 방면으로 올라가는 방법이 있다. 백사실계곡에서 이정표를 따라가다 보면 북악스카이웨이 팔각정도 둘러볼 수 있다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56902', '2993160', '백악마루', '', '', '6', '37.5939481211', '126.9695127307', '', '서울특별시 종로구 부암동 30', '백악마루', '<a href=''https://www.opencheongwadae.kr/board?menuId=MENU002040200000000'' target=''_blank'' title=''청와대, 국민 품으로 사이트로 이동''>https://www.opencheongwadae.kr</a>', '북악산 정상을 일컫는 이름. 해발 342m 높이로 수치상으로 그리 높진 않지만 일대에서는 단연 우뚝 솟은 봉우리라 경복궁과 청와대를 품 안에 감싸 안으면서 서울 중심부를 한눈에 내려다보는 탁 트인 뷰가 압권이다. 1936년 처음 한양도성을 쌓을 때 총 97개의 공사 구간을 나누고 각 구간의 이름을 천자문에서 따 붙였는데 이때 백악마루가 성곽의 시작점이라 ‘천’자 구간에 해당했다 한다. 한양도성에서는 가장 높은 위치이다 보니 백악산과 인왕산에 걸쳐 길게 이어진 한양도성의 모습을 한눈에 조망할 수 있으며 족두리봉부터 향로봉, 비봉, 승가봉, 문수봉, 형제봉 순으로 이어지는 북한산 봉우리까지 하나하나 감상하는 특별한 시간을 만끽할 수 있다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56903', '2993177', '백악정', '', '', '6', '37.5808997695', '126.9768652077', '1522-7760', '서울특별시 종로구 청와대로 1 청와대', '', '<a href=''https://www.opencheongwadae.kr/board?menuId=MENU002040200000000&siteId=SITE00002'' target=''_blank'' title=''새창 : 청와대 홈페이지로 이동''>http://www.opencheongwadae.kr/</a>', '북악산 등반의 새로운 시작점으로 떠오른 백악정은 청와대 관저 뒤편 쉼터로 춘추관 옆 담장을 따라 20분 정도 오르면 만날 수 있다. 이는 역대 대통령들이 가장 즐겨 찾던 산책 코스이기도 하다. 백악정 쉼터 오른편에는 김대중 전 대통령과 이휘호 여사가 함께 심은 느티나무가, 왼편에는 노무현 전 대통령과 권양숙 여사가 식수한 서어나무가 자리하고 있다. 두 나무의 가지가 서로 맞닿아 마치 사이좋은 형제가 서로의 손을 잡아 이끄는 형상처럼 보인다. 두 나무에서 100m쯤 떨어진 곳에는 문재인 전 대통령과 김정숙 여사가 심은 은행나무도 있다.2022년 청와대 개방 소식은 수많은 국민 중에서도 유독 등산객들에게 큰 환대를 받은 바 있다. 2020년에 북악산 북쪽에 해당하는 청운대, 평창동 구간을 개방한 것을 시작으로 2022년 4월에는 숙정문, 청운대, 삼청동 구간을 추가로 개방했으며, 2022년 5월 청와대 전면 개방과 더불어 북악산의 남측인 청와대, 백악정, 칠궁 구간을 개방하면서 온전한 북악산 등반의 호사를 누릴 수 있게 되었기 때문이다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56904', '2481866', '백인제가옥', 'http://tong.visitkorea.or.kr/cms/resource/92/3096392_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/92/3096392_image3_1.jpg', '6', '37.5804416194', '126.9840701194', '', '서울특별시 종로구 북촌로7길 16', '(가회동)', '<a title=''새창 : 서울역사박물관 백인제가옥 페이지로 이동'' href=''http://www.museum.seoul.kr/www/intro/annexIntro/annex_22/annex_22_01.jsp?sso=ok'' target=''_blank''>http://www.museum.seoul.kr/</a>', '북촌 가회동에 위치한 백인제가옥은 근대 한옥의 양식을 고스란히 보존하고 있는 대표적인 일제강점기 한옥이다. 북촌이 한눈에 내려다보이는 2,460㎡의 대지 위에 당당한 사랑채를 중심으로 넉넉한 안채와 넓은 정원이 자리하고, 가장 높은 곳에는 아담한 별당채가 들어서 있다. 백인제가옥은 전통적인 한옥의 아름다움을 유지하면서도 근대적 변화를 수용하여, 건축 규모나 역사적 가치 면에서 윤보선 가옥과 함께 북촌을 대표하는 건축물로 소개되고 있다. 1907년 경성박람회 때 서울에 처음 소개된 압록강 흑송(黑松)을 사용하여 지어진 백인제가옥은 동시대의 전형적인 상류주택과 구별되는 여러 특징들을 갖고 있다. 사랑채와 안채를 별동으로 구분한 다른 전통한옥들과는 달리 두 공간이 복도로 연결되어 있어, 문 밖으로 나가지 않아도 자유롭게 이동할 수 있다. 또한 일본식 복도와 다다미방을 두거나 붉은 벽돌과 유리창을 많이 사용한 것은 건축 당시의 시대적 배경을 반영한 것이다. 또한 사랑채의 일부가 2층으로 건축되었는데, 이는 조선시대 전통한옥에서는 유례를 찾을 수 없는 백인제가옥만의 특징이다. 정원에서는 당당한 사랑채를, 중정에서는 넉넉한 안채를, 그리고 후원에서는 아담한 별당채의 모습을 감상할 수 있는 백인제 가옥은 우리 한옥의 다양한 아름다움이 한 자리에 모인 소중한 문화유산이다. ◎ 한류의 매력을 만나는 여행 정보 - 드라마 <재벌집 막내 아들>미라클 대주주 진도준과 순양 그룹 회장 진양철이 대면한 장소는 ‘백인제가옥’으로 백인제가옥은 대표적인 근대 한옥으로 북촌을 한눈에 내려다볼 수 있는 곳이다. 사랑채부터 안채, 정원과 별당채를 통해 당시 상류층의 삶이 어떠했는지 짐작해 볼 수 있는 가옥이기도 하다. 진양철과 진도준이 마주한 곳은 사랑채로, 해설 예약을 할 시 이곳의 내부 관람도 가능하니 잊지 말고 체크해 두는 것을 추천한다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56905', '2757155', '법룡사', 'http://tong.visitkorea.or.kr/cms/resource/57/3463057_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/57/3463057_image3_1.jpg', '6', '37.4862099946', '127.0883799583', '', '서울특별시 강남구 광평로31길 56', '(수서동)', '<a href=''https://kbhiksuni.org/beopyongsa-temple/'' target=''_blank'' title=''새창 : 법룡사 홈페이지로 이동''>https://kbhiksuni.org/beopyongsa-temple</a>', '서울 강남구 수서동에 위치한 법룡사는 지하 2층, 지상 3층 규모의 배(반야용선) 모양의 현대식 건축물이다. 전국 비구니 스님과 불자들에게 수행과 포교, 사회복지 등 불교 전반에 대해 교육하고 사회 각 분야의 전문가를 양성하는 교육의 장으로 활용되고 있다. 재가불자를 위한 전문교육으로 입문반(주·야간반), 기본반(주·야간반)을 운영하고 있으며, 문화센터에서는 사찰요리, 민화 그리기, 라인 체조, 규방 공예, 생활자수, 요가 교실, 꽃꽂이 교실, 다도, 불화 그리기 강좌 등을 운영하고 있다.', '1', '1', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56906', '2751294', '법안정사', 'http://tong.visitkorea.or.kr/cms/resource/08/2779808_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/08/2779808_image2_1.jpg', '6', '37.535364411', '126.8783949148', '', '서울특별시 양천구 목동동로 371 (목동)', '', '', '양천구 목동에 위치한 큰절 법안정사는 한불 수교 100주년을 기념하여 1989년 서울 목동지구 아파트 단지 내 종교 부지에 건축된 현대식 도심 사찰이다. 도심 속에 위치해 있어 대중교통으로도 접근하기에 용이하다. 총 4층으로 구성되어 있으며, 현대식 사찰로 지어진 덕분에 흔히 보던 절과는 다르게 웅장함을 뽐내고 있다.', '1', '19', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56907', '2759967', '법종사', 'http://tong.visitkorea.or.kr/cms/resource/99/3467799_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/99/3467799_image3_1.jpg', '6', '37.6576110182', '127.0171593539', '', '서울특별시 도봉구 해등로 354', '(쌍문동)', '', '서울 도봉구 쌍문동에 있는 불교 사찰로 오대산 월정사 서울 포교원이다. 지상 5층으로 사찰 건물은 석탑 모양으로 높게 올려져 있다. 사찰 뒤편으로는 동네 야산과 같은 쌍문 근린공원이 있어 아름다운 자연을 병풍처럼 두르고 있다. 우이동에서 바라보는 북한산과 방학동에서 바라보는 도봉산을 배경으로 하고 있으며, 우이선 솔밭공원역 1번 출구를 통해 이용할 수 있다.', '1', '10', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56908', '3006899', '벽운계곡', 'http://tong.visitkorea.or.kr/cms/resource/98/3006898_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/98/3006898_image3_1.jpg', '6', '37.6831338792', '127.0631794277', '', '서울특별시 노원구 상계동', '', '', '수락산의 벽운계곡은 서울에서 지하철과 버스로 갈 수 있는 계곡이다. 지하철 7호선 수락산역에서 버스로 두 정거장, 도보로 약 15분 정도면 계곡에 도착한다.상류는 깊고 하류는 얕아서 알맞은 곳을 골라서 물놀이를 즐길 수 있다. 물개바위 방향으로 올라가는 길에 계곡이 이어져 있고 상류는 다이빙이 가능한 웅덩이가 나온다. 깊은 곳은 2m 정도 되서 큰 아이들이나 어른들이 놀기 좋다. 하류에는 물고기가 보이는데 여기는 10cm이상 큰 물고기도 많아서 스노쿨링하는 재미도 있다. 시원한 물놀이와 함께 캠핑, 등산까지 가능하지만 취사가 안 되니 간단한 도시락과 먹거리는 준비해야 한다. 텐트를 치고 1박 캠핑을 할 수도 있고 당일치기를 할 수도 있다. 입구 쪽으로는 계곡을 따라 백숙집도 많다.', '1', '9', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56909', '315921', '보광사(강북)', 'http://tong.visitkorea.or.kr/cms/resource/30/2025930_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/30/2025930_image3_1.jpg', '6', '37.6495950359', '127.0033608408', '', '서울특별시 강북구 삼양로145길 177 (우이동)', '', '<a href=''http://www.bokwangsa.org/main'' target=''_blank'' title=''새창 : 보광사 홈페이지로 이동''>http://www.bokwangsa.org</a>', '서울시 강북구 우이동에 위치한 보광사는 대한불교조계종 소속 사찰이다. 소나무가 빼곡히 들어서 숲을 이룬 우이동의 솔밭공원을 지나 포장된 길을 따라 걷다 보면 삼각산 기슭에 자리한 보광사에 닿는다. 사찰의 연혁은 문헌상으로는 전하는 것이 없는데, 여러 전승에 따르면 1788년 금강산에서 수도한 원담스님이 창건하여 신원사라 하였다고 한다. 이후 한국전쟁으로 인해 소실되고 일부 건물만이 남아 법등을 이어왔다고 한다. 그러던 것을 1979년 남산당(南山堂) 정일(正日) 스님이 포교의 원을 세우고 이곳에 주석하면서 불사를 시작 하여 1980년 11월 보광사로 절 이름을 바꾸었다. 이후 스님은 가람을 일구어 오늘의 모습이 되도록 하였고, 대승경전산림법회와 참선법회를 정기적으로 열어 사람들에게 부처님의 가르침과 선법을 전하여 스스로 바른 길을 찾게 하였다. 이로서 보광사는 스님의 발원대로 명실상부한 포교의 도량으로서 자리매김하게 되었다. 이렇게 대중 교화에 혼을 쏟으셨던 정일스님은 세연이 다해 2004년 열반에 드시고 말았다. 이후 보광사 대중들은 보다 발전적인 모습으로 스님의 뜻을 이어나가고 있다.', '1', '3', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56910', '1603241', '보광사(용산)', 'http://tong.visitkorea.or.kr/cms/resource/55/1567955_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/55/1567955_image3_1.jpg', '6', '37.5271063892', '126.9973954154', '', '서울특별시 용산구 장문로15나길 6', '', '', '보광사는 무후묘 주위에 좋지 않은 일이 많이 일어나고 사당터의 기가 세다는 설이 있어 이 기를 누르기 위해 사당 옆에 지었다. 도심 속의 적멸보궁이라 하여 부처님의 진신사리를 모시고 있으며 사람들이 직접 볼 수 있으며 티베트에서 적사리를 모셔 왔다. 수도권 지하철 6호선 이태원역 4번 출구에서 986m에 자리 잡고 있으며, 인근에 주한헝가리대사관, 주한투르크메니스탄대사관, 주한이라크대사관, 주한헝가리대사관 등 대사관 거리라고 불러도 될 만큼 대사관이 많이 있다.', '1', '21', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56911', '127940', '보라매공원', '', '', '6', '37.4929729132', '126.9231614728', '', '서울특별시 동작구 여의대방로20길 33', '', '<a href=''http://parks.seoul.go.kr/template/sub/boramae.do'' target=''_blank'' title=''보라매공원 사이트로 이동''>http://parks.seoul.go.kr/</a>', '옛 공군사관학교 자리에 마련된 보라매공원은 동작구와 관악구에 걸쳐있는 넓은 시민 휴식 공간으로 공군사관학교의 상징인 보라매를 이어받아 1986년 5월에 시립공원으로 개원하였다. 약 12만 평의 대지에 바닥분수를 포함하여 다목적운동장, 테니스장, 배드민턴장, 농구장, 인라인스케이트장, 게이트볼장 등의 체육 시설과 어린이놀이터, 에어파크, 테마 물놀이터, 정문폭포, 반려견 놀이터 등의 시설이 있다. 에어파크는 공군으로부터 기부받은 은퇴한 비행기가 전시되어 있고 비행기마다 기종과 설명이 적혀있어 아이들에게 교육적으로 유익하다. 5월부터 9월까지는 바닥분수가 7월부터 9월까지는 테마물놀이터가 매일 6회 운영되며 여름에는 물놀이장으로 개장하여 보라매 공원의 자랑거리이다. 연못의 음악분수는 매일 4회 운영되며 야간에는 화려한 빛의 점등으로 음악과 춤추는 분수가 조명과 함께 야간분수쇼가 더욱 볼만하다. 반려견 놀이터는 동물등록을 마친 반려견이 소유주와 함께 입장 가능하다. 저녁에도 야간 라이트가 있어서 한가로이 산책하거나 가족, 친구들과 함께 운동하기에 좋다. 수도권 지하철 신림선 보라매공원역 1번 출구에서 500m에 위치하고 있다.', '1', '12', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56912', '1091608', '보라매안전체험관', 'http://tong.visitkorea.or.kr/cms/resource/39/1095139_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/39/1095139_image3_1.jpg', '6', '37.4928228216', '126.9170398124', '', '서울특별시 동작구 여의대방로20나길 16 (신대방동)', '', '<a href=''https://fire.seoul.go.kr/boramea/main/main.do'' target=''_blank'' title=''새창: 보라매안전체험관 홈페이지로 이동''>https://fire.seoul.go.kr/boramea/main/main.do</a>', '보라매공원 내에 있는 보라매안전체험관은 연면적 8,020㎡에 지상 3층, 지하 1층 규모로 안전 서울 실현을 목표로 종합재난 체험 인프라를 구축하고 다양한 전문 체험 과정과 프로그램을 통해 재난 대처 능력과 안전의 소중함을 체험하는 공간이다.이곳에서는 지진, 태풍, 화재, 교통사고 등 4가지 재난체험장과, 심폐소생술과 같은 기본 응급처치와 다양한 소방시설의 작동 원리와 조작법을 배워 볼 수 있는 전문체험장으로 나뉘어져 있다. 실제에 준하는 가상 체험을 통해 안전에 대한 소중함과 경각심을 일깨워, 안전에 대하여 생각해 볼 수 있는 뜻깊은 체험의 장이다. 수도권 지하철 신림선 보라매공원역 1번 출구에서 388m에 위치하고 있다.', '1', '12', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56913', '316104', '보문사(서울)', 'http://tong.visitkorea.or.kr/cms/resource/19/3393219_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/19/3393219_image3_1.JPG', '6', '37.5844334073', '127.0166614803', '', '서울특별시 성북구 보문사길 20 (보문동3가)', '', '<a href=''http://www.bomunsa.or.kr/'' target=''_blank'' title=''새창: 한불교보문종보문사 홈페이지로 이동'' >www.bomunsa.or.kr</a>', '서울시의 전통사찰로 등록된 보문사는 서울특별시 성북구 보문동에 위치하고 있으며, 고려 예종 10년(1115)에 담진국사(曇眞國師)에 의해 창건되었다. 특히, 퇴경 권상로(1879~1965)가 저술한 『퇴경전서-보문사일신건축기(普門寺一新建築記)』에는 예부터 비구니스님들이 상주하며 국가의 안녕과 왕실의 번성을 기원하는 비구니 사찰로 기록하고 있다. 그 사이의 내용은 전해지지 않다가 2017년 극락보전 중수 불사를 진행하던 중 온전한 상태의 상량문이 발견되어 역사적 사실이 입증되었다. 지금의 형태를 갖추게 된 극락보전은 1747년(영조 23)에 최초 중건되었으며, 1824년(순조 24) 1차 중수, 1865년(고종 2)에 2차 중수되었다. 퇴경전서에 의하면 1826년에 수봉법총(秀峰法聰) 스님이 만세루를 신축하였으며, 1827년에 정운(正雲) 스님이 좌우 승당을 건축하였다고 한다. 1842년에는 영전(永典) 스님이 만세루를 수리하고, 1872년에 금훈(錦勳) 스님이 좌우 승당을 중수하였다고 전하고 있다.극락보전 상량문의 발견으로 보문사는 1,800년대부터 줄곧 비구니 스님들이 상주하며 수행·정진하였으며, 특히 동치 4년 상량문에서는 왕실발원 사찰로 조선왕조의 왕실의 시주를 받아 불사를 진행하고 후궁과 상궁 등 궁인에서부터 사대부, 평민에 이르기까지 불사에 동참하였음을 보여주고 있다. 보문사에는 왕실에서 하사한 연(輦)수식과 인로왕번(引路王幡)을 비롯하여 조선 후기에 제작한 석가불도, 신중도, 지장보살도 등 서울시 유형문화재, 조선 전기에 제작된 묘법연화경이 국가지정문화재로 지정되어 있으며 그 밖에도 다수의 유·무형의 문화재들이 남아있다.', '1', '17', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56914', '605497', '보성사터', 'http://tong.visitkorea.or.kr/cms/resource/50/2031850_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/50/2031850_image3_1.jpg', '6', '37.5739062155', '126.9811622368', '', '서울특별시 종로구 수송동', '', '<a href=''https://tour.jongno.go.kr/tour/tour/indvdlz/view.do?nttId=34744&menuId=2357&menuNo=2357'' target=''_blank'' title=''새창 : 종로 문화관광 홈페이지로 이동''>tour.jongno.go.kr</a>', '보성사는 1910년 말 창신사와 보성학원 소속 보성사 인쇄소를 합병하여 만든 천도교 계통의 인쇄소이다. 3.1운동 동시 2만 장의 독립선언서를 인쇄한 곳이기도 하다. 당시 보성사 사장이었던 이종일은 공장 감독 김홍규, 총무 장효근 등과 함께 1919년 2월 27일 이곳에서 독립선언서를 인쇄하여 자신의 집으로 운반한 뒤, 다음날 전국 각지에 배포함으로써 독립운동의 발판을 마련하였다. 보성사는 30평 2층 기와 벽돌집으로 전동 보성학교 구내에 있었으며, 보성사의 소유주이기도 했던 천도교 교주 의암 손병희의 특명으로 육당 최남선이 초안을 집필하고 민족대표 33인이 서명한 독립선언서를 넘겨받아 사장 이종일, 공장 감독 김흥규, 총무 장효근이 1919년 2월 27일 밤에 3만 5천 매를 인쇄하였다. 일본 측의 형사에게 발각되는 위기도 있었으나 족보책이라고 위장하여 위기를 넘겼다. 3월 1일에는 윤익선과 이종린, 이종일, 김홍규 등이 지하신문인 조선 독립신문 1만 부를 계속 발행하였다. 일경은 보성사를 즉각 폐쇄하였으며, 1919년 6월 28일 밤에는 불을 질러 태워버린 이후 터만 남아 오늘에 전한다. 보성사는 현 종로구 조계사 경내에 있으며, 기념비와 동상은 조계사 후문 맞은편 근린공원에 있다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56915', '126516', '보신각 터', '', '', '6', '37.5698150675', '126.9835760212', '', '서울특별시 종로구 종로 54', '', '<a href=''https://www.heritage.go.kr/heri/cul/culSelectDetail.do?pageNo=1_1_1_1&sngl=Y&ccbaCpno=2331100100000'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://www.heritage.go.kr</a>', '보신각 터는 도성 안팎에 시간을 알리는 종루 보신각이 있던 곳이다. 보신각은 일명 종각이라고도 하며 조선 고종 32년(1895) 보신각이란 사액을 내린 데서 이름 지었다. 태조 4년(1395) 처음 지어진 후 4번이나 불타 없어지고, 8번에 걸쳐 다시 지었다. 현재의 건물은 1979년 8월 서울시에서 지은 것으로, 동서 5칸, 남북 5칸의 2층 누각으로 되어 있다.보물 제2호인 보신각종은 조선 세조 14년(1468)에 만들어져 원각사에 있다가, 절이 폐사된 후 광해군 11년(1619)에 현재의 보신각 자리에 옮겨졌다. 보신각종은 오전 4시에 33번, 오후 7시에 28번을 울려 도성의 문을 여닫고 하루의 시각을 알리는 역할을 해왔다.오랜 세월이 흐르는 동안 몸통에 균열이 생겨 더 이상 종을 칠 수 없게 되자, 경복궁 안에 종각을 지어 보관하고 있다. 지금 보신각에 걸려 있는 종은 1985년 국민의 성금에 의하여 새로 만들어 그해 광복절에 처음 타종하였다. 해마다 12월 31일이면 보신각 인근은 차량이 통제되고 새해를 맞이하는 타종식을 함께 즐기기 위해 모여든 사람들로 가득해진다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56916', '316294', '본원정사(서울)', 'http://tong.visitkorea.or.kr/cms/resource/82/3466082_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/82/3466082_image3_1.jpg', '6', '37.6372881685', '127.0052887931', '', '서울특별시 강북구 삼각산로 1', '(수유동)', '', '본원정사(本願精舍)는 서울특별시 강북구 수유동 삼각산에 자리한 대한불교조계종 대각회 소속의 사찰이다. 본원정사에는 한국전쟁 때 태고사에 불이 나서 옮겨왔다고 전하는 목조지장보살좌상이 봉안되어 있다. 지장보살은 석가모니 부처님과 미륵불 사이인 부처님 없는 시대에 중생들을 교화하는 대비보살이다. 특히 지옥에서 고통받는 중생들을 구원하기 위해 지옥에 들어가 교화해 제도하는 보살로 널리 알려져 있다. 1980년대 초 주지소임을 맡은 원성스님은 이 지장보살상에 감화를 받아 지장보살의 본원을 따르고자 서원을 하였다고 한다. 그래서 절 이름도 본원정사로 바꾸었다. 이후 본원정사를 사바세계 중생들의 몸과 마음의 병을 다스릴 수 있는 도량으로 만들어 가고 있다. 또한 언제나 열려있는 도량으로 서울시민과 불자들에게 번잡한 마음을 쉴 수 있게 하고, 기도할 수 있는 도량으로 본원정사를 장엄하고 있다.', '1', '3', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56917', '3076259', '볼베어파크 롯데몰 은평점', '', '', '6', '37.6375893423', '126.9179162', '', '서울특별시 은평구 통일로 1050 (진관동)', '롯데몰 은평점 3층', '<a href=''http://ballbearpark.10pages.co.kr/skin/page/themepark.html'' target=''_blank'' title=''새창 : 홈페이지로 이동''>http://ballbearpark.10pages.co.kr/skin/page/themepark.html</a>', '볼베어파크 롯데몰 은평점은 14가지 이상의 다양한 스포츠 체험이 가능한 어린이 스포츠 테마파크이다.입장 신청은 입구 앞 키오스크, 태블릿이나 스마트폰으로 가능하다. 대형마트에 입점해 있어서 주말에는 사람이 많아 입장 대기가 있을 수도 있다. 입장 대기를 기다리며 놀 수 있는 작은 놀이공간이 마련되어 있어서 지루하지 않게 기다릴 수 있다.볼베어파크 전 지점은 어린이 미끄럼방지 양말을 꼭 신어야 한다. 보호자들도 안전과 청결을 위해 양말을 꼭 착용하여야 한다. 양말을 챙겨오지 못했어도 카운터에서 구매가 가능하다.볼베어파크에는 스포츠존, 챌린져 / 디지털존, 영유아존, 휴게·음식 / 포토존이 있다. 스포츠존에는 축구, 농구, 야구, 복싱 등의 다양한 스포츠 시설이 있고 챌린져 / 디지털존에는 볼링 슬라이드, 대형볼풀장, 스포츠 에어바운스 등의 시설이 있다. 인형 극장, 낚시터 등의 시설을 갖춘 영유아존과 안마의자, 푸드코트가 있는 휴게존도 있어서 유아부터 어른들까지 이용할 수 있다. 대형 놀이시설인 만큼 시간대마다 다양한 놀이시설을 운영한다. 각 놀이시설에 따른 키와 나이 제한이 있으니 확인하고 이용해야 한다. 입장권 포함 놀이시설 외에 따로 현장에서 결제하고 탑승할 수 있는 놀이시설도 있다. 이용권이 2시간 시간제한이 있어서 각 놀이시설의 시간과 위치를 잘 파악하고 계획을 세워야 알차게 이용할 수 있다.', '1', '22', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56918', '316295', '봉국사(서울)', '', '', '6', '37.6064140095', '127.006881192', '', '서울특별시 성북구 정릉로 202 (정릉동)', '', '<a href=''http://bongguksa.com/'' target=''_blank'' title=''새창: 봉국사 공식 홈페이지로 이동''>http://bongguksa.com</a>', '봉국사는 1395년 조선 왕조의 개창에 큰 역할을 했던 무학대사가 약사불을 봉안하고 약사사라 한 것부터라고 알려져 있다. 그 후 왕실의 지원으로 1468년 다시 한번 중창하였다. 약사사가 지금의 이름인 봉국사로 바뀌게 된 것은 조선시대 중기의 일이다. 1669년 태조의 두 번째 왕비인 신덕왕후(神德王后) 강씨의 묘를 정릉으로 복원하면서 약사사가 원찰로 지정된 것이다. 이때 사명을 봉국사로 바꾸었으나, 약사사라는 사명이 더 익숙했기 때문에 1900까지도 봉국사보다는 약사사로 불렸던 것으로 보인다. 원찰로 지정되면서부터 그 이후의 기록은 거의 보이지 않다가 1882년 임오군란의 와중에 절이 불에 타고, 이듬해 한계스님과 덕운스님이 힘을 모아 중건하였다. 1898년에는 운담스님, 영암스님, 취봉스님 등이 명부전을 중건하고, 1913년에는 주지 종능스님과 화주 월화 봉연스님이 칠성각을 중건하였다. 1938년 10월에는 화주 금파스님이 조인섭의 시주를 받아 염불당을 새로 지었다. 근래에 들어와서는 1979년 일음루 및 천왕문을, 1986년에는 산신각을 중수하였고, 1994년 안심당을 짓는 등 현재의 가람으로 거듭나게 되었다.', '1', '17', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56919', '126503', '봉원사(서울)', 'http://tong.visitkorea.or.kr/cms/resource/05/2017905_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/05/2017905_image3_1.jpg', '6', '37.5717780764', '126.9472292784', '', '서울특별시 서대문구 봉원사길 120 (봉원동)', '', '<a href=''http://bongwonsa.or.kr/index.php'' target=''_blank'' title=''새창 : 봉원사 공식 홈페이지로 이동''>http://bongwonsa.or.kr</a>', '봉원사(奉元寺)는 신라 진성여왕 3년(889) 도선국사가 현 연세대 터에 창건하고, 반야사라 하였다. 이후 이후 조선의 태조 이성계는 한산군이 이색에게 명하여 태고국사의 비문을 짓게 하고, 스스로 국사의 문도임을 자처하여 봉원사에 그 이름이 기록되어 있다. 태조 5년(1396)에는 원각사서 삼존불을 조성하여 봉원사에 봉안하였고, 태조 사후에는 전각을 세워 태조의 어진을 봉안하였다. 선조 25년(1592) 임진왜란 당시 전각이 소진되었으며, 효중 2년(1651)에 지인대사가 중창하였으나 동·서 요사채가 다시 소실되어 극령스님과 휴엄스님에 의해 중건되었다. 영조 24년(1748) 찬즙스님과 중암스님에 의해 지금의 터전으로 이전하였고, 영조는 친필로 봉원사라 현액 하였다. 신도들 사이에는 이때부터 새로 지은 절이라 하여 ‘새절’이라 부르게 되었다. 정조 12년(1788)에는 전국의 승려의 풍기를 바로잡기 위한 8도승풍규정소가 설치되었으며, 철종 6년(1856)에는 대웅전이 중건되었다. 고종 31년(1894) 주지 성곡스님이 약사전을 건립하였으나 소실되었다.1950년 9월 28일 서울 수복 당시 병화로 광복기념관이 소진되었고, 이때 영조의 친필 현판 등 사보와 이동인 스님 및 개화파 인사들의 유물이 함께 소실되었다. 1966년 주지 영월스님과 대중의 원력으로 소실된 염불당을 중건하였는데, 이 건물은 대원군의 별처였던 아소정을 헐어 옮긴 것이다. 1991년 주지 김성월 스님과 사부대중의 원력으로 삼천불전 건립도중 대웅전이 소진됨에 즉시 중건을 시작하여 1994년 주지 혜경스님과 사부대중의 원력으로 대웅전을 복원 낙성하였고, 같은 해 1,100평 규모의 삼천불전을 새로 건립하였다. 2011년 전통사찰로 등록되었으며, 2014년 7월에 아미타괘불도와 범종이 서울특별시 유형문화유산으로 등록되었다. 현재 봉원사는 한국불교의 전통 종단인 태고종의 총본산으로서 전법수행의 맥을 이어가고 있다. 영산재보존회에서 단청과 범패분야의 후학을 지도하고 있다. 교육기관으로는 옥천범음대학, 불교교양대학이 있고, 신행 단체로는 관음회, 화엄법회, 청년회, 학생회, 인경회, 거사림 등 법회가 개최 되고 있다. 대외적으로도 교도소, 소년원, 양로원, 고아원, 군부대 등을 정기적으로 방문하여 교화활동을 펼치고 있어 태고종 총본산으 로서 그 소임을 다하고 있다.', '1', '14', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56920', '126504', '봉은사(서울)', '', '', '6', '37.5158782211', '127.0577485719', '', '서울특별시 강남구 봉은사로 531', '', '<a href=''http://www.bongeunsa.org/'' target=''_blank'' title=''봉은사 홈페이지로 이동''>http://www.bongeunsa.org</a><br /><a href=''https://www.templestay.com/temple_info.asp?t_id=bongeunsa '' target=''_blank'' >☞ 템플스테이 보러가기</a>', '봉은사는 서울의 중심지인 강남구 삼성동에 자리 잡은 천년고찰이다. 794년 연화국사가 창건하였고 처음엔 견성사라고 하였다. 이후 1498년 성종의 계비 정현왕후가 성종의 능(선릉) 동편에 있던 이 절을 크게 중창하고 절 이름을 봉은사로 바꿨다.봉은사는 조선조에는 조계종을 대표하는 선종 수사찰로, 근대에는 역경사업과 도제를 양성했던 동국역경원이 세워진 터전이다. 봉은사에는 보물 2점과 성보문화재 40점을 소장하고 있다. 추사 김정희가 쓴 현판이 걸린 판전에는 화엄경, 금강경 등 13가지의 불경 경판 3,479판이 보관되어 있다. 매년 음력 9월 9일에는 사부대중이 함께 경판을 머리에 이고 법성게(불교 경전의 산문 내용을 시의 형태로 되풀이해서 설명한 것)를 독송하면서 법계도를 따라 행진하는 정대불사라는 의식이 행해진다. 오늘날 봉은사는 수행 중심의 사찰 운영으로 새로운 불교 역사를 만들어 가고 있다. 템플스테이를 비롯한 불교대학, 경전학교 등 다양한 프로그램을 통해 국내외적으로 양질의 한국불교문화를 널리 알리고 있을 뿐 아니라, 사회복지 또한 실현하는 도심 대찰의 위상을 이어가기 위해 노력하고 있다.', '1', '1', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56921', '3082002', '봉제산공원', 'http://tong.visitkorea.or.kr/cms/resource/94/3081994_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/94/3081994_image3_1.JPG', '6', '37.5423381374', '126.8524637493', '', '서울특별시 강서구 화곡동', '산5-6', '<a href=''https://www.gangseo.seoul.kr/munhwa/mh050202/view?cltSn=30'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://www.gangseo.seoul.kr</a>', '봉제산공원은 강서구 화곡동 산1번지 일대에 조성된 공원이다. 산 이름은 산을 위에서 내려다보면 마치 학이 알을 품고 앉아 있는 모습이라 하여 붙여졌고, 주봉을 매봉이라 부른다. 공원 이름은 처음엔 등촌동 백석중학교와 등촌초등학교 근방에서 과거에 흰 돌이 난다 하여 [백석]이라 불리다가 2004년 공원 명칭이 생소하다는 여론이 있어 산이름인 [봉제산]으로 알기 쉽게 변경되었다. 공원에는 울창한 산림과 산책로가 잘 조성되어 있으며, 식재된 주요 식물로는 상수리나무, 신갈나무, 소나무 등이 있다. 이 밖에도 공원에는 봉제산책쉼터, 자연체험학습원, 배드민턴장, 약수터 등이 있다.', '1', '4', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56922', '126912', '봉천동 마애미륵불좌상', '', '', '6', '37.4675599107', '126.9630530001', '', '서울특별시 관악구 낙성대로15길 56-39', '', '<a href=''https://www.gwanak.go.kr/site/gwanak/05/10505020400002016051205.jsp'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://www.gwanak.go.kr</a>', '관악산 북쪽 골짜기로 올라 약수터를 지나면 거대한 절벽이 서 있는데, 서울이 내려다보이는 북쪽 바위 면에 미륵불 좌상 새겨져 있다. 이 불상은 연꽃 봉오리를 든 불좌상으로 연꽃대좌 위에 앉아 있다. 얼굴은 온화하고 풍만한 모습을 하고 있으며 몸을 왼편으로 살짝 틀었다. 머리 광배에는 이중 테두리를 둘렀고, 몸 광배는 한 줄의 음각선으로 나타냈다. 몸에는 가사를 입혔는데, 가슴은 조금 돌출되었고 안에 입은 평행의 내의와 내의를 묶은 매듭이 자세히 조각되어 있다. 낮은 육계, 소반의 머리칼, 다소 풍성하게 보이는 갸름한 얼굴 등은 상당히 우수한 기법을 보여 주고 있다. 이러한 특징은 늘씬한 체구나 타원형의 어깨 등에서도 잘 나타나고 있는데, 17세기 조선조 사회에도 미륵신앙이 상당히 유행하였음을 알려 주고 있다.  봉천동 마애미륵불은 17세기 조선조 불상을 대표하는 기준이 될 만한 작품으로 높이 평가되고 있다.', '1', '5', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56923', '1115042', '봉화산(서울)', 'http://tong.visitkorea.or.kr/cms/resource/36/1894536_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/36/1894536_image3_1.jpg', '6', '37.6086568514', '127.0866155277', '', '서울특별시 중랑구 신내로21길', '(신내동)', '<a href=''https://www.jungnang.go.kr/portal/main/contents.do?menuNo=200596''target=''_blank'' title=''새창: 중랑구 문화관광''>https://www.jungnang.go.kr/</a>', '봉화산은 서울의 동북부 외곽인 중랑구 상봉동, 중화동, 묵동, 신내동에 접하여 있고 정상까지 높이는 160.1m로 평지에 돌출된 독립 구릉이다. 동쪽에 아차산 주 능선이 있는 것을 제외하고는 북쪽으로 불암산, 도봉산과 양주 일대까지 잘 조망되며, 서쪽과 남쪽으로도 높은 산이 없어 남산과 이남 지역도 잘 보이는 지역이다. 봉화산은 일명 봉우재라고 불리는데 1963년 1월 1일 경기도에서 서울시로 편입되었다. 이곳은 북쪽 양주의 한이산에서 연락을 받아 목멱산(남산)으로 전달하는 아차산 봉수대가 있던 곳으로 지난 1994년 11월 7일 봉수대를 복원하였다.봉화산의 주요 수종은 소나무이며, 기슭에는 먹골배로 유명한 배나무밭이 조성되어 있는데 점차 주변 지역으로 잠식되고 있다. 한편 봉화산 정상에서 약간 남쪽에 봉화산 도당인 산신각이 있으며, 약 400년 전에 주민들이 도당굿과 산신제를 지내던 곳이기도 하다. 현재 산신각은 1992년 여름에 일어난 화재로 소실되어 지금은 붉은 벽돌과 시멘트로 지은 새 건물이 들어서 있다. 해마다 음력 3월 3일에 산 정상 부근에 있는 산신각(도당)에서 서울시 무형문화재로 지정된 봉화산 도당제가 열린다.', '1', '25', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56924', '1115299', '봉화산근린공원', 'http://tong.visitkorea.or.kr/cms/resource/31/2350531_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/31/2350531_image3_1.jpg', '6', '37.610609504', '127.0922455148', '', '서울특별시 중랑구 봉화산로45길', '(묵동)', '', '봉화산근린공원은 점토벽돌로 포장된 110평 규모의 진입광장을 비롯해 어린이 놀이터, 전통놀이마당, 주민건강마당, 야외무대 및 주민 쉼터 등으로 꾸며져 있다. 또한 공원을 따라 순환하며 조깅과 산책을 즐길 수 있는 630m의 외곽순환산책로와 공원 정상부에 800평 규모의 잔디마당, 장애인을 위한 전용 체력단력장 등으로 조성되었다. 이 밖에도 중랑구청을 찾아온 민원인들을 위해 구청 정문에서 한눈에 바라다 볼 수 있는 위치에 인공폭포를 조성하여 휴식처를 제공하며,구청의 다양한 야외 문화예술 공연 장소로 활용하기 위해 이벤트광장을 인공폭포와 연계해 조성했다. 봉화산근린공원 뒤 등산로를 따라 20~30분정도 걸으면 봉화산 정상에 오를 수도 있다.<br>', '1', '25', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56925', '3013027', '봉화산옹기테마공원', 'http://tong.visitkorea.or.kr/cms/resource/87/3012987_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/87/3012987_image3_1.jpg', '6', '37.6127064158', '127.0885346818', '', '서울특별시 중랑구 신내로21길 116 (신내동)', '', 'https://www.jnfac.or.kr/index', '옹기테마공원은 봉화산 일대에 화약고가 들어서기 전 신내동 일대에 옹기터가 8개나 존재했던 점에 착안해 화약고를 이전하며 옹기터를 복원했다. 독특한 음식 저장 용기인 옹기의 역사를 알 수 있으며 옹기 체험도 할 수 있는 테마공원이다.공원은 9,000㎡ 면적에 한지체험장, 옹기체험장, 목공예체험장, 중앙광장, 데크광장, 옹기정원, 전통옹기가마, 전망대, 북카페가 들어서 있다.옹기체험장에서는 전문 강사에게 옹기의 역사와 특징을 배우고 그릇, 컵, 작은 항아리 등을 만들어 볼 수 있으며 월별로 다양한 체험프로그램이 있다. 한지체험장에서는 한지를 활용한 다양한 공예작품을 만들고 전통 체험을 즐길 수 있다. 공원 정상의 전망대에서는 공원과 배밭 전경을 감상할 수 있다. 체험은 전화예약은 불가하고 온라인 예약만 가능하다. 중랑문화재단 홈페이지에서 매달 20일 오전 9시부터 다음달 체험 프로그램 예약을 할 수 있다.지하철 6호선 4번 출구에서 550m 거리에 자리잡고 있다.', '1', '25', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56926', '1367251', '부암동', 'http://tong.visitkorea.or.kr/cms/resource/19/3109519_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/19/3109519_image3_1.JPG', '6', '37.5924870824', '126.9640854894', '', '서울특별시 종로구 창의문로 145', '', '', '종로구에 있는 부암동은 세련된 삼청동 분위기와 옛날 모습의 촌스러움이 고스란히 남아있는 구불구불한 골목길을 가진 동네이다. 부암동은 미술관, 개성 있는 카페와 레스토랑이 1960~70년대 방앗간과 이발소와 함께 자리 잡고 있다. 본래는 화가, 문인, 교수 등 다양한 분야를 아우르는 예술가들이 느릿느릿 살아가던 동네였으나 최근에 부암동사무소 앞에서부터 ‘커피 프린스 1호점, 최한성의 집’으로 유명해지면서 산모퉁이 카페에 이르는 길 곳곳에 개성 넘치는 카페와 가게가 줄지어 들어서고 있다. 삼청동, 신사동 가로수길과 더불어 감각적인 공간으로 주목받는 곳이다. 산책코스로도 좋으며 관광지 분위기도 나는 다양한 모습을 가진 동네이다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56927', '822384', '북서울꿈의숲', '', '', '6', '37.6233876457', '127.0407498436', '', '서울특별시 강북구 월계로 173', '', '<a href=''http://parks.seoul.go.kr/template/sub/dreamforest.do'' target=''_blank'' title=''새창 : 북서울꿈의숲 공식 홈페이지로 이동''>http://parks.seoul.go.kr/</a>', '서울 강북과 도봉 등 6개 구에 둘러싸여 있는 초대형 공원인 북서울꿈의숲은 과거 드림랜드가 있던 자리 68만여㎡에 조성된 녹지공원으로 월드컵공원과 올림픽공원에 이어 서울에서 3번째로 큰 공원이다. 숲이 울창해 아름다운 풍광을 자랑하는 벽오산, 오패산으로 둘러싸여 있는 북서울꿈의숲은 벚꽃길과 단풍숲 등의 생태적 조경 공간, 대형연못인 월영지와 월광폭포 등의 전통경관, 그리고 북한산과 도봉산, 수락산을 한눈에 볼 수 있는 49.7m의 전망대와 다양한 장르의 고품격 문화예술이 일 년 내내 펼쳐지는 꿈의 숲아트센터 등의 공연장과 전시장, 레스토랑, 전망타워 등이 있는 서울을 대표하는 세계적인 공원이다.', '1', '3', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56928', '3112044', '북서울꿈의숲 강북구 반려견 놀이터', '', '', '6', '37.619724251', '127.0445440464', '02-901-6463', '서울특별시 강북구 월계로 173', '', '', '강북구 반려견 놀이터\'는 서울 강북, 노원, 성북 등 6개구에 둘러쌓여 있는 초대형 공원인 \'북서울 꿈의숲\' 내에 위치하고 있다. 북서울 꿈의숲 주차장 인근 과거 배드민턴장이 있던 자리에 개장한 총면적 815㎡(중소형견 330㎡, 대형견 320㎡)의 반려견 놀이터로서, 반려견과 보호자들이 함께 뛰어놀 수 있는 안전한 공간을 제공한다. 또한 반려견 운동회, 무료 미용교실, 1:1 행동 교정 교육 등 반려견과 견주들을 위한 다양한 프로그램들도 마련되어 있다.', '1', '3', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56929', '809190', '북악산', '', '', '6', '37.5931538442', '126.9737151476', '', '서울특별시 종로구 청운동', '', '', '북악산은 서울 분지를 둘러싸는 내4산인 북악산, 남산, 낙산, 인왕산 중 가장 높은 산이며 북한산 남쪽 지맥 중 가장 높은 봉우리이다. 이들 능선을 연결한 옛 서울의 성곽은 북악산을 기점으로 축조되었다. 옛 성벽이 능선을 따라 보존되어 있고 사소문의 하나인 창의문, 일명 자하문이 서쪽 산기슭과 인왕산과의 사이에 있어 시내 중앙지와 쉽게 연결된다. 창의문에서 정릉의 아리랑고개에 이르는 북악스카이웨이는 창의문, 팔각정, 풀장 등을 연결하는 관광도로로 울창한 숲과 서울시가지를 한눈에 볼 수 있어 조망이 좋은 경승지이다. 청와대와 삼청공원과도 이어져 있다. 북악산은 예전부터 소나무가 울창한 경승지를 이루어 조선시대 선비들이 계곡을 찾아 탁족회를 자주 열었던 곳인데 이후 벌채로 소나무가 사라지고 현재는 참나무류가 많다.고려 숙종 때 북악산 아래는 남쪽의 수도인 남경의 궁궐이 있었던 자리로 당시에는 면악이라고 불렀다. 이후 조선시대에는 백악산신을 모시는 사당이 있어 북악산을 백악산이라고도 불렀다고 전해진다. 북악산이라는 이름은 도성인 한양을 둘러싸고 있는 산 중 북쪽에 위치하여 붙은 이름이다. 북악산의 높이는 342m이며 남쪽으로 완만한 평지를 이루고 있고 모양은 삼각형으로 뾰족하게 솟아난 형상으로 주변에 비해 도드라진다. 조선이 건국되면서 무학대사에 의해 왕가의 기운을 받은 명산으로 칭송되었으며 그 아래 조선왕조의 궁궐을 세우고 도읍으로 정하게 되었다. 1394년(태조 4년)에 북악산 아래 경복궁을 축성하였으며 대한민국에 들어서 대통령의 집무실인 경무대(景武臺)도 이곳에 위치했다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56930', '129500', '북악산 숙정문', 'http://tong.visitkorea.or.kr/cms/resource/41/3350341_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/41/3350341_image3_1.jpg', '6', '37.5955536984', '126.9811144048', '', '서울특별시 종로구 삼청동', '', '<a href=''https://korean.visitseoul.net/palace/sookjeongmun_/23760'' target=''_blank'' title=''새창 : 비짓서울로 이동''>https://korean.visitseoul.net</a>', '숙정문(肅靖門)은 서울성곽을 이루는 사대문(四大門) 가운데 하나로, 한양도성의 북쪽 대문이다. 처음에는 숙청문(肅淸門)이었으나 숙정문(肅靖門)으로 이름이 바뀌었다. 현존 도성문 중 좌우 양쪽으로 성벽이 연결된 것은 이 문이 유일하다. 1976년에 문루를 새로 지었다. 청와대 경비를 위해 일반인의 접근을 금지하다가 2006년 4월부터 서쪽 성곽 0.5㎞, 북쪽의 진입로 0.6㎞ 구간과 함께 다시 개방했다. 1963년 1월 21일 서울 한양도성에 포함되어 사적 제10호로 지정되었다.', '1', '17', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56931', '126943', '북악스카이 팔각정', 'http://tong.visitkorea.or.kr/cms/resource/32/2667332_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/32/2667332_image3_1.jpg', '6', '37.6020279352', '126.9805670485', '', '서울특별시 종로구 북악산로 267', '(평창동)', '<a href=''https://korean.visitseoul.net/attractions/%EB%B6%81%EC%95%85%EC%8A%A4%EC%B9%B4%EC%9D%B4%EC%9B%A8%EC%9D%B4-%ED%8C%94%EA%B0%81%EC%A0%95/KOP011117'' target=''_blank'' title=''새창 : 서울 문화관광 홈페이지로 이동''>https://korean.visitseoul.net</a>', '북악스카이 팔각정은 해발 342m 북악산 위에 한국 전통미를 살린 한옥형 정자로서 천혜의 입지조건을 갖춘 서울 도심 속의 관광 명소이다. 1968년 9월에 개통된 북악스카이웨이는 북악팔각정으로 오르는 드라이브 코스로 잘 알려진 명소로 서울의 주산인 북악산의 능선을 따라 동북으로 뻗친 관광도로의 길이가 약 19km에 이른다. 북악팔각정에서는 북한산 비봉과 문수봉이 이어지는 산세가 장관을 이루고, 다른 한쪽은 남산을 바라보고 앉은 서울시의 아름다운 경관이 보인다. 전 구간에 걸쳐 잘 가꾸어진 가로수와 약 10만 그루의 관상수들이 철 따라 꽃을 피워내며 남산의 순환도로와 함께 서울을 한눈에 내려다보는 진경을 만끽할 수 있다. 길의 중간쯤, 능선길 정상에 잠시 길을 멈추고 서울의 진경을 다시 한번 재확인하며 휴식을 취할 수 있는 휴식 공간도 마련되어 있다. 먼 곳에서 서울을 찾는 내·외국인 방문객들에게 서울을 한눈에 보여주는 관광코스로 한몫하고 있다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56932', '2661492', '북정마을', '', '', '6', '37.5929152943', '126.9910580174', '', '서울특별시 성북구 성북로23길 132-3', '', '<a href=''https://archive.sb.go.kr/isbcc/home/u/story/view/642.do'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://archive.sb.go.kr</a>', '북정마을은 서울 성북구 성북동에 위치한 마을로서 오래된 골목길의 정취를 즐길 수 있을 뿐만 아니라 젊은 예술가들이 모여 살고 있는 곳이다. 조선시대 이곳에서 군사가 진을 치면서 북적북적 거리는 소리 또는 메주 쑬 때 북적북적 끓는 소리를 따서 마을 이름을 지었다고 한다. 일제강점기에는 아름다운 경관과 맑은 공기 덕분에 한용운 등의 가난한 예술가들이 살기도 하였다. 한국전쟁 이후에 피난민과 지방에서 상경한 사람들이 무너진 한양도성 성곽 아래 판자촌을 이루어 살면서 마을이 커졌다. 마을을 한 바퀴 빙 두르는 도로는 1983년에 완공한 소방도로인데 이 도로 안팎에 500여 가구가 모여 산다. 마을버스 정류장 앞 너른 길은 크고 작은 마을 행사가 열리는 곳이다. 이 마을은 2015년 서울미래유산으로 지정되었으며, 한양도성 백악구간 코스에 포함되어 만해 한용운의 유택인 심우장과 연계하여 관광을 할 수 있다.수도권 지하철 4호선 한성대역에서 마을버스 성북 03번을 이용하면 된다.', '1', '17', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56933', '1100498', '북촌 8경', 'http://tong.visitkorea.or.kr/cms/resource/81/933781_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/81/933781_image3_1.jpg', '6', '37.5791612548', '126.9864254967', '', '서울특별시 종로구 계동길 37', '(계동)', '<a href=''https://hanok.seoul.go.kr/front/kor/town/town01.do'' target=''_blank'' title=''새창: 북촌한옥마을 홈페이지로 이동''>https://hanok.seoul.go.kr/</a>', '북촌 8경은 북촌의 명소를 하나로 꿰는 코스로 한옥의 아름다움과 북촌 골목길을 구석구석 즐길 수 있다. 북촌은 경복궁과 창덕궁 사이의 한옥마을을 말한다. 지금의 남산에 해당하는 하급 관리들이 살았던 종로의 아랫동네는 남촌이라 했고, 왕족이나 사대부들이 살았던 청계천과 종로의 윗동네는 북촌이라 했다. 북촌은 조선시대 왕족이나 권세 있는 사대부들이 살았던 동네라 멋스러운 한옥들이 많이 남아 있어 관광객이 많이 찾는다.북촌 8경에는 1경 창덕궁 전경, 2경 원서동 공방길, 3경 가회동 11번지 일대, 4경 가회동 31번지 언덕(북촌전망대), 5경 가회동 골목길(오르막길), 6경 가회동 골목길(내리막길), 7경 가회동 31번지, 8경 삼청동 돌계단길이 포함되어 있다. 지하철 3호선 안국역에서 하차하여 도보로 이동하는 당일여행 코스로 인기가 있으며 오래된 맛집을 비롯해 주변에 국립현대미술관 서울관, 운현궁, 정독도서관, 삼청공원 숲 속도서관 등의 볼거리가 있어 여행하기 좋다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56934', '3025266', '북촌 한옥청', 'http://tong.visitkorea.or.kr/cms/resource/45/3025245_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/45/3025245_image3_1.jpg', '6', '37.5827747096', '126.9856671302', '', '서울특별시 종로구 북촌로12길 29-1 (가회동)', '', '<a href=''https://hanok.seoul.go.kr/front/kor/life/lifeSarang.do?tab=7'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://hanok.seoul.go.kr/front/index.do</a>', '북촌은 조선시대에 조성된 양반층 주거지로서 1920년대까지 그다지 큰 변화가 없었는데, 1930년대에 서울의 행정구역이 확장되고, 도시구조도 근대적으로 변형되면서 변화가 일어났다. 주택경영회사들이 북촌의 대형 필지와 임야를 매입하여, 그 자리에 중소 규모의 한옥들을 집단적으로 건설하였는데, 현재 한옥들이 밀집되어있는 가회동 11번지와 31번지, 삼청동 35번지, 계동 135번지의 한옥주거지들이 모두 이 시기에 형성되었다.북촌 한옥청(聽)은 가회동 11번지 언덕에 위치한 공공한옥으로, 북촌에서는 보기 드문 120평 규모의 꽤 넓은 한옥이다. 이곳은 안채, 문간채, 행랑채, 앞마당과 대관이 가능한 전시실, 세미나실로 구성되어 있다.이곳은 시민이 스스로 만들고 함께 누리는 한옥문화콘텐츠의 장으로, 전시를 중심으로 한 연계 강연·공연·포럼 등 각종 시민 교육문화 활동이 펼쳐질 수 있도록 개방하는 공유한옥의 개념을 갖는다. 지하철 3호선 안국역 2번출구에서 재동초등학교 방향 도보 12분 거리에 위치하고 있다. *서울한옥포털', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56935', '3056278', '북촌전망대', 'http://tong.visitkorea.or.kr/cms/resource/39/3056239_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/39/3056239_image3_1.jpg', '6', '37.58313295', '126.9828007145', '', '서울특별시 종로구 북촌로11다길 22-3 (삼청동)', '', '', '북촌전망대는 북촌한옥마을 언덕에 자리한 빌라 옥탑을 개조하여 만든 사설 전망대이다. 북촌마을 골목 안에 있는 건물의 계단을 올라가다 보면 북촌전망대라는 안내와 함께 전통적인 분위기의 공간이 있다. 입장료를 내면 유리잔에 음료가 나온다. 음료는 커피, 차, 오렌지 주스가 마련되어 있다. 햇살이 비치는 통유리 창가에서 음료를 즐기며 북촌한옥마을의 전경을 감상할 수 있다. 또한 테이블에 놓여있는 종이에는 북촌전망대에서 조망할 수 있는 뷰 포인트가 설명되어 있으며, 앉은 자리에서 바로 남산타워가 보이고 멀리 있는 롯데타워에서부터 인왕산까지 조망할 수 있다. 이 밖에도 저녁 7시부터는 데이트, 파티, 회의 등의 장소 대여도 가능해서 특별한 날에 이벤트 장소로도 활용할 수 있다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56936', '2993183', '북촌전통공예체험관', '', '', '6', '37.5825469653', '126.9860532124', '02-741-2148', '서울특별시 종로구 북촌로12길 24-5', '', '<a href=''https://tour.jongno.go.kr/tour/main/contents.do?menuNo=110544'' target=''_blank'' title=''새창 : 종로 문화역사관광 홈페이지로 이동''>https://tour.jongno.go.kr</a>', '북촌을 방문하는 국내외 관광객들에게 한국 전통공예의 우수성을 알리기 위해 마련한 공간으로 체험장, 전시장, 교육장으로 구성되어 있다. 북촌 공방 장인들이 직접 40여 개의 전통공예 프로그램을 진행하기 때문에 한층 진지하게 전통공예 작품을 만나고 만들어 보는 특별한 체험이 가능하다. 시간과 요일에 따라 체험할 수 있는 공예 작품들이 다르니 전화로 만들고 싶은 공예 작품 시간을 미리 확인하는 것이 좋다. 아울러 개인 관람객은 별도 예약 없이 이용 가능하지만, 단체일 경우에는 최소 3일 전까지 전화로 예약 신청을 해야 하므로 주의해야 한다. 프로그램에 따라 소요 시간이 다른 만큼 사전에 시간을 알아보는 것도 일정을 짜는 데 도움이 된다. 전통공예 체험은 진행 프로그램이 조금씩 달라 여러 번 방문해서 하나씩 만들어 보는 재미도 있다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56937', '3085314', '북촌전통발효공방 빚담', 'http://tong.visitkorea.or.kr/cms/resource/46/3084046_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/46/3084046_image3_1.JPG', '6', '37.5824025279', '126.9855171542', '', '서울특별시 종로구 북촌로12길 5-12 (가회동)', '', '<a href=''https://bukchonbitdam.modoo.at/'' target=''_blank'' title=''새창 : 홈페이지로 이동''>https://bukchonbitdam.modoo.at/</a>', '북촌한옥마을에 위치한 빚담은 누룩을 이용한 발효를 체험할 수 있는 한옥 공방이다. 각종 요리경연대회 전통주 부문에서 다수 수상하고 수년간 전시 경험이 있는 빚담 대표가 공방을 운영하고 있다. 체험 프로그램으로는 누룩 만들기, 알메주 만들기, 저염누룩소금 만들기, 효소 만들기, 계절 전통주 체험, 저염 주박 장아찌 만들기 등이 준비되어 있으며, 전통 누룩 발효에 대한 관심이 있어 더 자세히 배우고 싶은 경우, 누룩 이론과 실습, 제철 재료 손질 및 활용법, 계절에 맞춰 전통주 실습과 이론 등을 배울 수 있는 3개월 정규 과정이 있으므로 상담 후 참여하면 된다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56938', '126537', '북촌한옥마을', 'http://tong.visitkorea.or.kr/cms/resource/04/3304404_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/04/3304404_image3_1.jpg', '6', '37.5790529392', '126.9867060298', '', '서울특별시 종로구 계동길 37 (계동)', '', '<a href=''https://hanok.seoul.go.kr/front/kor/town/town01.do'' target=''_blank'' title=''새창 : 서울한옥포털 홈페이지로 이동''>http://hanok.seoul.go.kr/</a>', '북촌은 경복궁과 창덕궁, 종묘 사이에 위치한 곳으로 전통한옥이 밀집되어 있는 서울의 대표적인 전통 주거지역이다. 그리고 많은 사적들과 문화재, 민속자료가 있어 도심 속의 거리 박물관이라 불리어지는 곳이기도 하다. 청계천과 종로의 윗동네라는 뜻에서 ‘북촌(North Village)’이라고 이름으로 불리었으며, 가회동과 송현동, 안국동, 그리고 삼청동이 있다. 사간동과 계동, 소격동 그리고 재동에는 역사의 흔적이 동네이름으로 남아 수백 년을 지켜온 곳이기도 하다. 북촌은 조선시대에 조성된 양반층 주거지로서 1920년대까지 그다지 큰 변화가 없었는데, 1930년대에 서울의 행정구역이 확장되고, 도시구조도 근대적으로 변형되면서 변화가 일어났다. 주택경영회사들이 북촌의 대형 필지와 임야를 매입하여, 그 자리에 중소 규모의 한옥들을 집단적으로 건설하였는데, 현재 한옥들이 밀집되어 있는 가회동 11번지와 31번지, 삼청동 35번지, 계동 135번지의 한옥주거지들이 모두 이 시기에 형성되었다. 대청에 유리문을 달고, 처마에 잇대어 함석 챙을 다는 등, 새로운 재료를 사용한 북촌의 한옥은 전통적인 한옥이 갖고 있는 유형적 성격을 잃지 않으면서, 근대적인 도시조직에 적응하여 새로운 도시주택유형으로 진화했다는 점에 주목할 수 있다. 북촌 지역이 모두 한옥으로 이루어져 있던 1960년대와 달리, 1990년대 이후 급속하게 들어선 다세대가구 주택 때문에 많은 수의 한옥이 사라졌지만, 일부지역은 양호한 한옥들이 군집을 이룬 채 많이 남아 있다. 여러 채의 한옥이 지붕처마를 잇대고 벽과 벽을 이웃과 함께 사용하고 있는 풍경은 우리가 잊고 살았던 따뜻한 정과 살아갈 맛을 느끼게 해 준다. 북촌 지역을 걷다 보면 이어진 처마선의 아름다운만큼이나 골목길의 정겨움을 느낄 수 있다. 북촌한옥마을이 지속가능한 관광지가 될 수 있도록 침묵관광을 하도록 주의를 요한다.※ 침묵관광이란 외부 관광객들의 관광지 방문으로 인해 주민들의 생활권과 환경권이 침해받지 않도록 큰 소리로 떠들지 않고 조용히 여행하는 관광형태를 말한다.※ <strong>북촌마을 방문 시 지켜야 할 ‘에티켓’ </strong>- 단체관광객 방문 시 반드시 가이드 동행- 관광버스 불법주차 금지- 무단 침입, 무단 촬영, 무단 투기, 노상방뇨, 소음 금지- 마을 방문시간 준수', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56939', '2946075', '북촌한옥마을 감고당길', 'http://tong.visitkorea.or.kr/cms/resource/07/2947307_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/07/2947307_image3_1.jpg', '6', '37.5779529898', '126.9824147854', '', '서울특별시 종로구 송현동', '34-2', '<a href=''https://tour.jongno.go.kr/tour/main/contents.do?menuId=400132&menuNo=400132''target=''_blank'' title=''새창 : 종로구 이동''>https://tour.jongno.go.kr/</a>', '감고당길은 아기자기한 카페와 갤러리가 자리한 골목이다. ‘감고당’의 뜻은 숙종의 계비였던 인현왕후의 사저를 의미하고 이 길 중간에 있는 덕성여고 자리에 감고당터가 있어 ‘감고당길’이라 불리게 되었다. 북촌한옥마을 서울공예박물관 뒤쪽으로 풍문여고에서 정독도서관까지 약 440m 정도 이어져 있다. 아늑한 길에 예쁜 카페와 갤러리가 많고 공예 마켓 등이 열리기도 해 구석구석 둘러볼 것이 풍성하다. 또한 좁은 기와 담벼락과 돌담은 사진 찍기 좋은 배경이 되어 인기 있는 포토존이며 데이트 장소로 안성맞춤인 곳이다. 그리고 2018년도 tvn방영 드라마인 ‘도깨비’의 촬영지로 김신(공유)과 지은탁(김고은) 이 서로 스치며 처음 만나게 되는 장면부터 여러 포인트가 담겼던 멋있는 장소이다.', '1', '23', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56940', '3339374', '북파크라운지', 'http://tong.visitkorea.or.kr/cms/resource/41/3339341_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/41/3339341_image3_1.jpg', '6', '37.5409243201', '127.0025028924', '', '서울특별시 용산구 이태원로 294 (한남동)', '블루스퀘어 3층', '<a href=''https://www.instagram.com/bookparklounge_official/'' target=''_blank'' title=''새창 : 북파크라운지 인스타그램으로 이동''>https://www.instagram.com/bookparklounge_official</a>', '뮤지컬 전문 공연장인 블루스퀘어 신한카드홀 3층에 위치한 프리미엄 북카페이다. 3,000여 권의 전문가의 큐레이션 도서들로 가득하며, 감각적인 카페 메뉴가 어우러진 특별한 공간이다. 편안하고 다양한 좌석들로 이루어진 곳에서 독서와 휴식을 동시에 즐길 수 있다. 이용 시간 내 자유로운 입퇴장이 가능하여 나만의 서재처럼 이용할 수 있다.', '1', '21', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56941', '2783363', '북한산 백운대(우이동)', 'http://tong.visitkorea.or.kr/cms/resource/83/2795083_image2_1.jpeg', 'http://tong.visitkorea.or.kr/cms/resource/83/2795083_image2_1.jpeg', '6', '37.6583104651', '126.9909450591', '', '서울특별시 강북구 도선사길 234 (우이동)', '', '', '서울의 진산으로 불리는 북한산은 해발 836m의 백운대 정상에서 바라보는 풍광이 그야말로 장관이다. 백운대를 오르는 방법은 다양한데, 그중에서도 북한산우이역에서 출발하는 코스는 등산이 처음이거나 익숙하지 않은 이들에게 추천한다. 도선사 입구에서 하루재와 인수봉 주변, 북한산성 백운대 암문을 거쳐 백운대에 오르는 코스로 길이도 가장 짧고 난도도 낮은 편이다. 총 소요 시간은 2~3시간 정도이다. 백운대 방향으로 천천히 걷다 보면 백제 온조왕이 형 비류와 함께 올라 나라의 도읍을 정했다고 하는 인수봉이 눈에 들어온다. 이때부터 가파른 경사와 로프를 잡고 이동해야 하는 바위 코스가 시작된다. 북한산성의 가장 높은 곳에 자리한 백운대 암문에 도착하면 정상까지 300m 정도 남겨둔 셈이다. 백운대 정상에는 널찍한 바위가 자리해 땀을 식히며 탁 트인 주변 경관을 즐기기에 더없이 좋다. 또한 암릉구간이 많아 등산화 착용을 권장한다.', '1', '3', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56942', '3407173', '북한산 생태탐방원', 'http://tong.visitkorea.or.kr/cms/resource/53/3405153_image2_1.JPG', 'http://tong.visitkorea.or.kr/cms/resource/53/3405153_image3_1.JPG', '6', '37.6871579053', '127.0351735342', '', '서울특별시 도봉구 도봉산길 86 (도봉동)', '', '<a href=''https://reservation.knps.or.kr/contents/E/serviceGuide.do?deptId=B971002''target=''_blank'' title=''새창: 국립공원공단 공식 홈페이지로 이동''>https://reservation.knps.or.kr</a>', '북한산생태탐방원은 도심 속 자연공원인 북한산국립공원 내에 위치한 국립공원 제1호 생태탐방원이다. 북한산권의 우수한 자연 생태계 및 문화자원을 바탕으로 다양한 자연생태관광 및 치유, 힐링 프로그램을 제공하고 있다. 또한 미래세대를 위한 환경교육과 함께 자연환경해설사, 지질공원해설사 등 환경전문가 양성교육을 운영하는 생태체험과 환경교육 전문기관이다. 자체 행사 및 교육 등을 위한 강당, 강의실, 생활관 등의 시설대여가 가능하다.', '1', '10', '12');
INSERT INTO `attractions` (`id`, `content_id`, `title`, `first_image1`, `first_image2`, `map_level`, `latitude`, `longitude`, `tel`, `addr1`, `addr2`, `homepage`, `overview`, `sido_code`, `sigungu_code`, `content_type_id`) VALUES ('56943', '2779558', '북한산 족두리봉', 'http://tong.visitkorea.or.kr/cms/resource/13/2796513_image2_1.jpg', 'http://tong.visitkorea.or.kr/cms/resource/13/2796513_image2_1.jpg', '6', '37.6183531539', '126.9437143919', '', '서울특별시 은평구 불광동', '산 42-1', '', '서울의 진산인 북한산은 은평구와 성북구, 강북구, 도봉구와 경기도 의정부시, 양주시, 고양시에 걸쳐 있다. 최고봉인 백운대와 동쪽의 인수봉, 남쪽의 만경대가 뻗어 내린 화려한 능선이 일 년 내내 등산객들의 발길을 끌어모은다. 족두리봉은 북한산 가장 남서쪽에 솟은 높이 370m의 봉우리다. 멀리서 보면 봉우리 모양이 족두리를 쓴 것처럼 보인다고 해서 붙은 이름인데, 한편으로는 독수리의 머리처럼 보인다고 하여 수리봉이라고도 불리며, 인수봉을 그대로 옮겨놓은 듯하다고 하여 작은 인수봉으로도 불린다. 불광역에서 출발하면 약 50분이면 족두리봉에 오를 수 있다. 시간은 짧지만, 난도는 조금 높다. 급경사의 계단은 물론 위험한 바위 구간이 있어 주의가 필요하다.', '1', '22', '12');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('1', '0', 'T_CRS_MNG0000005116', 'T_ROUTE_MNG0000000001', '남파랑길 1코스', '19.0', '420.0', '2', '0', '갈맷길 3-1구간, 3-2구간과 중첩되는 구간으로서 해파랑길 시종점인 오륙도 해맞이 공원에서부터 부산 중구 부산대교까지 이어지는 구간이다.  신선이 노닐던 신선대 및 부산항의 역동적인 파노라마을 만끽할 수 있는 구간이며 세계에서 하나뿐인 UN기념공원 및 부산박물관, 영화 “친구”로 유명한 부산 일대의 명소를 함께 체험할 수 있다.  아름다운 해안경관과 우리나라 제1의 항구도시 부산의 매력을 느낄 수 있는 구간이다.', '- 해파랑길 시종점인 오륙도 해맞이 공원에서부터 부산 중구 부산대교까지 이어지는 구간<br>- 신선이 노닐던 신선대 및 부산항의 역동적인 파노라마를 만끽할 수 있는 구간으로 세계에서 하나뿐인 UN기념공원 및 부산박물관, 영화 “친구”로 유명한 부산 일대의 명소를 함께 체험할 수 있는 코스<br>- 아름다운 해안경관과 우리나라 제1의 항구도시 부산의 매력을 느낄 수 있는 구간<br>- 부산 갈맷길 3-1, 3-2코스가 중첩됨', '- 시점 : 오륙도해맞이공원 (부산 남구 오륙도로 137) <br>교통편) 경성대부경대역 24번 버스, 오륙도스카이워크 하차<br>- 종점 : 부산역 (부산 동구 중앙대로 210) <br>교통편) 부산지하철 1호선 부산역<br>- 도시 구간을 지나는 코스이므로 화장실, 매점 등 편의시설 이용이 용이함 <br>- 재한유엔기념공원 운영시간 10월~4월 09:00~17:00 / 5월~9월 09:00~18:00 <br>* 재한유엔기념공원 입장시간은 운영시간 30분 전까지 가능합니다. 운영시간 외에는 우회하여 이용하시기 바랍니다.', '6', '4', '0', '2020-02-16 02:41:53', '2025-04-23 06:52:30', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('2', '0', 'T_CRS_MNG0000005117', 'T_ROUTE_MNG0000000001', '남파랑길 2코스', '19.0', '450.0', '2', '0', '갈맷길 3-3구간과 중첩되는 구간으로 부산대교에서 영도구로 진입하여 영도대교로 돌아나오는 구간이다.  영도구에 조성되어 있는 봉래산 둘레길 및 태종대 일원을 걷는 구간이 포함되어 있어 숲길과 바닷길, 마을길 등을 다양하게 접할 수 있는 매력을 보유하고 있다. 6.25전쟁 때 피난민의 추억과 애환이 서린 부산 최초의 연육교인 영도대교를 포하하여 태종대의 절경을 감상할 수 있으며, 영도구 원도심 개발에 따라 핫 스페이스로 떠오르고 있는 깡깡이 예술마을, 흰여울 마을 등이 인접하고 있어 매력적인 걷기여행이 가능한 구간이다. ', '- 부산역에서 시작하여 걷기 좋은 봉래산을 지나 흰여울문화마을로 이어지는 코스 <br>- 영도구에 조성되어 있는 봉래산 둘레길 및 태종대 일원을 걷는 구간이 포함되어 있어 숲길과 바닷길, 마을길 등을 다양하게 접할 수 있는 매력을 보유하고 있는 구간<br>- 6.25전쟁 때 피난민의 추억과 애환이 서린 부산 최초의 연륙교인 영도대교를 포하하여 영도구 원도심 개발에 따라 핫 스페이스로 떠오르고 있는 깡깡이 예술마을, 흰여울 마을 등이 인접하고 있어 매력적인 걷기여행이 가능한 코스<br>- 부산의 갈맷길 3-3코스가 포함된 구간으로 초보자에게도 어렵지 않은 코스', '- 시점 : 부산역 (부산 동구 중앙대로 210) <br>교통편) 부산지하철 1호선 부산역<br>- 종점 : 영도대교 입구(부산 중구 태종로 8) <br>교통편) 지하철 남포역 하차 후 도보 이동<br>- 길이 어렵지 않아 편히 걸을 수 있는 길이며 주변에 편의시설이 많아 부담이 없음 ', '6', '5', '0', '2020-02-16 03:10:49', '2024-10-16 04:49:35', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('3', '0', 'T_CRS_MNG0000005118', 'T_ROUTE_MNG0000000001', '남파랑길 3코스', '15.0', '330.0', '2', '0', '갈맷길 4-1구간과 중첩되는 구간으로 최근 부산에서 가장 떠오르는 관광명소인 송도해상케이블카, 송도해수욕장, 암남공원, 감천항까지 이어지는 구간이다. <br>구간내 입지한 송도해수욕장은 우리나라 제1호 해수욕장으로 동양의 나폴리라고 칭송받을 만큼 아름다운 해안선이 유명하며 송도해상케이블카는 해상을 지나는 케이블카로 최근 많은 관광객들이 방문하고 있다. ', '- 부산의 가장 떠오르는 관광명소인 송도해상케이블카와 송도해수욕장, 암남공원, 감천항까지 이어지는 코스<br>- 부산의 유명 관광지들을 둘러볼 수 있는 다채로운 코스이자 동양의 나폴리라 불리울 만큼 바다와 자연을 어우르는 아름다운 해안경관을 감상할 수 있는 구간<br>- 부산 갈맷길 4-1코스가 포함되어 있는 코스', '- 시점 : 영도대교 입구(부산 중구 태종로 8)<br>교통편) 지하철 남포역 6번출구 도보 이동<br>- 종점 : 감천사거리(부산 사하구 감천동 447-6)<br>교통편) 부산역 17번 버스, 감천우체국 하차 <br>- 영도대교에서 출발하므로 교통편 편리하며 부산역에서 지하철, 버스편 다수 있음 <br>- 국제시장, 자갈치 충무동, 송도해수욕장, 암남공원 등에 식당, 카페 다수 있어 이용 편리함<br>- 영도대교 도개시간은 오후 2시부터 2시15분까지로 다리가 열리는 모습을 볼 수 있음 ', '6', '15', '0', '2020-02-16 03:16:59', '2025-05-19 06:26:56', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('4', '0', 'T_CRS_MNG0000005119', 'T_ROUTE_MNG0000000001', '남파랑길 4코스', '22.0', '450.0', '2', '0', '갈맷길 4-2, 4-3구간 중첩되는 구간으로 몰운대, 다대포해변공원, 아미산전망대를 거쳐 사하구 신평동 교차로까지 이어지는 구간이다.  기암괴석과 해송으로 우거진 숲, 수려한 모래해안으로 빼어난 겨관을 자랑하고, 국가지질공원에 포함된 몰운대를 포함하여 낙동강 하구를 조망할 수 있는 아미산 전망대는 일몰 명소로 각광받는 부산의 새로운 관광명소로 자리잡아가고 있다.  다대포 해변공원에는 화려한 조명과 음악과 함껙 작동되는 꿈의낙조 분수가 있어 낭만적인 야간관광 명소로서의 특징을 보유하고 있다.  ', '- 몰운대, 다대포 해변공원, 아미산 전망대를 거쳐 사하구까지 이어지는 구간으로 국가지질공원으로 지정된 주요 자원을 두루 살펴볼 수 있는 코스<br>- 기암괴석과 해송으로 우거진 숲, 수려한 모래해안으로 빼어난 경관을 자랑하고, 국가지질공원에 포함된 몰운대와 일몰 명소 아미산 전망대가 포함된 부산의 각광받는 관광명소<br>- 다대포항을 지나면서 어촌 골목 곳곳을 구경할 수 있으며 해송이 우거진 숲길이 있는 몰운대가 있어 걷기 좋은 최고의 코스<br>- 부산의 갈맷길 4-1, 4-2코스가 포함된 코스', '- 시점 : 감천사거리(부산 사하구 감천동 449-2) <br>교통편) 부산역 17번 버스, 감천우체국 하차<br>- 종점 : 사하구 신평동교차로(부산 사하구 신평동 642-17)<br> 교통편) 지하철1호선 신평역 도보 이동<br>- 시내에 있으나 산길과 돌길이 많으므로 등산화 혹은 러닝화를 착용하는 것이 좋음', '6', '10', '0', '2020-02-16 03:21:54', '2024-11-27 11:09:49', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('5', '0', 'T_CRS_MNG0000005120', 'T_ROUTE_MNG0000000001', '남파랑길 5코스', '22.0', '420.0', '1', '0', '갈맷길 5-1구간 중첩되는 구간으로 낙동강하굿둑과 을숙도를 지나 송정공원가지 이어지는 구간이다. <br>낙동강하구둑 전망대와 을숙도대교 전망대를 통해 낙동강 하구의 매력을 조망할 수 있으며, 녹산국가산업단지를 지나 부산싱항만의 위용을 확인하며 걸을 수 있는 구간이다. 부산 갈맷길 5구간이 가덕도까지를 연결하는 구간인 것에 비해, 코리아둘레길 노선은 가덕도를 거치지 않고 바로 창원으로 연결되는 구간으로서의 특징을 보유한다.  다만, 가덕도 구간을 함께 연계하여 걸을 수 있다.', '- 낙동강 하굿둑과 을숙도를 지나 부산 강서구의 송정공원까지 이어지는 구간으로 낙동강 하구의 독특한 생태환경을 둘러볼 수 있는 코스<br>- 낙동강하구둑 전망대와 을숙도대교 전망대를 통해 낙동강 하구의 매력을 조망할 수 있으며, 녹산국가산업단지를 지나 부산싱항만의 위용을 확인하며 걸을 수 있는 구간<br>- 완만한 산책길로 경치가 아름다운 걷기 좋은 코스<br>- 갈맷길 5-1구간 중첩되는 구간', '- 시점 : 사하구 신평동교차로(부산 사하구 신평동 642-17)<br>교통편) 지하철1호선 신평역 도보 이동<br>- 종점 : 강서구 송정공원(부산 강서구 송정동 1476)<br>교통편) 하단역 9-1 버스, 용원사거리 하차<br>- 중간에 식수나 이용할 매점이 거의 없으므로 사전에 준비하는 것이 좋음.', '6', '10', '0', '2020-02-16 03:25:47', '2024-10-16 04:54:44', '300');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('6', '0', 'T_CRS_MNG0000005121', 'T_ROUTE_MNG0000000001', '남파랑길 6코스', '15.0', '300.0', '1', '0', '부산 강서구 송정공원에서부터 창원 진해구 제덕사거리까지 이어지는 구간으로 부산진해 경제자유구역을 거쳐, 최근 부산~창원 시민에게 사랑받고 있는 용원 어시장 및 웅천왜성, 웅천읍성 등을 지나는 구간이다.  우리나라 기독교에서 가장 존경받는 주기철목사 기념관과 웅천읍성을 탐방할 수 있어 먹거리, 배울거리가 있는 코스이다. ', '- 부산 강서구 송정공원에서부터 창원 진해구 제덕사거리까지 이어지는 구간<br>- 영화와 한류의 도시 창원의 시작으로 대도시와 자연의 반전 매력을 보유한 길<br>- 부산진해 경제자유구역을 거쳐 최근 부산 및 창원시민의 사랑을 받고 있는 용원 어시장과 웅천왜성, 웅천읍성 등을 경유하는 코스<br>- 우리나라 기독교에서 가장 존경받는 주기철목사 기념관과 웅천읍성을 탐방할 수 있어 먹거리, 배울거리가 있는 코스', '- 시점 : 강서구 송정공원(부산 강서구 송정동 1476)<br>교통편) 하단역 9-1번 버스 이용 후 용원사거리 정류장 하차 <br>- 종점 : 진해구 제덕사거리(경남 창원시 진해구 제덕동 865-3)<br>교통편) 진해시외버스터미널 앞 303번 버스, 제덕만택지 하차<br>- 차로를 따라 이동해야 하는 구간도 있으나 흰돌메공원 가는 길은 데크로 되어 있어 걷기 편함', '6', '1', '0', '2020-02-16 03:32:33', '2024-10-16 04:55:55', '300');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('7', '0', 'T_CRS_MNG0000005122', 'T_ROUTE_MNG0000000001', '남파랑길 7코스', '11.0', '240.0', '1', '0', '창원의 아름다운 해양경관을 살펴볼 수 있는 구간으로 창원의 대표 관광자원 중 하나인 창원해양공원을 지나는 구간이다.  길을 따라 걸으며 창원 앞바다의 섬 경관을 살펴볼 수 있으며, 바다를 배경으로 철길과 함께하는 행암마을 등을 함께 둘러볼 수 있다.  창원시가 기 조성한 진해 바다 70리길 중 일부 구간과 중첩되는 구간이다.', '- 창원의 아름다운 해양경관을 살펴볼 수 있는 구간으로 창원의 대표 관광자원 중 하나인 창원해양공원을 지나는 구간<br>- 길을 따라 걸으며 창원 앞바다의 섬 경관을 살펴볼 수 있으며, 바다를 배경으로 철길과 함께하는 행암마을 등을 함께 둘러볼 수 있는 길 <br>- 제덕선착장에서 해안가를 따라 삼포 가는 길, 행암항, 수치해변 등의 관광지와 바닷가 마을을 이동하여 상리마을까지 가는 코스<br>- 창원시가 조성한 진해바다 70리길 중 일부 구간이 중첩되어 있음', '- 시점 : 진해구 제덕사거리(창원시 진해구 명제로486번길 33)<br>교통편) 진해시외버스터미널 앞 303번 버스, 제덕만택지 하차<br>- 종점 : 진해구 상리마을입구(창원시 진해구 진해대로1167번길 4)<br> 교통편) 진해시외버스터미널 앞 303번 버스, 상리마을 하차<br>- 보행자와 자전거길이 차도와 따로 구분되어 있어 안전하게 이용가능함', '36', '16', '0', '2020-02-16 03:36:08', '2024-10-16 04:57:21', '300');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('8', '0', 'T_CRS_MNG0000005123', 'T_ROUTE_MNG0000000001', '남파랑길 8코스', '16.0', '390.0', '3', '0', '창원시 전역에 조성된 창원둘레길 중 진해드림로드 1구간, 2구간 구간이 중첩되는 구간으로 해안경관을 따라 걸어오던 길이 본격적으로 숲길로 변경되는 구간이다. <br>봄이면 온통 벚꽃길로 변화하는 길을 따라 걷다보면 아름다운 해안경관과 창원시의 도시경관을 두루 바라볼 수 있다. 전체 구간이 임도로 구성되어 걷기에 어렵지 않으며, 계졀에 따라 다양한 분위기를 연출하는 숲길로 매력적인 구간이다. ', '- 상리마을 입구 천자봉부터 웅산, 불모산 둘레를 따라 장복산 진해드림로드 입구까지 가는 코스<br>- 전체 구간이 임도로 구성되어 걷기에 어렵지 않으며, 계졀에 따라 다양한 분위기를 연출하는 숲길로 매력적인 구간<br>- 천자봉부터 장복산까지 산 둘레를 따라 임도를 걸으며 아름다운 해안경관과 창원시의 도시경관을 두루 바라보며 걸을 수 있는 코스<br>- 코스내 해병대훈련테마쉼터, 목재문화체험장, 황토길, 편백숲 등 체험시설이 풍부한 코스<br>- 창원시 전역에 조성된 창원둘레길 중 진해드림로드 1구간, 2구간 구간이 중첩되는 구간', '- 시점 : 진해구 상리마을입구(창원시 진해구 진해대로1167번길 4) <br> 교통편) 진해시외버스터미널 앞 303번 버스, 상리마을 하차<br>- 종점 : 진해드림로드 입구(창원시 진해구 진해대로 277)<br> 교통편) 진해시외버스터미널 앞 160번 버스, 여좌검문소 하차<br>- 대중교통 이용이 용이하여 접근성이 좋음', '36', '16', '0', '2020-02-16 03:40:02', '2024-10-16 04:58:48', '900');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('9', '0', 'T_CRS_MNG0000005124', 'T_ROUTE_MNG0000000001', '남파랑길 9코스', '15.0', '330.0', '2', '0', '창원둘레길에 포함된 진해드림로드 1코스가 중첩되는 구간으로 장복산 조각공원 및 편백림 구간 등의 아름다운 숲길구간을 거쳐 마산국자유산업단지를 거쳐 마산항까지 이르는 구간이다. 창원 8코스의 진해드림로드 코스가 계속 이어지는 구간으로 천자봉~장복산 일원의 잘 닦여진 임도를 따라 계절에 따라 시시각각 변화하는 숲길을 걸을 수 있으며, 코스 종점부에는 옛 마산의 도심을 걸을 수 있다. 마산 도심 구간은 원도심 개발과 함께 명소로 부각되고 있는 가고파 꼬부랑길 벽화마을 및 문신미술관, 창원시립미술관 등의 관광자원을 둘러볼 수 있으며, 마산만의 수려한 경관을 살펴볼 수 있다. ', '- 진해드림로드에서 마산항까지 이어지는 구간으로 숲길, 도심길, 해안길 등 다양한 걷기여행길을 만날 수 있는 코스 <br>- 추산근린공원, 문신미술관, 창원시립마산박물관 등 마산 원도심의 주요 자원을 경유하며, 가고파 꼬부랑 벽화마을 등을 다양한 관광 포인트들을 연계하여 즐기며 걸을 수 있는 코스<br>- 웅남동, 봉암교, 마산항제3부두를 지나 노산동, 임항선 그린웨이를 따라 걸어서 마산항 입구까지 가는 코스', '- 시점 : 진해드림로드 입구(창원시 진해구 진해대로 277)<br> 교통편) 진해시외버스터미널 앞 160번 버스, 여좌검문소 하차<br>- 종점 : 마산함포구 마산항입구(창원시 마산합포구 월포동 2-120)<br> 교통편) 마산고속버스터미널 703번 버스, 마산합포구청.의료원 하차<br>- 초반 길은 차도를 걷는 구간이 많아 위험할 수 있으므로 주의해야 함', '36', '16', '0', '2020-02-16 03:47:10', '2024-10-16 05:00:05', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('10', '0', 'T_CRS_MNG0000005125', 'T_ROUTE_MNG0000000001', '남파랑길 10코스', '16.0', '360.0', '3', '0', '마산항을 따라 우리나라 최초의 해상유원지인 돝섬 해상유원지와 마창대교를 바라보며 걸을 수 있는 길이자, 창원시가 핵심사업으로 추진 중인 마산해양신도시의 공사 현장을 살펴볼 수 있는 구간임.  마산항을 지나면 천마산 등산로를 따라 마산합포구 구산면 구서분교까지 이어지는 길이다.  코스 내에 일부 걷기여행 안전성이 떨어지는 도로구간이 포함되어 있어 걷기여행시 주의가 필요하다. ', '- 마산항 일원을 따라 걷는 구간으로 우리나라 최초의 해상유원지인 돝섬 해상유원지와 마창대교를 바라보며 걸을 수 있으며, 마산항 일원을 거쳐 천마산 일원의 등산로 구간으로 연결됨  <br>- 월영마을을 지나 청량산 산길을 걷는 코스는 마산시내를 내려다 볼 수 있고 마창대교도 볼수 있는 걷기 좋은 길<br>- 코스 내에 일부 걷기여행 안전성이 떨어지는 도로구간이 포함되어 있어 걷기여행시 주의가 필요', '- 시점 : 마산합포구 마산항 입구(창원시 마산합포구 월포동 2-120)<br>교통편) 마산고속버스터미널 703번 버스, 마산합포구청.의료원 하차<br>- 종점 : 마산합포구 구서분교 앞 사거리(창원시 마산합포구 구산면 해양관광로 72)<br>교통편) 마산역 65번 버스, 마전 하차<br>- 차도를 지나는 구간이 많아 보행자의 주의하며 안전성이 미흡한 구간이 있어 여행자의 주의가 요구됨 ', '36', '16', '0', '2020-02-16 03:51:09', '2024-10-16 05:01:33', '900');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('11', '0', 'T_CRS_MNG0000005126', 'T_ROUTE_MNG0000000001', '남파랑길 11코스', '16.0', '330.0', '2', '0', '구산면 구서분교 앞 사거리에서 시작하여 진전면 호산마을회관까지 이어지는 구간으로 대부분의 걷기여행길이 해안길을 따라 마을과 항구를 지나는 구간으로 이루어져 있다. 구간 내에 제말장군묘, 광암함, 고현어촌테마마을 등의 자원이 분포하고 있다. ', '- 구산면 구서분교 앞에서 진전면 호산마을까지 이어지는 구간으로 해안길을 따라 작은 항구와 해안마을을 감상하며 호젓하게 걸을 수 있는 코스 <br>- 광암항과 고현 어촌테마마을 등의 자원을 체험할 수 있으며, 최근 재개장한 창원시의 유일한 해수욕장인 광암해수욕장이 분포함<br>- 아름다운 진해 바다를 보며 걸을 수 있는 코스 ', '- 시점 : 마산합포구 구서분교 앞 사거리(창원시 마산합포구 구산면 해양관광로 721)<br>교통편) 마산역 65번 버스, 마전 하차<br>- 종점 : 암아교차로(창원시 마산합포구 진전면 근곡리 98-35)<br>교통편) 마산역 77번 버스, 진전신기 하차<br>- 광암해수욕장 인근 편의시설을 이용할 수 있음<br>- 바닷가 길을 따라 걷는 코스나 차도 위주로 되어 있어 위험한 구간이 있음', '36', '16', '0', '2020-02-16 03:54:50', '2025-05-19 05:12:52', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('12', '0', 'T_CRS_MNG0000005127', 'T_ROUTE_MNG0000000001', '남파랑길 12코스', '18.0', '390.0', '2', '0', '창원과 고성을 연결하는 구간으로 창원 호산마을회관에서 고성 회화면 배둔시외버스터미널까지 연결되는 구간이다. 고성군의 대표 관광자원인 당항포 관광지 및 경남고성 세계 엑스포 공원을 지나는 구간으로 걷기여행시 해당 자원을 연계하여 둘러볼 수  있다. 구간 내에 해안길, 숲길, 마을길 다양한 유형의 걷기여행길이 포함되어 있으며, 종점의 배둔시외버스터미널을 통해 타 지역으로의 이동이 용이한 특성을 보유하고 있다. ', '- 창원 진전면에서 고성군으로 이어지는 구간으로 고성의 대표 관광자원인 당항포 관광지와 고성 세계엑스포 공원 등의 자원을 경유하는 코스<br>- 산길과 바닷길, 마을길 등 다양한 테마의 걷기길을 체험할 수 있으며, 초보자도 걸을 수 있는 평이한 코스', '- 시점 : 암아교차로<br>교통편) 마산역 77번 버스, 진전신기 하차<br>- 종점 : 회화면 배둔시외버스터미널 앞<br>교통편) 고성여객자동차터미널 배둔시외버스터미널 경유 시외버스<br>- 코스 초입 호산마을 인근은 사유지화 되어서 길을 찾기 어려울 수 있으니 이동 시 확인 필요함', '36', '16', '0', '2020-02-16 03:59:00', '2025-05-19 05:14:12', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('13', '0', 'T_CRS_MNG0000005128', 'T_ROUTE_MNG0000000001', '남파랑길 13코스', '21.0', '450.0', '2', '0', '고성과 통영시가 연계되는 구간으로 고성 회화면 배둔시외버스터미널과 통영시의 황리사거리를 연결하는 구간으로 이루어져 있다. 넓은 들판을 지나는 구간이 특히 많은 구간으로 구간 내에 마동호 공사현장 및 통영시의 조선산업 현장을 일부 지나는 구간 등이 포함되어 있다. 구간 내 일부 구간에 도로 구간이 포함되어 있어 걷기여행시 안전에 유의하여야 하며, 마을을 지나는 구간 등도 일부 정비 등이 필요한 구간이다. ', '- 배둔시외버스터미널에서 화당마을을 지나 베니키아센트럴호텔로 이어져 고성과 통영을 모두 거치는 코스<br>- 걷기길 대부분이 해안가를 따라 이어져 있어 경치를 감상하기 좋음<br>- 바다와 산, 논과 밭이 어우러져 자연을 느낄 수 있는 편한 길', '- 시점 : 회화면 배둔시외버스터미널<br>교통편) 고성여객자동차터미널에서 고성-배둔, 고성-금곡, 고성-구만 이용 후 배둔시외버스터미널 하차<br>- 종점 : 광도면 황리사거리<br>교통편) 통영종합버스터미널 663번 버스, 임외사거리 하차<br>- 매점 외 식수가 없어 물을 미리 준비하는 것이 좋음', '36', '3', '0', '2020-02-16 04:50:37', '2025-05-19 05:15:16', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('14', '0', 'T_CRS_MNG0000005129', 'T_ROUTE_MNG0000000001', '남파랑길 14코스', '14.0', '270.0', '2', '0', '고성에서 통영으로 이어지는 구간으로 통영 광도면 황리사거리에서 용남면 장문리 충무도서관까지 이어지는 구간이다. 대부분 해안길을 따라 구간이 이어지며 코스 중간에 내죽도 수변공원 및 죽림소공원, 죽림수산시장, 회센터 등의 시설이 입지하고 있다. 해안가를 통해 이어지는 길이지만 수려한 해안경관보다는 바다를 끼고 살아가는 사람들의 삶의 모습을 보다 많이 확인할 수 있는 구간이다. 일부 구간에 갓길이 없는 도로구간이 포함되어 있어 걷기여행시 안전에 유의해야 한다. ', '- 통영 광도면 황리사거리에서 용남면 장문리 충무도서관까지 이어지는 구간으로 대부분 통영의 해안길을 따라 걷는 구간임  <br>- 구간 내에 내죽도 수변공원, 죽림소공원, 죽립수산시장 등이 입지하고 있음 <br>- 초보자도 쉽게 걸을 수 있으며 산길과 바닷길을 모두 걸을 수 있는 평이한 코스', '- 시점 : 광도면 황리사거리(통영시 광도면 안정로 921)<br>교통편) 통영종합버스터미널에서 139, 436, 536, 537, 663번 버스 이용 임외사거리 하차<br>- 종점 : 용남면 장운리 충무도서관(통영시 용남면 장문리 산 263-2)<br>교통편) 통영종합버스터미널 640번 버스, 통영시립충무도서관 하차<br>- 코스 중간에는 편의시설이 많지 않으므로 사전에 준비가 필요하나, 종점에는 식당, 숙소, 레포츠 시설 등 편의시설이 다수 있음', '36', '17', '0', '2020-02-16 04:54:50', '2025-05-19 05:31:14', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('15', '0', 'T_CRS_MNG0000005130', 'T_ROUTE_MNG0000000001', '남파랑길 15코스', '16.0', '360.0', '3', '0', '용남면 장문리 충무도서관에서 거제 사등면 사무소가지 이어지는 구간으로 통영과 거제가 연결되는 구간이다. 구간 중간에 삼봉산 등산로 구간이 포함되며 신거제대교에서 바라보이는 경관이 특히 아름다운 구간이다. 본 구간은 통영과 거제를 연결하는 구간으로 신거제대교를 통해 거제로 연결되는 코리아둘레길은 거제를 순환하여 거제대교로 다시 통영으로 연결된다. 갓길이 없는 도로구간이 많아 마을을 우회하는 구간이 많이 있으며 이에 따라 마을 고유의 먹거리 체험 등이 가능하다.', '- 통영과 거제가 연결되는 구간으로 신 거제대교에서 바라보는 도시와 해안경관이 특히 아름다운 코스 <br>- 출발지 부분에 있는 산길과 이후의 해안가로 이어진 마을을 따라 다양한 길을 걸을 수 있는 코스<br>- 남파랑길 통영 구간의 마지막 코스로, 종점 부근에서 아름다운 해안을 감상할 수 있음', '- 시점 : 용남면 장우리 충무도서관(통영시 용남면 장문리 산 263-2)<br>교통편) 통영종합버스터미널 640번 버스, 통영시립충무도서관 하차<br>- 종점 : 사등면사무소(거제시 사등면 성포로 110)<br>교통편) 고현버스터미널 43번 버스, 사등면사무소 하차<br>- 시종점 인근 대중교통 이용 용이하나 배차간격 확인 필요함<br>- 산길은 길이 험하므로 초보자는 주의가 필요함<br>- 식수가 없으므로 물을 미리 준비하는 것이 좋음', '36', '17', '0', '2020-02-16 04:58:54', '2025-05-19 05:33:29', '900');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('16', '0', 'T_CRS_MNG0000005131', 'T_ROUTE_MNG0000000001', '남파랑길 16코스', '13.0', '270.0', '2', '0', '통영 15코스와 이어지는 거제 구간으로 사등면 사무소에서 고현버스터미널까지 연결되는 구간이다. 코스 중간에 거제레저월드 등을 지나며 삼성중공업 거제조선소의 모습, 작은 어촌마을 등을 볼수 있다. 고현항 인근에 해안산책로 데크가 잘 조성되어 있다. 거제시의 경우, 해안가 옆으로 큰 도로가 지나는 경우가 많아 걷기여행안전성이 떨어지는 구간이 포함되어 있어 걷기여행시 안전에 유의할 필요가 있다. ', '- 한려해상국립공원의 해안 경관이 아름다운 ‘한려길\'의 거제 코스<br>- 거제시 조선산업의 위용을 체험할 수 있는 구간으로 대규모 조선소의 모습과 작은 어촌마을 등이 어우러져 거제시의 독특한 경관 감상이 가능함 <br>- 시종점 교통편이 잘 되어있어 접근성이 좋은 코스', '- 시점 : 사등면사무소(거제시 사등면 성포로 110)<br>교통편) 고현버스터미널 43번 버스, 사등면사무소 하차<br>- 종점 : 고현버스터미널(거제시 고현천로 10)<br>교통편) 거제 고현버스터미널 이용 <br>- 걷기길 내 마을에 갈림길이 많으므로 헤매지 않도록 주의<br>- 고현항 인근은 해안산책로가 잘 구축되어 있으나, 종점부로 갈 수록 도로구간 등 위험구간이 있어 여행자들의 주의가 필요함 <br>- 식수대가 없으므로 미리 물을 준비하는 것이 좋음', '36', '1', '0', '2020-02-16 05:02:37', '2025-05-16 12:11:09', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('17', '0', 'T_CRS_MNG0000005132', 'T_ROUTE_MNG0000000001', '남파랑길 17코스', '19.0', '450.0', '3', '0', '고현버스터미널에서부터 시작하여 거제 장목면 장목파출소까지 이어지는 구간으로 대부분 숲길 구간으로 이루어져 있다. 숲길 구간은 곳곳에 쉼터 및 정자 등이 잘 조성되어 있으며, 맹종죽순체험길로 명명되어 있는 대나무 구간이 일부 포함되어 있어 이색적인 체험이 가능하다. 실전마을회관 이후 장목파출소까지 연결되는 구간에 일부 갓길없는 도로구간이 포함되어 있어 걷기여행시 안전에 유의해야한다.', '- 고현버스터미널에서 시작하여 석름봉과 대성사를 지나 장목파출소에서 끝나는 코스<br>- 대부분의 노선이 숲길로 이어지는 구간으로 곳곳에 쉼터, 정자 등이 잘 갖춰져 있어 쾌적한 걷기여행이 가능함 <br>- 맹종죽순체험길로 명명되어 있는 거제시의 ‘섬&섬길’ 일부가 포함한 아름다운 산길을 걸으며 힐링할 수 있는 코스', '- 시점 : 고현버스터미널(거제시 고현천로 10)<br>교통편) 고현버스터미널 도보 이동<br>- 종점 : 장목면 장목파출소(거제시 장목면 거제북로 1212)<br>교통편) 고현버스터미널 31번 버스, 장목파출소 하차<br>- 산길 보행 시 안전상의 주의가 필요함', '36', '1', '0', '2020-02-16 05:06:25', '2025-05-16 12:11:51', '900');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('18', '0', 'T_CRS_MNG0000005133', 'T_ROUTE_MNG0000000001', '남파랑길 18코스', '16.0', '360.0', '3', '0', '거제 장목면 장목파출소에서 장목면 김영삼 대통령 생가까지 이어지는 길로 두모 몽돌해수욕장, 홍남 해수욕장, 외포항 등의 관광자원을 지나는 구간이다. 거제구간 특성 상, 갓길이 없는 도로구간이 많아 걷기여행시 안전에 유의해야하는 구간이다. ', '- 두모 몽돌해수욕장, 흥남해수욕장, 외포항, 김영삼 전 대통령 생가 등의 자원을 둘러볼 수 있는 코스<br>- 해안경관이 아름다운 구간으로 다양한 볼거리와 편의시설을 이용할 수 있는 코스', '- 시점 : 장목면 장목파출소(거제시 장목면 거제북로 1212)<br>교통편) 고현버스터미널 31번 버스, 장목파출소 하차<br>- 종점 : 장목면 김영삼대통령기록전시관(거제시 장목면 옥포대첩로 743)<br>교통편)고현버스터미널 32번 버스, 대계마을 하차<br>- 걷기여행 안전성에 미흡한 구간이 있으므로 주의가 필요함', '36', '1', '0', '2020-02-16 05:10:17', '2025-05-19 06:25:49', '900');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('19', '0', 'T_CRS_MNG0000005134', 'T_ROUTE_MNG0000000001', '남파랑길 19코스', '16.0', '360.0', '3', '0', '김영삼 대통령 생가에서부터 장승포 시외버스터미널까지 연결되는 구간으로 거제시의 걷기여행길인 “섬&섬길” 중 “충무공 이순신 만나러가는 길”과 중첩되는 구간이다. 김영삼 생가, 옥포대첩 기념공원 및 기념관, 덕포해수욕장 등 구간 곳곳에 볼거리가 입지하고 있으며, 안내체계가 잘 구축되어 있다. 해안길, 마을길, 숲길 등 다양한 걷기여행길 체험이 가능하여 걷기여행시 지루함이 느껴지지 않는 구간으로서의 특성을 보유하고 있다. 특히, 옥포국가산업단지를 지남에 따라 조선산업의 메카인 거제시의 매력을 걸으며 느낄 수 있다. ', '- 김영삼 대통령 생가에서 장승포까지 이어지는 구간으로 거제시가 조성한 ‘섬&섬길’ 중 ‘충무공 이순신 만나러가는 길’이 있는 코스<br>- 코스가 어렵지 않아 초보자도 쉽게 걸을 수 있고, 대중교통이 용이해 접근성이 좋은 코스<br>- 덕포해수욕장, 옥포대첩 기념관 등 관광코스를 즐길 수 있으며 해안가 데크가 잘 되어있어 바다를 조망하며 걷는 코스', '- 시점 : 장목면 김영삼대통령기록전시관(거제시 장목면 옥포대첩로 743)<br>교통편) 고현버스터미널 32번 버스, 대계마을 하차<br>- 종점 : 장승포시외버스터미널(거제시 능포로 126)<br>교통편)고현버스터미널 11번 버스, 옥수동 하차<br>- 코스 내 다수의 편의시설 이용 가능하며,  대부분 평지라 초보자도 걷기 어렵지 않음', '36', '1', '0', '2020-02-16 05:13:52', '2025-05-19 05:35:46', '900');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('20', '0', 'T_CRS_MNG0000005135', 'T_ROUTE_MNG0000000001', '남파랑길 20코스', '18.0', '390.0', '2', '0', '장승포 시외버스터미널에서 거제어촌민속전시관까지 이어지는 구간으로 능포항, 장승포항 등 주요 항구를 지나는 구간으로서의 특성을 보유한다. 섬&섬길 양지암등대길 일부 코스가 포함되며 곳곳에 잘 조성된 공원길 등이 포함되어 있고, 한려해상국립공원의 수려한 해안경관을 볼 수 있어 매력적인 구간이다. 구간 종점 구간에 대명리조트가 입지하고 있으며, 다양한 편의시설 및 숙박시설 등이 입지하고 있다.', '- ‘섬&섬길’ 중 양지암등대길 일부 코스가 포함된 구간으로 곳곳에 잘 정비된 공원길이 조성되어 있으며, 한려해상의 아름다운 해안경관을 감상할 수 있는 코스<br>- 다양한 관광 포인트들을 즐길 수 있고, 편의시설을 이용할 수 있는 코스<br>- 해안데크, 공원, 해안도로 등 조성이 잘 되어 있어 아름다운 거제 해안을 바라보며 걷기여행을 추천하고 싶은 최적의 코스', '- 시점 : 장승포 시외버스터미널(거제시 능포로 126)<br>교통편) 고현버스터미널 11번 버스, 옥수동 하차<br>- 종점 : 일운면 거제어촌민속전시관(거제시 일운면 지세포해안로 42)<br>교통편) 고현버스터미널 23번 버스, 일운농협 하차<br>- 화장실, 매점, 펜션과 민박, 호텔 등 편의시설이 다수 있어 이용하기 좋음', '36', '1', '0', '2020-02-16 05:17:42', '2025-05-19 05:37:25', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('21', '0', 'T_CRS_MNG0000005136', 'T_ROUTE_MNG0000000001', '남파랑길 21코스', '14.0', '300.0', '2', '0', '거제어촌민속전시관에서 일운면 구조라유람선 터미널가지 연결되는 구간으로 거제시의 “섬&섬길” 중 천주교순례길 구간과 일부 중첩되는 구간이다. 지세포항을 지난 와현모래숲해수욕장, 구조라항까지 연결되는 구간으로 아름다운 해안경관과 숲길 등을 지날 수 있다. 구조라항 일원에 일부 갓길없는 도로구간이 포함되어 있어 걷기여행시 안전에 유의해야한다. ', '- ‘섬&섬길’ 중 천주교 순례길 구간이 일부 포함되는 구간으로 지세포항을 지나 와현모래숲 해수욕장, 구조라항 등의 자원을 지나며 아름다운 해안경관과 숲길 등을 만날 수 있는 코스<br>- 해수욕장, 항구 등 바다의 다양한 모습을 감상할 수 있는 코스', '- 시점 : 일운면 거제어촌민속전시관(경남 거제시 일운면 지세포해안로 41)<br>교통편) 장승포시외버스터미널에서 62, 64, 64-1번 버스 이용 후 신촌 하차<br>- 종점 : 일운면 구조라유람선터미널(경남 거제시 일운면 구조라로 37)<br>교통편) 삼정경로당 정류장에서 61, 64, 64-1번 버스 이용 후 옥수동(장승포시외버스터미널) 하차. 또는 수정 정류장에서 22, 23, 4000번 버스 이용 후 고현버스터미널 하차<br>- 식수대가 없으므로 물은 미리 준비하거나 편의점에서 구입하는 것이 좋음', '36', '1', '0', '2020-02-16 05:21:11', '2025-05-19 05:44:00', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('22', '0', 'T_CRS_MNG0000005137', 'T_ROUTE_MNG0000000001', '남파랑길 22코스', '13.0', '330.0', '3', '0', '구조라유람선터미널에서 동부면 학동리 학동고개까지 이어지는 구간으로 전체 구간이 등산로 구간으로 이어진 구간이다. 거제시의 경우, 해안가의 길이 대부분 갓길이 없는 도로구간으로 이를 우회하기 위해 설정된 구간으로 구조라항, 구조라해수욕장, 망치몽돌해수욕장을 지나 산악구간으로 접어들게 되며, 산악구간 시 수려한 한려해상국립공원의 전경을 감상할 수 있는 구간으로서의 매력을 가진다. 다만, 국립공원에 포함된 산악구간인만큼 걷기여행시 철저한 산행준비가 있어야 한다.', '- 구조라항, 구조라 해수욕장, 망치몽돌 해수욕장을 지나 등산로로 진입하게 되며, 등산로에서 한려해상국립공원의 절경을 감상할 수 있는 코스<br>- 대부분 등산로를 따라 걷는 길로 산 정상을 오르는 가파른 등산로가 있어 난이도가 높은 코스<br>- 바닷길과 산길을 모두 경험하고 싶은 사람에게 추천하는 코스', '- 시점 : 일운면 구조라유람선터미널(경남 거제시 일운면 구조라로 37)<br>교통편) 장승포시외버스터미널에서 61, 64, 64-1번 버스 이용 후  삼정경로당 정류소 하차. 또는 고현버스터미널에서 22, 23, 4000번 버스 이용 후 수정 정류장 하차<br>- 종점 : 동부면 학동리 학동고개(경남 거제시 동부면 학동리 산 47-10)<br>교통편) 거제자연휴양림 정류장에서 55, 56, 67번 버스 이용 후 고현버스터미널 하차<br>- 식수대가 없어 해수욕장 인근 매점 또는 편의점에서 구입하여야 함<br>- 난이도 조정 및 안전사고 예방을 위해 북병산 임도 노선으로 이동하도록 함', '36', '1', '0', '2020-02-16 05:24:51', '2025-05-19 05:45:23', '900');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('23', '0', 'T_CRS_MNG0000005138', 'T_ROUTE_MNG0000000001', '남파랑길 23코스', '10.0', '300.0', '3', '0', '거제 동부면 학동고개에서 거제 남부면 명사해변까지 이어지는 구간으로 거제시의 “무지개길”과 중첩되는 구간이다.  산악 코스를 거쳐 여차홍포전망대 등을 거쳐 명사해변으로 이어지는 구간으로 거제시의 가장 아름다운 해안경관을 살펴볼 수 있다. 다만, 일부 구간이 갓길이 없는 도로구간으로 걷기여행시 안전에 유의해야하며, 해당 구간이 “무지개길”로 지정되어 있어 안내표지판 등은 비교적 잘 구축되어 있다.', '- 학동고개에서 명사해변까지 이어지는 구간으로 병대도 전망대 등 거제의 가장 아름다운 해안경관을 감상할 수 있는 거제 마지막 코스<br>- 거제시가 ‘무지개길’로 명명할 만큼 아름다운 경관을 보유한 코스', '- 시점 : 동부면 학동리 학동고개(경남 거제시 동부면 학동리 산 47-10)<br>교통편) 고현버스터미널에서 55, 56번 버스 이용 후 거제자연휴양림 정류장 하차<br>- 종점 : 남부면 저구항(경남 거제시 남부면 저구리 저구항)<br>교통편) 매물도여객선터미널에서 53, 53-1번 이용 후 고현버스터미널 하차. 배차간격이 2시간 이상이므로 시간확인 필요<br>- 식수대가 없으므로 물을 미리 준비해오거나 종점부 매점을 이용해야 함<br>- 기존 등산로 방향 안내도 잘 되어 있으나 가라산 정상을 오르는 등산로의 난이도가 어려우므로 사전에 준비가 필요함<br>- 일부 안전성 미확보 구간이 있어 이용자의 주의가 요구됨', '36', '1', '0', '2020-02-16 05:30:33', '2025-05-19 05:46:49', '900');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('24', '0', 'T_CRS_MNG0000005139', 'T_ROUTE_MNG0000000001', '남파랑길 24코스', '11.0', '240.0', '2', '0', '명사해변에서 남부면 탑포리 탐포마을까지 이어지는 구간으로 거제시의 “무지개길”과 중첩되는 구간이다. 명사해수욕장을 지나 거제시의 대표 관광자원이라 할 수 있는 매물도, 장사도 등으로 갈 수 있는 유람선 터미널 등이 입지하고 있으며, 아름다운 숲길, 어촌마을 등을 지나는 구간이 다수 포함되어 있다. 종점 부근에는 쌍근어촌체험마을이 입지하고 있다.', '- 명사해수욕장을 지나 매물도, 장사도를 오가는 여객선 터미널, 쌍근 어촌체험마을 등의 자원을 경유하는 코스<br>- 아름다운 숲길, 소박한 어촌마을 등을 지나는 구간으로 안전하게 걷기여행을 즐길 수 있는 걷기 좋은 코스', '- 시점 : 남부면 저구항(경남 거제시 남부면 저구리 저구항) <br>교통편) 고현버스터미널에서 53,53-1번 이용 후 매물도여객선터미널 하차. 배차간격이 2시간 이상이므로 시간확인 필요 <br>- 종점 : 남부면 탑포리 탑포마을(경남 거제시 남부면 탑포리 477-5)<br>교통편) 탑포마을 정류장 52, 53-1번 버스 이용 후 고현버스터미널 하차<br>- 시,종점에서 버스이용시 배차시간 확인필수', '36', '1', '0', '2020-02-16 05:34:05', '2025-05-16 12:18:42', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('25', '0', 'T_CRS_MNG0000005140', 'T_ROUTE_MNG0000000001', '남파랑길 25코스', '15.0', '300.0', '2', '0', '탑포리 탑포마을에서 거제면 거제파출소까지 연결되는 구간으로 마을길 및 해안길, 숲길 임도구간이 반복적으로 펼쳐지는 구간이다. 일부 구간이 도로구간으로 걷기여행시 유의가 필요하다. ', '- 탑포리 탑포마을에서 거제면 거제파출소까지 연결되는 구간으로 마을길 및 해안길, 숲길 임도구간이 반복적으로 펼쳐지는 코스<br>- 노선 곳곳에서 섬과 바다, 아름다운 리아스식 해변 경관을 감상할 수 있는 코스', '- 시점 : 남부면 탑포리 탑포마을(경남 거제시 남부면 탑포리 477-5) <br>교통편) 고현버스터미널에서 52, 53, 54-1, 55-1번 이용 후 탑포삼거리 정류장 하차<br>- 종점 : 거제면 거제파출소(경남 거제시 거제면 거제남서로 3431)<br>교통편) 제일중학교 정류장 50, 52, 54, 56번 버스 이용 후 고현버스터미널 하차<br>- 대중교통 이용 가능하나, 배차간격이 길어 주의해야 함', '36', '1', '0', '2020-02-16 05:37:26', '2025-05-16 12:19:20', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('26', '0', 'T_CRS_MNG0000005141', 'T_ROUTE_MNG0000000001', '남파랑길 26코스', '13.0', '270.0', '2', '0', '거제파출소에서 둔덕면 청마기념관가지 연결되는 구간으로 거제 스포츠파크를 거져 거제를 대표하는 작가 유치진의 생가 및 기념관 등을 둘러볼 수 있다. 대봉산, 산방산 등의 숲길(임도) 구간이 포함되어 있어 걷기여행시 이에 대비한 준비가 필요하다. ', '- 거제 스포츠파크를 거쳐 거제를 대표하는 작가 유치진의 생가와 기념관 등을 체험할 수 있으며, 대봉산, 산방산 등의 숲길 구간이 연결되는 코스<br>- 임도가 길어 걷기 좋은 구간', '- 시점 : 거제면 거제파출소(경남 거제시 거제면 거제남서로 3431)<br>교통편) 고현버스터미널에서 50, 51, 52, 56, 57, 54-1번 버스 이용하여 제일중학교 정류장 하차<br>- 종점 : 둔덕면 밤하리 청마기념관(경남 거제시 둔덕면 방하2길 6)<br>교통편) 방하삼거리 정류장 41번 버스 이용<br>- 대중교통 이용 가능하나, 배차간격이 길어 사전에 시간확인이  필요<br>- 대봉산, 산방산 등의 숲길(임도) 구간이 포함되어 있어 걷기여행시 이에 대비한 준비가 필요', '36', '1', '0', '2020-02-16 05:40:51', '2025-05-16 12:19:55', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('27', '0', 'T_CRS_MNG0000005142', 'T_ROUTE_MNG0000000001', '남파랑길 27코스', '10.0', '210.0', '2', '0', '거제 둔덕면 방하마을의 청마기념관을 지나 통영 용남면 장평리 신촌마을로 연결되는 구간으로 거제대교를 통해 거제 구간이 완료되는 거제 종점 구간이다. 타 구간과 마찬가지로 마을길과 숲길, 해안길 등이 중복되어 있으며, 코스 중간에 거제 둔덕기성 등의 역사문화자원을 보유하고 있다. 숲길 구간의 경우, 임도가 잘 조성되어 있어 걷기에 크게 어려움이 없는 구간이다. 다만, 하계시즌에는 일부 제초작업이 안되어있을 수 있으니 유의가 필요하다.', '- 신 거제대교에서 출발한 거제시 구간이 완료되는 구간으로 청마기념관과 거제 둔덕기성 등의 자원을 경유하는 코스', '- 시점 : 둔덕면 방하리 청마기념관(경남 거제시 둔덕면 방하2길 6)<br>교통편) 고현버스터미널에서 41번 이용하여 방하삼거리 정류장 하차<br>- 종점 : 용남면 장평리 신촌마을(경남 통영시 용남면 장평리 235-1)<br>교통편) 신촌마을 정류장 650, 906, 910, 953번 버스 이용 후 시외버스터미널(통영) 하차<br>- 식수가 없으니 사전 준비하거나 매점을 이용<br>- 방하마을은 하루에 4번 버스 운행하므로 버스 시간표 확인 필수<br>- 일부 등산로 구간에 이용자의 주의가 필요한 구간이 분포', '36', '1', '0', '2020-02-16 05:45:18', '2025-05-20 04:53:50', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('28', '0', 'T_CRS_MNG0000005143', 'T_ROUTE_MNG0000000001', '남파랑길 28코스', '14.0', '270.0', '2', '0', '통영 28코스는 옹냠면 장평리 신촌마을에서 동호동 남망산 조각공원까지 이어지는 구간으로 통영시가 이야기가 있는 문화생태탐방로로 기조성한 토영이야기길과 중첩되는 구간이다. 구간 내에 통영을 대표하는 이순신공원 등이 분포하고 있으며 다음 코스에 동피랑마을 및 서피랑마을 등과 연계되어 통영의 명실상부한 걷기여행의 핵심 구간이다. 볼거리, 먹거리가 다양하며, 여행자를 위한 편의시설도 비교적 잘 구축되어 있는 구간이다. 다만, 일부 구간이 갓길이 없는 도로구간으로 연결되어 해당구간을 여행할 때는 안전에 유의하여야 한다.  ', '- 통영 세자트라 숲, 이순신공원, 남망산 조각공원 입구까지 이어지는 구간으로 이순신 공원에서 바라보는 바다 경관이 아름다운 코스<br>- 해안 둘레길을 걷기에 최적의 코스이자 통영의 대표 걷기여행길인 토영 이야기길과 만나는 걷기에 편안 코스', '- 시점 : 용남면 장평리 신촌마을(경남 통영시 용남면 장평리 235-1)<br>교통편) 통영종합버스터미널 752번 버스, 신촌마을 하차<br>- 종점 : 동호동 남망산조각공원입구(경남 통영시 동호동 249-1)<br>교통편) 남망산조각공원입구 정류장에서 752, 610버스 다수 이용 후 통영시외버스터미널 하차<br>- 통영 세자트라 숲 입장료 및 주차비 무료<br>- 이순신 공원 입장료 무료, 주차비 유료<br>- 펜션이 많아 숙박하기 용이함', '36', '17', '0', '2020-02-16 06:13:52', '2024-10-16 11:34:58', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('29', '0', 'T_CRS_MNG0000005144', 'T_ROUTE_MNG0000000001', '남파랑길 29코스', '18.0', '360.0', '2', '0', '통영 28코스와 이어지는 29코스 시점 구간이 통영시의 토영이야기길과 중첩되는 구간으로 통영의 대표 관광명소인 동피랑마을, 서피랑마을, 윤이상기념관, 해저터널 등 핵심 시내구간이 포함되는 구간이다. 시내 구간을 지난 이후에는 해안길을 따라 무전동 해변공원까지 이어진다.  구간 중간중간에 한실포, 민양마을 등 어촌마을 지나게 되며, 해안을 따라 사람들이 살아가는 모습을 볼 수 있는 구간으로 매력이 있다.', '- 토영 이야기길을 따라 통영을 대표하는 동피랑마을, 서피랑마을, 해저터널 등 핵심 자원을 두루 경유하는 코스<br>- 동피랑 벽화마을, 서피랑 공원의 이곳저곳을 구경하고 예쁜 어촌마을을 볼 수 있으며 평인일주도로에서는 한적함을 느낄 수 있는 코스<br>- 다양한 살거리, 먹거리를 보유하고 있으며 활기찬 통영의 모습을 살펴볼 수 있는 매력적인 구간', '- 시점 : 동호동 남망산조각공원입구(경남 통영시 동호동 249-1)<br>교통편) 통영종합버스터미널에서 321, 420, 121, 610, 613, 211번 등 다수 버스 이용하여 남만산공원입구 정류장 하차<br>- 종점 : 무전동 해변공원(경남 통영시 무전동 1013)<br>교통편) 한진로즈힐 정류장 401, 341, 530, 500, 104, 501, 534, 535, 105, 301버스 등 다수 이용 후 시외버스터미널(통영) 하차<br>- 동피랑 마을 벽화는 2년에 한 번씩 새롭게 단장을 함<br>- 통영시립박물관 매일 09:00 - 18:00, 월요일 휴무, 성인 기준 관람료 2,000원<br>- 윤이상 기념관 관람료 무료<br>- 해저터널 관람료 무료, 약 1시간 소요', '36', '17', '0', '2020-02-16 06:18:08', '2024-10-16 11:38:02', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('30', '0', 'T_CRS_MNG0000005145', 'T_ROUTE_MNG0000000001', '남파랑길 30코스', '16.0', '450.0', '3', '0', '통영 무전동 북신만 해변공원에서 고성으로 연결되는 지점인 통영 도산면 원산리 바다휴게소까지 이어지는 구간으로 발암산 제석봉 및 도덕산 백우정사, 관덕저수지 등을 지나는 등산로 구간이 포함되어 있다.  구간 중 일부가 통제사 옛길 등으로 지정되어 있으며, 중간중간 쉼터가 잘 구성되어 있다. 구간 내 상당수가 등산로 구간이 포함되어 있음에 따라 걷기여행시 안전한 여행을 위한 준비가 필요한 구간이다. ', '- 무전동 해변공원에서 시작하여 발암산 제적봉 및 도덕산 백우정사, 관덕저수지 등 등산로 구간으로 이어지는 코스<br>- 대부분 산길로 구성되어 있으며 일부가 통제사 옛길로 지정되어 안전하게 걷기여행을 즐길 수 있는 코스', '- 시점 : 무전동 해변공원(경남 통영시 무전동 1013)<br>교통편) 통영종합버스터미널에서 504, 676, 121,231, 321, 420, 522, 521번 등 다수 버스 이용하여 새통영병원 하차<br>- 종점 : 도산면 원산리 바다휴게소(경남 통영시 도산면 남해안대로 2125)<br>교통편) 평촌창동마을 정류장에서 고성-원산 버스 이용 후 고성여객자동차터미널 하차. 또는 원산종점 정류장에서 676, 678, 676 버스 후 통영종합버스터미널 하차. 이용시 배차시간 확인 필수<br>- 제석산을 오르는 길에 찜질방이 있음<br>- 식수는 사전준비나 매점에서 준비하며 코스 대부분이 산길로 이에 대한 준비가 필요함', '36', '17', '0', '2020-02-16 06:22:16', '2025-06-13 07:41:37', '900');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('31', '0', 'T_CRS_MNG0000005146', 'T_ROUTE_MNG0000000001', '남파랑길 31코스', '16.0', '360.0', '2', '0', '통영에서 나와 고성군 내부를 관통하는 구간으로 통영 원산리 바다휴게소에서 상리면 부포사거리까지 이어지는 구간이다. 고성군 전역 및 고성만 일원을 조망할 수 있는 남산공원 일원을 통과하는 구간으로 해지게 다리, 남산공원 오토캠핑장, 남산정 등의 관광자원을 둘러볼 수 있다.  또 구간과 인접하여 갈모봉산림욕장이 분포하고 있어 해당 자원을 연계이용할 수 있다. 남산정을 지나 이어지는 구간은 고성군이 기 조성한 대독누리길 구간과 일부 중첩되는 구간이다.  ', '- 통영에서 나와 고성군 시내를 지나는 구간으로 고성 남산공원에서 바라보는 고성만의 해안경관이 특히 아름다운 구간<br>- 해지개 다리, 남산공원 오토캠핑장, 남산공원, 대독누리길 등을 경유<br>- 통영에서 고성으로 넘어가는 코스', '- 시점 : 도산면 원산리 바다휴게소(경남 통영시 도산면 남해안대로 2125) <br>교통편) 통영종합버스터미널 678번 평촌마을 하차<br>- 종점 : 상리면 부포리 부포사거리(경남 고성군 상리면 장치로 382-6)<br>교통편) 내부포 정류장 고성-고봉, 고성-봉발, 고성-상리, 고성- 하이 등 버스 다수 이용하여 고성여객자동차터미널 하차<br>- 대중교통 이용 시 배차 시간 확인 필수(1일 3~5회 배차)<br>- 식수는 사전준비나 매점에서 구입하여 이용', '36', '17', '0', '2020-02-16 06:26:09', '2025-05-19 05:47:46', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('32', '0', 'T_CRS_MNG0000005147', 'T_ROUTE_MNG0000000001', '남파랑길 32코스', '14.0', '330.0', '3', '0', '고성 상리면 부포리 부포사거리에서 하일면 학림리 임포항가지 이어지는 구간으로 문수암, 수태산 일원의 숲길 구간을 지나는 구간이다. 숲길 구간의 경우, 비교적 난이도가 있으나 임도가 형성되어 있어 걷기여행에 큰 불편은 없는 구간이 많으며, 숲길에서 고성의 앞바다를 조망할 수 있는 매력적인 뷰포인트가 형성되어 있다.  돌담으로 아름다운 정취를 보유하고 있는 학동마을을 지나는 구간이 특히 아름다우며, 종점인 임포항에는 다양한 식당이 분포하고 있어 원기충천에 용이하다. ', '- 문수암, 수태산 일원의 숲길 구간을 지나는 구간으로 걷기여행 난이도가 다소 높은 코스<br>- 부포사거리에서 무이산, 수태산, 향로봉을 둘러가는 등산로로 비교적 난이도가 있으나 임도가 잘 형성되어 있어 큰 어려움이 없는 코스<br>- 숲길에서 바라보는 해안경관과 학동마을의 돌담, 임포항의 풍부한 먹거리 체험이 가능', '- 시점 : 상리면 부포리 부포사거리(경남 고성군 상리면 장치로 382-6)<br>교통편) 고성여객자동차터미널에서 고성-상리, 고성-하이, 고성-봉발 시내버스 이용하여 내부포 정류장 하차<br>- 종점 : 하일면 학림리 임포항(경남 고성군 하일면 학림리 1518)<br>교통편) 임포 정류장에서 고성-하이 버스 이용 후 고성여객자동차터미널 하차<br>- 버스 이용시 배차시간 확인 필수<br>- 식수는 사전준비나 매점에서 구입하여 이용', '36', '3', '0', '2020-02-16 06:35:44', '2025-06-04 06:35:43', '900');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('33', '0', 'T_CRS_MNG0000005148', 'T_ROUTE_MNG0000000001', '남파랑길 33코스', '17.0', '390.0', '2', '0', '고성에서 가장 아름다운 구간으로 이미 상족암길 및 해수부의 해안누리길(공룡화석지해변길)이 중첩되는 구간으로 임포항에서 시작하여 하이면 덕호리 하이면사무소에서 구간이 완료된다. 전체 구간 중간쯤에 상족암 군립공원 및 고성공룡박물관 등이 분포하고 있으며 아름다운 해안경관을 살펴볼 수 있는 구간이다. 다만, 고성군이 상족암길로 기 지정한 길 중에서 갓길이 없는 도로구간이 포함되어 있음에 따라 걷기여행시 특별히 안전에 유의해야하는 구간이다.', '- 고성에서 가장 아름다운 구간으로 ‘상족암길’, 해수부의 해안누리길로 지정된 ‘공룡화석지해변길’이 포함되는 코스<br>- 상족암 군립공원 및 고성공룡박물관 등이 분포하고 있으며 아름다운 해안경관을 바라볼 수 있는 코스<br>- 어촌과 농촌을 함께 보고 느끼며 해안둘레길을 걸을 수 있는 구간', '- 시점 : 하일면 학림리 임포항(경남 고성군 하일면 학림리 1518)<br>교통편) 고성여객자동차터미널에서 고성-하이 버스 이용하여 임포 정류장 하차<br>- 종점 : 하이면 덕호리 하이면사무소(경남 고성군 하이면 공룡로 1)<br>교통편) 신덕마을 정류장 10번 버스 이용 후 삼천포시외버스터미널 하차<br>- 대중교통 이용 시 버스배차 간격을 고려하여 사전에 확인 필수<br>- 식수는 사전준비 또는 매점에서 구입해서 이용가능<br>- 구간 내에 안전이 미흡한 구간이 분포하고 있어 이용자의 주의가 필요', '36', '3', '0', '2020-02-16 06:42:09', '2024-10-16 11:43:28', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('34', '0', 'T_CRS_MNG0000005149', 'T_ROUTE_MNG0000000001', '남파랑길 34코스', '10.0', '210.0', '1', '0', '고성 하이면사무소에서 삼천포대교 사거리까지 연결되는 구간으로 사천시가 조성한 “이순신바닷길” 중 5코스-삼천포 코끼리길과 중첩되는 구간이다. 코스 중간에 삼천포 신항, 노산공원 등을 지나며 삼천포항 수산물시장 등을 거쳐 항구 특유의 역동적인 모습 및 먹거리 등을 체험할 수 있다. 또한, 노산공원 일원에는 사천이 배출한 박재삼의 문학관이 자리잡고 있어 볼거리, 먹거리가 다양하게 마련되어 있는 구간이다.', '- 사천시가 조성한 ‘이순신 바닷길’중 삼천포 코끼리길이 포함되는 구간으로 삼천포 신항, 노산공원, 삼천포항 수산물 시장 등을 지나는 코스<br>- 남일대 해수욕장에서 해변의 정취를 느끼고 삼천포항과 삼천포 수산시장까지 어촌의 활기찬 생활모습을 체험할 수 있는 코스<br>- 볼거리, 먹거리가 다양하게 마련되어 있는 구간', '- 시점 : 하이면 덕호리 하이면사무소(경남 고성군 하이면 공룡로 1)<br>교통편) 삼천포시외버스터미널에서 10번 버스 이용 후 신덕마을 정류장 하차. 배차간격 확인<br>- 종점 : 대방동 삼천포대교 사거리(경남 사천시 대방동 369)<br>교통편) 연륙교 대방사거리 정류장에서 20, 25, 남해-지족 버스 이용<br>- 편의시설이 곳곳에 많아 이동 시 편리함', '36', '3', '0', '2020-02-16 06:48:58', '2024-10-16 11:44:51', '300');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('35', '0', 'T_CRS_MNG0000005150', 'T_ROUTE_MNG0000000001', '남파랑길 35코스', '13.0', '300.0', '3', '0', '삼천포대교 사거리에서 사천 대방교차로를 돌아 순환하는 구간으로 사천시의 “이순신바닷길” 2코스-최초거북선길 및 “이순신바닷길” 4코스-실안노을길과 일부 중첩되는 구간이다. 삼천포 대교와 연결되는 실안노을길은 우리나라를 일몰명소 중 손꼽히는 명소이며 걷기여행자를 위한 다양한 편의시설 등이 잘 구축되어 있다. <br>삼천포대교를 통해 남해구간으로 연결되는 구간으로 본 구간은 실안노을길을 지나 각산(각산산성길) 등을 돌아나오는 구간으로 각산에서 아름다운 삼천포항 및 삼천포 대교 등을 조망할 수 있다. ', '- \'이순신 바닷길\' 중 최초 거북선길 및 실안노을길 일부가 포함되는 구간으로 실안노을길에서 바라보는 경관이 특히 유명한 코스<br>- 산과 바다를 고루 즐길 수 있는 코스<br>- 한려해상국립공원의 빼어난 아름다움을 감상할 수 있는 코스', '- 시점 : 대방동 삼천포대교 사거리(경남 사천시 대방동 369)<br>교통편) 삼천포터미널에서 20, 25번 버스 이용하여 새마을금고 정류장 하차<br>- 종점 : 대방동 대방교차로(경남 사천시 대방동 717-2)<br>교통편) 연륙교 대방사거리 정류장 20번 버스 이용 후 삼천포터미널 하차<br>- 각산은 각산산성 및 각산전망대에서 바라보는 경관이 아름다우나, 경사가 심한 편임(사천해상케이블카를 통해 각산전망대 이용이 가능함)', '36', '8', '0', '2020-02-16 06:53:07', '2025-05-26 04:15:27', '900');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('36', '0', 'T_CRS_MNG0000005151', 'T_ROUTE_MNG0000000001', '남파랑길 36코스', '18.0', '390.0', '2', '0', '사천시에서 삼천포대교를 지나 남해군 창선면 창선파출소까지 연결되는 구간으로 단항 해안길과 연태산 임도길을 지나는 구간이다. 단항 해안길에서는 사천 내륙 및 소초도, 대초도 등의 섬 경관을 볼 수 있으며, 연태산 임도길은 임도를 따라 숲길의 매력을 직접 체험할 수 있는 구간이다. 삼천포대교를 지나 단항까지 연결되는 일부 구간이 갓길이 없는 도로구간이나 도로 통행량이 많지는 않아 큰 위험요소는 없으나, 걷기여행자들의 주의는 필요하다. ', '- 사천시에서 삼천포 대교를 지나 남해 창선으로 연결되는 구간<br>- 삼천포대교, 늑도대교, 창선대교를 지나 남해로 들어가는 코스로 바다를 둘러보며 걷기 좋은 구간<br>- 동대만과 곤유마을을 한눈에 내려다 볼 수 있는 편백숲 임도를 길게 걸을 수 있는 코스', '- 시점 : 대방동 대방교차로(경남 사천시 대방동 717-2)<br>교통편) 삼천포터미널에서 20, 25번 버스 이용하여 연륙교대방사거리 정류장 하차<br>- 종점 : 창선면 상죽리 창선파출소(경남 남해군 창선면 창선로 99)<br>교통편) 삼동면사무소 앞 정류장에서 남해공용터미널방향 버스 이용<br>- 시점과 종점 버스 이용 시 배차시간 확인 필수<br>- 남해바래길 3코스와 동일하므로 남해바래길 관련 정보를 확인해도 좋음<br>*남해바래길탐방안내센터 (055)863-8778 / http://cafe.daum.net/namhaetrails', '36', '8', '0', '2020-02-16 06:57:31', '2025-05-16 12:20:32', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('37', '0', 'T_CRS_MNG0000005152', 'T_ROUTE_MNG0000000001', '남파랑길 37코스', '15.0', '330.0', '2', '0', '창선파출소에서 창선면 진동리 적량버스정류장까지 이어지는 구간으로 남해군이 이야기가 있는 문화생태탐방로로 조성한 “남해바래길” 7코스 구간가 일부 구간이 중첩되는 구간이다. 남해바래길 7코스의 경우, 고사리밭길이라는 이름으로 불리고 있으며 동대만 휴게소 및 적량 국사봉 자락을 지나며 아름다운 해안구간과 숲길, 갯마을 등이 어우러져 있어 걷기여행의 매력이 높은 구간이다. 구간 내에 공룡발자국 화석지로 알려진 가인리 해안을 지난다. ', '- 적량 국사봉 자락을 지나며 아름다운 해안구간과 숲길, 갯마을 등이 어우러져 있어 걷기여행의 매력이 높은 코스<br>- 우리나라 최대의 고사리 산지인 고사리밭을 지나는 길로 감탄을 자아내는 이국적인 정취로 걸음걸이가 자꾸 느려지는 길<br>- 바다 전경과 바다 멀리 삼천포대교 등의 전경을 보며 걷기 지루하지 않은 구간<br>*고사리 채취기간인 3월부터 6월까지 넉 달은 사전예약자에 한해 지정된 안내인 동반하에 걸을 수 있음 <br>(식포~가인마을구간/화,목,토,일 주 4회/1일 최대 120명) ', '- 시점 : 창선면 상죽리 창선파출소(경남 남해군 창선면 창선로 99)<br>교통편) 남해시외버스터미널에서 시내버스 남해-단항, 남해-연곡 이용하여 수산리 앞 정류장 하차<br>- 종점 : 창선면 진동리 적량버스정류장(경남 남해군 창선면 흥선로 1215)<br>교통편) 적량 정류장 장포-남해 시내버스 이용하여 남해공용터미널 하차<br>- 버스 이용시 배차 시간을 확인 필수 <br>- 3월부터 6월까지 고사리 채취기간으로 제약이 있으며, 무단으로 고사리 채취 시 즉시 퇴로조치<br>*예약 신청: https://cafe.daum.net/namhaetrails/AjnF/8<br>- 남해바래길 4코스와 동일하므로 남해바래길 관련 정보를 확인해도 좋음 <br>*남해바래길탐방안내센터 (055)863-8778 / http://cafe.daum.net/namhaetrails ', '36', '5', '0', '2020-02-16 07:01:07', '2024-10-16 11:52:05', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('38', '0', 'T_CRS_MNG0000005153', 'T_ROUTE_MNG0000000001', '남파랑길 38코스', '12.0', '270.0', '2', '0', '창선면 진동리 적량버스정류장에서 삼동면 지족리 하나로마트까지 이어지는 구간으로 “남해바래길” 6코스와 중첩되는 구간이다. 남해바래길 6코스는 말발굽길로 불리며 고려시대 적량에서 군마를 사육하였던 역사적 스토리에서 테마를 따온 것으로 알려진다.  남해도와 창선도 사이의 지족해협에서 원시어업의 형태인 죽방렴이 현재까지 이어지고 있어 걷기여행을 하며 죽방렴의 장관을 체험할 수 있다. <br>시점에서 일부 구간이 갓길이 없는 도로구간으로 걷기여행자의 안전이 유의된다. ', '- 고려시대 적량에서 군마를 사육하던 역사적 스토리를 활용하여 말발굽길로 명명된  코스<br>- 남해도와 창선도 사이의 지족해협에서 원시어업의 형태인 죽방렴의 정경을 감상할 수 있는 구간<br>- 그림같은 바다풍경과 마을이 이어지는 골목길을 따라 잘 정비된 추섬공원을 지나는 길로 산책하듯 편하게 걸을 수 있는 코스 ', '- 시점 : 참선면 진동리 적량버스정류장(경남 남해군 창선면 흥선로 1215)<br>교통편) 남해공용터미널에서 남해-단항, 수산-장포- 장포-남해 버스 환승 이용<br>- 종점 : 삼동면 진동리 하나로마트(경남 남해군 삼동면 동부대로 1875)<br>교통편) 지족 정류장에서 설리-남해, 부윤-남해, 연곡-남해 등 버스 이용하여 남해공용터미널 하차<br>- 대중교통 이용시 버스 배차시간 확인 필수<br>- 남해바래길 5코스와 동일하므로 남해바래길 관련 정보를 확인해도 좋음 <br>*남해바래길탐방안내센터 (055)863-8778 / http://cafe.daum.net/namhaetrails ', '36', '5', '0', '2020-02-16 07:05:20', '2025-05-19 06:24:02', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('39', '0', 'T_CRS_MNG0000005154', 'T_ROUTE_MNG0000000001', '남파랑길 39코스', '10.0', '240.0', '2', '0', '동면 지족리 하나로마트에서 삼동면 물건마을 버스정류장까지 이어지는 구간이다. <br>남해바래길로 지정된 구간은 아니나, 해안가를 따라 걸으며 죽방림의 장관을 경험할 수 있으며 전도마을, 둔촌마을 등 아름다운 어촌마을을 체험할 수 있는 구간이다. 구간 내에 꽃내 활성화 센터, 어부림 숲길 등 해안길, 마을길, 숲길 등 다양한 걷기여행길 유형을 만나볼 수 있는 구간이다.', '- 지족해협을 따라 죽방렴을 관람하며 걷는 길로 물건마을까지 이어지는 코스 <br>- 조붓한 마을길을 따라 걷다가 300년 이상의 역사를 가진 방품림이 물건방조어부림을 따라 임도 산책로를 걷는 코스 <br>- 해안가를 따라 걸으며 갯벌과 죽방림의 장관을 감상하며 전도마을, 둔촌마을에서 어촌마을 체험을 할 수 있는 코스', '- 시점 : 삼동면 지족리 하나로마트(경남 남해군 삼동면 동부대로 1875)<br>교통편) 남해시외버스터미널에서 남해-미조, 남해-단항, 남해-가인 버스 이용하여 삼동면사무소(지족) 정류장 하차<br>- 종점 : 삼동면 물건리 물건마을(경남 남해군 삼동면 동부대로1030번안길 49-10)<br>교통편) 물건마을 정류장에서 미조-남해, 설리-남해, 은점-남해 버스 이용하여 남해공용터미널 하차<br>- 식수는 시,종점 매점에서 구입하여 이용 가능하니 사전 준비 필수<br>- 코스 내 뚝방길이 있어 걷기 좋으나 조금 외진곳이라 혼자 걸을 땐 주의 필요<br>- 남해바래길 6코스와 동일하므로 남해바래길 관련 정보를 확인해도 좋음 <br>*남해바래길탐방안내센터 (055)863-8778 / http://cafe.daum.net/namhaetrails ', '36', '5', '0', '2020-02-16 07:09:06', '2025-03-06 11:13:41', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('40', '0', 'T_CRS_MNG0000005155', 'T_ROUTE_MNG0000000001', '남파랑길 40코스', '17.0', '390.0', '3', '0', '삼동면 물건바을 버스정류장에서 미조면 송정리 천하몽돌해변 입구까지 이어지는 구간으로 “남해바래길” 5코스와 중첩되는 구간이다. 남해바래길 5코스는 화전별곡길로 금산 자락으로 둘러싸인 내산을 중심으로 삼동 봉화마을로 이어지는 바다, 산, 강 등을 두루 지나며 자암 김구 선생의 화전별곡의 유유자적한 삶을 느낄 수 있는 구간이다. 내산의 편백숲과 원예예술촌, 바람흔적미술관, 나비생태공원 등을 지나며, 남해에서 가장 유명한 관광자원인 남해독일마을을 거치는 구간으로 남해에서 가장 아름다운 걷기여행길 구간 중 하나이다. ', '- 금산 자락으로 둘러싸인 내산을 중심으로삼동 봉화마을로 이어지는 바다, 산, 강 등을 두루 지나며 걸을 수 있는 구간<br>- 유유자적한 화전별곡의 삶을 느끼며 편백숲과 원예예술촌, 바람흔적미술관, 나비생태공원, 독일마을 등을 지나는 코스<br>- 대한민국 근현대사의 한 페이지를 장식한 파독 광부와 간호사 이야기를 들을 수 있는 이국적인 독일마을을 지나는 코스', '- 시점 : 삼동면 물건리 물건마을(경남 남해군 삼동면 동부대로1030번안길 49-10)<br>교통편) 남해시외버스터미널에서 시내버스 남해-미조, 남해-설리, 은점-남해 이용하여 물건마을 정류장 하차<br>- 종점 : 미조면 송정리 천하몽돌해변 입구(경남 남해군 미조면 송정리 1396-5)<br>교통편) 천하 정류장에서 시내버스 남해-미조 이용하여 남해시외버스터미널 하차 <br>- 식수는 시,종점 매점에서 구입하여 이용할 수 있으니 사전 준비 필수<br>- 남해바래길 7코스와 동일하므로 남해바래길 관련 정보를 확인해도 좋음 <br>*남해바래길탐방안내센터 (055)863-8778 / http://cafe.daum.net/namhaetrails ', '36', '5', '0', '2020-02-16 07:12:44', '2025-04-24 08:35:48', '900');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('41', '0', 'T_CRS_MNG0000005156', 'T_ROUTE_MNG0000000001', '남파랑길 41코스', '18.0', '390.0', '2', '0', '송정리 천하몽돌해변 입구에서 남해 이동면 신전리 원천항가지 이어지는 구간으로 “남해바래길” 3코스와 일부 중첩되는 구간이다. 남해바래길 3코스는 구운몽길로 명명되어 있으며 서포 김만중 선생의 유배지로 유명한 노도와 벽련마을을 시작으로 여러 유배객들의 문학작품의 바탕이 된 금산과 상주해안의 절경을 감상할 수 있는 구간이다. 본 구간은 한려해상국립공원에 포함되어 있어 걷기여행시 일부 구간이 국립공원관리공단의 출입통제구간에 해당함에 따라 남해바래길에서도 이 구간을 적극적으로 홍보하지는 못하고 있음. 수려한 국립공원 구간의 매력을 느낄 수 있는 구간으로 이용자의 자연생태 보존을 위한 노력이 필요한 구간임', '- 정리 천하몽돌해변 입구에서 남해 이동면 신전리 원천항가지 이어지는 구간<br>- 소설 <구운몽>의 저자인 서포 김만중의 유배지였던 노도를 보며 걷는 코스 <br>- 금산과 상주해안의 절경을 감상할 수 있는 수려한 한려해상국립공원의 매력을 느낄 수 있는 코스<br>- 일부 사라진 구간은 포장된 마을길과 찻길로 이어져 있으므로 찻길을 걸을 시 주의가 필요한 구간  ', '- 시점 : 미조면 송정리 천하몽돌해변 입구(경남 남해군 미조면 송정리 1396-5)<br>교통편) 남해시외버스터미널에서 시내버스 남해-미조 이용 후 천하 정류장 하차<br>- 종점 : 이동면 신전리 남해바래길탐방안내센터(경남 남해군 이동면 성남로 99)<br>교통편) 남해시외버스터미널에서 남해~가천 버스, 신전 하차 후 도보 15분<br>- 국립공원 구간으로 일부 출입이 통제될 수 있음<br>- 남해바래길 9코스와 동일하므로 남해바래길 관련 정보를 확인해도 좋음 <br>*남해바래길탐방안내센터 (055)863-8778 / http://cafe.daum.net/namhaetrails ', '36', '5', '0', '2020-02-16 07:19:23', '2024-10-16 11:57:18', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('42', '0', 'T_CRS_MNG0000005158', 'T_ROUTE_MNG0000000001', '남파랑길 42코스', '16.0', '360.0', '2', '0', '이동면 신전리 원천항에서 홍현리 다랭이마을까지 연결되는 구간으로 “남해바래길” 2코스 앵강다숲길이 포함된 구간이다. 남해에서 가장 유명한 다랭이 마을까지 연결되는 구간으로 앵강다숲마을 내에 남해바래길 탐방 지원을 위한 지원센터가 구축되어 있으며 수려한 해안경관 및 숲길, 마을길 등이 잘 어우러져 있는 길이다. <br>다만, 해안길 옆에 경사가 심한 구간이 일부 포함되어 있어 걷기여행을 위한 이용자의 사전 준비 등이 필요한 구간이다.', '- 원천마을에서 파도치는 소리가 앵무새 소리와 닮았다고 하여 \'앵강만\'이라고 불리는 만곡진 바다를 걷는 길 <br>- 꽃무릇 군락으로 이름 난 앵강다숲길과 앵강만이 내려다보이는 마을길을 지나 해안숲으로 이어지는 코스 <br>- 수려한 해안경관 및 숲길, 마을길 등이 잘 어우러져 있는 코스로 이용객들이 많은 구간 ', '- 시점 : 이동면 남해바래길탐방안내센터(경남 남해군 이동면 성남로 99) <br>교통편) 남해버스터미널 남해~가천 버스, 신전 하차 후 도보 15분<br>- 종점 : 남면 홍현리 다랭이마을(경남 남해군 남면 남면로 702)<br>교통편) 가천 정류장에서 시내버스 남해-가천 이용하여 남해시외버스터미널 하차<br>- 식수는 사전준비하거나 매점에서 구입하여 이용가능<br>- 해안길 옆에 경사가 심한 구간이 일부 포함되어 있어 걷기여행을 위한 이용자의 사전 준비 필요<br>- 남해바래길 10코스와 동일하므로 남해바래길 관련 정보를 확인해도 좋음 <br>*남해바래길탐방안내센터 (055)863-8778 / http://cafe.daum.net/namhaetrails ', '36', '5', '0', '2020-02-16 07:22:45', '2025-05-19 06:24:53', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('43', '0', 'T_CRS_MNG0000005159', 'T_ROUTE_MNG0000000001', '남파랑길 43코스', '14.0', '300.0', '2', '0', '남면 홍현리 다랭이마을에서 남면 평산리 평산항까지 연결되는 구간으로 “남해바래길” 1코스 다랭이 지겟길과 중첩되는 구간이다. 다랭이 지겟길은 가천 다랭이마을에서 평산항까지 연결되는 구간으로 남해의 수려한 자연환경과 더불어 척박한 생활환경을 극복하기 위하여 산비탈을 깎아 만든 논과 밭을 볼 수 잇으며, 해산물을 채취하기 위하여 바다로 다녔던 바래길을 통하여 선조들의 삶의 모습을 간접적으로 체험할 수 있다. 구간 내에 향촌전망대, 선구몽돌해변, 유구진달래 군락지 등의 관광자원을 볼 수 있다. ', '- 가천다랭이마을을 출발해 펜션단지인 빛담촌을 거쳐 바닷가를 걷는 코스 <br>- 산비탈을 깎아 만든 논과 밭에서 조상들의 억척스러운 삶의 터전과 푸른 바다가 함께 만들어 내는 풍경을 볼 수있는 아름다운 길 <br>- 향촌전망대, 선구몽돌해변, 유구진달래 군락지 등 해변길과 마을길, 숲길을 지나며 뷰포인트가 있어 지루할 틈 없이 걸을 수 있는 코스 ', '- 시점 :  남면 홍현리 다랭이마을(경남 남해군 남면 남면로 702)<br>교통편) 남해시외버스터미널에서 시내버스 남해-가천 이용 후 가천다랭이마을 정류장 하차. 배차간격 유의<br>- 종점 : 남면 평산리 평산항(경남 남해군 남면 남면로 1739번길 43-2)<br>교통편) 평산2리 정류장에서 남해-가천 시내버스 이용하여 남해시외버스터미널 하차. 배차간격 유의<br>- 향촌조약돌해안의 산길은 인적이 많지 않음으로 유의<br>- 남해바래길 11코스와 동일하므로 남해바래길 관련 정보를 확인해도 좋음 <br>*남해바래길탐방안내센터 (055)863-8778 / http://cafe.daum.net/namhaetrails ', '36', '5', '0', '2020-02-16 07:26:16', '2024-10-16 12:00:21', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('44', '0', 'T_CRS_MNG0000005160', 'T_ROUTE_MNG0000000001', '남파랑길 44코스', '14.0', '360.0', '3', '0', '평산항에서 남해 서상리 서상여객선터미널가지 연결되는 구간으로 남해바래길로 지정되지 않은 구간이다. 남해를 대표하는 리조트인 힐튼남해CC를 지나 임진성, 남해 스포츠파크 등을 지나는 구간으로 중간에 천왕산 임도 구간을 지나 타 구간과 마찬가지로 해안길 및 마을길, 숲길 등이 반복되는 구간이다. 일부 구간에 짧게 위험구간이 분포하고 있어 걷기여행자의 유의가 필요하다. 남해바래길의 단절구간을 연결하는 구간으로서의 특징을 보유하고 있다. ', '- 임진왜란 당시 축성되어 지역을 수호한 임진성으로 향하는 길 <br>- 평산항을 출발해 조붓한 농로와 숲길을 걸으며 임진성을 통과하여 마을길, 천황산 숲길로 이어지는 코스 <br>- 아름다운 뷰를 선사하는 천황산 숲길를 지나 노을이 아름다운 남해스포츠파크에서 마무리하는 코스 ', '- 시점 : 남면 평산리 평산항(경남 남해군 남면 남면로 1739번길 43-2<br>교통편) 남해시외버스터미널에서 시내버스 남해-가천 이용 후 평산2리 정류장 하차. 배차간격 유의<br>- 종점 : 서면 서상리 서상여객선터미널(경남 남해군 서면 남서대로168번길 28-4)<br>교통편) 서상마을 앞 정류장에서 남해-가천 버스 이용하여 남해시외버스터미널 하차. 배차간격 유의<br>- 인적이 많지 않은 산길이 있으므로 주의 필요<br>- 일부 구간에 짧게 위험구간이 분포하고 있어 걷기여행자의 유의가 필요<br>- 남해바래길 12코스와 동일하므로 남해바래길 관련 정보를 확인해도 좋음 <br>*남해바래길탐방안내센터 (055)863-8778 / http://cafe.daum.net/namhaetrails ', '36', '5', '0', '2020-02-16 07:30:39', '2025-04-07 08:31:45', '900');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('45', '0', 'T_CRS_MNG0000005161', 'T_ROUTE_MNG0000000001', '남파랑길 45코스', '13.0', '270.0', '2', '0', '서상여객선 터미널에서 서면 중련리 새남해농협중현지소까지 연결되는 구간으로 “남해바래길” 14코스와 일부 중복되는 구간이다. 남해바래길 14코스는 망운산노을길로 명명되어 있으며 망운산 자락을 따라 일몰을 감상할 수 있는 매력을 보유하고 있다. 일부 구간이 해안길을 따라 형성된 갯길을 통과함에 따라 조수간만에 차에 따라 걷기여행이 영향을 받을 수 있음에 따라 걷기여행자의 유의가 요구된다. ', '- 망운산 자락을 따라 아름다운 일몰을 감상할 수 있는 매력을 보유한 길<br>- 내륙의 숲속 농로와 몽돌해안, 기암괴석이 있는 눈이 시원해지는 해안길을 걸을 수 있는 볼거리가 많은 코스<br>- 만조 때는 안전을 위해 약간 우회해서 걸어야 하는 구간이 있음 ', '- 시점 : 서면 서상리 서상여객선터미널(경남 남해군 서면 남서대로1687번길 28-4) <br>교통편) 남해시외버스터미널에서 시내버스 남해-대곡, 남해-남면 이용 후 서상마을 앞 정류장 하차. 배차간격 유의<br>- 종점 : 서면 중현리 새남해농협중현지소(경남 남해군 서면 남서대로 2618)<br>교통편) 회룡 정류장에서 남해-우물, 남해-대곡(회룡), 남해-서상 버스 이용하여 남해시외버스터미널 하차. 배차간격 유의<br>- 일부 구간 갯길을 통과하여 만조 시 해안가를 걷지 않도록 유의함<br>- 어두울 때는 걷기가 불편한 해안 비포장길이 중간중간 있으므로 코스가 끝날 무렵 노을을 볼 수 있도록 계획하는 것이 좋음<br>- 남해바래길 13코스와 동일하므로 남해바래길 관련 정보를 확인해도 좋음 <br>*남해바래길탐방안내센터 (055)863-8778 / http://cafe.daum.net/namhaetrails ', '36', '5', '0', '2020-02-16 07:33:30', '2025-05-26 04:14:39', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('46', '0', 'T_CRS_MNG0000005162', 'T_ROUTE_MNG0000000001', '남파랑길 46코스', '18.0', '390.0', '2', '0', '서면 중현리 새남해농협중현지소에서 남해대교 교차로까지 연결되는 구간으로 “남해바래길” 13코스인 “이순신호국길”과 일부 코스가 중복되는 구간이다. 노량해전 당시 이순신장군의 유해가 최초로 육지에 오른 곳으로, 사적 232호로 지정된 남해 관음포 이충무공전몰유허(이락사)와 남해 충렬사 등이 구간 내 핵심 자원이다. 400여년전 이순신 장군의 운구행렬이 지나간 역사의 숨결과 호국정신을 느낄 수 있는 구간이자, 남해구간의 걷기여행을 마무리하는 종점 구간이기도 하다. ', '- 400여년전 이순신 장군의 운구행렬이 지나간 역사의 숨결과 호국정신을 느낄 수 있는 코스<br>- 바다와 떨어져 목가적인 느낌이 물씬한 마을길과 숲길을 따라 걷는 아름다운 구간', '- 시점 : 서면 중현리 새남해농협중현지소(경남 남해군 서면 남서대로 2618)<br>교통편) 남해시외버스터미널에서 시내버스 남해-서상, 남해-대곡, 남해-우물, 남해-중현 이용 후 회룡 정류장 하차<br>- 종점 : 구 노량공영주차장 해안길 데크 입구(금남면 노량리 산99-2번지) <br>교통편) 구노량 정류장에서 하동-진교, 진교-하동 버스 이용하여 하동시외버스터미널 하차<br>- 남해바래길과 동일하므로 남해바래길 관련 정보를 확인해도 좋음 <br>*남해바래길탐방안내센터 (055)863-8778 / http://cafe.daum.net/namhaetrails ', '36', '5', '0', '2020-02-16 07:36:55', '2025-05-19 05:57:51', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('47', '0', 'T_CRS_MNG0000005163', 'T_ROUTE_MNG0000000001', '남파랑길 47코스', '28.0', '570.0', '2', '0', '남해군 남해대교 교차로에서 하동읍 광평리 하동송림 입구까지 연결되는 구간으로 구간 초반에는 하동의 마을 및 농업경관을 감상하며 걷다가 섬진강을 따라 조성된 섬진강 100리 테마로드를 따라 송림공원까지 이어지는 구간이다. 섬진강변에 조성된 섬진강 100리 테마로드는 자전거길과 걷기여행길이 중복되는 구간으로 섬진강의 수려한 경관을 따라 길이 조성되어 있어 누구나 쉽게 걷기여행을 즐길 수 있는 구간이다. 드라마 허준 촬영지인 하동포구공원과 종점 구간인 하동송림구간은 특히 아름다운 경관을 뽐내고 있으며 하동송림공원은 음악과 함께 소나무숲의 매력을 만끽할 수 있다. ', '- 하동의 마을을 너른 벌판을 따라 걷다가 섬진강변을 따라 하동포구공원, 하동 송림공원, 섬진교까지 이어지는 코스<br>- 섬진강 수려한 경관을 따라 자전거길과 걷기길이 있어 누구나 쉽게 걷기여행을 즐길 수 있는 코스', '- 시점 : 구 노량공영주차장 해안길 데크 입구(금남면 노량리 산99-2번지)<br>교통편) 하동시외버스터미널에서 시내버스 하동-진교 이용 후 구노량 정류장 하차. 배차간격 유의<br>- 종점 : 하동읍 광평리 섬진교 동단(경남 하동군 하동읍 광평리 439-2)<br>교통편) 하동시외버스터미널 또는 하동역 도보 이동 가능. 또는 하동고등학교 정류장에서 18번 버스 이용<br>- 코스 내 음수대가 많이 없으므로 식수 사전 준비', '36', '18', '0', '2020-02-16 07:40:31', '2024-10-16 12:06:17', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('48', '0', 'T_CRS_MNG0000005164', 'T_ROUTE_MNG0000000001', '남파랑길 48코스', '13.0', '270.0', '1', '0', '하동송림공원에서부터 시작하여 광양 진원면 진월초등학교까지 이어지는 구간으로 섬진강 구간과 마찬가지로 섬진강변에 조성된 길을 따라 섬진강의 수려한 자연경관을 감상할 수 있는 걷기여행길이다. 다만, 하동구간의 길은 자전거와 걷기여행자 모두 이용할 수 있는 구간으로 특화되어 있는 반면, 광양구간의 섬진강 길은 자전거 이용자를 위한 구간으로 조성되어 있어 자전거 이용자가 많은 시즌에는 걷기여행자의 주의가 요구된다. 봄이면 아름다운 매화 및 벚꽂으로 아름다운 경관을 자랑하며 계절에 따라 다양한 아름다움을 경험할 수 있는 구간으로서의 특징을 보유하고 있다. ', '- 경상도와 전라도를 잇는 섬진교에서 시작하는 코스<br>- 섬진강 강변길 따라 섬진강의 수려한 자연경관을 감상하는 걷기여행길<br>- 봄이면 아름다운 매화 및 벚꽃으로 아름다운 경관을 자랑하며 계절에 따라 다양한 아름다움을 경험할 수 있는 구간', '- 시점 : 하동읍 광평리 섬진교 동단(경남 하동군 하동읍 광평리 439-2)<br>교통편) 하동시외버스터미널 시내버스 18, 35-1번 버스 이용 후 하동고등학교 정류장 하차. 또는 도보 이동 가능<br>- 종점 : 진월면 선소리 진월초등학교(전남 광양시 진월면 선소중앙길 69)<br>교통편) 진월초교정류장에서 54번 버스 이용하여 하동시외버스터미널 하차<br>- 자전거길과 걷기여행길이 함께 조성되어 걷기여행시 주의가 필요<br>- 남해고속도로 운전자 휴식처인 섬진강휴게소 이용 가능(섬진강휴게소 부산방향 뒤편 보행자 진입로 이용)', '36', '18', '0', '2020-02-16 07:43:36', '2024-10-16 12:07:47', '300');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('49', '0', 'T_CRS_MNG0000005165', 'T_ROUTE_MNG0000000001', '남파랑길 49코스', '15.0', '300.0', '1', '0', '진월초등학교에서 광양 중동 근린공원까지 연결되는 구간으로 광양 48코스 종점인 진월초등학교와 인접한 망덕포구에 윤동주 시인의 유고를 보존했던 정병욱 가옥이 입지하고 있으며, 윤동주 쉼터 등의 자원을 보유하고 있다. 망덕포구 일원은 전어축제 등으로 유명하고, 섬진강과 바다가 맞닿는 곳으로 광양제철 및 광양만의 경관을 감상할 수 있는 매력을 보유하고 있다. 망덕포구를 지나 광양을 대표하는 광양제철소를 따라 구간이 연결되어 광양의 산업, 생태, 문화를 다양하게 접할 수 있는 구간이다.', '- 망덕포구를 지나 광양을 대표하는 광양제철소를 따라 구간이 연결되어 광양의 산업, 생태, 문화를 다양하게 접할 수 있는 코스<br>- 간 내에 망덕포구와 윤동주 유고를 보관했던 정병욱 가옥이 입지하고 있음', '- 시점 : 진월면 선소리 진월초등학교(전남 광양시 진월면 선소중앙길 69)<br>교통편) 하동시외버스터미널 54번 버스, 진원남초교 하차<br>- 종점 : 중동 중동근린공원(전남 광양시 광장로 94)<br>교통편) 중마버스터미널 도보 이동 가능. 또는 홈플러스 정류장에서 9, 88, 1001번 버스 등 이용 <br>- 노선별 운행시간이 다를 수 있으니 미리 버스노선과 시간 확인 필요', '38', '4', '0', '2020-02-16 07:46:25', '2025-05-19 06:27:47', '300');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('50', '0', 'T_CRS_MNG0000005166', 'T_ROUTE_MNG0000000001', '남파랑길 50코스', '18.0', '390.0', '2', '0', '광양 중동 근린공원에서 광양읍 광양터미널까지 연결되는 구간으로 광양시청 인근의 도심구간과 구봉산 등산로를 지나 광양읍으로 연결되는 구간이다. 구간 대부분을 차지하는 구봉산 등산로 구간은 구봉산 전망대가 입지하고 있으며, 전망대에 올라서면 광양만 일대 및 광양시 전체를 내려다볼 수 있는 뷰포인트를 보유하고 있어 최근 특히 많은 관광객들이 방문하고 있는 구간이다. 구봉산 등산로가 끝나는 지점에 사라실 예술촌이 입지하고 있으며 종점인 광양터미널에서 타 지역으로의 이동이 용이하다. ', '- 광양시의 산업단지, 도심을 지나 구봉산 등산로로 연결되는 코스<br>- 봉산 전망대의 경우 광양만 일대와 광양시 전경을 감상할 수 있어 많은 관광객의 사랑을 받고 있으며 사라실 예술촌 등의 자원을 경유하는 코스', '- 시점 : 중동 중동근린공원(전남 광양시 광장로 94)<br>교통편) 광양중마버스터미널에서 87, 88번 버스 이용 후 성호3차 정류장 하차. 또는 도보 이동 가능.<br>- 종점 : 광양읍 인동리 광양터미널(전남 광양시 광양읍 순광로 692)<br>교통편) 광양공영버스터미널에서 버스 이용<br>- 차도 구간이 있어 걷기여행자의 유의 필요', '38', '4', '0', '2020-02-16 07:49:24', '2024-10-16 12:10:13', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('51', '0', 'T_CRS_MNG0000005167', 'T_ROUTE_MNG0000000001', '남파랑길 51코스', '15.0', '300.0', '1', '0', '광양터미널에서 순천시 해룡면 신성리 일원을 경유하여 여수 율촌면까지 이어지는 구간으로 광양구간에서 순천 해룡면으로 이어지는 구간은 농로 구간이 많아 걷기여행 매력이 높지 않으나 순천시 해룡면 신성리에 위치한 순천왜성 및 하천을 따라 잘 조성된 산책로를 따라 여수 율촌면 율촌파출소까지 걸을 수 있는 구간이다. <br>구간 내 핵심 자원은 순천왜성으로 왜성 꼭대기 망루에 서면 앞뒤로 여수, 광양, 남해 바다고 보이고, 구례, 남원 등의 내륙부까지 조망할 수 있다. <br>순천왜성은 유정, 권율, 이순신 등 역사 속 위대한 장군들의 이야기를 보유하고 있으며 이순신장군이 27일간을 머물면서 전사 하루 전 소서행장을 노량 앞바다로 유인하여 대첩을 거둔 유서깊은 전적지이다. ', '- 광양에서 순천시 해룡면을 지나 여수 율촌면으로 이어지는 구간으로 전형적인 농촌경관을 따라 걷는 코스<br>- 순천왜성 및 하천변을 따라 잘 조성된 산책로를 따라 여수까지 이어짐', '- 시점 : 광양읍 인동리 광양터미널(전남 광양시 광양읍 순광로 692)<br>교통편) 광양공영버스터미널행 버스 이용<br>- 종점 : 율촌면 조화리 율촌파출소(전남 여수시 율촌면 율촌로 103)<br>교통편) 율촌수생약국 정류장에서 94, 95, 96번 버스 등 이용하여 순천역 하차. 또는 율촌역(간이역) 이용<br>- 광양구간에서 순천 해룡면으로 이어지는 구간은 농로 구간이 많아 걷기여행 매력이 높지 않음<br>- 순천시 해룡면 신성리에 위치한 순천왜성 및 하천을 따라 잘 조성된 산책로를 따라 이동할 수 있음', '38', '4', '0', '2020-02-16 07:58:16', '2025-05-19 06:17:09', '300');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('52', '0', 'T_CRS_MNG0000005168', 'T_ROUTE_MNG0000000001', '남파랑길 52코스', '15.0', '300.0', '1', '0', '여수 율촌파출소에서 소하면 덕양리 소라초등학교까지 이어지는 구간으로 여수공항을 끼고 농촌경관을 따라 걸을 수 있는 구간이다.  구간과 직접적으로 연결되지는 않으나 우리나라 기독교에서 존경받는 손양원 목사의 순교기념관 및 애양원 역사관 등을 연계 관광할 수 있는 구간이다. 구간 종점 구간에 갓길이 없는 도로구간이 일부 포함되어 있어 걷기여행자들의 주의가 요구된다. ', '- 여수공항 및 여수국가산업단지의 전경을 바라보며 걸을 수 있는 구간으로 농업경관, 산업경관 등이 두루 어우러진 코스<br>- 걷는 동안 농촌의 삶을 엿 볼 수 있으며 길 주변에 식재된 농작물을 보고 배울 수 있고 확 트인 벌판과 여수 국가산단을 조망할 수 있는 코스<br>- 노선이 직접 지나지는 않으나 기독교에서 가장 존경받는 손양원 목사의 순교기념관 및 애양원 역사관 등을 연계 관광할 수 있음 ', '- 시점 : 율촌면 조화리 율촌파출소(전남 여수시 율촌면 율촌로 103)<br>교통편) 순천역 정류장에서 330번 버스 이용하여 율촌수생약국 정류장 하차<br>- 종점 : 소라면 덕양리 소라초등학교(전남 여수시 소라면 덕양로 431)<br>교통편) 소라초등학교 정류장에서 31, 32, 33, 34, 35, 36, 93번 버스 이용하여 여수공항 하차<br>- 봉정마을과 여수공항입구 신풍마을에 식당이 있어 식사 가능<br>- 시멘트 포장된 농로와 마을길로 그늘이 없고 쉼터 등이 많지 않으므로 준비 필수<br>- 종점 구간에 갓길이 없는 도로구간이 일부 포함되어 있어 걷기여행자들의 주의가 요구', '38', '13', '0', '2020-02-16 08:36:25', '2025-05-19 06:19:03', '300');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('53', '0', 'T_CRS_MNG0000005169', 'T_ROUTE_MNG0000000001', '남파랑길 53코스', '11.0', '210.0', '1', '0', '여수 소하면 소라초등학교에서 중흥동 흥국사 입구까지 연결되는 구간으로 여수국가산업단지를 지나는 구간이다. 산업단지를 지나는 구간 특성 상, 대부분 도로구간이 포함된다.  대부분 인도가 확보되어 있으나, 일부 걷기여행 안전성이 떨어지는 구간이 포함되어 있어 걷기여행자들의 주의가 요구된다. 구간 중간부에 입지한 남수제를 지나면서부터 숲길 임도구간을 지나 흥국사 입구까지 연결되어 구간 종반부에는 쾌적한 숲길을 걸으며 힐링을 체험할 수 있는 구간이다. ', '- 철로를 따라 이동하면서 폐쇄된 옛 간이역의 소박한 풍경을 감상할 수 있는 코스<br>- 마을에 흐르는 쌍봉천과 연등천을 따라 조성된 산책로를 걷는 코스', '- 시점 : 소라면 덕양리 소라초등학교(전남 여수시 소라면 하세동길 52-1) <br>교통편) 덕양시외버스터미널 도보 10분<br>- 종점 : 여수종합버스터미널(전남 여수시 좌수영로 268) <br>교통편) 여수종합버스터미널', '38', '13', '0', '2020-02-16 09:07:32', '2024-10-16 12:14:32', '300');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('54', '0', 'T_CRS_MNG0000005170', 'T_ROUTE_MNG0000000001', '남파랑길 54코스', '7.0', '180.0', '2', '0', '여수 중흥동 흥국사 입구에서 여수 종화동 여수해양공원까지 이어지는 구간으로 숲길과 해안길, 도심길 등 다양한 걷기여행길이 이어지는 구간이다. 구간에 위치한 숲길 구간은 여수시가 기 조성한 영취산 둘레길에 일부 포함되는 구간이며 봉화산 산림욕장 및 무장애 숲길 구간을 지난 이후에는 도심구간을 거쳐 여수해양공원으로 이어진다. 종점부에 하멜기념관 및 하멜등대 등이 입지하고 있어 연계 관광할 수 있으며 여수관광의 핵심공간이라 할 수 있는 여수해양공원 일대를 둘러볼 수 있다. ', '- 여수종합버스터미널에서 여수해양공원까지 이어지는 구간<br>- 여수엑스포공원, 자산공원, 오동도, 하멜기념관, 하멜등대 등 해양관광자원이 분포하여 걷기와 연계 관광을 즐길 수 있는 코스', '- 시점 : 여수종합버스터미널(전남 여수시 소라면 덕양로 431)<br>교통편) 여수종합버스터미널<br>- 종점 : 종화동 여수해양공원(전남 여수시 이순신광장로 188-1)<br>교통편) 여수엑스포역 도보 이동 가능. 또는 여수연합항운(여수해양공원)에서 555번 버스 이용하여 여수연합항운 하차<br>- 대중교통 이용 시 배차간격 확인 필요<br>- 노선 후반부는 여수시내를 통과하고 있어 편의시설이 이용 가능', '38', '13', '0', '2020-02-16 09:10:44', '2024-10-16 12:15:46', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('55', '0', 'T_CRS_MNG0000005171', 'T_ROUTE_MNG0000000001', '남파랑길 55코스', '16.0', '300.0', '1', '0', '여수해양공원에서부터 여수 소호동 소호요트장까지 연결되는 구간으로 여수 갯가길 및 여수밤바다길 등이 중첩되는 구간이다. 구간 시점부는 여수해양공원으로 밤이면 낭만포차가 들어서고 다양한 길거리 버스킹 등이 이뤄지는 젊음의 거리로서 여수 관광의 핵심 공간이다. 해양공원을 지난 남산공원, 남해안의 최대 수산물 집산어항 극동항, 웅촌 마리나 항만, 이충무공 유적지 등을 거쳐 여수 소호요트장까지 이어진다. 구간 곳곳에 공원이 잘 조성되어 있으며, 도로를 지나는 구간도 인도가 확보되어 있어 안전하게 걸을 수 있는 구간이다. ', '- 여수해양공원에서부터 여수 소호동 소호요트장까지 연결되는 구간<br>- 이순신광장, 해양공원, 고소동1004벽화마을, 여수항과 국동항, 웅천해변문화공원, 웅천친수공원(해수욕장), 선소유적지, 소호동동다리 등 다수의 관광지가 분포하여 다양한 볼거리와 먹거리가 풍부한 코스', '- 시점 : 종화동 여수해양공원(전남 여수시 이순신광장로 188-1)<br>교통편) 여수EXPO역 정류장에서 2, 6, 7번 버스 이용 후 경찰서 정류장 하차<br>- 종점 : 소호동 여수소호요트장(전남 여수시 소호로 392)<br>교통편) 소호요트경기장에서 27, 88, 89번 버스 이용하여 여수시외버스터미널 또는 여수엑스포역 하차<br>- 시,종점에서 대중교통 1일 60회 이상 운행<br>- 전반적으로 안전성이 양호하고 주변 풍광이 아름다우나 그늘이 없어 도보여행시 준비 필요', '38', '13', '0', '2020-02-16 09:14:01', '2024-10-16 12:17:17', '300');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('56', '0', 'T_CRS_MNG0000005172', 'T_ROUTE_MNG0000000001', '남파랑길 56코스', '15.0', '300.0', '2', '0', '여수 소호요트장에서부터 화양면 안포리 원포 버스정류장까지 연결되는 구간으로 초반에는 해안가를 따라 조성된 도로(인도 확보)구간을 따라 걷다가 다양한 마을을 지나는 구간으로 이루어져 있다.  돌담이 아름다운 마을, 한적한 해수욕장을 끼고 있는 어촌마을 등 다양한 마을을 지나며, 마을주민들의 푸근한 인심을 경험할 수 있는 구간이다. 다만, 구간 내에 갓길이 없는 도로구간이 다수 포함되어 있어 걷기여행자들의 안전이 유의된다.', '- 소호 요트장을 출발하여 해안가를 따라 돌담이 아름다운 마을 및 한적한 해수욕장 등 조용한 걷기여행이 가능한 코스<br>- 돌담이 아름다운 마을, 한적한 해수욕장을 끼고 있는 어촌마을 등 다양한 마을을 지나며, 마을주민들의 푸근한 인심을 경험할 수 있는 코스', '- 시점 : 소호동 여수소호요트장(전남 여수시 소호로 392)<br>교통편) 여수엑스포역 또는 여수종합버스터미널에서 27, 88번 버스 이용하여 소호요트경기장 정류장 하차<br>- 종점 : 화양면 안포리 원포마을 정류장(전남 여수시 화양면 셋돔길 6)<br>교통편) 원포 정류장 26-1, 28, 29번 버스 이용하여 여수종합버스터미널 하차<br>- 구간 내에 갓길이 없는 도로구간이 다수 포함되어 있어 걷기여행자들의 안전 유의<br>- 그늘이 없는 차도가 포함되어 있어 충분한 식수 준비', '38', '13', '0', '2020-02-16 09:17:01', '2024-10-16 12:18:12', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('57', '0', 'T_CRS_MNG0000005173', 'T_ROUTE_MNG0000000001', '남파랑길 57코스', '18.0', '360.0', '2', '0', '화양면 원포 버스정류장에서 화양만 서촌리 서촌마을가지 이어지는 구간으로 구간 내에 고봉산 둘레길이 다수 포함되어 있어 숲길이 많이 포함되어 있으며 고봉산에서 여수 가막만 일원 및 아름다운 해양경관 등을 바라보며 걸을 수 있는 매력적인 구간이다. 한적한 시골마을 및 어촌마을 등 다양한 마을을 지나는 구간으로 길을 걷가 갑작스럽게 펼쳐지는 남해안의 절경에 걷기여행의 보람을 느낄 수 있는 구간이다. ', '- 고봉산 둘레길이 포함되는 구간으로 고봉산에서 바라보는 여수 가막만 일원의 아름다운 해양경관이 특히 아름다운 코스<br>- 한적한 시골마을과 어촌마을을 따라 조용하게 걷기여행을 즐길 수 있음 ', '- 시점 : 화양면 안포리 원포마을 정류장(전남 여수시 화양면 셋돔길 6)<br>교통편) 여수종합버스터미널에서 28, 29, 26-1번 버스 이용하여 원포 정류장 하차. 배차간격 유의<br>- 종점 : 화양면 서촌리 서촌마을(전남 여수시 화양면 서촌리 584-7)<br>교통편) 서촌 정류장에서 21, 26, 29번 버스 이용하여 여수종합버스터미널 하차<br>- 편의시설이 많지 않으므로 사전에 준비 필요', '38', '13', '0', '2020-02-16 09:22:47', '2024-10-16 12:19:18', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('58', '0', 'T_CRS_MNG0000005174', 'T_ROUTE_MNG0000000001', '남파랑길 58코스', '16.0', '360.0', '2', '0', '화양면 서촌마을에서 소라면 창무신성교회 정류장가지 연결되는 구간으로 숲길, 마을길 등이 변화하며 반복되는 구간이다. 숲길 및 마을구간이 특별한 테마와 특색을 보유하고 있진 않으나, 가장 전형적인 숲길 및 마을구간을 걸을 수 있는 구간이다.', '- 화양면 서촌마을에서 소라면 창무신성교회 정류장가지 연결되는 구간으로 숲길, 마을길 등이 변화하며 반복되는 코스<br>- 특별한 자원이 분포하고 있지는 않으나 숲길, 마을길 등이 반복되어 다양한 경관 변화가 매력적인 구간', '- 시점 : 화양면 서촌리 서촌마을(전남 여수시 화양면 서촌리 584-7)<br>교통편) 여수종합버스터미널에서 21, 26, 26-1, 27번 버스 이용하여 서촌 정류장 하차<br>- 종점 : 소라면  현천리 소라면가사 정류장(전남 여수시 소라면 현천리 1038-3)<br>교통편) 소라면가사에서 84버스 등 이용하여 여수종합버스터미널 하차 <br>- 코스 내 화장실, 식수 등 편의시설이 없어 충분한 식수 준비를 요함', '38', '13', '0', '2020-02-16 09:26:52', '2024-10-16 12:20:16', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('59', '0', 'T_CRS_MNG0000005175', 'T_ROUTE_MNG0000000001', '남파랑길 59코스', '8.0', '180.0', '2', '0', '여수 창문신성교회에서 소라면 궁항정류장까지 이어지는 구간으로 농로를 따라 걷가보면 여수 YMCA 생태교육관이 나타나고 그 이후부터는 해안가를 따라 궁항정류장까지 이어진다. 해안가 구간은 작은 어촌마을들을 지나며 소소한 해안경관을 즐길 수 있는 구간으로 일부 구간은 자전거도로로 정비되어 있고, 대부분 인도가 확보되어 있어 걷기여행시 안전에 큰 불편은 없다. 갓길이 없는 구간의 경우도 교통량이 많지 않아 해안경관을 바라보며 한적한 마을을 걷는 구간으로서의 특징을 보유하고 있다. ', '- 창무마을에서 궁항마을까지 농로와 자전거길로 이어진 코스로 노선에서 조망되는 여자만 풍광이 아름다운 코스<br>- 리아스식 해안이 만들어내는 독특한 해안경관과 한적한 어촌마을들을 지나며 소소한 경관을 즐길 수 있는 코스', '- 시점 : 소라면 현천리 소라면가사 정류장(전남 여수시 소라면 현천리 1038-3)<br>교통편) 여수종합버스터미널에서 84버스 이용하여 소라면가사 정류장 하차<br>- 종점 : 소라면 사곡리 궁항정류장(전남 여수시 소라면 궁항길 131)<br>교통편) 궁항 정류장에서 91번 버스 이용하여 여천역 또는 여수종합버스터미널 하차<br>- 노선 내 화장실, 식수, 편의점 등 편의시설 없으므로 준비 필요<br>- 노선 전체가 농로 및 자전거길로 그늘이 없어 준비 필요', '38', '13', '0', '2020-02-16 09:30:18', '2024-10-16 12:21:59', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('60', '0', 'T_CRS_MNG0000005176', 'T_ROUTE_MNG0000000001', '남파랑길 60코스', '15.0', '300.0', '1', '0', '여수 소라면 사곡리 궁항 정류장에서 순천 해룡면 와온삼거리 정류장까지 이어지는 구간으로 해안길 및 일부 도로구간을 따라 순천까지 연결되는 구간이다. 해안길을 따라 여수시가 “갯노을길”이라는 푯말을 설치해놓은 구간이 있으며 일부 구간은 데크 등의 공사가 진행되고 있다. 순천으로 이어지는 종점부 구간에 일부 숲길 구간(가림산 일원)이 포함되고 있으며, 해당구간은 하계 시즌에는 제초작업이 원활하지 않아 걷기여행자의 안전이 유의된다. 구간 중간중간에 노을 및 일몰을 감상할 수 있는 구간이 있으며, 이런 구간에는 펜션, 카페 등이 자리잡고 있어 걷기여행시 쉬었다 갈 수 있다. ', '- 여수시와 순천시가 연결되는 구간으로 해안가를 따라 여수시의 ‘갯노을길’ 등이 조성되어 있는 코스<br>- 대부분이 여자만을 따라 개설된 자전거길과 농로로 주변 풍광이 아름다운 코스', '- 시점 : 소라면 사곡리 궁항정류장(전남 여수시 소라면 궁항길 131)<br>교통편) 여수종합버스터미널 또는 여천역 정류장에서 90, 91번 버스 이용 후 궁항 정류장 하차. 배차간격 유의<br>- 종점 : 해룡면 상내리 와온해변(전남 순천시 해룡면 와온길 133)<br>교통편) 순천역에서 98번 버스 이용하여 와온해변 하차. 배차간격 유의<br>- 편의시설이 많지 않아 식수 및 간식 사전 준비 필요하며 코스 대부분에 그늘이 없어 준비 필요<br>- 구간 중간에 노을 및 일몰을 감상할 수 있는 장소가 있으며 펜션, 카페 등이 자리하고 있어 걷기여행시 휴식 가능', '38', '13', '0', '2020-02-16 09:33:06', '2024-10-16 12:23:23', '300');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('61', '0', 'T_CRS_MNG0000005177', 'T_ROUTE_MNG0000000001', '남파랑길 61코스', '14.0', '300.0', '2', '0', '순천 해룡면 와온삼거리 정류장에서 별량면 학산리 별량화포까지 이어지는 길로서 순천시가 조성한 “남도삼백리길” 중 순천만갈대길에 포함되는 구간이다. 와온해변 일원부터 순천만 습지공원을 거쳐 화포해변까지 이어지는 구간으로 순천만 습지공원이 구간 가운데에 자리잡고 있다. 여수에서 순천구간으로 진입 시, 와온해변을 거쳐 순천만 습지공원 내 용산전망대까지 올라갈 수 있으며, 순천만 습지공원에서 입장료를 낸 후 용산전망대까지 걷는 코스가 가장 잘 알려진 구간이다. 안내표지판 등이 잘 설치되어 있으며 순천만의 절경을 따라 걸을 수 있는 구간이다. 다만, 본 구간은 순천시의 순천만 갈대길에서 동계시즌에 철새보호를 위해 폐쇄하는 구간을 우회하는 코스로 설정되어 있으며, 동계시즌 이외에는 순천 61-1코스를 통해 걷기여행을 즐기는 것이 일반적이다. ', '- 와온해변에서 별량면 학산리 별량화포까지 이어지는 구간으로 순천만 갈대길이 포함된 코스<br>- 순천만 습지공원의 절경을 감상할 수 있는 구간으로 대부분의 노선이 안전 관리가 잘된 흙길과 자전거길로 구성<br>- 순천만 습지공원의 용산전망대에서 특히나 아름답다는 일몰경관을 감상할 수 있는 코스<br>- 순천만 습지공원은 동계시즌에 철새보호를 위해 폐쇄되어 61-1코스로 우회하여 이동(3.4km/1시간 소요)', '- 시점 : 해룡면 상내리 와온해변(전남 순천시 해룡면 와온길 133)<br>교통편) 순천역에서 98번 버스 이용하여 와온해변 하차. 배차간격 유의<br>- 종점 : 별량면 학산리 별량화포(전남 순천시 별량면 학산해안길 67)<br>교통편) 화포 정류장에서 81, 82번 버스 이용하여 순천역 또는 순천종합버스터미널 하차. 배차간격 유의<br>- 사시사철 아름다운 길이나 칠면초가 붉게물든 9월부터 갈대가 만발한 11월이 가장 유명하며 특히 석양이 매우 아름다움<br>- 순천만습지를 통과하는 코스로 관람료 납부해야 함.<br>- 10월부터 이듬해 4월까지는 순천만습지에서 장산마을 사이 연인의길(인안방조제) 일부가 철새보호를 위해 통제하고 있어 우회 노선(남파랑길 61-1코스)으로 걸어야 함', '38', '11', '0', '2020-02-16 09:37:54', '2025-05-21 04:07:27', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('62', '0', 'T_CRS_MNG0000005178', 'T_ROUTE_MNG0000000001', '남파랑길 61-1코스(우회노선)', '16.0', '330.0', '2', '0', '순천만 갈대습지공원 내부의 걷기여행길로서 동계시즌에 철새보호를 위해 폐쇄되는 구간이다. 동계시즌 이외에는 순천만 습지공원 입장 후, 갈대밭길을 따라 조성된 데크길을 따라 걸을 수 있는 구간으로 순천시의 “남도삼백리길” 중 순천만 갈대길로 지정된 주 노선이다. 갈대소리를 따라 아름다운 일몰을 바라보는 길로 많은 국내외 관광객들에게 사랑을 받고 있다. ', '- 와온해변에서 별량면 학산리 별량화포까지 이어지는 구간으로 순천만 갈대길이 포함된 코스<br>- 순천만 습지공원의 절경을 감상할 수 있는 구간으로 대부분의 노선이 안전 관리가 잘된 흙길과 자전거길로 구성<br>- 순천만 습지공원의 용산전망대에서 특히나 아름답다는 일몰경관을 감상할 수 있는 코스<br>- 순천만 습지공원은 동계시즌에 철새보호를 위해 폐쇄되어 61-1코스로 우회하여 이동(3.4km/1시간 소요)', '- 시점 : 해룡면 상내리 와온해변(전남 순천시 해룡면 와온길 133)<br>교통편) 순천역에서 98번 버스 이용하여 와온해변 하차. 배차간격 유의<br>- 종점 : 별량면 학산리 별량화포(전남 순천시 별량면 학산해안길 67)<br>교통편) 화포 정류장에서 81, 82번 버스 이용하여 순천역 또는 순천종합버스터미널 하차. 배차간격 유의<br>- 10월부터 4월까지는 순천만습지에서 장산마을 사이 연인의길(인안방조제) 일부가 철새보호를 위해 통제하고 있어 우회 노선으로 걸어야 함', '38', '11', '0', '2020-02-16 09:40:36', '2025-05-21 04:08:36', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('63', '0', 'T_CRS_MNG0000005179', 'T_ROUTE_MNG0000000001', '남파랑길 62코스', '25.0', '510.0', '1', '0', '화포해변에서 보성 벌교읍 경계까지 이어지는 구간으로 순천시의 “남도삼백리길” 중 꽃산너머동화사길 일부가 중첩되는 구간이다. 순천만의 해안길과 마을길을 감상하며 걷는 해안, 마을길 복합구간으로 일출이 유명한 별량화푸에서 출발하여 별량면 용두마을, 구룡마을, 송기마을, 죽림마을, 거차마을 등 다양한 역사문화적 스토리를 보유한 마을을 지나는 구간이다. 순천시의 꽃산너머동화사길은 구룡마을을 지나 동화사까지 이어지나, 코리아둘레길은 구룡마을 전에서 해안길을 따라 보성군 벌교읍으로 이어진다.', '- 순천만 해변 방파제와 구룡리 농로 및 동초천 제방을 따라 이동하는 순천시의 ‘남도삼백리길’ 중 꽃산너머 동화사길 일부가 포함된 코스 <br>- 순천에서 이어지는 구간으로 조정래 작가의 소설 태백산맥의 무대인 벌교읍까지 연결되는 구간으로 ?태백산맥 문학길? 일부가 포함되는 구간<br>- 일출이 유명한 별량화포에서 출발하여 용두마을, 구룡마을, 송기마을, 죽림마을 등 다양한 스토리를 보유한 마을을 지나는 코스', '- 시점 : 별량면 학산리 별량화포(전남 순천시 별량면 학산해안길 67)<br>교통편) 순천역 정류장에서 81, 82번 버스 이용하여 화포 정류장 하차<br>- 종점 : 벌교읍 부용교 동쪽사거리(전남 보성군 벌교읍 회정리 513-2)<br>교통편) 벌교버스공용터미널, 벌교역 도보 10분 이내<br>- 노선 대부분이 그늘이 없으므로 식수  등 사전 준비 필요', '38', '11', '0', '2020-02-16 09:44:07', '2025-05-19 06:19:52', '300');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('64', '0', 'T_CRS_MNG0000005180', 'T_ROUTE_MNG0000000001', '남파랑길 63코스', '21.0', '420.0', '1', '0', '화포해변에서 보성 벌교읍 경계까지 이어지는 구간으로 순천시의 “남도삼백리길” 중 꽃산너머동화사길 일부가 중첩되는 구간이다. 순천만의 해안길과 마을길을 감상하며 걷는 해안, 마을길 복합구간으로 일출이 유명한 별량화푸에서 출발하여 별량면 용두마을, 구룡마을, 송기마을, 죽림마을, 거차마을 등 다양한 역사문화적 스토리를 보유한 마을을 지나는 구간이다. 순천시의 꽃산너머동화사길은 구룡마을을 지나 동화사까지 이어지나, 코리아둘레길은 구룡마을 전에서 해안길을 따라 보성군 벌교읍으로 이어진다.', '- 보성군의 기 조성길인 ‘태백산맥 문학길’을 따라 걷는 구간으로 소설 태백산맥에 등장하는 보성여관, 홍교, 채동선 생가 등의 자원을 두루 체험하며 걷기여행을 즐길 수 있는 코스<br>- 벌교꼬막거리, 시장 등이 형성되어 있어 먹거리, 살거리가 풍부함  ', '- 시점 : 벌교읍 부용교 동쪽사거리(전남 보성군 벌교읍 회정리 513-2)<br>교통편) 벌교버스공용터미널, 벌교역에서 도보 10분 이내<br>- 종점 : 남양면 망주리 팔영농협 망주지소(전남 고흥군 남양면 망월로 82)<br>교통편) 망주 정류장 시내버스 벌교-부도 이용하여 벌교역 또는 벌교버스터미널 하차. 배차간격 유의<br>- 노선 대부분이 그늘이 없으므로 식수  등 사전 준비 필요', '38', '10', '0', '2020-02-16 09:48:03', '2025-05-19 06:21:03', '300');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('65', '0', 'T_CRS_MNG0000005181', 'T_ROUTE_MNG0000000001', '남파랑길 64코스', '12.0', '240.0', '1', '0', '- 보성군에서 고흥군으로 이어지는 구간으로 전형적인 해안가의 농촌 지역을 지나는 코스', '- 보성군에서 고흥군으로 이어지는 구간으로 전형적인 해안가의 농촌 지역을 지나는 코스<br>- 걷기여행 중 전국 100대 명산으로 뽑히는 팔영산 및 백일도, 진지도 등을 조망하며 걷기여행을 즐길 수 있는 코스<br>- 다양한 농촌풍경을 보여주는 농로 구간을 지나며 작은 마을, 포구 등을 경유함에 따라 계절에 따라 다양한 풍경 감상이 가능한 구간<br>- 코스 주변에 경관이 아름답거나 문화재 등 관광 자원은 없으나 노선 주변의 농지와 마을 등에서 농촌의 삶과 생활상을 보고 느낄 수 있는 코스', '- 시점 : 남양면 망주리 팔영농협 망주지소(전남 고흥군 남양면 망월로 82)<br>교통편) 벌교버스터미널 또는 벌교역에서 벌교-부도 버스 이용하여 망주 하차. 배차간격 유의<br>- 종점 : 과역면 연등리 독대마을회관(전남 고흥군 과역면 과역로 1441)<br>교통편) 독대 정류장에서 과욕-백일, 과역-외호, 벌교-고흥 버스 이용하여 고흥과역버스터미널 하차. 배차간격 유의<br>- 중간에 편의점, 음수대 등이 없으므로 사전 충분한 준비를 요함', '38', '2', '0', '2020-02-16 09:52:10', '2025-05-19 06:22:08', '300');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('66', '0', 'T_CRS_MNG0000005182', 'T_ROUTE_MNG0000000001', '남파랑길 65코스', '25.0', '510.0', '1', '0', '- 고흥군과 연결되는 백일도, 원주도 등을 조망하며 걷는 구간으로 전형적인 농촌경관과 다도해 국립공원의 해안경관을 두루 감상할 수 있는 코스', '- 고흥군과 연결되는 백일도, 원주도 등을 조망하며 걷는 구간으로 전형적인 농촌경관과 다도해 국립공원의 해안경관을 두루 감상할 수 있는 코스<br>- 남해안의 복잡한 리아스식 해안 특성을 보유하고 있어 걷기여행시 다양한 풍경을 감상할 수 있는 코스<br>- 과역면 독대마을에서 시작해 영남면 간천마을까지 대부분 농로길을 걷는 코스', '- 시점 : 과역면 연등리 독대마을회관(전남 고흥군 과역면 과역로 1441)<br>교통편) 고흥공용터미널에서 고흥-벌교 버스 이용하여 과역버스터미널 정류장 하차 후 과역-백일 버스 환승 후 독대 정류장 하차. 배차간격 유의<br>- 종점 : 영남면 우천리 간천버스정류장(전남 고흥군 영남면 우천리 790-2)<br>교통편) 간천 정류장에서 과역-남열, 과역-용암, 과역-점암, 나명-과역 버스 이용하여 과역버스터미널 하차. 배차간격 유의<br>- 편의시설이 많지 않으므로 사전 충분한 준비가 필요하며 교통편이 용이하지 않으므로 숙박을 추천.<br>- 일부 마을구간에 안전성이 미흡한 구간이 있어 여행자의 주의가 요구', '38', '2', '0', '2020-02-16 10:02:36', '2025-05-19 06:23:20', '300');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('67', '0', 'T_CRS_MNG0000005183', 'T_ROUTE_MNG0000000001', '남파랑길 66코스', '11.0', '330.0', '3', '0', '\'- 고흥의 기 조성 걷기여행길인 ‘미르마루길’이 포함된 구간으로 용암전망대, 우암전망대, 천년오솔길, 우주발사전망대, 다랭이논 등 주요 자원을 두루 경유하는 매력적인 걷기여행길', '- 고흥의 기 조성 걷기여행길인 ‘미르마루길’이 포함된 구간으로 용암전망대, 우암전망대, 천년오솔길, 우주발사전망대, 다랭이논 등 주요 자원을 두루 경유하는 매력적인 걷기여행길<br>- 우미산 임도와 등산로로 안전성과 보행 쾌적성이 양호하며 다도해의 수려한 해안경관을 감상할 수 있는 코스', '- 시점 : 영남면 우천리 간천버스정류장(전남 고흥군 영남면 우천리 790-2)<br>교통편) 고흥공용터미널에서 고흥-영남 버스 이용하여 금사 정류장 하차. 과역-남열 버스 환승하여 간천 정류장 하차<br>- 종점 : 영남면 남열리 남열마을 입구(전남 고흥군 영남면 해맞이로 563)<br>교통편) 남열 정류장 고흥-영남, 과역-남열, 남열-고흥, 고흥-남열 등 버스 이용하여 고흥버스터미널 하차<br>- 등산로 구간이 길어 사전에 식수와 간식 충분한 준비 필요<br>- 우주발사전망대 관람시간 오전 9시 ~ 오후 6시, 휴관일 매주 월요일, 공휴일의 다음날, 설날, 추석 / 관람료 성인 기준 2,000원', '38', '2', '0', '2020-02-16 10:06:36', '2024-10-16 12:33:39', '900');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('68', '0', 'T_CRS_MNG0000005184', 'T_ROUTE_MNG0000000001', '남파랑길 67코스', '16.0', '300.0', '2', '0', ' 고흥 우주발사전망대에서 남열해수욕장, 지붕 없는 미술관 전망대 등 아름다운 해양경관을 감상하며 걷기여행의 매력을 느낄 수 있는 코스', '- 고흥 우주발사전망대에서 남열해수욕장, 지붕 없는 미술관 전망대 등 아름다운 해양경관을 감상하며 걷기여행의 매력을 느낄 수 있는 코스<br>- ‘고흥 마중길’이 일부 포함되어 있으며, 전체 구간이 안전성을 확보하고 있어 누구나 편하게 걷기여행을 즐길 수 있는 구간', '- 시점 : 영남면 남열리 남열마을 입구(전남 고흥군 영남면 해맞이로 563)<br>교통편) 고흥공용터미널에서 시내버스 고흥-남열 이용하여 남열 정류장 하차. 배차간격 유의<br>- 종점 : 포두면 오취리 해창만탬핑장 앞(전남 고흥군 포두면 송산리 1638-1)<br>교통편) 능정 정류장에서 고흥-남열, 고흥-양사, 고흥-영남 버스 등 이용하여 고흥공용버스터미널 하차<br>- 대중교통 이용 시 배차시간이 길어 운행시간 사전 확인 필수', '38', '2', '0', '2020-02-16 10:09:50', '2024-10-16 12:35:01', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('69', '0', 'T_CRS_MNG0000005185', 'T_ROUTE_MNG0000000001', '남파랑길 68코스', '21.0', '420.0', '2', '0', ' 해창만 간척지 공원에서 시작하여 나로도를 조망하며 해안도로를 경유하는 구간으로 수려한 해안경관을 감상할 수 있는 코스', '- 해창만 간척지 공원에서 시작하여 나로도를 조망하며 해안도로를 경유하는 구간으로 수려한 해안경관을 감상할 수 있는 코스', '- 시점 : 포두면 오취리 해창만캠핑장 앞(전남 고흥군 포두면 송산리 1638-1)<br>교통편) 고흥공용터미널에서 고흥-남열 버스 이용하여 능정 정류장 하차<br>- 종점 : 도화면 당오리 도화버스터미널(전남 고흥군 도화면 중심길 9)<br>교통편) 도화공용시외버스터미널에서 버스 이용<br>- 대중교통 이용 시 배차시간이 길어 운행시간 사전 확인 필수<br>- 식수대가 따로 없으니 사전 준비<br>- 해창만 간척지 공원에서 나로도로 연결되는 구간이 갓길이 없는 국도 15호선 차도 구간으로 연결되어 걷기여행시 차량이용 및 걷기여행자의 주의가 필요', '38', '2', '0', '2020-02-16 10:12:35', '2024-10-16 12:36:07', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('70', '0', 'T_CRS_MNG0000005186', 'T_ROUTE_MNG0000000001', '남파랑길 69코스', '16.0', '300.0', '2', '0', ' 고흥군의 천등산 일원을 통과하는 구간으로 기 조성 걷기여행길인 ‘싸목싸목길’ 및 ‘천등산 먼나무길’ 등이 일부 포함된 코스', '- 고흥군의 천등산 일원을 통과하는 구간으로 기 조성 걷기여행길인 ‘싸목싸목길’ 및 ‘천등산 먼나무길’ 등이 일부 포함된 코스<br>- 천등산에서 바라보는 해안경관이 일품으로 아름다운 숲길을 따라 쾌적하게 걷기여행을 즐길 수 있는 코스<br>- 안전성과 쾌적성이 양호하며 노선의 대부분인 천등산 임도 구간은 경사가 심하지 않아 편안하게 걸을 수 있는 구간', '- 시점 : 도화면 당오리 도화버스터미널(전남 고흥군 도화면 중심길 9)<br>교통편) 도화시외버스터미널 이용<br>- 종점 : 풍양면 매곡리 백석버스정류장(전남 고흥군 풍양면 백석길 19)<br>교통편) 동적 정류장에서 고흥-풍양 버스 이용하여 고흥공용버스터미널 하차. 배차간격 유의<br>- 편의시설이 많지 않으므로 사전에 충분한 준비가 필요함', '38', '2', '0', '2020-02-16 10:15:36', '2024-10-16 12:37:21', '600');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('71', '0', 'T_CRS_MNG0000005187', 'T_ROUTE_MNG0000000001', '남파랑길 70코스', '13.0', '270.0', '1', '0', '해안을 따라 거금도와 소록도 일대를 조망하며 걷는 구간으로 녹동항까지 연결되는 코스', '- 해안을 따라 거금도와 소록도 일대를 조망하며 걷는 구간으로 녹동항까지 연결되는 코스<br>- 한센인 추모공원, 녹동항 일원의 주요 관광자원을 경유할 수 있으며 한적하면서도 활기찬 어촌 풍경을 감상할 수 있음<br>- 전체적으로 노선 안전성을 확보하고 있으며 전형적인 남해안 어촌풍경을 감상할 수 있는 구간', '- 시점 : 풍양면 매곡리 백석버스정류장(전남 고흥군 풍양면 백석길 19)<br>교통편) 고흥공용터미널에서 고흥-풍양 버스 이용하여 백석 정류장 하차<br>- 종점 : 도양읍 봉암리 녹동버스공용정류장(전남 고흥군 도양읍 천마로 57)<br>교통편) 녹동버스공용정류장 이용<br>- 대중교통 이용 시 배차간격이 길어 운행시간 사전 확인 필수', '38', '2', '0', '2020-02-16 10:18:22', '2024-10-16 12:38:23', '300');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('72', '0', 'T_CRS_MNG0000005188', 'T_ROUTE_MNG0000000001', '남파랑길 71코스', '22.0', '420.0', '1', '0', '녹동항에서 고흥만 방조제공원까지 이어지는 구간으로 구간은 길지만 평이한 코스로 구성', '- 녹동항에서 고흥만 방조제공원까지 이어지는 구간으로 구간은 길지만 평이한 코스<br>- 유명 관광자원은 분포하고 있지 않으나, 해안가의 작은 마을을 경유하며 안전하게 걷기여행을 즐길 수 있는 코스', '- 시점 : 도양읍 봉암리 녹동버스공용정류장(전남 고흥군 도양읍 천마로 57)<br>교통편) 고흥공용터미널에서 고흥-녹동 버스 이용하여 녹독버스공용터미널 하차<br>- 종점 : 도덕면 용동리 고흥만방조제공원(전남 고흥군 도덕면 고흥만로 1140)<br>교통편) 금호 정류장에서 고흥-녹동, 녹동-고흥 등 버스 이용하여 고흥공용버스터미널 하차<br>- 코스가 길고 아스팔트 도로가 많으므로 준비 필요<br>- 버스 이용 시 배차간격이 길어 운행시간 사전 확인 필수', '38', '2', '0', '2020-02-16 10:20:53', '2024-10-16 12:39:31', '300');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('73', '0', 'T_CRS_MNG0000005189', 'T_ROUTE_MNG0000000001', '남파랑길 72코스', '15.0', '300.0', '1', '0', ' 고흥쪽에서 보성 등 내륙부를 조망하며 걷는 구간으로 해안길, 숲길, 마을길 등이 반복되어 다양한 경관을 감상할 수 있는 코스', '- 고흥쪽에서 보성 등 내륙부를 조망하며 걷는 구간으로 해안길, 숲길, 마을길 등이 반복되어 다양한 경관을 감상할 수 있는 코스<br>- 작은 해수욕장 및 어촌체험마을 등이 입지하고 있어 휴식을 취하며 걷기여행을 즐길 수 있는 구간', '- 시점 : 도덕면 용동리 고흥만방조제공원(전남 고흥군 도덕면 고흥만로 1140)<br>교통편) 고흥공용터미널에서 고흥-녹동 버스 이용 후 금호 정류장 하차<br>- 종점 : 두원면 대전리 대전해수욕장(전남 고흥군 두원면 두원로 1305)<br>교통편) 대전해수욕장 정류장에서 고흥-두원 버스 이용하여 고흥공용버스터미널 하차<br>- 버스 이용 시 배차간격이 길어 운행시간 사전 확인 필수', '38', '2', '0', '2020-02-16 10:25:01', '2024-10-16 12:41:05', '300');
INSERT INTO `courses` (`id`, `theme_id`, `idx`, `route_idx`, `name`, `distance`, `req_time`, `level`, `cycle`, `contents`, `summary`, `traveler_info`, `sido_code`, `sigungu_code`, `brd_div`, `created_at`, `modified_at`, `reward`) VALUES ('74', '0', 'T_CRS_MNG0000005190', 'T_ROUTE_MNG0000000001', '남파랑길 73코스', '17.0', '360.0', '1', '0', '대전해수욕장에서 내로마을까지 이어지는 구간으로 해안경관과 내륙경관을 동시에 조망할 수 있는 코스', '- 대전해수욕장에서 내로마을까지 이어지는 구간으로 해안경관과 내륙경관을 동시에 조망할 수 있는 코스<br>- 걷기여행 안전성을 확보하고 있으며, 고흥 해안을 따라 형성된 다양한 마을을 지나며 해안 어촌마을의 풍경을 감상하며 걷기여행을 즐길 수 있는 구간', '- 시점 : 두원면 대전리 대전해수욕장(전남 고흥군 두원면 두원로 1305)<br>교통편) 고흥공용터미널에서 고흥-두원 버스 이용 후 대전해수욕장 정류장 하차. 배차간격 유의<br>- 종점 : 과역면 노일리 내로마을회관 앞(전남 고흥군 과역면 내로당날길 3)<br>교통편) 내로 정류장에서 과역-도야 버스 이용하여 과역버스터미널 하차. 배차간격 유의<br>- 편의시설이 많지 않으므로 사전 준비 필수', '38', '2', '0', '2020-02-16 10:27:49', '2024-10-16 12:42:14', '300');
